/*Data for the table `resource` */

insert  into `resource`(`id`,`title`,`alias`,`parent`,`content_type`,`long_title`,`description`,`intro`,`content`,`menu_title`,`link_attr`,`external_link`,`hide_from_menu`,`image`,`icon`,`show_home`,`is_default`,`is_hot`,`deleted_status`,`menu_index`,`show_gallery`,`created_at`,`updated_at`,`published`) values (2,'Trung Dung Media','ve-trung-dung-media',0,'introduction','Về tâm điểm, Cty Tâm Điểm, Tâm Điểm, Tâm Điểm Communications, Quảng Cáo Tâm Điểm, Cty TNHH Tâm Điểm','Về tâm điểm, Tâm Điểm Communications','<p>Được th&agrave;nh lập v&agrave;o năm 2002, T&acirc;m Điểm l&agrave; c&ocirc;ng ty truyền th&ocirc;ng uy t&iacute;n h&agrave;ng đầu của Việt Nam, được b&igrave;nh chọn v&agrave;o TOP 500 doanh nghiệp lớn nhất v&agrave; c&oacute; tốc độ ph&aacute;t triển nhanh nhất năm 2010. Ch&uacute;ng t&ocirc;i cung cấp giải ph&aacute;p to&agrave;n diện về truyền th&ocirc;ng tiếp thị cho c&aacute;c Doanh nghiệp trong nước v&agrave; quốc tế.</p>\r\n<p>Ch&uacute;ng t&ocirc;i tự h&agrave;o khi l&agrave; đơn vị đi ti&ecirc;n phong v&agrave; định ra những khuynh hướng mới về truyền th&ocirc;ng tại Việt Nam, trong đ&oacute; phải kể đến sự th&agrave;nh c&ocirc;ng của chuỗi chương tr&igrave;nh &ldquo;Dự b&aacute;o thời tiết&rdquo; theo nhiều chủ đề được ph&aacute;t s&oacute;ng tr&ecirc;n hệ thống c&aacute;c k&ecirc;nh truyền h&igrave;nh cả nước: Thời tiết với Cuộc sống, Đ&ocirc; thị, Du lịch, Biển, N&ocirc;ng vụ...</p>','<p><strong><img style=\"float: left; margin-right: 20px;\" src=\"/tinymce/source/010.JPG\" alt=\"\" width=\"305\" height=\"229\" /></strong></p>\r\n<p><strong>GIỚI THIỆU CHUNG<br /></strong></p>\r\n<p style=\"text-align: justify;\">Được th&agrave;nh lập v&agrave;o năm 2002, T&acirc;m Điểm l&agrave; c&ocirc;ng ty truyền th&ocirc;ng uy t&iacute;n h&agrave;ng đầu của Việt Nam, được b&igrave;nh chọn v&agrave;o TOP 500 doanh nghiệp lớn nhất v&agrave; c&oacute; tốc độ ph&aacute;t triển nhanh nhất năm 2010. Ch&uacute;ng t&ocirc;i cung cấp giải ph&aacute;p to&agrave;n diện về truyền th&ocirc;ng tiếp thị cho c&aacute;c Doanh nghiệp trong nước v&agrave; quốc tế.</p>\r\n<p style=\"text-align: justify;\">Ch&uacute;ng t&ocirc;i tự h&agrave;o khi l&agrave; đơn vị đi ti&ecirc;n phong v&agrave; định ra những khuynh hướng mới về truyền th&ocirc;ng tại Việt Nam, trong đ&oacute; phải kể đến sự th&agrave;nh c&ocirc;ng của chuỗi chương tr&igrave;nh &ldquo;Dự b&aacute;o thời tiết&rdquo; theo nhiều chủ đề được ph&aacute;t s&oacute;ng tr&ecirc;n hệ thống c&aacute;c k&ecirc;nh truyền h&igrave;nh cả nước: Thời tiết với Cuộc sống, Đ&ocirc; thị, Du lịch, Biển, N&ocirc;ng vụ.</p>\r\n<p style=\"text-align: justify;\">B&ecirc;n cạnh đ&oacute;, với ti&ecirc;u ch&iacute; thực hiện &ldquo;T&acirc;m Điểm hướng về cộng đồng&rdquo;, thời gian qua, ch&uacute;ng t&ocirc;i lu&ocirc;n đồng h&agrave;nh, s&aacute;t c&aacute;nh c&ugrave;ng c&aacute;c chương tr&igrave;nh truyền h&igrave;nh x&atilde; hội: &ldquo;Hoa cuộc sống&rdquo;, &ldquo;Nhịp đập th&agrave;nh phố trẻ&rdquo;, &ldquo;L&agrave;ng biển Việt Nam&rdquo;, &ldquo;Chuyến xe nh&acirc;n &aacute;i&rdquo;, &ldquo;Vượt qua thử th&aacute;ch&rdquo;; &ldquo;Chuyện h&ocirc;m nay&rdquo;, &ldquo;Tiếp sức người thầy&rdquo;,&hellip;</p>\r\n<p style=\"text-align: justify;\">Ch&uacute;ng t&ocirc;i cam kết mang lại những sản phẩm dịch vụ chất lượng, hiệu quả, ch&iacute;nh s&aacute;ch gi&aacute; ưu đ&atilde;i. Với đội ngũ c&aacute;n bộ nh&acirc;n vi&ecirc;n chuy&ecirc;n nghiệp, năng động, s&aacute;ng tạo, ch&uacute;ng t&ocirc;i tin tưởng sẽ l&agrave;m h&agrave;i l&ograve;ng Qu&yacute; kh&aacute;ch h&agrave;ng khi đến với T&acirc;m Điểm.</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','Về TrungDung Media','','',0,'','',1,1,0,0,0,0,1419448607,1457218298,1),(13,'Tôn chỉ hoạt động','ton-chi-hoat-dong',0,'introduction','Tôn chỉ hoạt động, Về tâm điểm, Cty Tâm Điểm, Tâm Điểm, Tâm Điểm Communications, Quảng Cáo Tâm Điểm, Cty TNHH Tâm Điểm','Tôn chỉ hoạt động','<p>T&ocirc;n chỉ hoạt động</p>','<p><img src=\"/tinymce/source/Hinh up bai/tam diem_he-thong-ton-chi.jpg\" alt=\"\" width=\"437\" height=\"283\" /></p>\n<p style=\"text-align: justify;\"><strong>HỆ THỐNG T&Ocirc;N CHỈ CỦA C&Ocirc;NG TY T&Acirc;M ĐIỂM &ndash; GIAI ĐOẠN 2014 - 2018</strong></p>\n<p style=\"text-align: justify;\"><strong><span style=\"text-decoration: underline;\">TẦM NH&Igrave;N</span></strong>:</p>\n<p style=\"text-align: justify;\">Khẳng định vị thế uy t&iacute;n của T&acirc;m Điểm trong việc cung cấp giải ph&aacute;p to&agrave;n diện về truyền th&ocirc;ng tiếp thị, dẫn đầu (Top 5) về sản xuất chương tr&igrave;nh v&agrave; quảng c&aacute;o truyền h&igrave;nh tại Việt Nam.</p>\n<p style=\"text-align: justify;\"><strong><span style=\"text-decoration: underline;\">SỨ MỆNH:</span></strong></p>\n<p style=\"text-align: justify;\">Tạo ra những giải ph&aacute;p tối ưu đem lại gi&aacute; trị cao nhất cho kh&aacute;ch h&agrave;ng v&agrave; CBNV c&ocirc;ng ty, đ&oacute;ng g&oacute;p t&iacute;ch cực cho sự ph&aacute;t triển chung của cộng đồng.</p>\n<p style=\"text-align: justify;\"><strong><span style=\"text-decoration: underline;\">GI&Aacute; TRỊ CỐT L&Otilde;I:</span></strong></p>\n<p style=\"text-align: justify;\"><strong>Kh&aacute;ch h&agrave;ng l&agrave; trọng t&acirc;m</strong></p>\n<ul style=\"text-align: justify;\">\n<li>Thấu hiểu nhu cầu, nguyện vọng của kh&aacute;ch h&agrave;ng</li>\n<li>Mang lại giải ph&aacute;p tối ưu v&agrave; ph&ugrave; hợp</li>\n<li>Thoả m&atilde;n kh&aacute;ch h&agrave;ng - động lực l&agrave;m việc của CBNV</li>\n</ul>\n<p style=\"text-align: justify;\"><strong>Chuy&ecirc;n nghiệp</strong></p>\n<ul style=\"text-align: justify;\">\n<li>Chuy&ecirc;n m&ocirc;n nghiệp vụ vững v&agrave;ng</li>\n<li>Thao t&aacute;c chuẩn x&aacute;c: đảm bảo chất lượng, tiến độ cam kết</li>\n<li>Văn ho&aacute; ứng xử chuẩn mực.</li>\n</ul>\n<p style=\"text-align: justify;\"><strong>S&aacute;ng tạo</strong></p>\n<ul style=\"text-align: justify;\">\n<li>Ti&ecirc;n phong về &yacute; tưởng thực hiện</li>\n<li>Sản phẩm, dịch vụ kh&aacute;c biệt</li>\n<li>Li&ecirc;n tục cải tiến, đổi mới, ph&aacute;t triển</li>\n</ul>\n<p style=\"text-align: justify;\"><strong>Đối t&aacute;c chiến lược của c&ocirc;ng ty</strong>: Đ&agrave;i truyền h&igrave;nh Việt Nam, Đ&agrave;i truyền h&igrave;nh Tp.HCM, Đ&agrave;i truyền h&igrave;nh Vĩnh Long v&agrave; c&aacute;c đối t&aacute;c kh&aacute;c thuộc hệ thống ph&aacute;t thanh &ndash; truyền h&igrave;nh tr&ecirc;n cả nước</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><strong>SLOGAN</strong></p>\n<p style=\"text-align: justify;\">T&acirc;m Điểm&ndash;Giải ph&aacute;p truyền th&ocirc;ng gi&uacute;p Doanh nghiệp tỏa s&aacute;ng</p>','Tôn chỉ hoạt động','','',0,'','',0,0,0,0,0,0,1419495014,1421286930,1),(14,'Tin tổng hợp','tin-tong-hop',0,'cate_news','Tin tổng hợp','Tin tổng hợp','','','Tin tổng hợp','','',0,'','',0,0,0,0,2,0,1419561267,1420481294,1),(15,'Sự kiện','su-kien',0,'cate_news','Sự kiện','Sự kiện','Sự kiện','','Sự kiện','','',0,'','',0,0,0,0,1,0,1419561428,1420481277,1),(16,'Chương trình đặc biệt','chuong-trinh-dac-biet',0,'cate_news','Chương trình đặc biệt','Chương trình đặc biệt','','','Chương trình đặc biệt','','',0,'','',0,0,0,0,3,0,1419561460,1420469902,1),(18,'Game Show Vượt Qua Thử Thách ','game-show-vuot-qua-thu-thach-',16,'news_item','',' Truyền hình thực tế kết hợp với Gameshow dành cho những hộ gia đình có hoàn cảnh khó khăn trên địa bàn tỉnh Vĩnh Long. ',' Truyền hình thực tế kết hợp với Gameshow dành cho những hộ gia đình có hoàn cảnh khó khăn trên địa bàn tỉnh Vĩnh Long. ','<p style=\"text-align: justify;\"><strong>VƯỢT QUA THỬ TH&Aacute;CH</strong></p>\n<p style=\"text-align: justify;\"><strong><img alt=\"\" /><img src=\"/tinymce/source/Hinh up bai/Vuot Qua Thu Thach VL1.JPG\" alt=\"\" width=\"416\" height=\"312\" /></strong></p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh x&acirc;y dựng theo dạng Truyền h&igrave;nh thực tế kết hợp với Gameshow d&agrave;nh cho những hộ gia đ&igrave;nh c&oacute; ho&agrave;n cảnh kh&oacute; khăn tr&ecirc;n địa b&agrave;n tỉnh Vĩnh Long. Th&ocirc;ng qua chương tr&igrave;nh c&aacute;c hộ gia đ&igrave;nh n&agrave;y sẽ được nhận một số vốn hỗ trợ.</p>\n<p style=\"text-align: justify;\">Mỗi chương tr&igrave;nh gồm ph&oacute;ng sự ngắn giới thiệu về ho&agrave;n cảnh kh&oacute; khăn của 02 hộ gia đ&igrave;nh, phần tranh t&agrave;i của 02 hộ gia đ&igrave;nh v&agrave; phần r&uacute;t thăm may mắn.</p>\n<p style=\"text-align: justify;\">Ngo&agrave;i ra, mỗi kh&aacute;n giả đến dự đều được ph&aacute;t một l&aacute; phiếu r&uacute;t thăm may mắn. - Đại diện kh&aacute;n giả tại hiện trường sẽ lần lượt r&uacute;t 04 l&aacute; phiếu để chọn ra 04 kh&aacute;n giả may mắn. - 04 kh&aacute;n giả n&agrave;y sẽ l&ecirc;n s&acirc;n khấu để nhận phần qu&agrave; may mắn của m&igrave;nh (trị gi&aacute; mỗi phần qu&agrave; l&agrave; 1 triệu đồng).</p>\n<p style=\"text-align: justify;\">K&ecirc;nh ph&aacute;t s&oacute;ng : THVL1</p>\n<p style=\"text-align: justify;\">Thời lượng: 25 &ndash; 30 ph&uacute;t/chương tr&igrave;nh</p>\n<p style=\"text-align: justify;\">Thời gian ph&aacute;t s&oacute;ng: 2012-2013</p>','Game Show Vượt Qua Thử Thách ','','',0,'http://tamdiem.com.vn/sources/images/v%C6%B0%E1%BB%A3t-qua-th%E1%BB%AD-th%C3%A1ch.jpg','',1,0,0,0,200,0,1419565913,1440753300,1),(20,'Sản xuất chương trình','san-xuat-chuong-trinh',0,'cate_service','','','Ekip SXCT có nhiều năm kinh nghiệm cộng tác cùng các ban chương trình của các Đài truyền hình cả nước, tham gia sản xuất nhiều chương trình thuộc nhiều thể loại khác nhau phát sóng trên các kênh truyền hình từ bắc chí nam.','<p>Ekip SXCT c&oacute; nhiều năm kinh nghiệm cộng t&aacute;c c&ugrave;ng c&aacute;c ban chương tr&igrave;nh của c&aacute;c Đ&agrave;i truyền h&igrave;nh cả nước, tham gia sản xuất nhiều chương tr&igrave;nh thuộc nhiều thể loại kh&aacute;c nhau ph&aacute;t s&oacute;ng tr&ecirc;n c&aacute;c k&ecirc;nh truyền h&igrave;nh từ bắc ch&iacute; nam.</p>\r\n<p>Ph&ograve;ng SXCT của T&acirc;m Điểm l&agrave; đơn vị ti&ecirc;n phong nhiều &yacute; tưởng chương tr&igrave;nh mới như đưa người dẫn chương tr&igrave;nh truyền h&igrave;nh (MC) v&agrave;o bản tin thời tiết từ đầu những năm 2000; ph&aacute;t triển vị tr&iacute; v&agrave; vai tr&ograve; của nghề MC th&ocirc;ng qua c&aacute;c cuộc thi Người dẫn chương tr&igrave;nh truyền h&igrave;nh; x&acirc;y dựng v&agrave; ph&aacute;t triển phần mềm &ldquo;Thời tiết Việt&rdquo; phục vụ việc sản xuất c&aacute;c bản tin thời tiết tại Việt Nam; thực hiện c&aacute;c ph&oacute;ng sự/ k&yacute; sự truyền h&igrave;nh d&agrave;i tập kh&aacute;m ph&aacute; v&agrave; t&ocirc;n vinh vẻ đẹp đất nước con người Việt Nam;&hellip;</p>\r\n<p>Ph&ograve;ng SXCT của T&acirc;m Điểm sẵn s&agrave;ng hợp t&aacute;c c&ugrave;ng Qu&yacute; kh&aacute;ch h&agrave;ng x&acirc;y dựng v&agrave; ph&aacute;t triển c&aacute;c chương tr&igrave;nh mới, ph&ugrave; hợp với nhu cầu v&agrave; h&igrave;nh ảnh m&agrave; Qu&yacute; kh&aacute;ch h&agrave;ng muốn hướng tới, đồng thời mang đến những chương tr&igrave;nh hay v&agrave; &yacute; nghĩa cho cộng đồng.</p>','Sản xuất chương trình','','',0,'http://tamdiem.com.vn/sources/images/s%E1%BA%A3n-xu%E1%BA%A5t-ch%C6%B0%C6%A1ng-tr%C3%ACnh--icon.png','http://tamdiem.com.vn/sources/icons/s%E1%BA%A3n-xu%E1%BA%A5t-ch%C6%B0%C6%A1ng-tr%C3%ACnh--icon.png',0,1,0,0,2,0,1419567269,1457233208,1),(21,'Thi công bảng quảng cáo ngoài trời, băng-rôn, LCD, frame','thi-cong-bang-quang-cao-ngoai-troi',0,'cate_service','','','','<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>','Thi công bảng quảng cáo ngoài trời','','',0,'http://tamdiem.home/sources/images/02.jpg','http://tamdiem.com.vn/sources/icons/thi-c%C3%B4ng-b%E1%BA%A3ng-qu%E1%BA%A3ng-c%C3%A1o.png',0,0,0,0,9,0,1419567365,1420466549,0),(22,'Dựng TVC - phim giới thiệu doanh nghiệp','dung-tvc-phim-gioi-thieu-doanh-nghiep',0,'cate_service','Dựng TVC, Sản xuất phim quảng cáo, dựng phim quảng cáo, phim quảng cáo, quảng cáo truyền hình, phim giới thiệu doanh nghiệp, phim tự giới thiệu, TVC 30s, TVC 15s, phóng sự','Dựng TVC, Sản xuất phim quảng cáo, dựng phim quảng cáo, phim quảng cáo, quảng cáo truyền hình, phim giới thiệu doanh nghiệp, phim tự giới thiệu, TVC 30s, TVC 15s, phóng sự','TVC Quảng cáo mang lại hiệu quả quảng cáo nhanh chóng và rộng rãi, có thể xuất hiện ở khắp mọi nơi từ công sở đến trường học, gia đình, khu vui chơi… ','<p style=\"text-align: justify;\"><strong>1. Sản xuất TVC Quảng c&aacute;o</strong></p>\r\n<p style=\"text-align: justify;\"><strong>T&acirc;m Điểm Communications</strong>&nbsp;cung cấp dịch vụ&nbsp;sản xuất phim quảng cao (<strong>TVC</strong>),&nbsp;phim&nbsp;giới thiệu doanh nghiệp,&nbsp;&hellip;</p>\r\n<p style=\"text-align: justify;\"><strong>2. T&igrave;m hiểu về TVC</strong></p>\r\n<p style=\"text-align: justify;\">TVC&nbsp;Quảng c&aacute;o&nbsp;mang lại hiệu quả quảng c&aacute;o nhanh ch&oacute;ng v&agrave; rộng r&atilde;i, c&oacute; thể xuất hiện ở khắp mọi nơi từ c&ocirc;ng sở đến trường học, gia đ&igrave;nh, khu vui chơi&hellip;</p>\r\n<p style=\"text-align: justify;\">Kh&ocirc;ng ai c&oacute; thể phủ nhận hiệu quả nhanh ch&oacute;ng v&agrave; rộng r&atilde;i của h&igrave;nh thức quảng c&aacute;o TVC. Nhưng nếu bi&ecirc;n tập&nbsp;TVC&nbsp;kh&ocirc;ng c&oacute; mục ti&ecirc;u r&otilde; r&agrave;ng th&igrave; khoản tiền để l&agrave;m TVC sẽ trở n&ecirc;n l&atilde;ng ph&iacute;.</p>\r\n<p style=\"text-align: justify;\">TVC&nbsp;L&agrave; thể loại phim ngắn th&ocirc;ng thường c&oacute; thời lượng 30s, 15s, 10s<br /> Kịch bản, h&igrave;nh ảnh, lời b&igrave;nh, &acirc;m thanh của phim&nbsp;TVC&nbsp;được c&aacute;c nh&agrave; chuy&ecirc;n m&ocirc;n nhắm đến l&agrave; phải s&uacute;c t&iacute;ch, c&ocirc; đọng, hấp dẫn v&agrave; đảm bảo đầy đủ c&aacute;c yếu tố của nghệ thuật điện ảnh.<br /> Tuy hiện nay đ&atilde; xuất hiện nhiều thể loại v&agrave; c&ocirc;ng cụ quảng c&aacute;o kh&aacute;c nhau, nhưng&nbsp;TVC&nbsp;vẫn khẳng định l&agrave; một thể loại &ldquo;đỉnh cao&rdquo; trong c&aacute;c thể loại&nbsp;quảng c&aacute;o.</p>\r\n<p style=\"text-align: justify;\"><strong>3. Quy tr&igrave;nh thực hiện sản xuất TVC:</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><strong>Tiền kỳ: Kh&acirc;u chuẩn bị đầu ti&ecirc;n trước khi sản xuất</strong></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li>L&ecirc;n &yacute; tưởng kịch bản chung: L&agrave; bước đầu ti&ecirc;n kh&acirc;u tiền kỳ nhưng đặc biệt quan trọng. Sự cuốn h&uacute;t người xem mang gi&aacute; trị của quảng c&aacute;o phụ thuộc ch&iacute;nh v&agrave;o bước &yacute; tưởng kịch bản.</li>\r\n<li>Kịch bản văn học: Bi&ecirc;n tập vi&ecirc;n c&ugrave;ng với đạo diễn viết th&ocirc;ng điệp muốn truyền tải bằng ng&ocirc;n từ chỉnh chu nhất.</li>\r\n<li>Kịch bản h&igrave;nh ảnh: Sau khi đ&atilde; thống nhất về kịch bản văn học v&agrave; &yacute; tưởng kịch bản, họa sỹ vẽ c&aacute;ch thể hiện bằng h&igrave;nh ảnh của diễn vi&ecirc;n v&agrave; thoại của cảnh quay.</li>\r\n<li>L&ecirc;n kế hoạch sản xuất: Sau khi xong kh&acirc;u kịch bản, đạo diễn sẽ t&igrave;m diễn vi&ecirc;n, bối cảnh quay ph&ugrave; hợp với kịch bản\r\n<ul>\r\n<li><strong>Tổ chức sản xuất.</strong></li>\r\n</ul>\r\n</li>\r\n<li>Đạo diễn sẽ chỉ đạo: diễn vi&ecirc;n, &aacute;nh s&aacute;ng, quay phim, đạo cụ.... &nbsp;diễn xuất theo đ&uacute;ng y&ecirc;u cầu kịch bản\r\n<ul>\r\n<li><strong>Hậu kỳ:</strong></li>\r\n</ul>\r\n</li>\r\n<li>L&agrave; kh&acirc;u cối c&ugrave;ng của quy tr&igrave;nh sản xuất TVC</li>\r\n<li>Thực hiện dựng phim, kỹ xảo h&igrave;nh ảnh, &acirc;m thanh&hellip;\r\n<ul>\r\n<li><strong>Ph&aacute;t h&agrave;nh:</strong></li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">Đăng k&yacute; ph&aacute;t h&agrave;nh TVC với cơ quan chức năng v&agrave; truyền th&ocirc;ng rộng r&atilde;i tr&ecirc;n c&aacute;c phương tiện truyền h&igrave;nh.</p>\r\n<p style=\"text-align: justify;\"><strong>4. Đơn vị sản xuất TVC chuy&ecirc;n nghiệp</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li>&nbsp;<strong>T&acirc;m Điểm Communications</strong> đ&atilde; v&agrave; đang l&agrave; đơn vị tin cậy của c&aacute;c tổ chức, doanh nghiệp trong nước v&agrave; quốc tế về sản xuất TVC v&agrave; phim giới thiệu Doanh Nghiệp...</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','Dựng TVC - phim giới thiệu doanh nghiệp','','',0,'http://tamdiem.com.vn/sources/images/TVC-quang-cao%20copy.jpg','http://tamdiem.com.vn/sources/icons/d%E1%BB%B1ng-phim---icon.png',0,0,0,0,7,0,1419567585,1457216671,1),(24,'1. Thế mạnh của Tâm Điểm ở khu vực nào và các đài truyền hình nào?','question-2',0,'faq','','','','<p style=\"text-align: justify;\">Với phương ch&acirc;m mang đến cho kh&aacute;ch h&agrave;ng giải ph&aacute;p truyền th&ocirc;ng to&agrave;n diện, T&acirc;m Điểm kh&ocirc;ng ngừng ph&aacute;t triển mạng lưới hợp t&aacute;c với c&aacute;c Đ&agrave;i truyền h&igrave;nh l&otilde;i tr&ecirc;n c&aacute;c thị trường lớn cũng như c&aacute;c đ&agrave;i địa phương. Ch&uacute;ng t&ocirc;i sẽ tư vấn cho Qu&yacute; Kh&aacute;ch h&agrave;ng việc chọn lựa k&ecirc;nh truyền h&igrave;nh hiệu quả về chi ph&iacute; &amp; tiếp cận đ&uacute;ng kh&aacute;n giả mục ti&ecirc;u.</p>','Question 2','','',0,'','',0,0,0,0,0,0,1419568559,1419960696,1),(28,'Khách hàng nói về chúng tôi','khach-hang-noi-ve-chung-toi',0,'introduction','Khách hàng nói về chúng tôi','Khách hàng nói về chúng tôi','','','Khách hàng nói về chúng tôi','','http://tamdiem.com.vn/client-say.html',0,'','',0,0,0,0,0,0,1419665876,1420075857,0),(30,'Xu hướng quảng cáo trên thế giới năm 2015','xu-huong-quang-cao-tren-the-gioi-nam-2015',14,'news_item','Cục Kiểm ngư hoàn thành tốt nhiệm vụ năm 2014','Ngày nay hầu hết các mẫu quảng cáo hiện đại đang có những bước chuyển mình khác biệt so với quảng cáo truyền thống trước kia. 10 thông tin dưới đây sẽ giúp bạn biết được các nhà quảng cáo trên thế giới đang đi theo xu hướng nào:','Ngày nay hầu hết các mẫu quảng cáo hiện đại đang có những bước chuyển mình khác biệt so với quảng cáo truyền thống trước kia. 10 thông tin dưới đây sẽ giúp bạn biết được các nhà quảng cáo trên thế giới đang đi theo xu hướng nào:','<p style=\"text-align: justify;\">Ng&agrave;y nay hầu hết c&aacute;c mẫu quảng c&aacute;o hiện đại đang c&oacute; những bước chuyển m&igrave;nh kh&aacute;c biệt so với quảng c&aacute;o truyền thống trước kia. 10 th&ocirc;ng tin dưới đ&acirc;y sẽ gi&uacute;p bạn biết được c&aacute;c nh&agrave; quảng c&aacute;o tr&ecirc;n thế giới đang đi theo xu hướng n&agrave;o:<br /> <br />1. H&atilde;ng xe hơi Acura đang ph&aacute;t động một chiến dịch quảng c&aacute;o mới trong đ&oacute; kh&ocirc;ng cần tới truyền h&igrave;nh hay TV. Thay v&agrave;o đ&oacute;, c&ocirc;ng ty n&agrave;y sẽ chi tiền v&agrave;o quảng c&aacute;o online v&agrave; những video kỹ thuật số. Trong đ&oacute; 1/3 ng&acirc;n s&aacute;ch sẽ gi&agrave;nh cho quảng c&aacute;o kỹ thuật số.</p>\n<p style=\"text-align: justify;\">2. Nhiều c&ocirc;ng ty nổi tiếng, như Universal Picture hay Vegas.com bắt đầu sử dụng một ứng dụng tr&ecirc;n smartphone c&oacute; t&ecirc;n Anomo để quảng c&aacute;o. Với Anomo, c&aacute;c thương hiệu kh&ocirc;ng hướng tới kh&aacute;ch h&agrave;ng mục ti&ecirc;u cụ thể bởi tất cả tr&ecirc;n đ&oacute; đều hoạt động ẩn danh.</p>\n<p style=\"text-align: justify;\">3. 100 c&ocirc;ng ty B2B lớn nhất thế giới đ&atilde; chi gần 5 tỉ USD v&agrave;o quảng c&aacute;o trong năm 2013, theo số liệu của Ad Age. Trong đ&oacute;, khu vực tăng trưởng nhanh nhất l&agrave; quảng c&aacute;o online.</p>\n<p style=\"text-align: justify;\">4. Virool, c&ocirc;ng ty chuy&ecirc;n hỗ trợ tăng lượng view cho viral video tr&ecirc;n Youtube vừa ph&aacute;t h&agrave;nh ActivView, một hệ thống nhiều nền tảng mới cho c&aacute; nh&agrave; quảng c&aacute;o ph&aacute;t t&aacute;n video của m&igrave;nh. 3 sản phẩm mới bao gồm ActivStream, AcitvPage, v&agrave; ActivSocial</p>\n<p style=\"text-align: justify;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Hinh up bai/ad-pep.jpg\" alt=\"\" width=\"259\" height=\"362\" /><br />5. C&ocirc;ng ty chuy&ecirc;n đo lường hiệu quả truyền th&ocirc;ng Rentrak đặt cược v&agrave;o tương lai của truyền h&igrave;nh x&aacute;c định (Addressable TV), truyền tải một quảng c&aacute;o cụ thể tới đối tượng x&aacute;c định.</p>\n<p style=\"text-align: justify;\">6. Facebook đ&atilde; cấm c&aacute;c thương hiệu bắt người d&ugrave;ng phải ủng hộ page để đổi lấy c&aacute;c đặc quyền, kể cả đặc quyền được truy cập v&agrave;o những nội dung đặc biệt.</p>\n<p style=\"text-align: justify;\">7. Nhiều c&ocirc;ng ty truyền th&ocirc;ng cổ điển đ&atilde; trong qu&yacute; kinh doanh tệ nhất, khi quảng c&aacute;o tr&ecirc;n TV chỉ tăng trưởng 0.2%. Tờ Wall Street Journal nhận định quảng c&aacute;o tr&ecirc;n mạng x&atilde; hội đang gi&uacute;p c&aacute;c nh&agrave; quảng c&aacute;o tiết kiệm được nhiều chi ph&iacute;.</p>\n<p style=\"text-align: justify;\">8. HP vừa kết hợp với Vine trong quảng c&aacute;o mới nhất. Quảng c&aacute;o d&agrave;i 30 gi&acirc;y dưới đ&acirc;y được thực hiện dưới dạng Video lặp (looping video).</p>\n<p style=\"text-align: justify;\">9. Gi&aacute;m đốc quản l&yacute; rủi ro của Marshable, Seth Rogin cho rằng n&ecirc;n bỏ đi kh&aacute;i niệm &ldquo;navtive advertising&rdquo; (quảng c&aacute;o tự nhi&ecirc;n), bởi c&oacute; qu&aacute; nhiều c&aacute;ch hiểu kh&aacute;c nhau về định nghĩa n&agrave;y. Thay v&agrave;o đ&oacute;, c&aacute;c nh&agrave; sản xuất n&ecirc;n sử dụng kh&aacute;i niệm &ldquo;Branded content&rdquo; (lồng gh&eacute;p thương hiệu v&agrave;o chương tr&igrave;nh).</p>\n<p style=\"text-align: justify;\">10. Một nghi&ecirc;n cứu của TubeMogul cho thấy c&aacute;ch tốt nhất dể tăng doanh thu cho c&ocirc;ng ty v&agrave; khiến nhiều người mua h&agrave;ng online hơn l&agrave; ph&aacute;t h&agrave;nh những quảng c&aacute;o c&oacute; khả năng hiển thị cao.</p>\n<p style=\"text-align: justify;\">Theo Tr&iacute; Thức Trẻ/BI</p>\n<div id=\"__if72ru4sdfsdfrkjahiuyi_once\" style=\"display: none;\">&nbsp;</div>\n<div id=\"__if72ru4sdfsdfruh7fewui_once\" style=\"display: none;\">&nbsp;</div>\n<div id=\"__hggasdgjhsagd_once\" style=\"display: none;\">&nbsp;</div>','Xu hướng quảng cáo trên thế giới năm 2015','','',1,'http://tamdiem.com.vn/sources/images/Xu%20h%C6%B0%E1%BB%9Bng%20th%E1%BA%BF%20gi%E1%BB%9Bi%202015.jpg','',0,0,0,0,500,0,1419673715,1453106595,1),(32,'Sáng tạo Logo và thiết lập quy chuẩn hệ thống nhận diện cho doanh nghiệp','sang-tao-logo-va-thiet-lap-quy-chuan-he-thong-nhan-dien-cho-doanh-nghiep',0,'cate_service','','','','<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>','Sáng tạo Logo và thiết lập quy chuẩn hệ thống ','','',0,'','http://tamdiem.com.vn/sources/icons/s%C3%A1ng-t%E1%BA%A1o-logo.png',0,0,0,0,10,0,1419740893,1420466557,0),(33,'Booking TVC - phim giới thiệu doanh nghiệp','booking-tvc-phim-gioi-thieu-doanh-nghiep',0,'cate_service','booking tvc, booking phim quảng cáo, đăng TVC, thời lượng sóng, kinh doanh thời lượng, bán thời lượng, dịch vụ truyền hình, ',' booking tvc, booking phim quảng cáo, đăng TVC, thời lượng sóng, kinh doanh thời lượng, bán thời lượng, dịch vụ truyền hình, dịch vụ quảng cáo truyền hình, Tâm Điểm, ','đơn vị chuyên booking quảng cáo và cung cấp các dịch vụ truyền thông, giữ vai trò là đại lý khai thác quảng cáo (TVC) cho nhiều đài truyền hình trong cả nước, có những chương trình sản xuất và liên kết, sẵn sàng phục vụ nhu cầu quảng cáo trên truyền hình cho mọi đối tượng khách hàng','<h4 style=\"text-align: justify;\"><strong>T&acirc;m Điểm Communications</strong> l&agrave; đơn vị chuy&ecirc;n booking quảng c&aacute;o v&agrave; cung cấp c&aacute;c dịch vụ truyền th&ocirc;ng, giữ vai tr&ograve; l&agrave; đại l&yacute; khai th&aacute;c quảng c&aacute;o (TVC) cho nhiều đ&agrave;i truyền h&igrave;nh trong cả nước, c&oacute; những chương tr&igrave;nh sản xuất v&agrave; li&ecirc;n kết, sẵn s&agrave;ng phục vụ nhu cầu quảng c&aacute;o tr&ecirc;n truyền h&igrave;nh cho mọi đối tượng kh&aacute;ch h&agrave;ng</h4>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h4>&nbsp;</h4>\r\n<h4>BẢNG GI&Aacute; C&Aacute;C Đ&Agrave;I TRUYỀN H&Igrave;NH</h4>\r\n<h4>(Cập nhật ng&agrave;y 26/3/2015)</h4>\r\n<h4><strong>VTV</strong></h4>\r\n<h4><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fjB0dXNGa2tvS0JHM0FnbjFyM1FGNkxzNnlIOThGWF9OQ1NZcEprRUtGajA&amp;authuser=0\" target=\"_blank\"><strong>HTV</strong></a></h4>\r\n<h4><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fnd4TkcwTnRydC04R3FkSnNnbVNGZDZvWERTdUZTdTBkX1VCelcyNDJIN0U&amp;authuser=0\" target=\"_blank\"><strong>KI&Ecirc;N GIANG&nbsp;</strong></a></h4>\r\n<h4><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fk5ua18xdHlkOEJxNDFVV29IYUV2QkhUYVBKTEVURUdXVjk1ZExINHVNNTA&amp;authuser=0\" target=\"_blank\"><strong>VĨNH LONG</strong></a></h4>\r\n<h4><strong><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fkU1cUZzQnRRQ1R4dEF0eWdxdk9yay1ieWFDZk5GQWR1QmxqNW8yR0E4QVE&amp;authuser=0\">CẦN THƠ 43</a></strong></h4>\r\n<h4><strong><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fmtBLWt1cDJwNzF5UnltdGE5UGFJeFduakFpR0lDTXluSk0tLWZMUXNPaGs&amp;authuser=0\">QUẢNG B&Igrave;NH</a></strong></h4>\r\n<h4><strong><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fi1fUnZQRlo0TjI2SmRSdHpJZW5ZMlRqNWw5Q2paZ2VubktDX1NhZWRLdTg&amp;authuser=0\">QUẢNG NAM</a></strong></h4>\r\n<h4><strong><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fmlQdWNRYmg2LU5uSWZ3LWs2SG1WeXdkQ1d0X0oyQ01lRlhFaU1aai1pRFU&amp;authuser=0\">BẮC NINH</a></strong></h4>\r\n<h4><strong><a href=\"https://drive.google.com/open?id=0B4uEhp7DCQj-fnNTbkM2Q0FaQUZDb3FSQ2pTb0pTYjdCb0x6aVVVcGxPUWp1RGd1NEtyM1k&amp;authuser=0\">TH&Aacute;I NGUY&Ecirc;N</a></strong></h4>','Booking TVC - phim giới thiệu doanh nghiệp','','',0,'http://tamdiem.com.vn/sources/images/Booking%20quang%20cao.jpg','http://tamdiem.com.vn/sources/icons/qu%E1%BA%A3ng-c%C3%A1o-tr%C3%AAn-%C4%91%C3%A0i.png',0,0,0,0,8,0,1419740939,1457216697,1),(34,'Tổ chức sự kiện','to-chuc-su-kien',0,'cate_service','Tổ chức sự kiện, tổ chức event, event chuyên nghiệp, tổ chức sự kiện chuyên nghiệp, tổ chức hợp báo','Event, khai trương, khánh thành, động thổ, giới thiệu sản phẩm, ra mắt sản phẩm mới, liên hoan, kỷ niệm ngày thành lập','Tổ chức sự kiện là một gói truyền thông đa phương , sống động được thực hiện với một chủ đề định sẵn, được điều chỉnh và chỉnh sửa để đạt được mục tiêu của khách hàng muốn hướng đến và tác động hợp lý tới một đối tượng cụ thể. Đồng thời tập trung chính vào đối tượng khách hàng mục tiêu bằng cách tạo ra các trải nghiệm lý thú tại một địa điểm nào đó và có tính tương tác hai chiều. - See more at: http://tamdiem.com.vn/dich-vu/to-chuc-su-kien.html#sthash.gkFlWsAE.dpuf','<div class=\"vbbox-selectcontent2\">\r\n<div>\r\n<p><strong>Sự</strong> <strong>kiện</strong> <strong>l&agrave;</strong> <strong>g&igrave;</strong><strong>? </strong></p>\r\n<p><strong>C&oacute;</strong> <strong>rất</strong> <strong>nhiều</strong> <strong>c&aacute;ch</strong> <strong>ph&aacute;t</strong> <strong>biểu</strong> <strong>kh&aacute;c</strong> <strong>nhau</strong> <strong>về</strong><strong> event </strong><strong>đ&oacute;</strong> <strong>c&oacute;</strong> <strong>thể</strong> <strong>l&agrave;</strong></p>\r\n<p>1.Một live show ca nhạc</p>\r\n<p>2.Một giải đấu thể thao ( World Cup, Euro, Sea Game...)</p>\r\n<p>3.Một lễ hội (Festival caf&eacute;, festival hoa Đ&agrave; lạt, Festival bia ở Đức..)</p>\r\n<p>4.Một hội nghị kh&aacute;ch h&agrave;ng</p>\r\n<p>5.Một sự kiện mang t&iacute;nh ch&iacute;nh trị (APEC, ASEM, ASEAN, G8, OPEC, ...)</p>\r\n<p>6.Một lễ động thổ, kh&aacute;nh th&agrave;nh</p>\r\n<p>7.Một buổi giới thiệu SP mới (New product lauching event)</p>\r\n<p>8.Một buổi khai trương c&ocirc;ng ty (corporate event)</p>\r\n<p>9.Một buổi thuyết tr&igrave;nh đ&agrave;o tạo &hellip;</p>\r\n<p style=\"text-align: justify;\"><strong>Tổ chức sự kiện</strong> l&agrave; một g&oacute;i truyền th&ocirc;ng đa phương , sống động được thực hiện với một chủ đề định sẵn, được điều chỉnh v&agrave; chỉnh sửa để đạt được mục ti&ecirc;u của kh&aacute;ch h&agrave;ng muốn hướng đến v&agrave; t&aacute;c động hợp l&yacute; tới một đối tượng cụ thể. Đồng thời tập trung ch&iacute;nh v&agrave;o đối tượng kh&aacute;ch h&agrave;ng mục ti&ecirc;u bằng c&aacute;ch tạo ra c&aacute;c trải nghiệm l&yacute; th&uacute; tại một địa điểm n&agrave;o đ&oacute; v&agrave; c&oacute; t&iacute;nh tương t&aacute;c hai chiều.</p>\r\n<p><strong>Tổ chức sự kiện</strong> l&agrave; một lĩnh vực m&agrave; ở đ&oacute; c&aacute;c c&ocirc;ng ty chuy&ecirc;n tr&aacute;ch c&oacute; tr&aacute;ch nhiệm l&agrave;m cho c&aacute;c sự kiện diễn ra một c&aacute;ch ho&agrave;n hảo nhằm gi&uacute;p c&aacute;c doanh nghiệp một mặt quảng b&aacute; thương hiệu v&agrave; sản phẩm (hay n&oacute;i c&aacute;ch kh&aacute;c ch&iacute;nh l&agrave; đ&aacute;nh b&oacute;ng thương hiệu v&agrave; sản phẩm) mặt kh&aacute;c lại tạo cơ hội cho c&aacute;c doanh nghiệp gặp gỡ, trao đổi với c&aacute;c cơ quan truyền th&ocirc;ng, cơ quan c&ocirc;ng quyền&hellip;tăng cường quan hệ c&oacute; lợi cho c&aacute;c doanh nghiệp.</p>\r\n<p><strong>Tổ chức sự kiện</strong> l&agrave; một c&ocirc;ng việc kh&ocirc;ng hề đơn giản. C&oacute; trong nghề mới thấy được sự vất vả của những con người l&agrave;m c&ocirc;ng việc n&agrave;y. H&agrave;ng trăm những c&ocirc;ng việc đ&ograve;i hỏi sự cẩn thận, tỉ mẩn đến từng tiểu tiết v&igrave; chỉ cần một sai s&oacute;t nhỏ c&oacute; thể ảnh hưởng đến cả một &ecirc;kip v&agrave; hơn hết ch&iacute;nh l&agrave; ảnh hưởng đến h&igrave;nh ảnh của kh&aacute;ch h&agrave;ng v&agrave; uy t&iacute;n của c&ocirc;ng ty. C&ocirc;ng việc tổ chức sự kiện như một bức tranh của tr&ograve; chơi gh&eacute;p h&igrave;nh v&agrave; người chơi chỉ th&agrave;nh c&ocirc;ng khi gh&eacute;p ho&agrave;n chỉnh bức tranh đ&oacute; bằng h&agrave;ng trăm, h&agrave;ng ng&agrave;n mẩu nhỏ chi tiết. Đẳng cấp của mỗi c&ocirc;ng ty thể hiện ở ch&iacute;nh sự ho&agrave;n hảo trong từng tiểu tiết ở mỗi sự kiện họ tổ chức.</p>\r\n<p>Người l&agrave;m tổ chức sự kiện đ&ograve;i hỏi phải kh&ocirc;ng ngừng s&aacute;ng tạo để đem đến những trải nghiệm mới lạ v&agrave; th&uacute; vị cho kh&aacute;ch h&agrave;ng.Tr&ecirc;n thực tế,những người tổ chức sự kiện phải thường xuy&ecirc;n đối mặt với việc t&igrave;m phương &aacute;n mới để những sự kiện được tổ chức ra kh&ocirc;ng bị nh&agrave;m ch&aacute;n v&agrave; l&agrave;m sao phải tạo được sức h&uacute;t đối với kh&aacute;ch h&agrave;ng.</p>\r\n<p><strong>T&acirc;m Điểm Communications</strong> tự tin tổ chức sự kiện chuy&ecirc;n nghiệp với quy m&ocirc; lớn</p>\r\n<p>Nắm bắt được xu hướng đ&oacute;, <strong>T&acirc;m Điểm Communications</strong> mong muốn đ&aacute;p ứng được y&ecirc;u cầu của kh&aacute;ch h&agrave;ng, đem lại sự thoải m&aacute;i, h&agrave;i l&ograve;ng đến cả những kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh nhất. Với kinh nghiệm 10 năm hoạt động trong lĩnh truyền th&ocirc;ng, c&oacute; đội ngũ nh&acirc;n vi&ecirc;n năng động, kh&eacute;o l&eacute;o, c&oacute; c&aacute;c trang thiết bị hiện đại, <strong>T&acirc;m Điểm Communications</strong> đ&atilde; từng tổ chức th&agrave;nh c&ocirc;ng nhiều sự kiện lớn, tạo được niềm tin từ ph&iacute;a kh&aacute;ch h&agrave;ng.</p>\r\n<p><strong>T&acirc;m Điểm Communications</strong> chuy&ecirc;n tổ chức sự kiện:</p>\r\n<p>&nbsp;&nbsp; - Lễ khởi c&ocirc;ng, lễ động thổ, lễ kh&aacute;nh th&agrave;nh.</p>\r\n<p>&nbsp;&nbsp; - Tổ chức hội nghị, hội thảo, họp b&aacute;o.</p>\r\n<p>&nbsp;&nbsp; - Tổ chức triển l&atilde;m.</p>\r\n<p>&nbsp;&nbsp; - C&aacute;c hoạt động ra mắt sản phẩm mới v&agrave; c&aacute;c hoạt động Marketing trực tiếp.</p>\r\n<p>&nbsp;&nbsp; - Tổ chức lễ hội, c&aacute;c chương tr&igrave;nh ca nhạc.</p>\r\n<p>&nbsp;&nbsp; - Tổ chức c&aacute;c lễ kỉ niệm.</p>\r\n<p>&nbsp;&nbsp; - C&aacute;c sự kiện thiếu nhi.</p>\r\n<p>- V&agrave; c&aacute;c sự kiện mang t&iacute;nh x&atilde; hội kh&aacute;c.</p>\r\n<p>H&atilde;y đến với <strong>T&acirc;m Điểm Communications</strong> ngay h&ocirc;m nay để được tư vấn v&agrave; hỗ trợ tốt nhất</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n</div>\r\n</div>','Tổ chức sự kiện','','',0,'http://www.tamdiem.com.vn/sources/images/to%20chuc%20su%20kien.jpg','http://tamdiem.com.vn/sources/icons/t%E1%BB%95-ch%E1%BB%A9c-s%E1%BB%B1-ki%E1%BB%87n.png',0,0,0,0,1,0,1419740955,1457216533,1),(35,'Quảng cáo báo chí','quang-cao-bao-chi',0,'cate_service','Quảng cáo báo chí','','Quảng cáo trên báo chí là một trong những loại hình quảng cáo ra đời sớm nhất và cũng là kiểu quảng cáo đầu tiên mà các công ty nghĩ tới trong các chiến dịch quảng cáo.','<p style=\"text-align: justify;\"><strong>Quảng c&aacute;o tr&ecirc;n b&aacute;o ch&iacute;</strong> l&agrave; một trong những loại h&igrave;nh quảng c&aacute;o ra đời sớm nhất v&agrave; cũng l&agrave; kiểu quảng c&aacute;o đầu ti&ecirc;n m&agrave; c&aacute;c c&ocirc;ng ty nghĩ tới trong c&aacute;c chiến dịch quảng c&aacute;o. <br /> B&aacute;o ch&iacute; l&agrave; một c&aacute;ch thức tốt để tiếp cận một số lượng lớn người ti&ecirc;u d&ugrave;ng, đặc biệt l&agrave; những người từ 45 tuổi trở l&ecirc;n - những người c&oacute; xu hướng đọc b&aacute;o thường xuy&ecirc;n hơn giới trẻ vốn chỉ lấy tin tức từ truyền h&igrave;nh hay Internet. V&agrave; bạn c&oacute; thể hướng quảng c&aacute;o của bạn tới c&aacute;c thị trường th&iacute;ch hợp bằng việc đề nghị quảng c&aacute;o được đăng tải trong c&aacute;c chuy&ecirc;n mục tin tức c&oacute; li&ecirc;n quan mật thiết nhất đối với c&aacute;c kh&aacute;ch h&agrave;ng tiềm năng của bạn, chẳng hạn như thể thao, lối sống hay kinh doanh.</p>\r\n<p style=\"text-align: justify;\"><strong>T&acirc;m Điểm Communications</strong> cung cấp dịch vụ đăng quảng c&aacute;o, b&agrave;i PR tr&ecirc;n c&aacute;c trang b&aacute;o (nhắc t&ecirc;n, đ&iacute;nh k&egrave;m logo v&agrave;o h&igrave;nh ảnh) nhằm thu h&uacute;t người xem biết đến Doanh nghiệp cũng như sản phẩm m&agrave; Doanh nghiệp muốn quảng c&aacute;o.</p>\r\n<p style=\"text-align: justify;\">Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i c&oacute; mạng lưới li&ecirc;n kết quảng c&aacute;o tr&ecirc;n cả b&aacute;o mạng. Ch&uacute;ng t&ocirc;i sẽ gi&uacute;p cho Doanh nghiệp chọn lựa đầu b&aacute;o mạng ph&ugrave; hợp ng&acirc;n s&aacute;ch v&agrave; chiến lược truyền th&ocirc;ng.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','Quảng cáo báo chí','','',0,'http://www.tamdiem.com.vn/sources/images/Quang%20cao%20bao%20in_TAM%20DIEM.jpg','http://tamdiem.com.vn/sources/icons/qu%E1%BA%A3ng-c%C3%A1o-tr%C3%AAn-b%C3%A1o.png',0,0,0,0,4,0,1419740991,1457216639,1),(36,'Tiếp thị và tài trợ','tiep-thi-va-tai-tro',0,'cate_service','tiếp thị và tài trợ, kêu gọi tài trợ, mời tài trợ, cần tìm tài trợ, Doanh Nghiệp tài trợ, chương trình tài trợ, gói tài trợ','doanh nghiệp tài trợ, chương trình được tài trợ, tìm người tài trợ, đơn vị tài trợ, gói tài trợ, tài trợ chương trình truyền hình, tài trợ game show, tài trợ chương trình dự báo thời tiết','Tài trợ được chứng minh là hình thức marketing có tốc độ tăng trưởng cao nhất hiện nay ở nhiều nước phát triển. Với hoạt động này, doanh nghiệp có thể tìm được những cơ hội lớn để tăng năng lực cạnh tranh bằng cách tạo thêm niềm tin, khuếch trương hình ảnh và uy tín đối với các thị trường mục tiêu.','<div class=\"vbbox-selectcontent2\">\r\n<div>\r\n<div class=\"vbbox-selectcontent2\">\r\n<div style=\"text-align: justify;\">T&agrave;i trợ được chứng minh l&agrave; h&igrave;nh thức marketing c&oacute; tốc độ tăng trưởng cao nhất hiện nay ở nhiều nước ph&aacute;t triển. Với hoạt động n&agrave;y, doanh nghiệp c&oacute; thể t&igrave;m được những cơ hội lớn để tăng năng lực cạnh tranh bằng c&aacute;ch tạo th&ecirc;m niềm tin, khuếch trương h&igrave;nh ảnh v&agrave; uy t&iacute;n đối với c&aacute;c thị trường mục ti&ecirc;u. T&agrave;i trợ tạo ra những cơ hội để doanh nghiệp c&oacute; thể đồng thời đạt được nhiều mục ti&ecirc;u. Qua hoạt động t&agrave;i trợ, doanh nghiệp c&oacute; thể được hưởng nhiều nguồn lợi, như:</div>\r\n<div style=\"text-align: justify;\"><strong>1. Tăng cường h&igrave;nh ảnh của doanh nghiệp, định h&igrave;nh th&aacute;i độ của người ti&ecirc;u d&ugrave;ng.</strong> C&aacute;c doanh nghiệp thường muốn cải thiện h&igrave;nh ảnh của m&igrave;nh trước con mắt người ti&ecirc;u d&ugrave;ng, cả kh&aacute;ch h&agrave;ng hiện tại lẫn kh&aacute;ch h&agrave;ng tiềm năng. Hoạt động t&agrave;i trợ c&oacute; thể định h&igrave;nh th&aacute;i độ của người mua v&agrave; tạo ra phản ứng t&iacute;ch cực đối với sản phẩm/dịch vụ của doanh nghiệp bạn.</div>\r\n<div style=\"text-align: justify;\"><strong>2. Tạo động lực cho hoạt động b&aacute;n h&agrave;ng. </strong>Hoạt động t&agrave;i trợ tạo ra động lực rất lớn để th&uacute;c đẩy b&aacute;n h&agrave;ng. Đối với hoạt động b&aacute;n h&agrave;ng, đ&acirc;y được coi l&agrave; một c&ocirc;ng cụ khuếch trương cực kỳ hiệu quả. Mục ti&ecirc;u n&agrave;y tạo điều kiện để doanh nghiệp trưng b&agrave;y, giới thiệu sản phẩm của m&igrave;nh, nhiều khi cho ph&eacute;p sản phẩm tiếp cận trực tiếp với nhiều đối tượng kh&aacute;ch h&agrave;ng. C&aacute;c c&ocirc;ng ty sản xuất thực phẩm v&agrave; đồ uống rất hay sử dụng h&igrave;nh thức t&agrave;i trợ để tạo điều kiện cho kh&aacute;ch h&agrave;ng thử sản phẩm - một hoạt động thường dẫn đến quyết định mua h&agrave;ng.</div>\r\n<div style=\"text-align: justify;\"><strong>3. T&agrave;i trợ được coi l&agrave; h&igrave;nh thức quảng b&aacute; đ&aacute;ng tin cậy v&agrave; tăng cường khả năng nhận biết của kh&aacute;ch h&agrave;ng.</strong> C&aacute;c nh&agrave; t&agrave;i trợ lu&ocirc;n t&igrave;m kiếm những c&aacute;ch thức để quảng b&aacute; rộng r&atilde;i h&igrave;nh ảnh của m&igrave;nh. C&aacute;c phương tiện truyền th&ocirc;ng phục vụ sự kiện lu&ocirc;n n&ecirc;u t&ecirc;n v&agrave; đưa ra h&igrave;nh ảnh của nh&agrave; t&agrave;i trợ. Do vậy, việc quảng b&aacute; th&ocirc;ng qua t&agrave;i trợ được kh&aacute;ch h&agrave;ng tin cậy hơn nhiều so với quảng c&aacute;o thuần t&uacute;y, gi&uacute;p kh&aacute;ch h&agrave;ng nhận biết r&otilde; hơn sản phẩm của doanh nghiệp. Để tối đa h&oacute;a mục ti&ecirc;u quảng b&aacute; trong t&agrave;i trợ, điều quan trọng l&agrave; doanh nghiệp t&agrave;i trợ phải c&oacute; một chiến dịch truyền th&ocirc;ng to&agrave;n diện hỗ trợ th&ecirc;m cho việc khuếch trương doanh nghiệp.</div>\r\n<div style=\"text-align: justify;\"><strong>4. T&igrave;m ra sự kh&aacute;c biệt so với c&aacute;c đối thủ cạnh tranh.</strong> Việc t&agrave;i trợ cho c&aacute;c sự kiện, đặc biệt l&agrave; trong những trường hợp độc quyền t&agrave;i trợ, l&agrave; một c&aacute;ch thức quan trọng để tạo ra sự kh&aacute;c biệt so với c&aacute;c đối thủ cạnh tranh. T&ecirc;n của doanh nghiệp bạn c&oacute; cơ hội để nổi bật hơn c&aacute;c đối thủ cạnh tranh kh&aacute;c. Việc n&agrave;y đặc biệt hữu &iacute;ch nếu doanh nghiệp bạn muốn cạnh tranh với c&aacute;c đối thủ c&oacute; tiềm lực t&agrave;i ch&iacute;nh lớn hơn.</div>\r\n<div style=\"text-align: justify;\"><strong>5. Tạo được sự th&acirc;n thiện.</strong> Kh&aacute;ch h&agrave;ng mục ti&ecirc;u thường cảm nhận về sự t&agrave;i trợ với th&aacute;i độ t&iacute;ch cực, thiện cảm. Họ nghĩ bạn c&oacute; nhiều nỗ lực đem lại th&agrave;nh c&ocirc;ng cho sự kiện, tức l&agrave; l&agrave;m họ được thỏa m&atilde;n hơn. Nhờ t&agrave;i trợ, h&igrave;nh ảnh của doanh nghiệp được thiện cảm hơn, trở n&ecirc;n gần gũi hơn đối với kh&aacute;ch h&agrave;ng. Sự th&acirc;n thiện n&agrave;y chắc chắn sẽ th&uacute;c đẩy doanh thu của doanh nghiệp bạn.</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\"><strong>Năm 2015 T&acirc;m Điểm c&oacute; những g&oacute;i t&agrave;i trợ tr&ecirc;n c&aacute;c k&ecirc;nh truyền h&igrave;nh:</strong></div>\r\n<div style=\"text-align: justify;\"><strong><br />T&agrave;i trợ chương tr&igrave;nh : </strong></div>\r\n<div style=\"text-align: justify;\"><strong>- <a href=\"https://www.youtube.com/watch?v=9qJ1__SAreA\">Dự B&aacute;o Thời Tiết tr&ecirc;n HTV7 - Đ&agrave;i Truyền H&igrave;nh TP.HCM</a><br /></strong></div>\r\n<div style=\"text-align: justify;\"><strong>- Dự B&aacute;o Thời Tiết tr&ecirc;n BTV1 - Đ&agrave;i Truyền H&igrave;nh B&igrave;nh Dương</strong></div>\r\n<div style=\"text-align: justify;\"><a href=\"https://www.youtube.com/watch?v=HSzQefHJtSg\"><strong>- Chuyến Xe Nh&acirc;n &Aacute;i tr&ecirc;n THVL1 - Đ&agrave;i Truyền H&igrave;nh Vĩnh Long</strong></a></div>\r\n<strong><a href=\"https://www.youtube.com/watch?v=j_KxzR2Yfyw\">- Vượt Qua Thử Th&aacute;ch tr&ecirc;n THVL1 - Đ&agrave;i Truyền H&igrave;nh Vĩnh Long</a></strong><br />\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n<div style=\"text-align: justify;\">Mọi chi tiết về t&agrave;i trợ xin li&ecirc;n hệ: Ph&ograve;ng Kinh Doanh</div>\r\n<div style=\"text-align: justify;\"><strong>CTY TNHH T&Acirc;M ĐIỂM <br /></strong></div>\r\n<div style=\"text-align: justify;\">10B Nguyễn Thị Minh Khai, P. Đa Kao, Q.1, TP.HCM</div>\r\n<div style=\"text-align: justify;\"><strong>Tel :</strong> (84-8) 3910 4081 - <strong>Fax:</strong> (84-8) 3910 4218</div>\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>','Tiếp thị và tài trợ','','',0,'http://www.tamdiem.com.vn/sources/images/Tai%20tro.gif','http://www.tamdiem.com.vn/sources/icons/thi-c%C3%B4ng-b%E1%BA%A3ng-qu%E1%BA%A3ng-c%C3%A1o.png',0,0,0,0,3,0,1419741023,1457216603,1),(37,'Tư vấn chiến lược truyền thông - thương hiệu','tu-van-chien-luoc-truyen-thong-thuong-hieu',0,'cate_service','tư vấn truyền thông, truyền thông, thương hiệu, quảng cáo, quảng bá, hình ảnh doanh nghiệp, kế hoạch marketing','tư vấn truyền thông, truyền thông, thương hiệu, quảng cáo, quảng bá, hình ảnh doanh nghiệp, kế hoạch marketing','Dựa trên năng lực & nhu cầu của Doanh nghiệp, chúng tôi tư vấn chọn lựa phương tiện, cách thức truyền thông phù hợp để giúp Doanh nghiệp tiếp cận mục tiêu tốt nhất và hiệu quả nhất.','<p style=\"text-align: justify;\"><strong>Truyền th&ocirc;ng l&agrave; một c&ocirc;ng cụ hữu hiệu kh&ocirc;ng thể thiếu trong việc ph&aacute;t triển thương hiệu của một doanh nghiệp. B&ecirc;n cạnh việc cung cấp sản phẩm c&oacute; chất lượng ph&ugrave; hợp với nhu cầu người ti&ecirc;u d&ugrave;ng, doanh nghiệp c&ograve;n cần c&oacute; kế hoạch tiếp cận v&agrave; quảng b&aacute; thương hiệu của m&igrave;nh đến với kh&aacute;ch h&agrave;ng sao cho hiệu quả v&agrave; tiết kiệm chi ph&iacute; nhất, đặc biệt l&agrave; trong thời kỳ kinh tế ph&aacute;t triển với tốc độ ch&oacute;ng mặt, sự cạnh tranh giữa c&aacute;c nh&agrave; cung cấp ng&agrave;y c&agrave;ng gay gắt như hiện nay.</strong></p>\n<p style=\"text-align: justify;\">Một chiến lược truyền th&ocirc;ng hiệu quả cần c&oacute; sự kết hợp nhuần nhuyễn giữa c&aacute;c c&ocirc;ng cụ sao cho hiệu quả mang lại l&agrave; tối đa. Để c&oacute; được điều n&agrave;y, chiến lược truyền th&ocirc;ng cần được l&ecirc;n kế hoạch b&agrave;i bản, chi tiết v&agrave; quan trọng l&agrave; được đề ra bởi một đội ngũ c&oacute; chuy&ecirc;n m&ocirc;n cao, hoạt động chuy&ecirc;n nghiệp v&agrave; nhiều kinh nghiệm. Đối với những doanh nghiệp vừa v&agrave; nhỏ tại Việt Nam, điều n&agrave;y l&agrave; rất kh&oacute; để thực hiện do để duy tr&igrave; một đội ngũ như thế th&igrave; kinh ph&iacute; m&agrave; doanh nghiệp bỏ ra sẽ kh&ocirc;ng nhỏ trong khi hiệu quả mang lại vẫn chưa c&oacute; g&igrave; l&agrave; đảm bảo.</p>\n<p style=\"text-align: justify;\">Hiểu được trở ngại n&agrave;y, <strong>T&acirc;m Điểm Communications</strong> với đội ngũ chuy&ecirc;n gia tư vấn chiến lược đ&atilde; từng triển khai nhiều kế hoạch cho những nh&atilde;n hiệu tại Việt Nam sẵn s&agrave;ng hỗ trợ doanh nghiệp trong vấn đề n&agrave;y.</p>\n<p style=\"text-align: justify;\">B&ecirc;n cạnh việc xem x&eacute;t c&aacute;c yếu tố tổng quan, m&ocirc;i trường b&ecirc;n ngo&agrave;i, ch&uacute;ng t&ocirc;i sẽ tiếp cận trực tiếp m&ocirc;i trường kinh doanh của doanh nghiệp nhằm c&oacute; một c&aacute;i nh&igrave;n ch&acirc;n thực, cụ thể nhất của doanh nghiệp, từ đ&oacute; đưa ra một chiến lược d&agrave;i hạn để định hướng v&agrave; ph&aacute;t triển thương hiệu một c&aacute;ch nhất qu&aacute;n, song song đ&oacute; l&agrave; những kế hoạch truyền th&ocirc;ng ngắn hạn hiệu quả, ph&ugrave; hợp với từng giai đoạn ph&aacute;t triển của doanh nghiệp.</p>\n<p style=\"text-align: justify;\">Dựa tr&ecirc;n năng lực &amp; nhu cầu của Doanh nghiệp, ch&uacute;ng t&ocirc;i tư vấn chọn lựa phương tiện, c&aacute;ch thức truyền th&ocirc;ng ph&ugrave; hợp để gi&uacute;p Doanh nghiệp tiếp cận mục ti&ecirc;u tốt nhất v&agrave; hiệu quả nhất.</p>','Tư vấn chiến lược truyền thông - thương hiệu','','',0,'http://www.tamdiem.com.vn/sources/images/T%C6%B0%20van%20truyen%20thong_tamdiem.jpg','http://tamdiem.com.vn/sources/icons/t%C6%B0-v%E1%BA%A5n-chi%E1%BA%BFn-l%C6%B0%E1%BB%A3c.png',0,0,0,0,6,0,1419741056,1421647683,1),(43,'Giải trí','giai-tri',20,'cate_product','','','','<p><strong>SV 2012</strong></p>\n<p>Tiếp nối sự th&agrave;nh c&ocirc;ng của SV 96 v&agrave; SV 2000, chương tr&igrave;nh gameshow &ldquo;SV 2012&rdquo; với chủ đề &ldquo;Sự trở lại của những nh&agrave; th&ocirc;ng th&aacute;i - vui t&iacute;nh&rdquo; hứa hẹn l&agrave; s&acirc;n chơi hấp dẫn v&agrave; bổ &iacute;ch đối với c&aacute;c bạn sinh vi&ecirc;n tr&ecirc;n cả nước.</p>\n<p>Chương tr&igrave;nh gồm ba phần thi: Lời ch&agrave;o SV (c&aacute;c đội lựa chọn h&igrave;nh thức nghệ thuật như &acirc;m nhạc, vũ đạo, kịch n&oacute;i, thời trang&hellip; để ch&agrave;o hỏi, giới thiệu về trường ĐH của m&igrave;nh), SV th&ocirc;ng th&aacute;i (mỗi đội lựa chọn một trong ba h&igrave;nh thức: bức tranh, đồ vật, h&agrave;nh động kỳ quặc để hỏi đ&aacute;p lẫn nhau), SV t&agrave;i năng (mỗi đội thể hiện chủ đề cuộc thi th&ocirc;ng qua m&agrave;n thể hiện t&agrave;i năng của đội m&igrave;nh).</p>\n<p>99 đội chơi của 3 miền sẽ phải trải qua 5 v&ograve;ng thi từ sơ loại đến chung kết. Trận chung kết l&agrave; sự thi đấu của 4 đội với 3 đội xuất sắc nhất của 3 miền v&agrave; 1 đội nh&igrave; c&oacute; số điểm cao nhất.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : VTV3</p>\n<p>Thời lượng: 60 ph&uacute;t/ chương tr&igrave;nh</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2012</p>','Giải trí','','',0,'','',0,0,0,0,0,0,1419787886,1419988619,1),(45,'Sản phẩm 1','san-pham-1',42,'product_item','','','','<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p><iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"//www.youtube.com/embed/W13qDdJDHp8\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>','Sản phẩm 1','','',0,'','',0,0,0,0,0,0,1419789175,1419871143,1),(47,'Sản phẩm 2','san-pham-2',42,'product_item','','','','<p><iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"//www.youtube.com/embed/euCNWhs7ivQ\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>','Sản phẩm 2','','',0,'','',0,0,0,0,0,0,1419789601,1419789859,1),(48,'2. Việc tài trợ 1 chương trình dự báo thời tiết có những lợi ích gì tốt hơn so với các thể loại chương trình khác?','question-1',0,'faq','','','','<p style=\"text-align: justify;\"><strong><em>Thời điểm ph&aacute;t s&oacute;ng</em>:</strong> Bản tin Dự b&aacute;o thời tiết tr&ecirc;n c&aacute;c k&ecirc;nh truyền h&igrave;nh nh&igrave;n chung thường nằm s&aacute;t chương tr&igrave;nh phim v&agrave; thời sự trong ng&agrave;y. Ch&iacute;nh v&igrave; thế, Quảng c&aacute;o của Qu&yacute; vị sẽ thu h&uacute;t 2 luồng kh&aacute;n giả xem chương tr&igrave;nh giải tr&iacute; v&agrave; thời sự.</p>\n<p style=\"text-align: justify;\"><strong><em>Thời gian ph&aacute;t s&oacute;ng</em>:</strong> Bản tin Dự b&aacute;o thời tiết được ph&aacute;t s&oacute;ng h&agrave;ng ng&agrave;y suốt cả năm n&ecirc;n tần suất xuất hiện đều đặn v&agrave; ổn định hơn.</p>\n<p style=\"text-align: justify;\">Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i đảm bảo quyền lợi quảng c&aacute;o trong chương tr&igrave;nh v&agrave; chi ph&iacute; t&agrave;i trợ một c&aacute;ch hợp l&yacute; nhằm gi&uacute;p Qu&yacute; Kh&aacute;ch h&agrave;ng gắn kết l&acirc;u d&agrave;i c&ugrave;ng chương tr&igrave;nh.</p>','Question 1','','',0,'','',0,0,0,0,0,0,1419789952,1419960819,1),(49,'3. Những điều kiện được nhận chế độ hậu mãi, giá ưu đãi?','lorem-ipsum-dolor-sit-amet-consectetuer-adipiscing-elit-',0,'faq','','','','<p>Ch&uacute;ng t&ocirc;i xem mỗi kh&aacute;ch h&agrave;ng l&agrave; một đối t&aacute;c l&acirc;u d&agrave;i &amp; mong muốn c&ugrave;ng nhau ph&aacute;t triển bền vững. Căn cứ v&agrave;o từng y&ecirc;u cầu cụ thể của mỗi kh&aacute;ch h&agrave;ng, ch&uacute;ng t&ocirc;i cam kết đảm bảo quyền lợi của Kh&aacute;ch h&agrave;ng một c&aacute;ch tối ưu &amp; c&oacute; lợi nhất.</p>','Lorem ipsum dolor sit amet, consectetuer adipiscing elit.','','',0,'','',0,0,0,0,0,0,1419789988,1419899683,1),(52,'Hình ảnh hoạt động','hinh-anh-hoat-dong',0,'guide','','','','<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>\n<p>Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>','Hình ảnh hoạt động','','',0,'','',0,0,0,0,0,0,1419790797,1420552075,0),(53,'Điện thoại hỗ trợ','dien-thoai-ho-tro',0,'phone_support','Điện thoại cho Tâm Điểm','','','<h2><img style=\"float: left;\" src=\"/tinymce/source/Hinh up bai/Dich-vu-ho-tro.jpg\" alt=\"\" width=\"270\" /></h2>\n<h2>&nbsp;</h2>\n<h2>&nbsp;<img src=\"/tinymce/source/Hinh up bai/Hotline.gif\" alt=\"\" /></h2>\n<h2>&nbsp;</h2>\n<h2>&nbsp;</h2>\n<h2>&nbsp;</h2>\n<h2>&nbsp;</h2>','Điện thoại hỗ trợ','','',0,'','',0,0,0,0,0,0,NULL,1421307652,1),(54,'Sơ đồ đường đi','so-do-duong-di',0,'map','','','','<p><img src=\"/tinymce/source/Ban Do TD.JPG\" alt=\"\" /></p>','Sơ đồ đường đi','','',0,'','',0,0,0,0,0,0,NULL,1440841907,1),(56,'4.	Làm sao tôi biết được lịch đăng ký quảng cáo của tôi có được phát sóng hay không?','4-	lam-sao-toi-biet-duoc-lich-dang-ky-quang-cao-cua-toi-co-duoc-phat-song-hay-khong-',0,'faq','','','','<p>Mỗi th&aacute;ng ch&uacute;ng t&ocirc;i sẽ đưa cho kh&aacute;ch h&agrave;ng chứng nhận ph&aacute;t s&oacute;ng do Đ&agrave;i cung cấp. Dựa v&agrave;o chứng nhận n&agrave;y, kh&aacute;ch h&agrave;ng c&oacute; thể biết được quảng c&aacute;o của b&ecirc;n m&igrave;nh c&oacute; được ph&aacute;t đ&uacute;ng như lịch đ&atilde; đăng k&yacute; hay kh&ocirc;ng.</p>','4.	Làm sao tôi biết được lịch đăng ký quảng cáo của tôi có được phát sóng hay không?','','',0,'','',0,0,0,0,0,0,1419899798,1419899798,1),(57,'5. Trong trường hợp Đài không phát hoặc phát sai khung giờ quảng cáo, Tâm Điểm sẽ giải quyết như thế nào?','5-trong-truong-hop-dai-khong-phat-hoac-phat-sai-khung-gio-quang-cao-tam-diem-se-giai-quyet-nhu-the-nao-',0,'faq','','','','<p>T&acirc;m Điểm sẽ đại diện kh&aacute;ch h&agrave;ng tiến h&agrave;nh t&igrave;m hiểu &amp; thương lượng với Đ&agrave;i truyền h&igrave;nh nhằm đảm bảo quyền lợi tốt nhất cho kh&aacute;ch h&agrave;ng.</p>','5. Trong trường hợp Đài không phát hoặc phát sai khung giờ quảng cáo, Tâm Điểm sẽ giải quyết như thế nào?','','',0,'','',0,0,0,0,0,0,1419899829,1419899829,1),(58,'Lý do chọn TrungDung Media','ly-do-chon-trung-dung-media',0,'whychooseTD','TrungDung Media','','','<p><img src=\"/tinymce/source/Hinh up bai/LY DO CHON TAM DIEM 2015.jpg\" alt=\"\" width=\"892\" height=\"672\" /></p>','Lý do chọn TrungDung Media','','',0,'','',0,0,0,0,0,0,1419900162,1457218802,1),(59,'MV của tôi 2014','noi-dung-1',43,'product_item','','','Xuất phát từ xu hướng và sở thích hiện nay của nhiều bạn trẻ là làm những clip ca nhạc, phim ngắn hay video mang đậm dấu ấn cá nhân, MV của tôi đã được xây dựng với format hoàn toàn mới. Chương trình là nơi các bạn trẻ đam mê lĩnh vực điện ảnh có cơ hội được thể hiện khả năng của mình qua các video clip ca nhạc do chính các bạn tự làm từ đầu đến cuối.','<p style=\"text-align: justify;\"><strong>MV CỦA T&Ocirc;I 2014</strong></p>\n<p>Xuất ph&aacute;t từ xu hướng v&agrave; sở th&iacute;ch hiện nay của nhiều bạn trẻ l&agrave; l&agrave;m những clip ca nhạc, phim ngắn hay video mang đậm dấu ấn c&aacute; nh&acirc;n, MV của t&ocirc;i đ&atilde; được x&acirc;y dựng với format ho&agrave;n to&agrave;n mới. Chương tr&igrave;nh l&agrave; nơi c&aacute;c bạn trẻ đam m&ecirc; lĩnh vực điện ảnh c&oacute; cơ hội được thể hiện khả năng của m&igrave;nh qua c&aacute;c video clip ca nhạc do ch&iacute;nh c&aacute;c bạn tự l&agrave;m từ đầu đến cuối.</p>\n<p>12 đội chơi được ban tổ chức lựa chọn ở v&ograve;ng sơ khảo sẽ lần lượt trải qua 3 v&ograve;ng thi để chọn ra 2 đội xuất sắc nhất c&ugrave;ng tranh t&agrave;i ở v&ograve;ng chung kết. Trong 5 ng&agrave;y kể từ khi ban tổ chức đưa ra đề b&agrave;i, c&aacute;c đội sẽ phải ho&agrave;n th&agrave;nh MV của m&igrave;nh tại địa phương v&agrave; gửi về cho chương tr&igrave;nh để c&aacute;c th&agrave;nh vi&ecirc;n ban gi&aacute;m khảo xem v&agrave; chấm điểm.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : VTV3</p>\n<p>Thời lượng: 45 ph&uacute;t/ chương tr&igrave;nh</p>\n<p>Năm ph&aacute;t s&oacute;ng : th&aacute;ng 6-9/2014</p>\n<p>&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/RnSf_QGYTTU\" width=\"441\" height=\"286\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe><img src=\"/tinymce/source//BGK-giatri.jpg\" alt=\"\" width=\"441\" height=\"248\" /></p>','Nội dung 1','','',0,'http://tamdiem.com.vn/sources/images/MV-c%E1%BB%A7a-t%C3%B4i.jpg','',0,0,1,0,0,0,1420075725,1421394225,1),(60,'SV 2012','sv-2012',43,'product_item','','','Tiếp nối sự thành công của SV 96 và SV 2000, chương trình gameshow “SV 2012” với chủ đề “Sự trở lại của những nhà thông thái - vui tính” hứa hẹn là sân chơi hấp dẫn và bổ ích đối với các bạn sinh viên trên cả nước.\n\nChương trình gồm ba phần thi: Lời chào SV (các đội lựa chọn hình thức nghệ thuật như âm nhạc, vũ đạo, kịch nói, thời trang… để chào hỏi, giới thiệu về trường ĐH của mình), SV thông thái (mỗi đội lựa chọn một trong ba hình thức: bức tranh, đồ vật, hành động kỳ quặc để hỏi đáp lẫn nhau), SV tài năng (mỗi đội thể hiện chủ đề cuộc thi thông qua màn thể hiện tài năng của đội mình).','<p><strong>SV 2012</strong></p>\n<p style=\"text-align: justify;\">Tiếp nối sự th&agrave;nh c&ocirc;ng của SV 96 v&agrave; SV 2000, chương tr&igrave;nh gameshow &ldquo;SV 2012&rdquo; với chủ đề &ldquo;Sự trở lại của những nh&agrave; th&ocirc;ng th&aacute;i - vui t&iacute;nh&rdquo; hứa hẹn l&agrave; s&acirc;n chơi hấp dẫn v&agrave; bổ &iacute;ch đối với c&aacute;c bạn sinh vi&ecirc;n tr&ecirc;n cả nước.</p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh gồm ba phần thi: Lời ch&agrave;o SV (c&aacute;c đội lựa chọn h&igrave;nh thức nghệ thuật như &acirc;m nhạc, vũ đạo, kịch n&oacute;i, thời trang&hellip; để ch&agrave;o hỏi, giới thiệu về trường ĐH của m&igrave;nh), SV th&ocirc;ng th&aacute;i (mỗi đội lựa chọn một trong ba h&igrave;nh thức: bức tranh, đồ vật, h&agrave;nh động kỳ quặc để hỏi đ&aacute;p lẫn nhau), SV t&agrave;i năng (mỗi đội thể hiện chủ đề cuộc thi th&ocirc;ng qua m&agrave;n thể hiện t&agrave;i năng của đội m&igrave;nh).</p>\n<p style=\"text-align: justify;\">99 đội chơi của 3 miền sẽ phải trải qua 5 v&ograve;ng thi từ sơ loại đến chung kết. Trận chung kết l&agrave; sự thi đấu của 4 đội với 3 đội xuất sắc nhất của 3 miền v&agrave; 1 đội nh&igrave; c&oacute; số điểm cao nhất.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : VTV3</p>\n<p>Thời lượng: 60 ph&uacute;t/ chương tr&igrave;nh</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2012</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/VdQ-F2DE8_E\" width=\"475\" height=\"267\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>&nbsp;</p>\n<p align=\"center\"><img src=\"/tinymce/source/2.jpg\" alt=\"\" /></p>\n<p align=\"center\"><img src=\"/tinymce/source/4.jpg\" alt=\"\" /></p>','SV 2012','','',0,'http://tamdiem.com.vn/sources/images/SV-2012.jpg','',0,0,1,0,0,0,1420115187,1421395592,1),(61,'Tài năng SV 2014','tai-nang-sv-2014',43,'product_item','','','','<p><strong>T&Agrave;I NĂNG SV 2014</strong></p>\n<p>Chương tr&igrave;nh truyền h&igrave;nh d&agrave;nh cho sinh vi&ecirc;n đang theo học tại c&aacute;c trường ĐH, CĐ tr&ecirc;n cả nước. Th&ocirc;ng qua chương tr&igrave;nh, c&aacute;c bạn SV c&oacute; cơ hội được bộc lộ v&agrave; thể hiện t&agrave;i năng của m&igrave;nh ở c&aacute;c lĩnh vực nghệ thuật.</p>\n<p>Mỗi chương tr&igrave;nh l&agrave; cuộc tranh t&agrave;i giữa 2 trường với 2-3 tiết mục dự thi. C&aacute;c bạn SV c&oacute; thể tham gia c&aacute;c thể loại như: H&aacute;t (đơn ca, nh&oacute;m), tr&igrave;nh tấu nhạc cụ, vũ đạo (nhảy, m&uacute;a đương đại, m&uacute;a truyền thống, khi&ecirc;u vũ...), biểu diễn tạp kỹ (kịch, ảo thuật, v&otilde; thuật...).</p>\n<p>Sau mỗi tiết mục dự thi của 2 đội, ban gi&aacute;m khảo sẽ đưa ra nhận x&eacute;t v&agrave; quyết định lựa chọn tiết mục của đội m&igrave;nh y&ecirc;u th&iacute;ch nhất. Đội n&agrave;o chiến thắng được &iacute;t nhất 2 trong số 3 tiết mục dự thi sẽ d&agrave;nh quyền đi tiếp v&agrave;o v&ograve;ng trong của chương tr&igrave;nh.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : VTV3</p>\n<p>Thời lượng: 45 ph&uacute;t/ chương tr&igrave;nh</p>\n<p>Năm ph&aacute;t s&oacute;ng : th&aacute;ng 9-12/2014</p>\n<p style=\"text-align: center;\">&nbsp;<iframe src=\"//www.youtube.com/embed/YtfceehzqvU\" width=\"505\" height=\"284\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>&nbsp;</p>','Tài năng SV 2014','','',0,'http://tamdiem.com.vn/sources/images/t%C3%A0i-n%C4%83ng-sv.jpg','',0,0,0,0,0,0,1420116384,1421395930,1),(62,'Việt Nam của Tôi 2014','viet-nam-cua-toi-2014',43,'product_item','','','Chương trình gameshow dành cho sinh viên trên khắp cả nước nhằm giúp các bạn trẻ thêm hiểu rõ và tự hào về đất nước, con người Việt Nam, khơi dậy tình cảm yêu nước của các bạn trẻ.\n\nMỗi chương trình là một cuộc thi riêng biệt giữa 2 đội với 3 phần thi được lựa chọn ngẫu nhiên trong các phần: Sản vật quê hương tôi, Vẽ hình đất nước, Du lịch phượt, Thần tượng của tôi, Biểu diễn nhạc cụ dân gian, Học và hát dân ca, Con đường xúc xắc…','<p><strong>VIỆT NAM CỦA T&Ocirc;I 2014</strong></p>\n<p>Chương tr&igrave;nh gameshow d&agrave;nh cho sinh vi&ecirc;n tr&ecirc;n khắp cả nước nhằm gi&uacute;p c&aacute;c bạn trẻ th&ecirc;m hiểu r&otilde; v&agrave; tự h&agrave;o về đất nước, con người Việt Nam, khơi dậy t&igrave;nh cảm y&ecirc;u nước của c&aacute;c bạn trẻ.</p>\n<p>Mỗi chương tr&igrave;nh l&agrave; một cuộc thi ri&ecirc;ng biệt giữa 2 đội với 3 phần thi được lựa chọn ngẫu nhi&ecirc;n trong c&aacute;c phần: Sản vật qu&ecirc; hương t&ocirc;i, Vẽ h&igrave;nh đất nước, Du lịch phượt, Thần tượng của t&ocirc;i, Biểu diễn nhạc cụ d&acirc;n gian, Học v&agrave; h&aacute;t d&acirc;n ca, Con đường x&uacute;c xắc&hellip;</p>\n<p>Th&agrave;nh vi&ecirc;n của mỗi đội gồm một nghệ sĩ nổi tiếng v&agrave; nh&oacute;m 5 bạn sinh vi&ecirc;n từ c&aacute;c trường đại học v&agrave; cao đẳng tr&ecirc;n cả nước.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : VTV3</p>\n<p>Thời lượng: 45 ph&uacute;t/ chương tr&igrave;nh</p>\n<p>Năm ph&aacute;t s&oacute;ng : th&aacute;ng 1-6/2014</p>\n<p style=\"text-align: center;\">&nbsp;<iframe src=\"//www.youtube.com/embed/BKdgHRUPMrM\" width=\"423\" height=\"317\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>','Việt Nam của Tôi 2014','','',0,'http://tamdiem.com.vn/sources/images/vi%E1%BB%87t-nam-c%E1%BB%A7a-t%C3%B4i-2014.jpg','',0,0,1,0,0,0,1420116947,1421396228,1),(63,'Vượt qua thử thách','vuot-qua-thu-thach',43,'product_item','','','','<p><strong>VƯỢT QUA THỬ TH&Aacute;CH</strong></p>\n<p>Chương tr&igrave;nh x&acirc;y dựng theo dạng Truyền h&igrave;nh thực tế kết hợp với Gameshow d&agrave;nh cho những hộ gia đ&igrave;nh c&oacute; ho&agrave;n cảnh kh&oacute; khăn tr&ecirc;n địa b&agrave;n tỉnh Vĩnh Long. Th&ocirc;ng qua chương tr&igrave;nh c&aacute;c hộ gia đ&igrave;nh n&agrave;y sẽ được nhận một số vốn hỗ trợ.</p>\n<p>Mỗi chương tr&igrave;nh gồm ph&oacute;ng sự ngắn giới thiệu về ho&agrave;n cảnh kh&oacute; khăn của 02 hộ gia đ&igrave;nh, phần tranh t&agrave;i của 02 hộ gia đ&igrave;nh v&agrave; phần r&uacute;t thăm may mắn.</p>\n<p>Ngo&agrave;i ra, mỗi kh&aacute;n giả đến dự đều được ph&aacute;t một l&aacute; phiếu r&uacute;t thăm may mắn. - Đại diện kh&aacute;n giả tại hiện trường sẽ lần lượt r&uacute;t 04 l&aacute; phiếu để chọn ra 04 kh&aacute;n giả may mắn. - 04 kh&aacute;n giả n&agrave;y sẽ l&ecirc;n s&acirc;n khấu để nhận phần qu&agrave; may mắn của m&igrave;nh (trị gi&aacute; mỗi phần qu&agrave; l&agrave; 1 triệu đồng).</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : THVL1</p>\n<p>Thời lượng: 25 &ndash; 30 ph&uacute;t/chương tr&igrave;nh</p>\n<p>Thời gian ph&aacute;t s&oacute;ng: 2012-2013</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/j_KxzR2Yfyw\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>&nbsp;<img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Hinh up bai/Vuot Qua Thu Thach VL1.JPG\" alt=\"\" width=\"425\" height=\"319\" /></p>','Vượt qua thử thách','','',0,'http://tamdiem.com.vn/sources/images/v%C6%B0%E1%BB%A3t-qua-th%E1%BB%AD-th%C3%A1ch.jpg','',0,0,0,0,0,0,1420117229,1421396439,1),(64,'Các chương trình khác','cac-chuong-trinh-khac',43,'product_item','','','','<p><strong>C&Aacute;C CHƯƠNG TR&Igrave;NH KH&Aacute;C :</strong></p>\n<p>NGƯỜI DẪN CHƯƠNG TR&Igrave;NH TRUYỀN H&Igrave;NH HTV </p>\n<p>NGƯỜI DẪN CHƯƠNG TR&Igrave;NH TRUYỀN H&Igrave;NH VTV CẦN THƠ </p>\n<p>NHỊP CẦU &Acirc;M NHẠC HTV7 </p>\n<p>QU&Agrave; TẶNG TR&Aacute;I TIM HTV7 </p>\n<p>SẢN PHẨM CỦA BẠN VTV CẦN THƠ 1</p>','Các chương trình khác','','',0,'http://tamdiem.com.vn/sources/images/ng%C6%B0%E1%BB%9Di-d%E1%BA%ABn-ch%C6%B0%C6%A1ng-tr%C3%ACnh-truy%E1%BB%81n-h%C3%ACnh11.jpg','',0,0,0,0,0,0,1420117629,1420485374,1),(65,'Ký sự','ky-su',20,'cate_product','','','','','Ký sự','','',0,'','',0,0,0,0,0,0,1420117752,1420117752,1),(66,'Làng biển Việt Nam','lang-bien-viet-nam',65,'product_item','','','Chương trình khắc họa bức tranh toàn cảnh kinh tế, văn hóa, lịch sử và con người miền biển Việt Nam. Mỗi tập phim sẽ đưa khán giả truyền hình đến với 1 vùng biển của nước ta. Ở đó, khán giả có thể khám phá những đặc trưng của vùng biển từ những hình ảnh kỳ thú tuyệt đẹp của thiên nhiên, những lễ hội truyền thống đến hình ảnh bình dị thân thương của những ngôi làng chài và hình ảnh can trường, dũng cảm của những con người làng biển trong những chuyến ra khơi đầy sóng gió.','<p><strong>L&Agrave;NG BIỂN VIỆT NAM</strong></p>\n<p>Chương tr&igrave;nh khắc họa bức tranh to&agrave;n cảnh kinh tế, văn h&oacute;a, lịch sử v&agrave; con người miền biển Việt Nam. Mỗi tập phim sẽ đưa kh&aacute;n giả truyền h&igrave;nh đến với 1 v&ugrave;ng biển của nước ta. Ở đ&oacute;, kh&aacute;n giả c&oacute; thể kh&aacute;m ph&aacute; những đặc trưng của v&ugrave;ng biển từ những h&igrave;nh ảnh kỳ th&uacute; tuyệt đẹp của thi&ecirc;n nhi&ecirc;n, những lễ hội truyền thống đến h&igrave;nh ảnh b&igrave;nh dị th&acirc;n thương của những ng&ocirc;i l&agrave;ng ch&agrave;i v&agrave; h&igrave;nh ảnh can trường, dũng cảm của những con người l&agrave;ng biển trong những chuyến ra khơi đầy s&oacute;ng gi&oacute;.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: HTV9</p>\n<p>Thời lượng : 20 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2012</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/langbien.jpg\" alt=\"\" width=\"600\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/langbien-1.jpg\" alt=\"\" width=\"600\" /></p>','Làng biển Việt Nam','','',0,'http://tamdiem.com.vn/sources/images/lang-bien-viet.jpg','',0,0,0,0,0,0,1420117886,1420332636,1),(67,'Ngàn dặm biển đảo','ngan-dam-bien-dao',65,'product_item','','','Chương trình khám phá cuộc sống, con người, văn hóa của những vùng biển đảo Việt Nam trên hành trình đi dọc biển Đông. Bắt đầu từ mũi Nai-Hà Tiên – điểm tận cùng của dải đất biển hình chữ S cho đến mũi Ngọc Móng Cái- địa đầu phía bắc của vùng biển Tổ Quốc.','<p><strong>NG&Agrave;N DẶM BIỂN ĐẢO</strong></p>\n<p>Chương tr&igrave;nh kh&aacute;m ph&aacute; cuộc sống, con người, văn h&oacute;a của những v&ugrave;ng biển đảo Việt Nam tr&ecirc;n h&agrave;nh tr&igrave;nh đi dọc biển Đ&ocirc;ng. Bắt đầu từ mũi Nai-H&agrave; Ti&ecirc;n &ndash; điểm tận c&ugrave;ng của dải đất biển h&igrave;nh chữ S cho đến mũi Ngọc M&oacute;ng C&aacute;i- địa đầu ph&iacute;a bắc của v&ugrave;ng biển Tổ Quốc.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng : KG1</p>\n<p>Thời lượng: 10 ph&uacute;t</p>\n<p>Thời gian ph&aacute;t s&oacute;ng : Từ th&aacute;ng 5-7/2012</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/bien-dao.jpg\" alt=\"\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/biendao-1.jpg\" alt=\"\" /></p>','Ngàn dặm biển đảo','','',0,'http://tamdiem.com.vn/sources/images/bien-dao.jpg','',0,0,1,0,0,0,1420118492,1420332711,1),(70,'Phóng sự','phong-su',20,'cate_product','','','','','Phóng sự','','',0,'','',0,0,0,0,0,0,1420140019,1420140019,1),(71,'Chuyến xe nhân ái','chuyen-xe-nhan-ai',70,'product_item','','','Là chương trình truyền hình nhân đạo kết hợp phóng sự thực tế với gameshow. Mỗi chương trình là một cuộc hành trình tìm đến các xã vùng sâu trong tỉnh Vĩnh Long để chọn ra mỗi địa phương 10 hộ nghèo, giúp đỡ bà con một phần vốn hỗ trợ xây nhà và một số bò giống để bà con chăn nuôi cải thiện cuộc sống.','<p><strong>CHUYẾN XE NH&Acirc;N &Aacute;I</strong></p>\n<p>L&agrave; chương tr&igrave;nh truyền h&igrave;nh nh&acirc;n đạo kết hợp ph&oacute;ng sự thực tế với gameshow. Mỗi chương tr&igrave;nh l&agrave; một cuộc h&agrave;nh tr&igrave;nh t&igrave;m đến c&aacute;c x&atilde; v&ugrave;ng s&acirc;u trong tỉnh Vĩnh Long để chọn ra mỗi địa phương 10 hộ ngh&egrave;o, gi&uacute;p đỡ b&agrave; con một phần vốn hỗ trợ x&acirc;y nh&agrave; v&agrave; một số b&ograve; giống để b&agrave; con chăn nu&ocirc;i cải thiện cuộc sống.</p>\n<p>Chương tr&igrave;nh kh&ocirc;ng chỉ gắn liền với ch&iacute;nh s&aacute;ch của Nh&agrave; Nước nhằm hỗ trợ nh&agrave; cho người ngh&egrave;o trong tỉnh m&agrave; c&ograve;n mang đến niềm vui cho những ho&agrave;n cảnh kh&oacute; khăn v&agrave; chuyển tải một th&ocirc;ng điệp s&acirc;u sắc: L&ograve;ng nh&acirc;n &aacute;i, sự đo&agrave;n kết chung tay của cộng đồng l&agrave; sức mạnh đẩy l&ugrave;i ngh&egrave;o kh&oacute;, g&oacute;p phần x&acirc;y dựng qu&ecirc; hương đất nước ng&agrave;y c&agrave;ng văn minh gi&agrave;u đẹp hơn.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: THVL1</p>\n<p>Thời lượng: 30 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2011-2012</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/HSzQefHJtSg\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/chuyenxenhanai.jpg\" alt=\"\" width=\"425\" height=\"283\" /></p>','Chuyến xe nhân ái','','',0,'http://tamdiem.com.vn/sources/images/chuyen-xe-nhan-ai.jpg','',0,0,1,0,0,0,1420140209,1421401081,1),(72,'Hoa cuộc sống','hoa-cuoc-song',70,'product_item','','','Có những con người kém may mắn, gặp nhiều thử thách, mỗi ngày mới đến lại mở ra nhiều lo toan... nhưng không vì thế mà họ bỏ cuộc, chẳng những vươn lên, vượt qua những khó khăn, mà họ còn chung tay giúp đỡ những hoàn cảnh kém may mắn khác...','<p><strong>HOA CUỘC SỐNG</strong></p>\n<p>C&oacute; những con người k&eacute;m may mắn, gặp nhiều thử th&aacute;ch, mỗi ng&agrave;y mới đến lại mở ra nhiều lo toan... nhưng kh&ocirc;ng v&igrave; thế m&agrave; họ bỏ cuộc, chẳng những vươn l&ecirc;n, vượt qua những kh&oacute; khăn, m&agrave; họ c&ograve;n chung tay gi&uacute;p đỡ những ho&agrave;n cảnh k&eacute;m may mắn kh&aacute;c...</p>\n<p>Chương tr&igrave;nh giới thiệu đến cộng đồng những tấm gương người thật-việc thật trong đời thường. Họ l&agrave; những con người lu&ocirc;n cố gắng vượt qua nghịch cảnh, những con người gi&agrave;u l&ograve;ng nh&acirc;n &aacute;i sẵn s&agrave;ng gi&uacute;p đỡ những người c&ograve;n trong cảnh kh&oacute; khăn. Qua đ&oacute; x&acirc;y dựng niềm tin y&ecirc;u v&agrave; &yacute; thức cộng đồng trong việc sẻ chia tr&aacute;ch nhiệm v&agrave; động vi&ecirc;n mọi người vươn l&ecirc;n trong cuộc sống để biến ước mơ th&agrave;nh hiện thực.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: HTV9</p>\n<p>Thời lượng: 5 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2008-2014</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/hoa-cuoc-song-1.jpg\" alt=\"\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/hoa-cuoc-song-2.jpg\" alt=\"\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/hoa-cuoc-song-3.jpg\" alt=\"\" /></p>','Hoa cuộc sống','','',0,'http://tamdiem.com.vn/sources/images/hoa-cuoc-song.jpg','',0,0,1,0,0,0,1420140654,1420332980,1),(73,'Nhịp đập thành phổ trẻ','nhip-dap-thanh-pho-tre',70,'product_item','','','','<p><strong>NHỊP ĐẬP TH&Agrave;NH PHỐ TRẺ</strong></p>\n<p>Trong giai đoạn ph&aacute;t triển v&agrave; hội nhập, TpHCM lu&ocirc;n khẳng định được sức trẻ, sự năng động của m&igrave;nh qua rất nhiều hoạt động tr&ecirc;n mọi lĩnh vực từ đời sống, x&atilde; hội, ch&iacute;nh trị, kinh tế đến văn h&oacute;a, gi&aacute;o dục.</p>\n<p>Chương tr&igrave;nh ra đời như một k&ecirc;nh th&ocirc;ng tin li&ecirc;n tục cập nhật những thay đổi, những tiến bộ của th&agrave;nh phố. Đồng thời tổng hợp c&aacute;c vấn đề bất cập từ thực tiễn đời sống đ&ocirc; thị, đề xuất c&aacute;c giải ph&aacute;p, gợi &yacute; một c&aacute;ch nh&igrave;n t&iacute;ch cực, hướng đến sự ph&aacute;t triển lạc quan của th&agrave;nh phố.</p>\n<p>Chương tr&igrave;nh cũng l&agrave; một diễn đ&agrave;n cho c&aacute;c cơ quan quản l&yacute; Nh&agrave; nước, chủ đầu tư c&ocirc;ng tr&igrave;nh, chủ dự &aacute;n, doanh nghiệp, khu phố, hộ nh&acirc;n d&acirc;n&hellip; c&ugrave;ng trao đổi th&ocirc;ng tin đa chiều.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: HTV9</p>\n<p>Thời lượng: 5 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2008-2014</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/vioJ8Cw_UCI\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/nhip-dap-thanh-pho-tre1.jpg\" alt=\"\" width=\"425\" height=\"237\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/nhip-dap-thanh-pho-tre2.jpg\" alt=\"\" width=\"428\" height=\"244\" /></p>','Nhịp đập thành phổ trẻ','','',0,'http://tamdiem.com.vn/sources/images/nh%E1%BB%8Bp-%C4%91%E1%BA%ADp-th%C3%A0nh-ph%E1%BB%91-tr%E1%BA%BB.jpg','',0,0,0,0,0,0,1420140905,1421401749,1),(74,'Tiếp sức người thầy','tiep-suc-nguoi-thay',70,'product_item','','','Mỗi chương trình là một hình ảnh, một tấm gương của thầy cô trên khắp mọi miền đất nước, là những bài học về nghị lực và ý chí phi thường của thầy cô trong cuộc sống.\n\nĐây cũng là một nhịp cầu mà qua đó, kêu gọi toàn xã hội chung vai gánh vác 1 phần trách nhiệm giúp đỡ những người thầy người cô có hoàn cảnh khó khăn vươn lên trong cuộc sống, tiếp tục cống hiến chăm lo vun đắp cho thế hệ tương lai. Đồng thời khẳng định truyền thống tôn sư trọng đạo của người Việt Nam.','<p><strong>TIẾP SỨC NGƯỜI THẦY</strong></p>\n<p>Mỗi chương tr&igrave;nh l&agrave; một h&igrave;nh ảnh, một tấm gương của thầy c&ocirc; tr&ecirc;n khắp mọi miền đất nước, l&agrave; những b&agrave;i học về nghị lực v&agrave; &yacute; ch&iacute; phi thường của thầy c&ocirc; trong cuộc sống.</p>\n<p>Đ&acirc;y cũng l&agrave; một nhịp cầu m&agrave; qua đ&oacute;, k&ecirc;u gọi to&agrave;n x&atilde; hội chung vai g&aacute;nh v&aacute;c 1 phần tr&aacute;ch nhiệm gi&uacute;p đỡ những người thầy người c&ocirc; c&oacute; ho&agrave;n cảnh kh&oacute; khăn vươn l&ecirc;n trong cuộc sống, tiếp tục cống hiến chăm lo vun đắp cho thế hệ tương lai. Đồng thời khẳng định truyền thống t&ocirc;n sư trọng đạo của người Việt Nam.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: HTV9</p>\n<p>Thời lượng: 10 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng: 2009-2011</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/p9HQM7gpoH8\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/tiep-suc-nguoi-thay.jpg\" alt=\"\" width=\"422\" height=\"315\" /></p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/tiep-suc-nguoi-thay1.jpg\" alt=\"\" width=\"431\" height=\"311\" /></p>','Tiếp sức người thầy','','',0,'http://tamdiem.com.vn/sources/images/ti%E1%BA%BFp-s%E1%BB%A9c-ng%C6%B0%E1%BB%9Di-th%E1%BA%A7y.jpg','',0,0,1,0,0,0,1420141108,1421402109,1),(75,'Thiết kế phim trường ảo','thiet-ke-phim-truong-ao',20,'cate_product','','','','<p>Hiện nay tất cả c&aacute;c bản tin dự b&aacute;o thời tiết của T&acirc;m Điểm đều sử dụng phim trường ảo do T&acirc;m Điểm tự thiết kế. Kh&ocirc;ng chỉ gi&uacute;p tiết kiệm chi ph&iacute; đầu tư cơ sở hạ tầng v&agrave; trang thiết bị, phim trường ảo c&ograve;n c&oacute; thể tạo ra những g&oacute;c quay đẹp, điều chỉnh linh hoạt, ph&ugrave; hợp y&ecirc;u cầu h&igrave;nh ảnh cụ thể. Những kh&ocirc;ng gian v&ocirc; tận được tạo ra từ c&aacute;c kỹ thuật vi&ecirc;n đồ họa c&oacute; thể được đưa v&agrave;o chương tr&igrave;nh.</p>\n<p>C&ugrave;ng với sự ph&aacute;t triển của c&ocirc;ng nghệ truyền h&igrave;nh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin, giải ph&aacute;p n&agrave;y l&agrave; sự thay thế hữu hiệu cho phim trường truyền thống.</p>','Thiết kế phim trường ảo','','',0,'','',0,0,0,0,0,0,1420164208,1420164208,1),(76,'1','phim-truong-ao',75,'product_item','','','Hiện nay tất cả các bản tin dự báo thời tiết của Tâm Điểm đều sử dụng phim trường ảo do Tâm Điểm tự thiết kế. Không chỉ giúp tiết kiệm chi phí đầu tư cơ sở hạ tầng và trang thiết bị, phim trường ảo còn có thể tạo ra những góc quay đẹp, điều chỉnh linh hoạt, phù hợp yêu cầu hình ảnh cụ thể. Những không gian vô tận được tạo ra từ các kỹ thuật viên đồ họa có thể được đưa vào chương trình.','<p>Hiện nay tất cả c&aacute;c bản tin dự b&aacute;o thời tiết của T&acirc;m Điểm đều sử dụng phim trường ảo do T&acirc;m Điểm tự thiết kế. Kh&ocirc;ng chỉ gi&uacute;p tiết kiệm chi ph&iacute; đầu tư cơ sở hạ tầng v&agrave; trang thiết bị, phim trường ảo c&ograve;n c&oacute; thể tạo ra những g&oacute;c quay đẹp, điều chỉnh linh hoạt, ph&ugrave; hợp y&ecirc;u cầu h&igrave;nh ảnh cụ thể. Những kh&ocirc;ng gian v&ocirc; tận được tạo ra từ c&aacute;c kỹ thuật vi&ecirc;n đồ họa c&oacute; thể được đưa v&agrave;o chương tr&igrave;nh.</p>\n<p>C&ugrave;ng với sự ph&aacute;t triển của c&ocirc;ng nghệ truyền h&igrave;nh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin, giải ph&aacute;p n&agrave;y l&agrave; sự thay thế hữu hiệu cho phim trường truyền thống.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/thiet-ke-phim-truong-ao1.jpg\" alt=\"\" /></p>','Phim trường ảo','','',0,'http://tamdiem.com.vn/sources/images/thi%E1%BA%BFt-k%E1%BA%BF-phim-tr%C6%B0%E1%BB%9Dn-%E1%BA%A3o.jpg','',0,0,0,0,0,0,1420164477,1420333389,1),(77,'2','2',75,'product_item','','','','<p>Hiện nay tất cả c&aacute;c bản tin dự b&aacute;o thời tiết của T&acirc;m Điểm đều sử dụng phim trường ảo do T&acirc;m Điểm tự thiết kế. Kh&ocirc;ng chỉ gi&uacute;p tiết kiệm chi ph&iacute; đầu tư cơ sở hạ tầng v&agrave; trang thiết bị, phim trường ảo c&ograve;n c&oacute; thể tạo ra những g&oacute;c quay đẹp, điều chỉnh linh hoạt, ph&ugrave; hợp y&ecirc;u cầu h&igrave;nh ảnh cụ thể. Những kh&ocirc;ng gian v&ocirc; tận được tạo ra từ c&aacute;c kỹ thuật vi&ecirc;n đồ họa c&oacute; thể được đưa v&agrave;o chương tr&igrave;nh.</p>\n<p>C&ugrave;ng với sự ph&aacute;t triển của c&ocirc;ng nghệ truyền h&igrave;nh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin, giải ph&aacute;p n&agrave;y l&agrave; sự thay thế hữu hiệu cho phim trường truyền thống.</p>\n<p>&nbsp;<img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/thiet-ke-phim-truong-ao2.jpg\" alt=\"\" /></p>','2','','',0,'http://tamdiem.com.vn/sources/images/thi%E1%BA%BFt-k%E1%BA%BF-phim-tr%C6%B0%E1%BB%9Dn-%E1%BA%A3o2.jpg','',0,0,0,0,0,0,1420165489,1440843457,1),(78,'3','3',75,'product_item','','','','<p>Hiện nay tất cả c&aacute;c bản tin dự b&aacute;o thời tiết của T&acirc;m Điểm đều sử dụng phim trường ảo do T&acirc;m Điểm tự thiết kế. Kh&ocirc;ng chỉ gi&uacute;p tiết kiệm chi ph&iacute; đầu tư cơ sở hạ tầng v&agrave; trang thiết bị, phim trường ảo c&ograve;n c&oacute; thể tạo ra những g&oacute;c quay đẹp, điều chỉnh linh hoạt, ph&ugrave; hợp y&ecirc;u cầu h&igrave;nh ảnh cụ thể. Những kh&ocirc;ng gian v&ocirc; tận được tạo ra từ c&aacute;c kỹ thuật vi&ecirc;n đồ họa c&oacute; thể được đưa v&agrave;o chương tr&igrave;nh.</p>\n<p>C&ugrave;ng với sự ph&aacute;t triển của c&ocirc;ng nghệ truyền h&igrave;nh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin, giải ph&aacute;p n&agrave;y l&agrave; sự thay thế hữu hiệu cho phim trường truyền thống.</p>\n<p>&nbsp;<img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/images/thiet-ke-phim-truong-ao3.jpg\" alt=\"\" /></p>','3','','',0,'http://tamdiem.com.vn/sources/images/thi%E1%BA%BFt-k%E1%BA%BF-phim-tr%C6%B0%E1%BB%9Dn-%E1%BA%A3o3.jpg','',0,0,0,0,0,0,1420165540,1420333591,1),(79,'Tin tức','tin-tuc',20,'cate_product','','','','','Tin tức','','',0,'','',0,0,0,0,0,0,1420166030,1420166030,1),(80,'Chuyện hôm nay','chuyen-hom-nay',79,'product_item','','','','<p><strong><img src=\"/tinymce/source/Hinh up bai/3.png\" alt=\"\" width=\"270\" /><img src=\"/tinymce/source/images/chuyen-hom-nay.jpg\" alt=\"\" width=\"250\" /><img src=\"/tinymce/source/Hinh up bai/5.png\" alt=\"\" width=\"260\" /></strong></p>\n<p><strong>CHUYỆN H&Ocirc;M NAY</strong></p>\n<p>Chương tr&igrave;nh cập nhật tin tức, sự kiện diễn ra h&agrave;ng ng&agrave;y tại Tp.HCM trong c&aacute;c lĩnh vực từ kinh tế, t&agrave;i ch&iacute;nh, văn h&oacute;a, gi&aacute;o dục đến đời sống gia đ&igrave;nh, sức khỏe, du lịch, giải tr&iacute;, mua sắm, hướng nghiệp.</p>\n<p>Được ph&aacute;t s&oacute;ng v&agrave;o mỗi buổi trưa c&aacute;c ng&agrave;y trong tuần. Chủ đề của chương tr&igrave;nh thay đổi mỗi ng&agrave;y nhằm tạo sự mới mẻ đồng thời cung cấp nhiều th&ocirc;ng tin bổ &iacute;ch gi&uacute;p kh&aacute;n giả truyền h&igrave;nh theo d&otilde;i được những sự kiện mới nhất của TpHCM.</p>\n<p>K&ecirc;nh ph&aacute;t s&oacute;ng: HTV7</p>\n<p>Thời lượng: 5 ph&uacute;t</p>\n<p>Năm ph&aacute;t s&oacute;ng : 2009-2012</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/k_nQApwowaE\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<p>&nbsp;</p>','Chuyện hôm nay','','',0,'http://tamdiem.com.vn/sources/images/chuy%E1%BB%87n-h%C3%B4m-nay.jpg','',0,0,0,0,2,0,1420166250,1421402294,1),(81,'Dự báo thời tiết','du-bao-thoi-tiet',79,'product_item','dự báo thời tiết, chương trình dự báo thời tiết, bản tin thời tiết, chương trình dự báo thời tiết, thời tiết biển, thời tiết đồng bằng, thời tiết tây nguyên, thời tiết trung du, thời tiết ba miền','bản tin dự báo thời tiết, chương trình dự báo thời tiết, dự báo thời tiết truyền hình, bản tin dự báo thời tiết trên htv7, bản tin dự báo thời tiết trên btv, dự báo thời tiết du lịch, thời tiết và cuộc sống','','<p><strong><img src=\"/tinymce/source/Hinh up bai/DU BAO THOI TIET.jpg\" alt=\"\" width=\"250\" height=\"188\" /><img src=\"/tinymce/source/Hinh up bai/VTC14_DL.png\" alt=\"\" width=\"250\" height=\"188\" /><img src=\"/tinymce/source/Hinh up bai/MC_CT43.JPG\" alt=\"\" width=\"250\" height=\"188\" /></strong></p>\n<p>&nbsp;</p>\n<p style=\"text-align: justify;\"><strong>DỰ B&Aacute;O THỜI TIẾT</strong></p>\n<p style=\"text-align: justify;\">Ngay từ khi th&agrave;nh lập, T&acirc;m Điểm l&agrave; đơn vị đi ti&ecirc;n phong trong việc đưa người dẫn chương tr&igrave;nh v&agrave;o chuỗi c&aacute;c bản tin &ldquo;Dự b&aacute;o thời tiết&rdquo;.</p>\n<p style=\"text-align: justify;\">Hiện nay c&aacute;c bản tin &ldquo;Dự b&aacute;o thời tiết&rdquo; của T&acirc;m Điểm đang được ph&aacute;t s&oacute;ng tr&ecirc;n hệ thống nhiều k&ecirc;nh truyền h&igrave;nh cả nước v&agrave; lần lượt s&aacute;ng tạo c&aacute;c phi&ecirc;n bản mới theo chủ đề nhằm đa dạng h&oacute;a v&agrave; cung cấp nhiều th&ocirc;ng tin tư vấn cho kh&aacute;n giả.</p>\n<p style=\"text-align: justify;\">C&aacute;c phi&ecirc;n bản &ldquo;Dự b&aacute;o thời tiết&rdquo;: Thời tiết tổng hợp, Thời tiết với Cuộc sống, Thời tiết Đ&ocirc; thị, Thời tiết Du lịch, Thời tiết Biển, Thời tiết N&ocirc;ng vụ.</p>\n<p style=\"text-align: justify;\">K&ecirc;nh ph&aacute;t s&oacute;ng: HTV7, VTC14, Cần Thơ 43, Ki&ecirc;n Giang, Long An</p>\n<p style=\"text-align: justify;\">Thời lượng: 3-5 ph&uacute;t</p>\n<p style=\"text-align: justify;\">Năm ph&aacute;t s&oacute;ng : từ&nbsp; năm 2004 đến nay</p>\n<p style=\"text-align: justify;\"><strong>Xem Video tham khảo:</strong></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: left;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Buổi s&aacute;ng tr&ecirc;n HTV7</strong> <strong>(10/09/2015)</strong> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Buổi trưa tr&ecirc;n HTV7&nbsp;&nbsp;(10/09/2015) &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; Buổi tối tr&ecirc;n HTV7 (09/09/2015) <br /></strong></p>\n<table>\n<tbody>\n<tr>\n<td><iframe src=\"http://www.youtube.com/embed/nJdm9OTI84s\" width=\"250\" height=\"200\"></iframe></td>\n<td><iframe src=\"http://www.youtube.com/embed/ZJyZCes7TDg\" width=\"250\" height=\"200\"></iframe></td>\n<td><iframe src=\"http://www.youtube.com/embed/hRc0Eu-iSH0\" width=\"250\" height=\"200\"></iframe></td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: left;\"><strong>&nbsp; &nbsp; &nbsp; Buổi tối tr&ecirc;n HTV7 (26/04/2015) &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; Buổi tối tr&ecirc;n HTV7 (27/04/2015)</strong></p>\n<table style=\"height: 21px;\" width=\"757\">\n<tbody>\n<tr>\n<td><iframe src=\"http://www.youtube.com/embed/yCGWWamWc1I\" width=\"250\" height=\"204\"></iframe><iframe src=\"http://www.youtube.com/embed/tI0LL3ViQGg\" width=\"247\" height=\"204\"></iframe></td>\n<td>&nbsp;</td>\n<td>&nbsp;</td>\n</tr>\n</tbody>\n</table>','Dự báo thời tiết','','',0,'http://tamdiem.com.vn/sources/images/DBTT.JPG','',0,0,0,0,1,0,1420166720,1441880413,1),(82,'Gõ cửa ngày mới','go-cua-ngay-moi',79,'product_item','','','','<p style=\"text-align: justify;\"><strong><img src=\"/tinymce/source/images/go-cua-ngay-moi.jpg\" alt=\"\" width=\"260\" /><img src=\"/tinymce/source/Hinh up bai/2.png\" alt=\"\" width=\"250\" /><img src=\"/tinymce/source/Hinh up bai/6.png\" alt=\"\" width=\"250\" /></strong></p>\n<p style=\"text-align: justify;\"><strong>G&Otilde; CỬA NG&Agrave;Y MỚI</strong></p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh thời sự cập nhật những hoạt động, những sự kiện nổi bật nhất của cả nước v&agrave;o mỗi buổi s&aacute;ng c&aacute;c ng&agrave;y trong tuần.</p>\n<p style=\"text-align: justify;\">Mỗi chương tr&igrave;nh sẽ giới thiệu đến kh&aacute;n giả truyền h&igrave;nh một nh&acirc;n vật đang c&ocirc;ng t&aacute;c trong 1 lĩnh vực ( ch&iacute;nh trị, kinh tế, văn h&oacute;a, nghệ thuật&hellip;), t&igrave;m hiểu những đ&oacute;ng g&oacute;p của họ với c&ocirc;ng ch&uacute;ng hay c&aacute;i nh&igrave;n của họ đối với những vấn đề n&oacute;ng của x&atilde; hội đang được đề cập trong chương tr&igrave;nh.</p>\n<p style=\"text-align: justify;\">K&ecirc;nh ph&aacute;t s&oacute;ng: VTV1</p>\n<p style=\"text-align: justify;\">Thời lượng: 5 ph&uacute;t</p>\n<p style=\"text-align: justify;\">Năm ph&aacute;t s&oacute;ng: 2013</p>\n<p style=\"text-align: center;\"><iframe src=\"//www.youtube.com/embed/Lu4Mty5BcU4\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>','Gõ cửa ngày mới','','',0,'http://tamdiem.com.vn/sources/images/g%C3%B5-c%E1%BB%ADa-ng%C3%A0y-m%E1%BB%9Bi.jpg','',0,0,0,0,3,0,1420166920,1421402412,1),(85,'TUYẾN GẤP KỸ THUẬT VIÊN ĐỒ HỌA','tuyen-gap-ky-thuat-vien-do-hOa',0,'job','','','Mô tả công việc: thiết kế, chỉnh sửa video/hình ảnh, xử lý âm thanh; dựng phim; biên tập hình ảnh các bản tin dự báo thời tiết…','<p><strong>M&ocirc; tả c&ocirc;ng việc:</strong> thiết kế, chỉnh sửa video/h&igrave;nh ảnh, xử l&yacute; &acirc;m thanh; dựng phim; bi&ecirc;n tập h&igrave;nh ảnh c&aacute;c bản tin dự b&aacute;o thời tiết&hellip;</p>\n<p><strong>Điều kiện tuyển dụng:</strong></p>\n<ul>\n<li>Biết về Graphic Design</li>\n<li>Sử dụng th&agrave;nh thạo c&aacute;c phần mềm: Ps, AE, AI, Corel Draw&hellip;</li>\n<li>C&oacute; học qua về mỹ thuật ứng dụng</li>\n<li>Ưu ti&ecirc;n những ứng vi&ecirc;n biết th&ecirc;m về quay phim v&agrave; phần mềm thiết kế đồ họa 3D</li>\n</ul>\n<p><strong>Mức lương:</strong> Thỏa thuận</p>\n<p>Ứng vi&ecirc;n nộp hồ sơ trước ng&agrave;y <strong>31/05/2015.</strong></p>\n<p>Mọi chi tiết vui l&ograve;ng li&ecirc;n hệ: (08) 3910 4081 &ndash; Gặp Ms Quy&ecirc;n (Ph&ograve;ng Sản xuất chương tr&igrave;nh).</p>','TUYẾN GẤP KỸ THUẬT VIÊN ĐỒ HỌA','','',0,'http://tamdiem.com.vn/sources/images/tuyendung.jpg','',0,0,0,0,1,0,1422441777,1431311967,1),(88,'Ngọc Giàu diễn bi hài trong phim \'Oshin nổi loạn\'','ngoc-giau-dien-bi-hai-trong-phim-oshin-noi-loan',16,'news_item','Ngọc Giàu, nghệ sĩ nhân dân, Oshin nổi loạn','Ở tuổi 70, nữ Nghệ sĩ Nhân dân thể hiện nét duyên diễn xuất linh hoạt trong phim mới đề cập đến mối quan hệ giữa người già và thế hệ trẻ.','Ở tuổi 70, nữ Nghệ sĩ Nhân dân thể hiện nét duyên diễn xuất linh hoạt trong phim mới đề cập đến mối quan hệ giữa người già và thế hệ trẻ.','<div id=\"page\">\n<div id=\"wrapper_container\">\n<div id=\"container\">\n<div class=\"width_common\">\n<div id=\"detail_page\" class=\"width_common line_col\">\n<div id=\"col_660\" class=\"left\">\n<div id=\"box_details_news\" class=\"box_width_common\">\n<div class=\"w670 left\">\n<div class=\"main_content_detail width_common\">\n<div class=\"width_common line_col_480 space_bottom_20\">\n<div class=\"block_col_480\">\n<h4 style=\"text-align: justify;\"><strong>Ở tuổi 70, nữ Nghệ sĩ Nh&acirc;n d&acirc;n thể hiện n&eacute;t duy&ecirc;n diễn xuất linh hoạt trong phim mới đề cập đến mối quan hệ giữa người gi&agrave; v&agrave; thế hệ trẻ.</strong></h4>\n<div class=\"short_intro txt_666\">&nbsp;</div>\n<div class=\"fck_detail width_common\">\n<table class=\"tplCaption\" style=\"width: 85.034%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-5-9840-1427084056.jpg\" alt=\"ngoc-giau-5-9840-1427084056.jpg\" data-pwidth=\"470.4\" data-width=\"400\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Đạo diễn L&ecirc; Ngọc Linh mời NSND Ngọc Gi&agrave;u đ&oacute;ng vai b&agrave; mẹ tật nguyền trong phim truyền h&igrave;nh mới mang t&ecirc;n \"Oshin nổi loạn\". Bộ phim c&ograve;n quy tụ d&agrave;n diễn vi&ecirc;n đa dạng như: Nghệ sĩ Ưu t&uacute; L&ecirc; Thiện (phải), ca sĩ Quang Th&agrave;nh, Phương Thanh, Ho&agrave;ng Anh, Tường Vy... Trong ảnh: Ngọc Gi&agrave;u v&agrave; L&ecirc; Thiện đều l&agrave; hai nghệ sĩ cao tuổi nhưng sau những cảnh quay vất vả họ vẫn thoải m&aacute;i đ&ugrave;a nghịch tr&ecirc;n phim trường.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 100%;\" src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-4-4752-1427084055.jpg\" alt=\"ngoc-giau-4-4752-1427084055.jpg\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Từ tr&aacute;i qua: ca sĩ Quang Th&agrave;nh, nghệ sĩ Ngọc Gi&agrave;u v&agrave; L&ecirc; Thiện. Vai b&agrave; mẹ của Ngọc Gi&agrave;u kh&ocirc;ng phải l&agrave; vai ch&iacute;nh của phim, nhưng đ&acirc;y l&agrave; một trong những nh&acirc;n vật c&oacute; thể lấy nước mắt lẫn tiếng cười của kh&aacute;n giả. B&agrave; v&agrave;o vai người mẹ qu&ecirc; bị liệt, phải được con h&agrave;ng ng&agrave;y c&otilde;ng theo khi đi&nbsp;l&agrave;m Oshin. C&ograve;n nghệ sĩ L&ecirc; Thiện (phải) đ&oacute;ng vai b&agrave; mẹ c&oacute; nickname ngộ nghĩnh l&agrave; T&ocirc;m Mẫu Ni, mẹ của T&ocirc;m Ho&agrave;ng Đế (do Quang Th&agrave;nh thể hiện).</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 100%;\" src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-6-8747-1427084057.jpg\" alt=\"ngoc-giau-6-8747-1427084057.jpg\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Trong phim, Ngọc Gi&agrave;u c&ograve;n ph&aacute;t huy thế mạnh ca vọng cổ, cải lương. Nh&acirc;n vật người mẹ của b&agrave; chuyển tải th&ocirc;ng điệp về việc g&igrave;n giữ những điều tưởng chừng như xưa cũ, nhưng l&agrave; gi&aacute; trị đạo đức trong lối sống d&agrave;nh cho giới trẻ.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 100%;\" src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-2-6344-1427084053.jpg\" alt=\"ngoc-giau-2-6344-1427084053.jpg\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Tr&ecirc;n phim trường, Ngọc Gi&agrave;u tỏ ra kh&ocirc;ng c&oacute; khoảng c&aacute;ch tuổi t&aacute;c với c&aacute;c diễn vi&ecirc;n. B&agrave; tung hứng hết sức v&agrave; lu&ocirc;n mang đến sự thoải m&aacute;i, dễ chịu cho mọi người. B&agrave; cũng rất nghi&ecirc;m t&uacute;c trong c&ocirc;ng việc. Ca sĩ Phương Thanh (tr&aacute;i) v&agrave;o vai người chuy&ecirc;n m&ocirc;i giới c&aacute;c Oshin cho nh&agrave; chủ.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 85.034%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-3-7792-1427084054.jpg\" alt=\"ngoc-giau-3-7792-1427084054.jpg\" data-pwidth=\"470.4\" data-width=\"400\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n nam ca sĩ Quang Th&agrave;nh (tr&aacute;i) đ&oacute;ng phim. Sống ở Mỹ, do t&acirc;m đắc với kịch bản mang nhiều &yacute; nghĩa nh&acirc;n văn, anh sắp xếp c&ocirc;ng việc trở về nước tham gia c&ugrave;ng c&aacute;c đồng nghiệp.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 100%;\" src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-7-2726-1427084058.jpg\" alt=\"ngoc-giau-7-2726-1427084058.jpg\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Từ tr&aacute;i qua: diễn vi&ecirc;n Ho&agrave;ng Anh v&agrave;o vai Kinh Long, Tường Vy thể hiện nh&acirc;n vật Tơ v&agrave; Quang Th&agrave;nh đ&oacute;ng Tom Ho&agrave;ng Đế. Đ&acirc;y l&agrave; tuyến nh&acirc;n vật ch&iacute;nh v&agrave; thứ ch&iacute;nh cho phim.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<table class=\"tplCaption\" style=\"width: 100%;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 100%;\" src=\"http://m.f9.img.vnecdn.net/2015/03/23/ngoc-giau-8-3425-1427095944.jpg\" alt=\"ngoc-giau-8.jpg\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-=\"\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Nghệ sĩ Thanh Thủy (tr&aacute;i) l&agrave; gi&aacute;m đốc sản xuất cho bộ phim. Phim đang trong qu&aacute; tr&igrave;nh thực hiện, dự kiến ra mắt kh&aacute;n giả v&agrave;o m&ugrave;a h&egrave; với 36 tập ph&aacute;t s&oacute;ng tr&ecirc;n truyền h&igrave;nh.</p>\n<p class=\"Image\" style=\"text-align: right;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Theo VNE</p>\n</td>\n</tr>\n</tbody>\n</table>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>\n</div>','Ngọc Giàu diễn bi hài trong phim \'Oshin nổi loạn\'','','',0,'http://tamdiem.com.vn/sources/images/ngoc%20giau.jpg','',0,0,0,0,199,0,1427279083,1442286911,1),(89,'Phương Thanh khóc nức nở vì bị tát giữa phim trường','phuong-thanh-khoc-nuc-no-vi-bi-tat-giua-phim-truong',16,'news_item','Phương Thanh, Thanh Thủy, oshin nổi loạn','Để hóa thân trong một cảnh quay, nữ ca sĩ đã không ngại diễn xuất như thật. Và cuối cùng, chị đã phải hứng lấy một cái tát như trời giáng.','Để hóa thân trong một cảnh quay, nữ ca sĩ đã không ngại diễn xuất như thật. Và cuối cùng, chị đã phải hứng lấy một cái tát như trời giáng.','<h4 style=\"text-align: justify;\"><strong>Để h&oacute;a th&acirc;n trong một cảnh quay, nữ ca sĩ đ&atilde; kh&ocirc;ng ngại diễn xuất như thật. V&agrave; cuối c&ugrave;ng, chị đ&atilde; phải hứng lấy một c&aacute;i t&aacute;t như trời gi&aacute;ng.</strong></h4>\n<div class=\"fon34 mt3 mr2 fon43 detail-content\">\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\">\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-001-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh <em>&Ocirc;-sin nổi loạn.</em></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-002-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Phương Thanh kể, sau nhiều lần bị t&aacute;t chưa thật n&ecirc;n cảnh quay kh&ocirc;ng đạt hiệu quả như mong muốn, nữ ca sĩ đ&atilde; chấp nhận hy sinh \"ăn t&aacute;t\" thật.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-003-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">V&agrave; c&ograve;n hơn cả những g&igrave; mong đợi, với c&uacute; t&aacute;t như trời gi&aacute;ng từ tay nghệ sĩ Thanh Thủy, Phương Thanh đ&atilde; kh&oacute;c nức nở v&agrave; diễn xuất ho&agrave;n hảo ph&acirc;n cảnh n&agrave;y.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-004-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Đ&acirc;y cũng l&agrave; ph&acirc;n cảnh m&agrave; Phương Thanh nghĩ l&agrave; kh&oacute; nhất v&agrave; ấn tượng nhất giữa chị với nghệ sĩ Thanh Thủy.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-005-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Trong phim n&agrave;y, Phương Thanh h&oacute;a th&acirc;n v&agrave;o vai của một người m&ocirc;i giới &ocirc;-sin với nhiều t&iacute;nh c&aacute;nh kh&aacute;c nhau trong t&acirc;m l&yacute; diễn xuất.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-006-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Nữ ca sĩ hớn hở chia sẻ, mặc d&ugrave; phim ảnh chỉ l&agrave; sở đoản, sở trường nhưng v&igrave; t&igrave;nh cảm qu&aacute; lớn với c&aacute;c đạo diễn, c&aacute;c nh&agrave; l&agrave;m phim. Hơn nữa, phim ảnh cũng gi&uacute;p nữ ca sĩ phủ s&oacute;ng h&igrave;nh ảnh rộng r&atilde;i hơn n&ecirc;n đ&atilde; quyết định tạm ngưng việc ca h&aacute;t đến giữa năm nay, để tập trung cho việc đ&oacute;ng phim.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-007-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Phương Thanh c&ograve;n tiết lộ th&ecirc;m, thời gian trong Tết, nữ ca sĩ cũng đ&atilde; tranh thủ chạy show để đủ sống cho suốt mấy th&aacute;ng đ&oacute;ng phim.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-008-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Ngo&agrave;i ra, thời gian tới, chị c&ograve;n đảm nhận vai tr&ograve; gi&aacute;m khảo của hai chương tr&igrave;nh gameshow. Sau khi kết th&uacute;c c&ocirc;ng việc n&agrave;y, nữ ca sĩ <em>Trống vắng</em> mới t&aacute;i ngộ với &acirc;m nhạc.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-009-0b973.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">L&agrave; người tương t&aacute;c rất tốt với c&aacute;c fans qua trang c&aacute; nh&acirc;n, Phương Thanh thường xuy&ecirc;n cập nhật th&ocirc;ng tin, h&igrave;nh ảnh, cảm x&uacute;c rất đời thường để chia sẻ c&ugrave;ng mọi người tr&ecirc;n Facebook. V&igrave; vậy, mỗi ng&agrave;y nữ ca sĩ c&agrave;ng nhận được rất nhiều t&igrave;nh cảm của kh&aacute;n giả.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-010-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Với Phương Thanh, d&ugrave; l&agrave; vai diễn nhỏ hay lớn, ch&iacute;nh hay phụ th&igrave; nữ ca sĩ vẫn l&agrave;m việc hết m&igrave;nh v&agrave; hết khả năng để mang lại th&agrave;nh c&ocirc;ng nhất cho vai diễn của m&igrave;nh.</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">&nbsp;</span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\"><img style=\"margin: 5px;\" src=\"http://dantri4.vcmedia.vn/SUgiZlbyqa0QDIWkn7VPLPvNnx84JL/Image/2015/03/pthanh-011-926a9.jpg\" alt=\"Đ&acirc;y l&agrave; một cảnh quay giữa ca sĩ Phương Thanh v&agrave; nghệ sĩ Thanh Thủy trong bộ phim truyền h&igrave;nh\" width=\"450\" align=\"middle\" /></span></div>\n<div style=\"text-align: center;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">Tr&ecirc;n phim trường, Phương Thanh c&ograve;n nhận được sự chia sẻ v&agrave; hướng dẫn chuy&ecirc;n m&ocirc;n của nghệ sĩ Thanh Thủy, đồng thời cũng l&agrave; gi&aacute;m đốc sản xuất của bộ phim d&agrave;i 36 tập n&agrave;y.</span></div>\n<div style=\"text-align: right;\"><span style=\"font-size: 10pt; font-family: Tahoma;\">(Theo D&acirc;n tr&iacute;)</span></div>\n<div>&nbsp;</div>\n</div>\n</div>\n</div>\n</div>','Phương Thanh khóc nức nở vì bị tát giữa phim trường','','',0,'http://tamdiem.com.vn/sources/images/pthanh.jpg','',0,0,0,0,198,0,1427338180,1442286922,1),(95,'Hậu trường thú vị của phim “Những nàng ô-sin nổi loạn”','hau-truong',16,'news_item','Hậu trường thú vị của phim “Những nàng ô-sin nổi loạn”','Vào cuối tháng 9 tới đây, khán giả sẽ được thưởng thức bộ phim hài-tâm lý xã hội “Những nàng Ô-sin nổi loạn” gồm 36 tập được phát sóng trên kênh truyền hình Vĩnh Long. Hiện phim đang trong quá trình thực hiện với nhiều hình ảnh hài hước bên lề được ghi nhận','Vào cuối tháng 9 tới đây, khán giả sẽ được thưởng thức bộ phim hài-tâm lý xã hội “Những nàng Ô-sin nổi loạn” gồm 36 tập được phát sóng trên kênh truyền hình Vĩnh Long. Hiện phim đang trong quá trình thực hiện với nhiều hình ảnh hài hước bên lề được ghi nhận','<h4>V&agrave;o cuối th&aacute;ng 9 tới đ&acirc;y, kh&aacute;n giả sẽ được thưởng thức bộ phim mới &ldquo;Những n&agrave;ng &Ocirc;-sin nổi loạn&rdquo; được ph&aacute;t s&oacute;ng tr&ecirc;n k&ecirc;nh truyền h&igrave;nh Vĩnh Long. Hiện phim đang trong qu&aacute; tr&igrave;nh thực hiện với nhiều h&igrave;nh ảnh h&agrave;i hước b&ecirc;n lề được ghi nhận:</h4>\n<p><img src=\"/tinymce/source/20150306_114121.jpg\" alt=\"\" width=\"500\" height=\"700\" /></p>\n<p>Nghệ sĩ Thanh Thủy bị đ&aacute;nh m&eacute;o mặt tr&ecirc;n trường quay.</p>\n<p><img src=\"/tinymce/source//20150310_113718.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>Gi&acirc;y ph&uacute;t đứng h&igrave;nh của Tường Vy.</p>\n<p><img src=\"/tinymce/source//20150309_170852.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>Đọ sức xem ai khỏe hơn?</p>\n<p><img src=\"/tinymce/source//20150313_111620.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>Người lớn cũng kh&ocirc;ng tha&hellip;</p>\n<p><img src=\"/tinymce/source//20150311_180706.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>Em đẹp chẳng thua mấy chị đ&acirc;u nha!</p>\n<p><img src=\"/tinymce/source/20150304_205547.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>1,2,3... xem ai ngầu hơn!</p>\n<p><img src=\"/tinymce/source//20150312_191020.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\n<p>Đ&atilde; giả vờ ngủ m&agrave; vẫn bị n&agrave;ng &ocirc;-sin n&agrave;y tra tấn, thiệt l&agrave; khổ qu&aacute; đi m&agrave;!</p>\n<p><img src=\"/tinymce/source/20150316_140704.jpg\" alt=\"\" width=\"500\" height=\"700\" /></p>\n<p>Sao con c&otilde;ng ho&agrave;i vẫn kh&ocirc;ng l&ecirc;n được nhỉ!?</p>\n<p><img src=\"/tinymce/source//20150325_164330.jpg\" alt=\"\" width=\"500\" height=\"700\" /></p>\n<p>N&agrave;ng &ocirc;-sin&nbsp; thời @.</p>\n<p>&nbsp;</p>\n<table style=\"height: 193px;\" border=\"1\" width=\"506\">\n<tbody>\n<tr>\n<td>\n<h4 style=\"text-align: center;\"><strong>NHỮNG N&Agrave;NG &Ocirc;SIN NỔI LOẠN</strong></h4>\n<h4 style=\"text-align: justify;\"><strong>&nbsp;Thực hiện</strong>: CTY TNHH T&Acirc;M ĐIỂM phối hợp CTY TNHH NGHỆ THUẬT THANH THỦY</h4>\n<h4 style=\"text-align: justify;\"><strong>&nbsp;Thể loại</strong> : H&agrave;i -T&acirc;m l&yacute; x&atilde; hội</h4>\n<h4 style=\"text-align: justify;\"><strong>&nbsp;Thời lượng</strong>: 36 tập ( 45 ph&uacute;t/1 tập)</h4>\n<h4 style=\"text-align: justify;\"><strong>&nbsp;Diễn vi&ecirc;n</strong>: NSND Ngọc Gi&agrave;u, NSUT L&ecirc; Thiện, ca sĩ Phương Thanh, Ho&agrave;ng Anh, Tường Vy...</h4>\n<div id=\"stcpDiv\" style=\"position: absolute; top: -1999px; left: -1988px;\">Bộ phim c&ograve;n quy tụ d&agrave;n diễn vi&ecirc;n đa dạng như: Nghệ sĩ Ưu t&uacute; L&ecirc; Thiện (phải), ca sĩ Quang Th&agrave;nh, Phương Thanh, Ho&agrave;ng Anh, Tường Vy... - See more at: http://tamdiem.com.vn/tin-tuc/ngoc-giau-dien-bi-hai-trong-phim-oshin-noi-loan.html#detail_entry</div>\n</td>\n</tr>\n</tbody>\n</table>','Hậu Trường','','',0,'http://tamdiem.com.vn/sources/images/1.jpg','',0,0,0,0,197,0,1428288971,1442286939,1),(96,'Ra mắt Hội Nữ doanh nhân TPHCM','ra-mat-hoi-nu-doanh-nhan-tphcm',14,'news_item','Ra mắt Hội Nữ doanh nhân TPHCM','Sáng 5/4/2015 tại Hội trường Thành phố, Đại hội Nữ Doanh nhân TP HCM lần thứ I, nhiệm kì 2015-2020 đã diễn ra trong không khí vô cùng vui tươi và phấn khởi với sự tham gia của Phó Chủ tịch Nước Nguyễn Thị Doan, cùng nhiều Lãnh đạo đại diện các Tổ chức Hội, Hiệp hội doanh nhân trên toàn quốc và 165 đại biểu là hội viên cá nhân, tập thể của Hội.','Sáng 5/4/2015 tại Hội trường Thành phố, Đại hội Nữ Doanh nhân TP HCM lần thứ I, nhiệm kì 2015-2020 đã diễn ra trong không khí vô cùng vui tươi và phấn khởi với sự tham gia của Phó Chủ tịch Nước Nguyễn Thị Doan, cùng nhiều Lãnh đạo đại diện các Tổ chức Hội, Hiệp hội doanh nhân trên toàn quốc và 165 đại biểu là hội viên cá nhân, tập thể của Hội.','<p>S&aacute;ng 5/4/2015 tại Hội trường Th&agrave;nh phố, Đại hội Nữ Doanh nh&acirc;n TP HCM lần thứ I, nhiệm k&igrave; 2015-2020 đ&atilde; diễn ra trong kh&ocirc;ng kh&iacute; v&ocirc; c&ugrave;ng vui tươi v&agrave; phấn khởi với sự tham gia của Ph&oacute; Chủ tịch Nước Nguyễn Thị Doan c&ugrave;ng nhiều L&atilde;nh đạo đại diện c&aacute;c Tổ chức Hội, Hiệp hội doanh nh&acirc;n tr&ecirc;n to&agrave;n quốc v&agrave; 165 đại biểu l&agrave; hội vi&ecirc;n c&aacute; nh&acirc;n, tập thể của Hội.</p>\n<p><img src=\"/tinymce/source/8.png\" alt=\"\" width=\"700\" height=\"400\" /></p>\n<p>Với sứ mệnh li&ecirc;n kết, Hội Nữ Doanh nh&acirc;n TPHCM (HAWEE) sẽ l&agrave; nơi tập hợp c&aacute;c nữ doanh nh&acirc;n thuộc mọi th&agrave;nh phần kinh tế tr&ecirc;n địa b&agrave;n Th&agrave;nh phố để ph&aacute;t huy t&agrave;i năng, tr&iacute; tuệ v&agrave; sức mạnh của doanh nh&acirc;n nữ trong ph&aacute;t triển kinh tế, n&acirc;ng cao hiệu quả sản xuất, kinh doanh, dịch vụ, bảo vệ lợi &iacute;ch hợp ph&aacute;p của hội vi&ecirc;n, tạo cơ hội cho c&aacute;c phụ nữ trao nhau sức mạnh để c&acirc;n bằng v&agrave; tiến bộ trong đời sống bản th&acirc;n, kinh doanh v&agrave; gia đ&igrave;nh; tham gia v&agrave;o c&aacute;c hoạt động x&atilde; hội gi&uacute;p n&acirc;ng cao điều kiện sống v&agrave; kĩ năng kinh doanh cho phụ nữ trong ph&aacute;t triển kinh tế hộ gia đ&igrave;nh, bảo vệ m&ocirc;i trường v&agrave; ph&aacute;t triển gi&aacute;o dục, thực hiện c&aacute;c mục ti&ecirc;u tiến bộ v&agrave; b&igrave;nh đẳng giới.</p>\n<p><img src=\"/tinymce/source//6.png\" alt=\"\" width=\"700\" height=\"400\" /></p>\n<p>Sau phi&ecirc;n họp nội bộ, Đại hội Nữ Doanh Nh&acirc;n TP HCM lần thứ nhất đ&atilde; bầu chọn b&agrave; Cao Thị Ngọc Dung, Chủ tịch HĐQT C&ocirc;ng ty v&agrave;ng bạc đ&aacute; qu&yacute; Ph&uacute; Nhuận l&agrave;m Chủ tịch đầu ti&ecirc;n - Nhiệm k&igrave; I (2015-2020) của Hội Nữ Doanh Nh&acirc;n TP HCM. Tiếp theo đ&oacute; lần lượt c&aacute;c th&agrave;nh vi&ecirc;n Ban chấp h&agrave;nh, Ban Thường vụ, Ban kiểm tra được bầu chọn v&agrave; ra mắt tại đại hội.</p>\n<p><img src=\"/tinymce/source//7png.png\" alt=\"\" width=\"700\" height=\"400\" /></p>\n<p>Trong giai đoạn đầu, hội Nữ Doanh Nh&acirc;n TP HCM định hướng x&acirc;y dựng v&agrave; ho&agrave;n thiện cơ cấu tổ chức Hội chuy&ecirc;n nghiệp, vững mạnh qua c&aacute;c hoạt động trọng t&acirc;m năm 2015 như:</p>\n<ol>\n<li>Tuyển dụng v&agrave; đ&agrave;o tạo nh&acirc;n sự, x&acirc;y dựng c&aacute;c quy chế hoạt động, x&acirc;y dựng cơ sở dữ liệu hội vi&ecirc;n, website, blog.</li>\n<li>Kết nối hoạt động với Hiệp hội Nữ doanh nh&acirc;n Việt nam VAWE v&agrave; c&aacute;c hội, c&acirc;u lạc bộ nữ doanh nh&acirc;n c&aacute;c địa phương kh&aacute;c trong cả nước. Kết nối hoạt động với c&aacute;c tổ chức quốc tế v&agrave; c&aacute;c tổ chức doanh nghiệp nước ngo&agrave;i tr&ecirc;n địa b&agrave;n TPHCM;</li>\n<li>Khảo s&aacute;t nhu cầu hội vi&ecirc;n, x&acirc;y dựng c&aacute;c chương tr&igrave;nh sinh hoạt, c&acirc;u lạc bộ về cơ hội giao thương giữa c&aacute;c nh&oacute;m ng&agrave;nh nghề, chia sẻ kinh nghiệm quản trị kinh doanh, cuộc sống gia đ&igrave;nh, phong c&aacute;ch sống.</li>\n<li>X&acirc;y dựng v&agrave; triển khai chương tr&igrave;nh Đối t&aacute;c c&ugrave;ng HAWEE để đem lại lợi &iacute;ch thiết thực cho hội v&agrave; hội vi&ecirc;n.</li>\n<li>X&acirc;y dựng c&aacute;c chương tr&igrave;nh, sự kiện, hoạt động x&atilde; hội cho HAWEE.</li>\n</ol>\n<p style=\"text-align: right;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&acirc;m Điểm</p>','Ra mắt Hội Nữ doanh nhân TPHCM','','',0,'http://tamdiem.com.vn/sources/images/1_1.jpg','',0,0,0,0,496,0,1428401746,1452659992,1),(102,'Chương trình văn nghệ đặc biệt ','chuong-trinh-van-nghe-dac-biet-ngay-hoi-non-song',14,'news_item','Chương trình văn nghệ đặc biệt ','Hòa trong không khí cả nước chào mừng kỷ niệm 40 năm ngày miền Nam hoàn toàn giải phóng, thống nhất Đất nước (30/04/1975-30/04/2015) và 125 năm ngày sinh Chủ tịch Hồ Chí Minh, Công ty TNHH Tâm Điểm hợp cùng Đài PTTH Vĩnh Long tổ chức chương trình văn nghệ đặc biệt với chủ đề ““Ngày Hội Non Sông” - diễn ra tối ngày 29/04/2015, tại Quảng trường TP. Vĩnh Long.','Hòa trong không khí cả nước chào mừng kỷ niệm 40 năm ngày miền Nam hoàn toàn giải phóng, thống nhất Đất nước (30/04/1975-30/04/2015) và 125 năm ngày sinh Chủ tịch Hồ Chí Minh, Công ty TNHH Tâm Điểm hợp cùng Đài PTTH Vĩnh Long tổ chức chương trình văn nghệ đặc biệt với chủ đề ““Ngày Hội Non Sông” - diễn ra tối ngày 29/04/2015, tại Quảng trường TP. Vĩnh Long.','<p style=\"text-align: justify;\"><strong>H&ograve;a trong kh&ocirc;ng kh&iacute; cả nước ch&agrave;o mừng kỷ niệm 40 năm ng&agrave;y miền Nam ho&agrave;n to&agrave;n giải ph&oacute;ng, thống nhất Đất nước (30/04/1975-30/04/2015) v&agrave; 125 năm ng&agrave;y sinh Chủ tịch Hồ Ch&iacute; Minh, C&ocirc;ng ty TNHH T&acirc;m Điểm hợp c&ugrave;ng Đ&agrave;i ph&aacute;t thanh-truyền h&igrave;nh Vĩnh Long tổ chức chương tr&igrave;nh văn nghệ đặc biệt với chủ đề &ldquo;&ldquo;Ng&agrave;y Hội Non S&ocirc;ng&rdquo; - diễn ra tối ng&agrave;y 29/04/2015, tại Quảng trường TP. Vĩnh Long.</strong></p>\n<p style=\"text-align: justify;\">Đến tham dự chương tr&igrave;nh c&oacute; sự hiện diện của &ocirc;ng Trần Văn R&oacute;n - B&iacute; thư Tỉnh ủy, Chủ tịch Ủy ban Nh&acirc;n d&acirc;n tỉnh Vĩnh Long; &ocirc;ng Đỗ Ho&agrave;ng Huynh - Chủ tịch Ủy ban Mặt tr&acirc;n Tổ quốc Việt Nam, tỉnh Vĩnh Long; c&ugrave;ng c&aacute;c đồng ch&iacute; l&agrave; nguy&ecirc;n l&atilde;nh đạo tỉnh, l&atilde;nh đạo th&agrave;nh phố, c&aacute;c sở ban ng&agrave;nh đo&agrave;n thể, c&aacute;c vị l&atilde;o th&agrave;nh c&aacute;ch mạng đại diện nh&acirc;n sỹ, tr&iacute; thức, chức sắc, t&ocirc;n gi&aacute;o v&agrave; đ&ocirc;ng đảo kh&aacute;n giả c&oacute; mặt tại quảng trường, kh&aacute;n giả theo d&otilde;i chương tr&igrave;nh truyền h&igrave;nh trực tiếp tr&ecirc;n k&ecirc;nh THVL1.</p>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//IMG_8694.jpg\" alt=\"\" width=\"780\" height=\"500\" /></p>\n<p style=\"text-align: justify;\">Trong suốt hai giờ diễn ra, chương tr&igrave;nh mang đến cho kh&aacute;n giả 20 tiết mục, gồm những b&agrave;i h&aacute;t ca ngợi Đảng, ca ngợi B&aacute;c Hồ, t&igrave;nh y&ecirc;u qu&ecirc; hương đất nước, về cuộc kh&aacute;ng chiến trường kỳ đưa d&acirc;n tộc ta đến ng&agrave;y thống nhất, non s&ocirc;ng thu về một mối với sự g&oacute;p mặt của c&aacute;c nghệ sĩ, ca sĩ đến từ TP Hồ Ch&iacute; Minh như: NSƯT Tạ Minh T&acirc;m, Ngọc &Aacute;nh, Đan Trường, Kh&aacute;nh Ngọc, Đ&ocirc;ng Đ&agrave;o, Phương Thanh, L&acirc;m Ngọc Hoa,...</p>\n<p><img src=\"/tinymce/source/TMT.PNG\" alt=\"\" /></p>\n<p style=\"text-align: center;\">NSƯT Tạ Minh T&acirc;m tr&agrave;n đầy kh&iacute; thế khi thể hiện b&agrave;i h&aacute;t &ldquo;Đất nước trọn niềm vui&rdquo;</p>\n<p>&nbsp;</p>\n<p><img src=\"/tinymce/source//4.PNG\" alt=\"\" /></p>\n<p style=\"text-align: center;\">Ca sĩ Đ&ocirc;ng Đ&agrave;o dịu d&agrave;ng với ca kh&uacute;c &ldquo;Trường Sơn Đ&ocirc;ng Trường Sơn T&acirc;y&rdquo;</p>\n<p>&nbsp;</p>\n<p><img src=\"/tinymce/source//NA.PNG\" alt=\"\" /></p>\n<p style=\"text-align: center;\">Ca sĩ Ngọc &Aacute;nh mang đến bầu kh&ocirc;ng kh&iacute; s&ocirc;i nổi qua li&ecirc;n kh&uacute;c &ldquo;L&aacute; xanh- Anh Ba Hưng&rdquo;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p><img src=\"/tinymce/source//Phao hoa.PNG\" alt=\"\" /></p>\n<p style=\"text-align: justify;\">Kết th&uacute;c đ&ecirc;m văn nghệ, h&agrave;ng ng&agrave;n người d&acirc;n đ&atilde; c&ugrave;ng nhau hướng l&ecirc;n bầu trời thưởng thức m&agrave;n bắn ph&aacute;o hoa ch&agrave;o mừng ng&agrave;y lễ trọng đại của đất nước.</p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh &rdquo;Ng&agrave;y hội non s&ocirc;ng&rdquo; đ&atilde; gi&uacute;p cho những người l&iacute;nh đ&atilde; từng v&agrave;o sinh ra tử trong thời chiến c&oacute; dịp &ocirc;n lại những kỷ niệm, đồng thời gi&uacute;p cho thế hệ trẻ hiểu hơn về những chặng đường lịch sử của đất nước ta, những đ&oacute;ng g&oacute;p hy sinh của biết bao người để c&oacute; được h&ograve;a b&igrave;nh như ng&agrave;y h&ocirc;m nay. Qua đ&oacute;, ch&uacute;ng ta th&ecirc;m tự h&agrave;o v&agrave; c&ugrave;ng nhau chung tay x&acirc;y dựng đất nước tốt đẹp hơn.</p>\n<p style=\"text-align: right;\">T&acirc;m Điểm</p>\n<p>&nbsp;</p>','Chương trình văn nghệ đặc biệt ','','',0,'http://tamdiem.com.vn/sources/images/MC.PNG','',0,0,0,0,494,0,1430898620,1440732843,1),(104,'Học cách phòng chống thảm họa từ \'Storm\'','hoc-cach-phong-chong-tham-hoa-tu-storm',14,'news_item','Học cách phòng chống thảm họa từ ','Storm là một bộ phim tài liệu dài khoảng 20 phút hướng về cộng đồng nhằm giúp đỡ mọi người có kỹ năng tự bảo vệ và tồn tại trong cơn bão.','Storm là một bộ phim tài liệu dài khoảng 20 phút hướng về cộng đồng nhằm giúp đỡ mọi người có kỹ năng tự bảo vệ và tồn tại trong cơn bão.','<p><strong>Storm l&agrave; một bộ phim t&agrave;i liệu d&agrave;i khoảng 20 ph&uacute;t hướng về cộng đồng nhằm gi&uacute;p đỡ mọi người c&oacute; kỹ năng tự bảo vệ v&agrave; tồn tại trong cơn b&atilde;o.</strong></p>\n<p><iframe style=\"float: left;\" src=\"http://www.youtube.com/embed/li7rr6x0sWU\" width=\"700\" height=\"400\"></iframe></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Storm do tổ chức phi lợi nhuận World Climate Change Challenge thực hiện với sự t&agrave;i trợ của Đại sứ qu&aacute;n Đan Mạch tại Việt Nam.&nbsp;Những th&ocirc;ng điệp m&agrave;&nbsp;bộ phim t&agrave;i liệu n&agrave;y đưa ra ch&iacute;nh&nbsp;l&agrave;&nbsp;\"Đừng sai lầm khi&nbsp;tin rằng bạn sẽ được an to&agrave;n v&igrave; bạn đang sống ở một th&agrave;nh phố lớn\", \"D&ugrave; đang sống ở đ&acirc;u, tất cả ch&uacute;ng ta đều phải học c&aacute;ch chăm s&oacute;c cho bản th&acirc;n v&agrave; gia đ&igrave;nh &iacute;t nhất trong 3 ng&agrave;y thiếu thốn khi một cơn si&ecirc;u b&atilde;o hay lũ tấn c&ocirc;ng\".</p>\n<p style=\"text-align: justify;\">Do ảnh hưởng của biến đổi kh&iacute; hậu, những cơn b&atilde;o v&agrave; lũ đang ho&agrave;nh h&agrave;nh dữ dội ở cả những nơi m&agrave; trước đ&acirc;y n&oacute; hiếm khi xảy ra. Ch&iacute;nh sự thay đổi đột ngột của m&ocirc; h&igrave;nh thời tiết cộng với sự chủ quan của con người m&agrave; số lượng tử vong, chấn thương do b&atilde;o đ&atilde; tăng cao bất thường. V&igrave;&nbsp;vậy, c&aacute;ch tốt nhất l&agrave; ch&uacute;ng ta n&ecirc;n chuẩn bị những phương &aacute;n đề ph&ograve;ng đơn giản để bản th&acirc;n m&igrave;nh v&agrave; gia đ&igrave;nh được an to&agrave;n, đồng thời l&agrave;m giảm thiểu những thiệt hại do&nbsp;b&atilde;o g&acirc;y ra.</p>\n<p style=\"text-align: right;\">T&acirc;m Điểm</p>','Học cách phòng chống thảm họa từ ','','',0,'http://tamdiem.com.vn/sources/images/storm%202.JPG','',0,0,0,0,493,0,1433581273,1440732862,1),(105,'Nữ Doanh Nhân - Cân bằng quan hệ gia đình và xã hội','nu-doanh-nhan-can-bang-quan-he-gia-dinh-va-xa-hoi',14,'news_item','Nữ Doanh Nhân - Cân bằng quan hệ gia đình và xã hội','Nhân kỷ niệm ngày Gia Đình Việt Nam, Hội Nữ Doanh Nhân TPHCM sẽ tổ chức ngày Gia Đình Hawee với chủ đề “Nữ Doanh Nhân –Cân bằng quan hệ gia đình và xã hội” vào lúc 16h ngày 20/06/2015 tại Nhà hàng Tiệc cưới Hòa Bình.','Nhân kỷ niệm ngày Gia Đình Việt Nam, Hội Nữ Doanh Nhân TPHCM sẽ tổ chức ngày Gia Đình Hawee với chủ đề “Nữ Doanh Nhân –Cân bằng quan hệ gia đình và xã hội” vào lúc 16h ngày 20/06/2015 tại Nhà hàng Tiệc cưới Hòa Bình.','<p style=\"text-align: justify;\"><strong>Nh&acirc;n kỷ niệm ng&agrave;y Gia Đ&igrave;nh Việt Nam, Hội Nữ Doanh Nh&acirc;n TPHCM sẽ tổ chức ng&agrave;y Gia Đ&igrave;nh Hawee với chủ đề &ldquo;Nữ Doanh Nh&acirc;n &ndash;C&acirc;n bằng quan hệ gia đ&igrave;nh v&agrave; x&atilde; hội&rdquo; v&agrave;o l&uacute;c 16h ng&agrave;y 20/06/2015 tại Nh&agrave; h&agrave;ng Tiệc cưới H&ograve;a B&igrave;nh.</strong></p>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//8.png\" alt=\"\" width=\"700\" height=\"420\" /></p>\n<p style=\"text-align: justify;\">Với sự tham gia của c&aacute;c chuy&ecirc;n gia trong lĩnh vực gia đ&igrave;nh c&ugrave;ng những chia sẻ ch&acirc;n t&igrave;nh của c&aacute;c nữ doanh nh&acirc;n th&agrave;nh đạt, ng&agrave;y Gia Đ&igrave;nh Hawee sẽ đem lại nhiều kinh nghiệm v&agrave; b&iacute; quyết để tạo dựng sự c&acirc;n bằng cuộc sống của nữ doanh nh&acirc;n, để gia đ&igrave;nh thực sự trở th&agrave;nh chốn đi về b&igrave;nh y&ecirc;n, nơi tiếp sức v&agrave; đem lại hạnh ph&uacute;c cho c&aacute;c chị em.</p>\n<p style=\"text-align: justify;\">Ngo&agrave;i những hoạt động x&atilde; hội, c&aacute;c nữ doanh nh&acirc;n c&ograve;n đảm nhiệm vai tr&ograve; quan trọng l&agrave; người vợ, người mẹ, người chị, người con trong gia đ&igrave;nh. Trong bối cảnh x&atilde; hội thay đổi nhanh ch&oacute;ng v&agrave; những gi&aacute; trị truyền thống đang bị mai một, c&aacute;c vấn đề ph&aacute;t sinh trong mối quan hệ gia đ&igrave;nh ng&agrave;y c&agrave;ng nhiều, do vậy việc c&acirc;n bằng giữa vai tr&ograve; x&atilde; hội v&agrave; hạnh ph&uacute;c gia đ&igrave;nh sẽ l&agrave; một th&aacute;ch thức lớn của c&aacute;c nữ doanh nh&acirc;n.</p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh ng&agrave;y Gia Đ&igrave;nh Hawee c&ograve;n c&oacute; sự tham gia chia sẻ v&agrave; giao lưu của c&aacute;c nữ doanh nh&acirc;n thuộc c&aacute;c tỉnh th&agrave;nh ph&iacute;a Nam.</p>\n<p style=\"text-align: right;\">T&acirc;m Điểm</p>','Nữ Doanh Nhân - Cân bằng quan hệ gia đình và xã hội','','',0,'http://tamdiem.com.vn/sources/images/1_1.jpg','',0,0,0,0,492,0,1434162448,1440732873,1),(106,'HAWEE chung sức vì đồng bào nghèo tại Trùng Khánh - Cao Bằng','hawEE-chung-suc-vi-dong-bao-ngheo-tai-trung-khanh-cao-bang',14,'news_item','HAWEE, TRÙNG KHÁNH, CAO BẰNG','Vừa qua, Hội Nữ Doanh nhân Thành phố Hồ Chí Minh đã có chuyến thăm và làm việc tại tỉnh Cao Bằng, tham gia đoàn gồm có chị Cao Thị Ngọc Dung – Chủ tịch Hội HAWEE, chị  Nguyễn Thị Hạnh – Phó chủ tịch thường trực, chị Nguyễn Thị Hoa Lệ - Ủy viên BCH cùng các hội viên danh dự của HAWEE.','Vừa qua, Hội Nữ Doanh nhân Thành phố Hồ Chí Minh đã có chuyến thăm và làm việc tại tỉnh Cao Bằng, tham gia đoàn gồm có chị Cao Thị Ngọc Dung – Chủ tịch Hội HAWEE, chị  Nguyễn Thị Hạnh – Phó chủ tịch thường trực, chị Nguyễn Thị Hoa Lệ - Ủy viên BCH cùng các hội viên danh dự của HAWEE.','<p style=\"text-align: justify;\">Vừa qua, Hội Nữ Doanh nh&acirc;n Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; c&oacute; chuyến thăm v&agrave; l&agrave;m việc tại tỉnh Cao Bằng, tham gia đo&agrave;n gồm c&oacute; chị Cao Thị Ngọc Dung &ndash; Chủ tịch Hội HAWEE, chị&nbsp; Nguyễn Thị Hạnh &ndash; Ph&oacute; chủ tịch thường trực, chị Nguyễn Thị Hoa Lệ - Ủy vi&ecirc;n BCH c&ugrave;ng c&aacute;c hội vi&ecirc;n danh dự của HAWEE.</p>\n<p style=\"text-align: justify;\">Trong chuyến l&agrave;m việc tại tỉnh Cao Bằng,&nbsp; Hội đ&atilde; trao 240 triệu đồng (bao gồm c&aacute;c phần đ&oacute;ng g&oacute;p của c&aacute;c c&ocirc;ng ty: Li&ecirc;n hiệp HTX Thương mại TP (SaiGonCo.op), Cty CP V&agrave;ng Bạc Đ&aacute; Qu&yacute; Ph&uacute; Nhuận (PNJ), Cty CP Du lịch H&ograve;a B&igrave;nh ) để mua lợn giống gi&uacute;p đồng b&agrave;o ngh&egrave;o x&atilde; Đ&agrave;m Thủy, huyện Tr&ugrave;ng Kh&aacute;nh, tỉnh Cao Bằng ph&aacute;t triển kinh tế, ổn định cuộc sống v&agrave; tiến tới tho&aacute;t ngh&egrave;o bền vững.</p>\n<p style=\"text-align: justify;\">Nh&acirc;n dịp n&agrave;y, c&aacute;c chị cũng gh&eacute; thăm Đền thờ B&aacute;c v&agrave; thắng cảnh Th&aacute;c Bản Giốc thuộc tỉnh Cao Bằng.</p>\n<p><img src=\"http://hawee.vn/sites/default/files/IMG_2333.JPG\" alt=\"\" width=\"454\" height=\"604\" /></p>\n<p><img src=\"http://hawee.vn/sites/default/files/IMG_2335.JPG\" alt=\"\" width=\"458\" height=\"343\" /></p>\n<p><img src=\"http://hawee.vn/sites/default/files/IMG_2337.JPG\" alt=\"\" width=\"457\" height=\"610\" /></p>\n<p><em>&nbsp;Theo Hawee.vn</em></p>','HAWEE CHUNG SỨC VÌ ĐỒNG BÀO NGHÈO TẠI TRÙNG KHÁNH-CAO BẰNG','','',0,'http://tamdiem.com.vn/sources/images/7.JPG','',0,0,0,0,491,0,1435658413,1453177706,0),(107,'Truyền hình giải trí: Khán giả muốn là chiều','truyen-hinh-giai-tri-khan-gia-muon-la-chieu',14,'news_item','Truyền hình giải trí: Khán giả muốn là chiều','“Khán giả là Thượng đế” và để chiều lòng “Thượng đế”, các nhà sản xuất chương trình truyền hình giải trí cũng có thể làm những điều mà họ chưa hẳng muốn ','“Khán giả là Thượng đế” và để chiều lòng “Thượng đế”, các nhà sản xuất chương trình truyền hình giải trí cũng có thể làm những điều mà họ chưa hẳng muốn ','<p style=\"text-align: justify;\"><strong>&ldquo;Kh&aacute;n giả l&agrave; Thượng đế&rdquo; v&agrave; để chiều l&ograve;ng &ldquo;Thượng đế&rdquo;, c&aacute;c nh&agrave; sản xuất chương tr&igrave;nh truyền h&igrave;nh giải tr&iacute; cũng c&oacute; thể l&agrave;m những điều m&agrave; họ chưa hẳng muốn.</strong></p>\n<p style=\"text-align: justify;\"><span style=\"line-height: inherit;\">Kh&aacute;n giả quyết định đến sự sống c&ograve;n của một chương tr&igrave;nh truyền h&igrave;nh giải tr&iacute; bằng doanh thu cao hay thấp. Rating (tỉ lệ kh&aacute;n giả theo d&otilde;i chương tr&igrave;nh) l&agrave; thước đo được xem chuẩn mực cho sự ăn kh&aacute;ch hay kh&ocirc;ng của tất cả c&aacute;c chương tr&igrave;nh truyền h&igrave;nh giải tr&iacute; hiện nay. Vậy n&ecirc;n, từ chỗ l&agrave;m chương tr&igrave;nh &ldquo;phục vụ&rdquo; kh&aacute;n giả với &yacute; đồ d&agrave;n dựng của &ecirc;-k&iacute;p thực hiện, c&aacute;c đơn vị sản xuất chương tr&igrave;nh hiện nay &ldquo;nương&rdquo; theo &yacute; th&iacute;ch của kh&aacute;n giả để l&agrave;m chương tr&igrave;nh.</span></p>\n<p style=\"text-align: justify;\"><strong>&ldquo;Kh&aacute;n giả th&iacute;ch thế&rdquo;</strong></p>\n<p style=\"text-align: justify;\">Trong buổi họp b&aacute;o giới thiệu chương tr&igrave;nh truyền h&igrave;nh thực tế &ldquo;The winner is&hellip; T&ocirc;i l&agrave; người chiến thắng&rdquo; m&ugrave;a thứ 3, đơn vị sản xuất (Đ&ocirc;ng T&acirc;y Promotion) c&ocirc;ng bố danh s&aacute;ch gi&aacute;m khảo của cuộc thi gồm cựu người mẫu Xu&acirc;n Lan, diễn vi&ecirc;n h&agrave;i Trấn Th&agrave;nh, diễn vi&ecirc;n h&agrave;i Ch&iacute; T&agrave;i, nhạc sĩ Đức Huy, nhạc sĩ Lưu Thi&ecirc;n Hương, &nbsp;ca sĩ Noo Phước Thịnh v&agrave; ca sĩ T&oacute;c Ti&ecirc;n. Đ&acirc;y l&agrave; chương tr&igrave;nh truyền h&igrave;nh thi ca h&aacute;t (ngo&agrave;i phần thưởng tiền mặt 300 triệu đồng c&ograve;n c&oacute; một bản hợp đồng ghi &acirc;m với Universal để ph&aacute;t triển sự nghiệp ca h&aacute;t chuy&ecirc;n nghiệp), việc chọn những người nổi tiếng ở lĩnh vực kh&aacute;c ngo&agrave;i &acirc;m nhạc l&agrave;m gi&aacute;m khảo chuy&ecirc;n m&ocirc;n khiến nhiều người lo ngại.</p>\n<p style=\"text-align: justify;\">Thực tế, ở m&ugrave;a giải trước, việc mời Nam Trung - một chuy&ecirc;n vi&ecirc;n trang điểm - l&agrave;m gi&aacute;m khảo đ&atilde; dấy l&ecirc;n những cuộc tranh c&atilde;i theo chiều hướng kh&ocirc;ng đồng t&igrave;nh bởi sự thiếu thuyết phục về chuy&ecirc;n m&ocirc;n. Truyền th&ocirc;ng thường xuy&ecirc;n nhắc đến sự cố đ&oacute; như một lựa chọn &ldquo;vụng về&rdquo; của đơn vị sản xuất chương tr&igrave;nh. D&ugrave; vậy, ở m&ugrave;a giải mới, ban tổ chức tiếp tục mời những gương mặt nổi tiếng tưởng chừng kh&ocirc;ng mấy li&ecirc;n quan ngồi ghế gi&aacute;m khảo, minh chứng l&agrave; Xu&acirc;n Lan v&agrave; c&aacute;c diễn vi&ecirc;n h&agrave;i. Trước thắc mắc của truyền th&ocirc;ng, đại diện đơn vị sản xuất trả lời trong buổi họp b&aacute;o rằng: &ldquo;D&ugrave; c&oacute; li&ecirc;n quan hay kh&ocirc;ng (về mặt chuy&ecirc;n m&ocirc;n) th&igrave; những gương mặt celeb (người nổi tiếng) m&agrave; ch&uacute;ng t&ocirc;i mời v&agrave;o danh s&aacute;ch gi&aacute;m khảo đều l&agrave; những người kh&aacute;n giả muốn. Kh&aacute;n giả theo d&otilde;i chương tr&igrave;nh th&iacute;ch nghe họ n&oacute;i l&agrave; điều kh&ocirc;ng thể phủ nhận&rdquo;.</p>\n<p>&nbsp;</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_216529\" style=\"max-width: 100% ! important;\" title=\"Cảnh trong live show 1 chương tr&igrave;nh &ldquo;T&ocirc;i l&agrave; người chiến thắng&rdquo;. (Ảnh do chương tr&igrave;nh cung cấp)\" src=\"http://nld.vcmedia.vn/2015/chot-1436888993837.jpg\" alt=\"Cảnh trong live show 1 chương tr&igrave;nh &ldquo;T&ocirc;i l&agrave; người chiến thắng&rdquo;. (Ảnh do chương tr&igrave;nh cung cấp)\" /></div>\n<div class=\"PhotoCMS_Caption\">Cảnh trong live show 1 chương tr&igrave;nh &ldquo;T&ocirc;i l&agrave; người chiến thắng&rdquo;. (Ảnh do chương tr&igrave;nh cung cấp)</div>\n</div>\n<p>&nbsp;</p>\n<p style=\"text-align: justify;\">Điều n&agrave;y cũng l&yacute; giải v&igrave; sao ở m&ugrave;a giải trước, một chương tr&igrave;nh t&igrave;m kiếm giọng ca triển vọng lại mang đậm dấu ấn h&agrave;i từ duy&ecirc;n h&agrave;i của gi&aacute;m khảo kh&aacute;ch mời đến chủ &yacute; d&agrave;n dựng của ban tổ chức. Sức h&uacute;t của chương tr&igrave;nh với m&agrave;u sắc h&agrave;i hước đối với kh&aacute;n giả l&agrave; điều kh&ocirc;ng thể phủ nhận nhưng việc h&agrave;i h&oacute;a một chương tr&igrave;nh thi ca h&aacute;t đ&atilde; l&agrave;m sai lệnh t&iacute;nh chất cũng như th&ocirc;ng điệp ban đầu của cuộc thi.</p>\n<p style=\"text-align: justify;\">L&yacute; giải điều n&agrave;y, đơn vị sản xuất lại khẳng định: &ldquo;V&igrave; đ&oacute; l&agrave; điều kh&aacute;n giả muốn&rdquo;.</p>\n<p style=\"text-align: justify;\">&ldquo;Kh&aacute;n giả muốn&rdquo; l&agrave; &ldquo;b&ugrave;a&rdquo; gi&uacute;p c&aacute;c nh&agrave; sản xuất chương tr&igrave;nh h&oacute;a giải nhiều thắc mắc, tranh c&atilde;i từ nội dung, kết cấu chương tr&igrave;nh đến kết quả nếu đ&oacute; l&agrave; cuộc thi. Việc giao to&agrave;n quyền hay n&oacute;i đơn giản hơn l&agrave; ph&oacute; mặc quyền quyết định thắng thua cho kh&aacute;n giả l&agrave; một c&aacute;ch l&agrave;m kh&ocirc;n ngoan của nh&agrave; tổ chức trong c&aacute;c chương tr&igrave;nh truyền h&igrave;nh c&oacute; yếu tố tranh t&agrave;i. V&igrave; c&aacute;ch l&agrave;m n&agrave;y chứng tỏ đơn vị sản xuất đứng ngo&agrave;i cuộc đua tranh một c&aacute;ch c&ocirc;ng t&acirc;m v&agrave; minh bạch. Tuy nhi&ecirc;n, thực tế c&oacute; c&ocirc;ng t&acirc;m, minh bạch hay kh&ocirc;ng chỉ những người sản xuất mới biết.</p>\n<p style=\"text-align: justify;\"><strong>&Aacute;p lực rating</strong></p>\n<p style=\"text-align: justify;\">Trong buổi giới thiệu chương tr&igrave;nh mới &ldquo;The kid host&rdquo; (Người dẫn chương tr&igrave;nh tương lai), BTV Quỳnh Hương cho biết đến nay, chương tr&igrave;nh n&agrave;y vẫn chưa kiếm được nh&agrave; t&agrave;i trợ v&igrave; mỗi lần x&aacute;ch hồ sơ gặp đối t&aacute;c nh&atilde;n h&agrave;ng xin t&agrave;i trợ, c&acirc;u hỏi m&agrave; chị được nhận lu&ocirc;n l&agrave; &ldquo;chương tr&igrave;nh c&oacute; rating cao kh&ocirc;ng?&rdquo;. Chị cho biết: &ldquo;T&ocirc;i nghĩ bụng, chương tr&igrave;nh chưa diễn ra l&agrave;m sao biết c&oacute; rating cao hay thấp&rdquo;. D&ugrave; vậy, chị cũng hiểu rating ch&iacute;nh l&agrave; điều kiện ti&ecirc;n quyết để nh&agrave; t&agrave;i trợ quyết định mở hầu bao với chương tr&igrave;nh hay kh&ocirc;ng.</p>\n<p style=\"text-align: justify;\">&Aacute;p lực rating lớn đến mức c&aacute;c đơn vị sản xuất chương tr&igrave;nh truyền h&igrave;nh hiện nay phải dựa v&agrave;o kh&aacute;n giả để x&acirc;y dựng nội dung. Người xem c&agrave;ng nhiều (rating cao), đồng nghĩa chương tr&igrave;nh th&agrave;nh c&ocirc;ng bất kể hay dở thế n&agrave;o, gi&aacute; trị ra sao. V&igrave; rating của chương tr&igrave;nh, nh&agrave; sản xuất sẵn s&agrave;ng sử dụng chi&ecirc;u tr&ograve; x&igrave;-căng-đan nhằm thu h&uacute;t sự ch&uacute; &yacute; của c&ocirc;ng ch&uacute;ng.</p>\n<p style=\"text-align: justify;\">&ldquo;Ch&aacute;y&rdquo; v&agrave; &ldquo;sốt&rdquo; gi&aacute;m khảo l&agrave; t&igrave;nh trạng dễ thấy ở c&aacute;c chương tr&igrave;nh truyền h&igrave;nh thực tế hiện nay. Cảm gi&aacute;c giới showbiz Việt Nam kh&ocirc;ng c&ograve;n mặn m&agrave; với c&ocirc;ng việc chuy&ecirc;n m&ocirc;n, thay v&agrave;o đ&oacute; l&agrave; chạy s&ocirc; l&agrave;m gi&aacute;m khảo ở c&aacute;c chương tr&igrave;nh truyền h&igrave;nh giải tr&iacute;. Từ ng&ocirc;i sao h&agrave;ng đầu đến ng&ocirc;i sao trẻ đều được mời ngồi vị tr&iacute; gi&aacute;m khảo ở c&aacute;c chương tr&igrave;nh kh&aacute;c nhau. Ng&ocirc;i sao lớn ngồi ghế gi&aacute;m khảo để kiếm tiền, số tiền một ng&ocirc;i sao ăn kh&aacute;ch được nhận khi l&agrave;m gi&aacute;m khảo cho một chương tr&igrave;nh ăn kh&aacute;ch l&ecirc;n đến cả tỉ đồng. Trong khi với ng&ocirc;i sao trẻ, truyền h&igrave;nh ch&iacute;nh l&agrave; c&aacute;ch để quảng b&aacute; h&igrave;nh ảnh tốt nhất cho bản th&acirc;n. Thậm ch&iacute;, một MC nổi tiếng đ&atilde; chấp nhận nửa tiền th&ugrave; lao của một chương tr&igrave;nh truyền h&igrave;nh thực tế với điều kiện anh sẽ l&agrave;m gi&aacute;m khảo thay v&igrave; MC. Đơn vị sản xuất chương tr&igrave;nh tất nhi&ecirc;n đồng &yacute; v&igrave; họ cần những người như anh trong chương tr&igrave;nh để l&agrave;m thỏa m&atilde;n kh&aacute;n giả y&ecirc;u th&iacute;ch, bất kỳ anh ở vị tr&iacute; n&agrave;o. Thế n&ecirc;n, mới c&oacute; trường hợp Kh&aacute;nh Thi gần ng&agrave;y sinh nở vẫn rất h&agrave;o hứng l&ecirc;n s&oacute;ng truyền h&igrave;nh; hay b&agrave; mẹ mới sinh con 3 tuần đ&atilde; trở lại &ldquo;ghế n&oacute;ng&rdquo; như Thu Minh.</p>\n<p style=\"text-align: right;\"><em>Theo nld.com.vn</em></p>\n<p>&nbsp;</p>','Truyền hình giải trí: Khán giả muốn là chiều','','',0,'http://tamdiem.com.vn/sources/images/TTGT.jpg','',0,0,0,0,490,0,1439450463,1440732922,1),(108,'Mỹ Linh, Huy Tuấn làm giám khảo \'Tài năng Nữ Doanh nhân Việt 2015\'','cuoc-thi-tai-nang-nu-doanh-nhan-viet-2015',14,'news_item','Mỹ Linh, Huy Tuấn làm giám khảo \'Tài năng Nữ Doanh nhân Việt 2015\'','Cuộc thi Tài năng Nữ Doanh nhân Việt 2015 do Hiệp hội Nữ Doanh nhân Việt Nam (VAWE)  tổ chức với sự bảo trợ của Hội Liên hiệp Phụ nữ Việt Nam, Phòng Thương mại và Công nghiệp Việt Nam (VCCI).','Cuộc thi Tài năng Nữ Doanh nhân Việt 2015 do Hiệp hội Nữ Doanh nhân Việt Nam (VAWE)  tổ chức với sự bảo trợ của Hội Liên hiệp Phụ nữ Việt Nam, Phòng Thương mại và Công nghiệp Việt Nam (VCCI).','<div style=\"padding-top: 15px; padding-bottom: 15px; font-weight: bold; text-align: justify;\">Diva Mỹ Linh v&agrave; nhạc sĩ Huy Tuấn sẽ l&agrave; 2 gương mặt nằm trong hội đồng gi&aacute;m khảo cuộc thi \"T&agrave;i năng Nữ Doanh nh&acirc;n Việt 2015\".</div>\n<div id=\"detail-content\" class=\"detail-content\" style=\"margin-bottom: 10px;\">\n<p style=\"text-align: justify;\">Cuộc thi \"T&agrave;i năng Nữ Doanh nh&acirc;n Việt 2015\" được VAWE tổ chức với sự bảo trợ của Hội Li&ecirc;n hiệp Phụ nữ Việt Nam, Ph&ograve;ng Thương mại v&agrave; C&ocirc;ng nghiệp Việt Nam (VCCI). Đ&acirc;y l&agrave; cuộc thi t&agrave;i năng, lần đầu ti&ecirc;n được tổ chức tr&ecirc;n quy m&ocirc; to&agrave;n quốc, d&agrave;nh cho c&aacute;c nữ doanh nh&acirc;n v&agrave; nữ l&atilde;nh đạo doanh nghiệp. Cuộc thi nhằm mục đ&iacute;ch kh&aacute;m ph&aacute;, t&ocirc;n vinh những t&agrave;i năng nghệ thuật trong cộng đồng Nữ Doanh nh&acirc;n Việt Nam; qua đ&oacute;, t&ocirc;n vinh những phụ nữ hiện đại, t&agrave;i năng, s&aacute;ng tạo trong quản l&yacute; v&agrave; l&atilde;nh đạo doanh nghiệp; tinh tế, t&agrave;i hoa, y&ecirc;u nghệ thuật.</p>\n<p style=\"text-align: justify;\">Chương tr&igrave;nh được tổ chức để ch&agrave;o mừng kỷ niệm c&aacute;c ng&agrave;y lễ lớn của đất nước trong năm 2015: 70 năm C&aacute;ch mạng th&aacute;ng T&aacute;m (19/08/1945 - 19/08/2015) v&agrave; Quốc kh&aacute;nh nước Cộng h&ograve;a XHCN Việt Nam (02/09/1945 - 02/09/2015), 85 năm ng&agrave;y th&agrave;nh lập Hội Li&ecirc;n hiệp phụ nữ Việt Nam (20/10/1930 - 20/10/2015); đồng thời, hướng tới Kỷ niệm 11 năm Ng&agrave;y Doanh nh&acirc;n Việt Nam (13/10/2004 - 13/10/2015) v&agrave; một năm th&agrave;nh lập của VAWE.</p>\n<p style=\"text-align: center;\"><img title=\"Mỹ Linh l&agrave;m gi&aacute;m khảo 0\" src=\"http://media.ngoisao.vn/resize_580/news/2015/07/30/my-linh-lam-giam-khao2-ngoisao.vn.jpg\" alt=\"Mỹ Linh l&agrave;m gi&aacute;m khảo 0\" /></p>\n<h1 style=\"text-align: center;\"><strong><span style=\"font-size: 14px;\"><em>Diva Mỹ Linh l&agrave;m gi&aacute;m khảo \"T&agrave;i năng Nữ Doanh nh&acirc;n Việt 2015\"</em></span></strong></h1>\n<p style=\"text-align: justify;\">C&aacute;c tiết mục tham gia dự thi thuộc 03 bộ m&ocirc;n nghệ thuật: Ca h&aacute;t (nhạc nhẹ, d&acirc;n gian,&hellip;); M&uacute;a (d&acirc;n gian, đương đại, dancer sport,&hellip;) v&agrave; Biểu diễn nhạc cụ. Theo dự kiến, cuộc thi sẽ diễn ra trong thời gian từ ng&agrave;y 30/07/2015 đến ng&agrave;y 31/10/2015.</p>\n<p style=\"text-align: justify;\">V&ograve;ng Sơ tuyển của cuộc thi được triển khai từ ng&agrave;y 30/07 đến ng&agrave;y 25/08/2015. Từ v&ograve;ng Sơ tuyển n&agrave;y, c&aacute;c gương mặt th&iacute; sinh đạt ti&ecirc;u chuẩn do Ban Tổ chức đề ra sẽ tham gia v&ograve;ng B&aacute;n kết khu vực, dự kiến được tổ chức tại 05 tỉnh/th&agrave;nh phố: H&agrave; Nội, Hải Ph&ograve;ng, Đ&agrave; Nẵng, Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; Cần Thơ từ ng&agrave;y 01/09/2015 đến ng&agrave;y 30/09/2015.</p>\n<p style=\"text-align: justify;\">C&aacute;c th&iacute; sinh xuất sắc nhất sau v&ograve;ng B&aacute;n kết khu vực sẽ được lựa chọn để dự thi v&ograve;ng Chung kết khu vực ph&iacute;a Bắc (dự kiến được tổ chức tại Nh&agrave; h&aacute;t &Acirc;u Cơ, H&agrave; Nội v&agrave;o ng&agrave;y 11/10/2015) v&ograve;ng Chung kết khu vực ph&iacute;a Nam (dự kiến được tổ chức tại Trung t&acirc;m ca nhạc Lan Anh, th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave;o ng&agrave;y 22/10/2015).</p>\n<p style=\"text-align: center;\"><img title=\"Mỹ Linh l&agrave;m gi&aacute;m khảo 1\" src=\"http://media.ngoisao.vn/resize_580/news/2015/07/30/my-linh-lam-giam-khao1-ngoisao.vn.jpg\" alt=\"Mỹ Linh l&agrave;m gi&aacute;m khảo 1\" /><br /> <em>Nhạc sĩ Huy Tuấn đồng h&agrave;nh c&ugrave;ng diva Mỹ Linh trong suốt cuộc thi</em></p>\n<p style=\"text-align: justify;\">Từ v&ograve;ng thi Chung kết khu vực ph&iacute;a Bắc v&agrave; ph&iacute;a Nam, 15 th&iacute; sinh xuất sắc nhất sẽ được lựa chọn để tham gia đ&ecirc;m Chung kết to&agrave;n quốc tại H&agrave; Nội. Để chuẩn bị cho đ&ecirc;m Chung kết to&agrave;n quốc, c&aacute;c th&iacute; sinh sẽ được tham gia c&aacute;c buổi huấn luyện chuy&ecirc;n s&acirc;u về thanh nhạc, vũ đạo, kỹ thuật tr&igrave;nh diễn, kỹ năng trả lời phỏng vấn, x&acirc;y dựng thương hiệu c&aacute; nh&acirc;n&hellip;</p>\n<p style=\"text-align: justify;\">Đồng h&agrave;nh c&ugrave;ng cuộc thi &ldquo;T&agrave;i năng Nữ Doanh nh&acirc;n Việt&rdquo; năm 2015 trong vai tr&ograve; l&agrave; Huấn luyện vi&ecirc;n, th&agrave;nh vi&ecirc;n Hội đồng gi&aacute;m khảo sẽ l&agrave; những nghệ sĩ h&agrave;ng đầu của Việt Nam như ca sĩ Mỹ Linh, nhạc sĩ Huy Tuấn, nhạc sĩ Gi&aacute;ng Son, nhạc sĩ Hồng Ki&ecirc;n, bi&ecirc;n đạo m&uacute;a Trần Ly Ly, bi&ecirc;n đạo m&uacute;a Phan Lương, nh&agrave; b&aacute;o Ng&ocirc; B&aacute; Lục&hellip; c&ugrave;ng c&aacute;c doanh nh&acirc;n c&oacute; uy t&iacute;n trong cộng đồng doanh nghiệp.</p>\n<p style=\"text-align: right;\"><em>Theo ngoisao.vn</em></p>\n</div>\n<p>&nbsp;</p>','Cuộc Thi Tài năng Nữ Doanh nhân Việt 2015','','',1,'http://tamdiem.com.vn/sources/images/vawe.jpg','',0,0,0,0,489,0,1439522392,1453113084,1),(109,'Mạng sống đang bị coi rẻ trên phim trường? ','mang-song-dang-bi-coi-re-tren-phim-truong-',14,'news_item','Mạng sống đang bị coi rẻ trên phim trường? ','Sẽ còn những cái chết oan uổng như diễn viên Nguyễn Giàu trong phim “Hùng Ali và Sáu Lóc Cóc” nếu mạng sống con người không được xem trọng trên phim trường như hiện nay ','Sẽ còn những cái chết oan uổng như diễn viên Nguyễn Giàu trong phim “Hùng Ali và Sáu Lóc Cóc” nếu mạng sống con người không được xem trọng trên phim trường như hiện nay ','<p style=\"text-align: justify;\"><strong>Sẽ c&ograve;n những c&aacute;i chết oan uổng như diễn vi&ecirc;n Nguyễn Gi&agrave;u trong phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo; nếu mạng sống con người kh&ocirc;ng được xem trọng tr&ecirc;n phim trường như hiện nay.</strong></p>\n<p style=\"text-align: justify;\">Tin anh Nguyễn Gi&agrave;u diễn vi&ecirc;n bị tai nạn đa chấn thương tr&ecirc;n trường quay bộ phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo; do C&ocirc;ng ty Giải tr&iacute; Ưng Ho&agrave;ng Ph&uacute;c v&agrave; C&ocirc;ng ty Ngộ Entertainment đầu tư sản xuất, đ&atilde; qua đời l&uacute;c 11 giờ 40 ph&uacute;t ng&agrave;y 10-8 khi mới 25 tuổi đ&atilde; khiến nhiều đồng nghiệp, c&ocirc;ng ch&uacute;ng thương tiếc, x&oacute;t xa lẫn bức x&uacute;c. Anh Gi&agrave;u sẽ kh&ocirc;ng phải chết oan uổng nếu mạng sống con người kh&ocirc;ng bị coi rẻ tr&ecirc;n phim trường như vậy.</p>\n<p style=\"text-align: justify;\"><strong>Đ&oacute;ng vai quần ch&uacute;ng phải chịu</strong></p>\n<p style=\"text-align: justify;\">Như B&aacute;o Người Lao Động đ&atilde; phản &aacute;nh trong b&agrave;i viết: &ldquo;Ph&oacute; mặc t&iacute;nh mạng diễn vi&ecirc;n&rdquo; số ra ng&agrave;y 10-8, &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo; l&agrave; phim điện ảnh h&agrave;nh động - h&agrave;i n&ecirc;n huy động một lực lượng diễn vi&ecirc;n quần ch&uacute;ng rất đ&ocirc;ng. Theo lời kể của nhiều người c&oacute; mặt tại đo&agrave;n phim, h&ocirc;m xảy ra tai nạn của anh Gi&agrave;u (24-7), đo&agrave;n phim triệu tập khoảng 50 diễn vi&ecirc;n quần ch&uacute;ng v&agrave; cascadeur (người đ&oacute;ng thế) tham gia. Hầu hết họ đều kh&ocirc;ng được k&yacute; hợp đồng lao động v&agrave; mua bảo hiểm. Anh Gi&agrave;u cũng nằm trong số đ&oacute;.</p>\n<p>&nbsp;</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; box-sizing: border-box;\">\n<div><img id=\"img_226195\" style=\"max-width: 100% ! important;\" title=\"Một cảnh quay h&agrave;nh động c&oacute; sự hỗ trợ của nhiều diễn vi&ecirc;n quần ch&uacute;ng trong phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo;. (Ảnh do đo&agrave;n phim cung cấp)\" src=\"http://nld.vcmedia.vn/2015/8-9chot-1439302694724.jpg\" alt=\"Một cảnh quay h&agrave;nh động c&oacute; sự hỗ trợ của nhiều diễn vi&ecirc;n quần ch&uacute;ng trong phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo;. (Ảnh do đo&agrave;n phim cung cấp)\" /></div>\n<div class=\"PhotoCMS_Caption\"><em>Một cảnh quay h&agrave;nh động c&oacute; sự hỗ trợ của nhiều diễn vi&ecirc;n quần ch&uacute;ng trong phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo;. </em></div>\n<div class=\"PhotoCMS_Caption\"><em>(Ảnh do đo&agrave;n phim cung cấp)</em></div>\n</div>\n<p style=\"text-align: justify;\">Kh&ocirc;ng chỉ anh Gi&agrave;u m&agrave; đa số diễn vi&ecirc;n quần ch&uacute;ng hiện nay đều xuất th&acirc;n từ sinh vi&ecirc;n, người chưa c&oacute; việc l&agrave;m hay gia đ&igrave;nh ngh&egrave;o kh&oacute;. Họ đến với phim v&igrave; muốn kiếm th&ecirc;m ch&uacute;t tiền trang trải cuộc sống. V&igrave; thế, ai cũng mang t&acirc;m trạng &ldquo;đi đ&oacute;ng phim được l&agrave; mừng rồi&rdquo;. Anh Văn Th&agrave;nh, một diễn vi&ecirc;n quần ch&uacute;ng chia sẻ: &ldquo;T&ocirc;i đi đ&oacute;ng vai quần ch&uacute;ng đ&atilde; 3 năm nay nhưng chẳng bao giờ quan t&acirc;m tới chuyện hợp đồng hay bảo hiểm&rdquo;. Một người phụ tr&aacute;ch gọi diễn vi&ecirc;n quần ch&uacute;ng cũng x&aacute;c nhận: &ldquo;L&acirc;u nay, t&ocirc;i &iacute;t thấy diễn vi&ecirc;n quần ch&uacute;ng l&ecirc;n tiếng hỏi về hợp đồng hay bảo hiểm. V&igrave; họ đ&oacute;ng v&agrave;i ba cảnh quay đơn giản, kh&ocirc;ng nặng nhọc v&agrave; nguy hiểm n&ecirc;n chỉ mong kiếm được v&agrave;i ba trăm ng&agrave;n l&agrave; xong!&rdquo;.</p>\n<p style=\"text-align: justify;\">Song, n&oacute;i như anh Quang B&igrave;nh, một diễn vi&ecirc;n quần ch&uacute;ng kh&aacute;c: &ldquo;N&oacute;i kh&ocirc;ng quan t&acirc;m cũng kh&ocirc;ng ch&iacute;nh x&aacute;c, đ&uacute;ng hơn l&agrave; ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; quyền đ&ograve;i hỏi. Ban đầu v&agrave;o nghề, t&ocirc;i cũng sợ tai nạn v&igrave; đ&ocirc;i khi cũng phải đ&oacute;ng những cảnh nguy hiểm, tiềm ẩn rủi ro như đ&aacute;nh ch&eacute;m nhau hay nhảy từ tr&ecirc;n cao xuống. Nhưng khi hỏi hợp đồng hay bảo hiểm th&igrave; mọi người đều lắc đầu bảo: Đừng c&oacute; mơ! Đ&oacute;ng vai quần ch&uacute;ng phải chấp nhận thiệt th&ograve;i th&ocirc;i&rdquo;. Diễn vi&ecirc;n Th&acirc;n Th&uacute;y H&agrave; cũng cho rằng c&aacute;c diễn vi&ecirc;n c&oacute; đ&ograve;i quyền lợi nhưng kh&ocirc;ng được n&ecirc;n họ nản, kh&ocirc;ng c&ograve;n quan t&acirc;m. Rất nhiều diễn vi&ecirc;n quần ch&uacute;ng v&igrave; y&ecirc;u nghề, kiếm tiền m&agrave; chẳng m&agrave;ng đến sự nguy hiểm của bản th&acirc;n, chấp nhận l&agrave;m nghề với t&acirc;m trạng &ldquo;may nhờ rủi chịu&rdquo;.</p>\n<p style=\"text-align: justify;\"><strong>Đổ hết cho ngh&egrave;o</strong></p>\n<p style=\"text-align: justify;\">Chuyện tai nạn n&oacute;i ri&ecirc;ng v&agrave; an to&agrave;n cho diễn vi&ecirc;n tr&ecirc;n trường quay n&oacute;i chung được cảnh b&aacute;o từ h&agrave;ng chục năm nay. B&aacute;o Người Lao Động từng c&oacute; loạt b&agrave;i &ldquo;Tai nạn trường quay: Kh&ocirc;ng ai bảo hiểm&rdquo; từ năm 2005 cũng đ&atilde; đề cập thực trạng n&agrave;y. Song từ đ&oacute; đến nay, những tồn tại đ&atilde; đề cập vẫn kh&ocirc;ng cải thiện. Trường hợp của anh Nguyễn Gi&agrave;u mới đ&acirc;y lại một lần nữa cho thấy nguy hiểm lu&ocirc;n chực chờ c&aacute;c diễn vi&ecirc;n khi họ l&agrave;m việc tr&ecirc;n trường quay.</p>\n<p style=\"text-align: justify;\">Một diễn vi&ecirc;n cho biết: &ldquo;Với những diễn vi&ecirc;n c&oacute; k&yacute; hợp đồng th&igrave; đều được mua bảo hiểm. C&ograve;n những diễn vi&ecirc;n kh&ocirc;ng k&yacute; hợp đồng th&igrave; đương nhi&ecirc;n l&agrave; kh&ocirc;ng được mua bảo hiểm&rdquo;. Diễn vi&ecirc;n Th&acirc;n Th&uacute;y H&agrave; n&ecirc;u thực tế: &ldquo;Kh&ocirc;ng chỉ c&oacute; diễn vi&ecirc;n quần ch&uacute;ng kh&ocirc;ng được mua bảo hiểm m&agrave; hiện nay, phần lớn diễn vi&ecirc;n phụ cũng kh&ocirc;ng được mua bảo hiểm&rdquo;.</p>\n<p style=\"text-align: justify;\">Theo đạo diễn Đinh Th&aacute;i Thụy, việc mua bảo hiểm cho diễn vi&ecirc;n l&acirc;u nay bị c&aacute;c nh&agrave; sản xuất phớt lờ. Rất nhiều nh&agrave; sản xuất lấy cớ do ngh&egrave;o để giải th&iacute;ch cho việc kh&ocirc;ng thể mua bảo hiểm cho diễn vi&ecirc;n. &ldquo;Trong thời buổi l&agrave;m phim kinh ph&iacute; thấp, phải chi đủ thứ n&ecirc;n đo&agrave;n phim phải ưu ti&ecirc;n những khoản chi cần thiết hơn. Bảo hiểm cũng quan trọng nhưng th&ugrave; lao diễn vi&ecirc;n c&ograve;n quan trọng hơn n&ecirc;n tiết giảm được chừng n&agrave;o hay chừng ấy&rdquo; - đại diện một nh&agrave; sản xuất phim truyền h&igrave;nh th&uacute; nhận. Trong khi việc bảo hiểm cho đo&agrave;n l&agrave;m phim chưa được quy định trong Luật Điện ảnh c&agrave;ng khiến cho c&aacute;c nh&agrave; sản xuất l&agrave;m lơ.</p>\n<p style=\"text-align: justify;\">Vấn đề bảo đảm an to&agrave;n tại trường quay cũng bị c&aacute;c nh&agrave; sản xuất, đo&agrave;n phim viện l&yacute; do v&igrave; &ldquo;ngh&egrave;o&rdquo;. M&ocirc;i trường l&agrave;m việc của diễn vi&ecirc;n chưa bảo đảm, tiềm ẩn nhiều nguy cơ xảy ra tai nạn cũng v&igrave; đo&agrave;n phim kh&ocirc;ng trang bị bảo hộ lao động. Chẳng hạn như địa điểm quay của đo&agrave;n phim &ldquo;H&ugrave;ng Ali v&agrave; S&aacute;u L&oacute;c C&oacute;c&rdquo; ngay tr&ecirc;n lầu 1, nơi c&ocirc;ng tr&igrave;nh x&acirc;y dựng đang dang dở, c&oacute; nhiều lỗ trống. Ai cũng biết chỉ cần sơ suất l&agrave; diễn vi&ecirc;n c&oacute; nguy cơ thương vong.</p>\n<p style=\"text-align: justify;\">R&otilde; r&agrave;ng đo&agrave;n phim ho&agrave;n to&agrave;n lơ l&agrave; đến chuyện bảo đảm điều kiện an to&agrave;n, bảo hộ, bảo hiểm phim trường. Cứ c&oacute; sự cố th&igrave; giải quyết theo kiểu &ldquo;mất b&ograve; mới lo l&agrave;m chuồng&rdquo;. Trong khi tai nạn xảy ra, kh&ocirc;ng những diễn vi&ecirc;n bị tổn hại sức khỏe, t&iacute;nh mạng, gia đ&igrave;nh của họ bị ảnh hưởng m&agrave; đo&agrave;n l&agrave;m phim cũng tốn k&eacute;m chi ph&iacute; lo liệu, bồi thường, trợ cấp cho người bị tai nạn. Hơn nữa, việc quay bị gi&aacute;n đoạn, uy t&iacute;n của đo&agrave;n l&agrave;m phim bị ảnh hưởng nghi&ecirc;m trọng.</p>\n<p style=\"text-align: justify;\">Kathy Uy&ecirc;n, diễn vi&ecirc;n người Mỹ gốc Việt, cho biết khi đ&oacute;ng phim ở Mỹ, to&agrave;n bộ việc bảo hiểm cũng như mọi quyền lợi kh&aacute;c của c&ocirc; đều do Hiệp hội Diễn vi&ecirc;n Mỹ (SAG) bảo vệ. &ldquo;Tai nạn đau l&ograve;ng cứ lần lượt xảy ra nhưng ai l&agrave; người bảo vệ, gi&aacute;m s&aacute;t an to&agrave;n tr&ecirc;n phim trường l&agrave; một c&acirc;u hỏi lớn kh&ocirc;ng c&oacute; lời giải đ&aacute;p&rdquo; - đạo diễn Đinh Th&aacute;i Thụy băn khoăn.</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<div id=\"ObjectBoxContent_1439304659445\" class=\"VCSortableInPreviewMode\" dir=\"center\" style=\"border: 1px solid #999999; background: #e6e6fa none repeat scroll 0% 0%; float: none; width: 90%; margin: 0px auto;\" data-back=\"E6E6FA\" data-border=\"999\">\n<p style=\"text-align: justify;\"><strong>Mảng trống trong quản l&yacute;</strong></p>\n<p style=\"text-align: justify;\">Theo c&aacute;c nh&agrave; chuy&ecirc;n m&ocirc;n, ph&aacute;p luật lao động quy định khi c&oacute; quan hệ lao động, người sử dụng lao động (NSDLĐ) v&agrave; người lao động (NLĐ) phải giao kết hợp đồng lao động (HĐLĐ). HĐLĐ l&agrave; sự thỏa thuận về việc l&agrave;m c&oacute; trả c&ocirc;ng, điều kiện lao động, quyền v&agrave; nghĩa vụ của hai b&ecirc;n trong quan hệ lao động. Trường hợp NLĐ l&agrave; diễn vi&ecirc;n được thu&ecirc; đ&oacute;ng phim, được trả tiền c&ocirc;ng (c&aacute;t-s&ecirc;) l&agrave; c&oacute; đủ yếu tố để x&aacute;c định giữa hai b&ecirc;n đ&atilde; ph&aacute;t sinh quan hệ lao động. V&igrave; vậy, phải tu&acirc;n thủ c&aacute;c quy định của ph&aacute;p luật lao động, trong đ&oacute; c&oacute; việc giao kết HĐLĐ v&agrave; thực hiện ch&iacute;nh s&aacute;ch về bảo hiểm.</p>\n<p style=\"text-align: justify;\">Như vậy, t&ugrave;y từng trường hợp thu&ecirc; mướn lao động cho c&aacute;c c&ocirc;ng việc trong qu&aacute; tr&igrave;nh sản xuất phim, NSDLĐ c&oacute; thể giao kết HĐLĐ với NLĐ theo từng loại kh&aacute;c nhau. Nhưng d&ugrave; giao kết HĐLĐ loại n&agrave;o th&igrave; khi xảy ra tai nạn lao động với diễn vi&ecirc;n, NSDLĐ cũng phải thanh to&aacute;n to&agrave;n bộ chi ph&iacute; từ khi sơ, cấp cứu cho đến khi điều trị ổn định thương tật cho họ, sau đ&oacute;, phải đưa NLĐ đi gi&aacute;m định tỉ lệ suy giảm khả năng lao động để giải quyết việc bồi thường v&agrave; chế độ trợ cấp tai nạn lao động.</p>\n<p style=\"text-align: justify;\">Ph&aacute;p luật lao động cũng quy định NSDLĐ c&oacute; tr&aacute;ch nhiệm bảo đảm điều kiện l&agrave;m việc an to&agrave;n cho NLĐ v&agrave; NLĐ c&oacute; quyền từ chối l&agrave;m c&ocirc;ng việc khi thấy r&otilde; nguy cơ tai nạn lao động xảy ra, đe dọa t&iacute;nh mạng, sức khỏe của m&igrave;nh.</p>\n<p style=\"text-align: justify;\">Từ l&acirc;u nay, theo c&aacute;c nh&agrave; quản l&yacute; chuy&ecirc;n m&ocirc;n, đ&acirc;y l&agrave; một mảng trống trong việc quản l&yacute;, thực hiện c&aacute;c chế độ lao động cho đối tượng NLĐ l&agrave; diễn vi&ecirc;n trong quan hệ lao động hiện nay. Phần lớn c&aacute;c diễn vi&ecirc;n kh&ocirc;ng được giao kết HĐLĐ v&agrave; kh&ocirc;ng được bảo hiểm tai nạn, nhất l&agrave; diễn vi&ecirc;n phụ, diễn vi&ecirc;n quần ch&uacute;ng.</p>\n<p style=\"text-align: justify;\">Thực tế, c&oacute; những tai nạn ho&agrave;n to&agrave;n kh&ocirc;ng thuộc nguy&ecirc;n nh&acirc;n chủ quan của con người. C&oacute; một v&agrave;i h&atilde;ng phim mỗi khi th&agrave;nh lập đo&agrave;n phim, bất kể c&oacute; pha nguy hiểm hay kh&ocirc;ng đều mua bảo hiểm cho to&agrave;n đo&agrave;n theo quy định của nh&agrave; nước, cả bảo hiểm bệnh tật lẫn bảo hiểm tai nạn. Nhưng phần lớn c&aacute;c h&atilde;ng phim hiện nay lấy l&yacute; do kinh ph&iacute; l&agrave;m phim eo hẹp n&ecirc;n chỉ những phim dự kiến c&oacute; những cảnh nguy hiểm nh&agrave; sản xuất mới mua bảo hiểm tai nạn cho đo&agrave;n phim, kể cả m&aacute;y m&oacute;c trang thiết bị. Một số h&atilde;ng cho biết chỉ mua bảo hiểm nếu diễn vi&ecirc;n y&ecirc;u cầu c&ograve;n kh&ocirc;ng th&igrave; th&ocirc;i. Nếu chẳng may c&oacute; xảy ra tai nạn trong qu&aacute; tr&igrave;nh quay phim, nh&agrave; sản xuất c&oacute; tr&aacute;ch nhiệm chăm lo việc điều trị cho diễn vi&ecirc;n l&agrave; được.</p>\n<p style=\"text-align: justify;\">C&oacute; phim để đ&oacute;ng, c&oacute; tiền th&ugrave; lao l&agrave; đ&atilde; mừng rồi n&ecirc;n diễn vi&ecirc;n &iacute;t ai &yacute; thức, quan t&acirc;m đến việc c&oacute; hay kh&ocirc;ng c&oacute; bảo hiểm tai nạn tr&ecirc;n trường quay. V&igrave; muốn c&oacute; phim để đ&oacute;ng, nhiều diễn vi&ecirc;n chẳng m&agrave;ng đến sự nguy hiểm của bản th&acirc;n. Khi tai nạn xảy ra, nh&agrave; sản xuất n&oacute;i lời xin lỗi, thăm hỏi, động vi&ecirc;n v&agrave; chịu to&agrave;n bộ viện ph&iacute; xem như l&agrave; hết tr&aacute;ch nhiệm. C&ograve;n t&iacute;nh mạng của diễn vi&ecirc;n, cuộc sống người th&acirc;n của họ sẽ ra sao khi c&oacute; trường hợp tai nạn tử vong hoặc mang thương tật nặng đến suốt đời th&igrave; gần như bỏ mặc!</p>\n<p style=\"text-align: justify;\">Bảo vệ t&iacute;nh mạng diễn vi&ecirc;n tr&ecirc;n phim trường đang cần sự ra tay quyết liệt của c&aacute;c cơ quan quản l&yacute; nh&agrave; nước v&agrave; sự l&ecirc;n tiếng mạnh mẽ của c&aacute;c hội điện ảnh trong việc bảo vệ lợi &iacute;ch người l&agrave;m nghề.</p>\n<p style=\"text-align: right;\"><strong>&Acirc;n Th&ocirc;ng</strong></p>\n</div>\n<p>&nbsp;</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><em>Theo nld.com.vn</em></div>','Mạng sống đang bị coi rẻ trên phim trường? ','','',0,'http://tamdiem.com.vn/sources/images/phim.jpg','',0,0,0,0,488,0,1439877729,1440758020,1),(111,'Tung \'chiêu\', chi đậm để quảng bá phim ','tung-chieu-chi-dam-de-quang-ba-phim-',14,'news_item','Tung chiêu, chi đậm để quảng bá phim ','Các hãng phim của Hollywood đang sáng tạo ra nhiều kiểu quảng bá phim mới lạ, tận dụng tối đa lợi thế của mạng xã hội để tạo sức hút, lôi kéo khán giả đến rạp. ','Các hãng phim của Hollywood đang sáng tạo ra nhiều kiểu quảng bá phim mới lạ, tận dụng tối đa lợi thế của mạng xã hội để tạo sức hút, lôi kéo khán giả đến rạp. ','<h4 class=\"sapo\" style=\"text-align: justify;\" data-field=\"sapo\">C&aacute;c h&atilde;ng phim của Hollywood đang s&aacute;ng tạo ra nhiều kiểu quảng b&aacute; phim mới lạ, tận dụng tối đa lợi thế của mạng x&atilde; hội để tạo sức h&uacute;t, l&ocirc;i k&eacute;o kh&aacute;n giả đến rạp.</h4>\n<p style=\"text-align: justify;\">Mới đ&acirc;y, H&atilde;ng &nbsp;20th Century Fox đ&atilde; mời giới truyền th&ocirc;ng đến ph&ograve;ng&nbsp;th&iacute; nghiệm sức đẩy phản lực thuộc Cơ quan H&agrave;ng kh&ocirc;ng vũ trụ Mỹ (NASA) tại California &ndash; Mỹ, để quảng b&aacute; trailer phim <em>The Martian</em>. Phim n&agrave;y thuộc thể loại khoa học viễn tưởng của đạo diễn Ridley Scott với phần kịch bản của Drew Goddard, dựa tr&ecirc;n tiểu thuyết c&ugrave;ng t&ecirc;n của t&aacute;c giả Andy Weir.</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_231961\" style=\"max-width: 100% ! important;\" title=\"C&aacute;c h&atilde;ng phim kh&ocirc;ng ngại chi tiền để quảng b&aacute; phim\" src=\"http://nld.vcmedia.vn/thumb_w/540/2015/tai-xuong-1440647594273.jpg\" alt=\"C&aacute;c h&atilde;ng phim kh&ocirc;ng ngại chi tiền để quảng b&aacute; phim\" /></div>\n<div class=\"PhotoCMS_Caption\">C&aacute;c h&atilde;ng phim kh&ocirc;ng ngại chi tiền để quảng b&aacute; phim</div>\n</div>\n<p>&nbsp;</p>\n<p style=\"text-align: justify;\">C&aacute;c ph&oacute;ng vi&ecirc;n xem lướt 50 ph&uacute;t đầu của phim, đi thưởng l&atilde;m v&ograve;ng quanh ph&ograve;ng th&iacute; nghiệm rồi sau đ&oacute; phỏng vấn với diễn vi&ecirc;n Damon về vai diễn của m&igrave;nh. Nội dung kể về phi h&agrave;nh gia Mark Watney được cho l&agrave; đ&atilde; chết tr&ecirc;n sao hỏa sau khi gặp cơn b&atilde;o dữ dội. Anh bị bỏ lại ph&iacute;a sau khi c&aacute;c th&agrave;nh vi&ecirc;n kh&aacute;c của phi h&agrave;nh đo&agrave;n rời khỏi sao hỏa v&agrave; trở về tr&aacute;i đất.</p>\n<p style=\"text-align: justify;\">&ldquo;Ch&uacute;ng t&ocirc;i ở trong một thế giới đ&ocirc;ng đ&uacute;c. Ch&uacute;ng t&ocirc;i muốn đảm bảo rằng tất cả mọi người đều nhận được th&ocirc;ng tin phim ch&uacute;ng t&ocirc;i sắp ra mắt&rdquo; &ndash; nh&agrave; sản xuất phim cho biết. Một ng&agrave;y sau buổi họp mặt giới truyền th&ocirc;ng, trailer của <em>The Martian </em>trở th&agrave;nh c&acirc;u chuyện mới của l&agrave;ng giải tr&iacute; khi n&oacute; đạt đến 4,8 triệu lượt xem tr&ecirc;n Facebook, 3,3 triệu lượt xem tr&ecirc;n YouTube chỉ trong 48 giờ. Một con số qu&aacute; ấn tượng d&ugrave; nh&agrave; sản xuất đ&atilde; bỏ c&ocirc;ng sức v&agrave; tiền kh&ocirc;ng &iacute;t để quảng b&aacute;!</p>\n<p style=\"text-align: justify;\">&ldquo;H&atilde;ng phim chi tiền, tạo n&ecirc;n c&acirc;u chuyện để c&aacute;c ph&oacute;ng vi&ecirc;n chia sẻ ch&uacute;ng tr&ecirc;n c&aacute;c trang mạng x&atilde; hội. Họ h&agrave;o ph&oacute;ng như thế v&igrave; tin rằng bằng c&aacute;ch n&agrave;o đ&oacute; số tiền chi ra ban đầu sẽ chuyển h&oacute;a th&agrave;nh USD v&agrave; quay lại ph&ograve;ng v&eacute;, về t&uacute;i của họ&rdquo; &ndash; ph&oacute;ng vi&ecirc;n của Entertainment Weekly n&oacute;i.</p>\n<p style=\"text-align: justify;\">Trailers lan nhanh tr&ecirc;n mạng, k&iacute;ch th&iacute;ch sự t&ograve; m&ograve;, tạo ch&uacute; &yacute; cho kh&aacute;n giả.</p>\n<p style=\"text-align: justify;\">Trong th&aacute;ng 8, c&aacute;c ph&oacute;ng vi&ecirc;n giải tr&iacute;, được viếng thăm từ hai người đ&agrave;n &ocirc;ng h&oacute;a trang th&agrave;nh x&aacute;c sống, hệt như nh&acirc;n vật sinh đ&ocirc;i trong loạt phim kinh dị <em>Sinister 2</em>. Hai người đ&agrave;n &ocirc;ng n&agrave;y mang theo hộp gỡ chứa một con chuột nhồi b&ocirc;ng v&agrave; một USB lưu trailer mới nhất của bộ phim n&agrave;y tặng c&aacute;c ph&oacute;ng vi&ecirc;n.</p>\n<p style=\"text-align: justify;\">H&atilde;ng Warner Bros cũng tổ chức một buổi tiệc tại kh&aacute;ch sạn Los Angeles để quảng b&aacute; loạt phim về <em>Peter Pan</em>. H&atilde;ng n&agrave;y chi tiền để trang tr&iacute; kh&ocirc;ng gian buổi tiệc, trần nh&agrave; giống hệt &ldquo;miền đất hứa&rdquo; Neverland v&agrave; c&ograve;n nhiều những s&aacute;ng tạo kh&aacute;c.</p>\n<p style=\"text-align: right;\"><em>Theo nld.com.vn</em></p>','Tung \'chiêu\', chi đậm để quảng bá phim ','','',0,'http://tamdiem.com.vn/sources/images/phim%20Hollywood.jpg','',0,0,0,0,486,0,1440733463,1441860408,1),(113,'Phim Ấn Độ \'Âm Mưu và Tình Yêu\'','phim-truyen-hinh-am-muu-va-tinh-yeu',16,'news_item','Phim truyền hình Âm mưu Tình yêu','Chuyện kể về 2 chị em Gopi và Rashi với 2 tính cách trái ngược. Gopi là đứa bé mồ côi sống cùng bà dì Mama-Mami và cô em họ Rashi. Cô thật thà nhưng lại ko được ăn học. Rashi ranh mãnh và tham lam như bà mẹ Urmila Shah của mình.','Chuyện kể về 2 chị em Gopi và Rashi với 2 tính cách trái ngược. Gopi là đứa bé mồ côi sống cùng bà dì Mama-Mami và cô em họ Rashi. Cô thật thà nhưng lại ko được ăn học. Rashi ranh mãnh và tham lam như bà mẹ Urmila Shah của mình.','<p style=\"text-align: justify;\">Chuyện kể về 2 chị em Gopi v&agrave; Rashi với 2 t&iacute;nh c&aacute;ch tr&aacute;i ngược. Gopi l&agrave; đứa b&eacute; mồ c&ocirc;i sống c&ugrave;ng b&agrave; d&igrave; Mama-Mami v&agrave; c&ocirc; em họ Rashi. C&ocirc; thật th&agrave; nhưng lại kh&ocirc;ng được ăn học. Rashi ranh m&atilde;nh v&agrave; tham lam như b&agrave; mẹ Urmila Shah của m&igrave;nh.</p>\n<table style=\"margin-left: auto; margin-right: auto; height: 329px;\" width=\"458\">\n<tbody>\n<tr>\n<td>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//AmMuuVaTinhYeu0.jpg\" alt=\"\" width=\"450\" height=\"320\" /></p>\n<h6 style=\"text-align: center;\"><em>Nữ diễn vi&ecirc;n&nbsp;Giaa Manek&nbsp;trong vai Gopi</em></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<table style=\"height: 342px; margin-left: auto; margin-right: auto;\" width=\"458\">\n<tbody>\n<tr>\n<td>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//AmMuuVaTinhYeu1.jpg\" alt=\"\" width=\"450\" height=\"320\" /></p>\n<h6 style=\"text-align: center;\">&nbsp;<em>Vai diễn Rashi do Rucha Hasabnis thể hiện</em></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Chuyện bắt đầu khi b&agrave; Kokila Modi đi t&igrave;m một n&agrave;ng vợ ph&ugrave; hợp cho cậu con trai cưng Ahem. B&agrave; chọn Gopi v&agrave; khiến mẹ con Rashi sinh l&ograve;ng ghen tức, họ lừa người anh họ Jigar của Ahem cưới Rashi, v&agrave; rắp t&acirc;m ph&aacute; họai h&ocirc;n nh&acirc;n của Gopi. Tuy nhi&ecirc;n mọi kế họach đều thất bại, Gopi mang thai v&agrave; sinh ra 1 b&eacute; g&aacute;i, được đặt t&ecirc;n l&agrave; Meera. Chuyện chưa dừng ở đ&acirc;y, c&ograve;n rất nhiều những toan t&iacute;nh cay độc đang chờ Gopi ph&iacute;a trước.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//4.jpg\" alt=\"\" width=\"450\" height=\"300\" /></td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>Hiện phim đang được ph&aacute;t s&oacute;ng tr&ecirc;n k&ecirc;nh THVL1 v&agrave;o l&uacute;c 22h00 c&aacute;c ng&agrave;y trong tuần. Mời qu&yacute; vị v&agrave; c&aacute;c bạn đ&oacute;n xem.</p>\n<p>&nbsp;</p>\n<table style=\"height: 188px;\" border=\"1\" width=\"631\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: center;\"><strong>TH&Ocirc;NG TIN TH&Ecirc;M VỀ BỘ PHIM \"&Acirc;M MƯU T&Igrave;NH Y&Ecirc;U\"</strong></p>\n<p><strong>&nbsp; Nước sản xuất</strong>:&nbsp; Ấn Độ</p>\n<p><strong>&nbsp; Thời lượng&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>: 200 tập x 45 ph&uacute;t</p>\n<p style=\"text-align: justify;\">&nbsp; Tại Ấn Độ, mặc d&ugrave; được ph&aacute;t s&oacute;ng v&agrave;o ngay l&uacute;c hầu hết c&aacute;c b&agrave; nội trợ đều bận chuẩn bị buổi cơm chiều v&agrave; rất &iacute;t xem tivi, bộ phim vẫn đủ gay cấn để duy tr&igrave; lượng kh&aacute;n giả cao đến 4 chấm TRP trong hơn 3 năm, trở th&agrave;nh 1 hiện tượng đ&aacute;ng ch&uacute; &yacute; trong l&agrave;ng giải tr&iacute; Ấn độ.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em> T&acirc;m Điểm</em></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>','Phim truyền hình Âm mưu và Tình yêu','','',0,'http://tamdiem.com.vn/sources/images/Am%20muu%20tinh%20yeu.jpg','',0,0,0,0,196,0,1440755283,1448245783,1),(114,'Tâm Điểm vinh dự được trao tặng Giấy khen thực hiện tốt nghĩa vụ nộp thuế','tam-diem-vinh-du-duoc-trao-tang-giay-khen-thuc-hien-tot-nghia-vu-nop-thue',15,'news_item','Tâm Điểm vinh dự được trao tặng Giấy khen thực hiện tốt nghĩa vụ nộp thuế','Vừa qua, tại hội nghị tuyên dương tổ chức - cá nhân thực hiện tốt nghĩa vụ nộp thuế năm 2014, Chi Cục Thuế Quận 1, TP.HCM đã trao giấy khen cho công ty TNHH Tâm Điểm cùng 120 tổ chức, cá nhân tiêu biểu về thành tích chấp hành tốt những quy định về chính sách thuế.','Vừa qua, tại hội nghị tuyên dương tổ chức - cá nhân thực hiện tốt nghĩa vụ nộp thuế năm 2014, Chi Cục Thuế Quận 1, TP.HCM đã trao giấy khen cho công ty TNHH Tâm Điểm cùng 120 tổ chức, cá nhân tiêu biểu về thành tích chấp hành tốt những quy định về chính sách thuế.','<p style=\"text-align: justify;\"><strong>Vừa qua, tại hội nghị tuy&ecirc;n dương tổ chức - c&aacute; nh&acirc;n thực hiện tốt nghĩa vụ nộp thuế năm 2014, Chi Cục Thuế Quận 1, TP.HCM đ&atilde; trao giấy khen cho c&ocirc;ng ty TNHH T&acirc;m Điểm c&ugrave;ng 120 tổ chức, c&aacute; nh&acirc;n ti&ecirc;u biểu về th&agrave;nh t&iacute;ch chấp h&agrave;nh tốt những quy định về ch&iacute;nh s&aacute;ch thuế.</strong></p>\n<p><em><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/thue 4.jpg\" alt=\"\" width=\"500\" height=\"330\" />&nbsp;</em></p>\n<p style=\"text-align: justify;\">Ph&aacute;t biểu tại Hội nghị, &ocirc;ng L&ecirc; Duy Minh &ndash; Ph&oacute; cục trưởng cục thuế TP.Hồ Ch&iacute; Minh, Chi cục trưởng Chi cục thuế Quận 1 cho biết: Những năm qua, trong điều kiện nền kinh tế Th&agrave;nh phố cũng như cả nước phải đối mặt với nhiều biến động, t&igrave;nh h&igrave;nh sản xuất kinh doanh gặp nhiều kh&oacute; khăn, c&aacute;c Doanh nghiệp tr&ecirc;n địa b&agrave;n Quận 1 đ&atilde; nỗ lực, n&acirc;ng cao &yacute; thức tự gi&aacute;c chấp h&agrave;nh ph&aacute;p luật. Số thu ng&acirc;n s&aacute;ch nh&agrave; nước trong những năm qua li&ecirc;n tục tăng, đạt tỷ lệ tr&ecirc;n 20% mỗi năm giai đoạn 2012 &ndash; 2014. Ri&ecirc;ng năm 2014, số thu ng&acirc;n s&aacute;ch nh&agrave; nước của Quận 1 l&agrave; 6,385 tỷ đồng, đạt 142% dự to&aacute;n ph&aacute;p lệnh v&agrave; tăng 67% so với c&ugrave;ng kỳ năm 2013. Quận 1 c&oacute; số thu lớn nhất trong c&aacute;c chi cục thế của cả nước.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//thue 3.jpg\" alt=\"\" width=\"500\" height=\"330\" /></p>\n<p><em>&nbsp;</em></p>\n<p style=\"text-align: justify;\">V&agrave; để ghi nhận sự nỗ lực cũng như những đ&oacute;ng g&oacute;p to lớn của c&aacute;c doanh nghiệp, Chi cục thuế Quận 1 đ&atilde; được sự đồng thuận của Tổng cục thuế tổ chức vinh danh 120 doanh nghiệp &ndash; c&aacute; nh&acirc;n ti&ecirc;u biểu đ&atilde; thực hiện tốt nghĩa vụ nộp thuế năm 2014, đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước. C&ocirc;ng ty TNHH T&acirc;m Điểm vinh dự được nhận giấy khen đ&atilde; c&oacute; th&agrave;nh t&iacute;ch chấp h&agrave;nh tốt nghĩa vụ nộp thuế năm 2014.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//thue 2.JPG\" alt=\"\" width=\"500\" height=\"330\" /></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: right;\"><em>T&acirc;m Điểm</em></p>','Tâm Điểm vinh dự được trao tăng Giấy khen thực hiện tốt nghĩa vụ nộp thuế','','',0,'http://tamdiem.com.vn/sources/images/thue%205.jpg','',0,0,0,0,200,0,1441081544,1446529889,1),(115,'Kênh truyền hình xã hội hóa: Vất vả tồn tại ','kenh-truyen-hinh-xa-hoi-hoa-vat-va-ton-tai-',14,'news_item','Kênh truyền hình xã hội hóa: Vất vả tồn tại ','Xã hội hóa truyền hình được gọi chung là liên kết, liên doanh sản xuất chương trình truyền hình. Sau 10 năm hoạt động, so với sự hồ hởi và nở rộ lúc ban đầu, giờ đây những nhà đầu tư, các đơn vị hợp tác với nhà đài có phần dè dặt hơn vì họ hiểu rằng mình đang phải đối mặt với nhiều rủi ro hơn.','Xã hội hóa truyền hình được gọi chung là liên kết, liên doanh sản xuất chương trình truyền hình. Sau 10 năm hoạt động, so với sự hồ hởi và nở rộ lúc ban đầu, giờ đây những nhà đầu tư, các đơn vị hợp tác với nhà đài có phần dè dặt hơn vì họ hiểu rằng mình đang phải đối mặt với nhiều rủi ro hơn.','<p style=\"text-align: justify;\"><strong>X&atilde; hội h&oacute;a truyền h&igrave;nh được gọi chung l&agrave; li&ecirc;n kết, li&ecirc;n doanh sản xuất chương tr&igrave;nh truyền h&igrave;nh. Sau 10 năm hoạt động, so với sự hồ hởi v&agrave; nở rộ l&uacute;c ban đầu, giờ đ&acirc;y những nh&agrave; đầu tư, c&aacute;c đơn vị hợp t&aacute;c với nh&agrave; đ&agrave;i c&oacute; phần d&egrave; dặt hơn v&igrave; họ hiểu rằng m&igrave;nh đang phải đối mặt với nhiều rủi ro hơn.</strong></p>\n<p style=\"text-align: justify;\"><strong>&ldquo;Đốt tiền&rdquo; h&agrave;ng ng&agrave;y</strong></p>\n<p style=\"text-align: justify;\">Từ khi được nh&agrave; nước cho ph&eacute;p x&atilde; hội h&oacute;a truyền h&igrave;nh, tư nh&acirc;n được quyền li&ecirc;n kết, li&ecirc;n doanh với nh&agrave; đ&agrave;i để sản xuất một phần (giờ ph&aacute;t s&oacute;ng) hoặc to&agrave;n bộ sản phẩm truyền h&igrave;nh (k&ecirc;nh ph&aacute;t s&oacute;ng). Nh&agrave; đ&agrave;i chịu tr&aacute;ch nhiệm về nội dung, bi&ecirc;n tập v&agrave; truyền dẫn ph&aacute;t s&oacute;ng; đơn vị li&ecirc;n kết lo đầu tư kinh ph&iacute;, sản xuất chương tr&igrave;nh. Truyền h&igrave;nh vốn l&agrave; một sản phẩm đặc th&ugrave;, kinh ph&iacute; đầu tư cho sản phẩm n&agrave;y lu&ocirc;n được giới trong nghề gọi l&agrave; &ldquo;kinh doanh mặt h&agrave;ng đốt tiền h&agrave;ng ng&agrave;y&rdquo; nhưng lợi nhuận thu về cũng v&ocirc; c&ugrave;ng lớn - nếu kinh doanh hiệu quả. Ch&iacute;nh v&igrave; thế, nhiều doanh nghiệp, c&ocirc;ng ty truyền th&ocirc;ng rơi rụng, &ldquo;chết tức tưởi&rdquo; trong lĩnh vực n&agrave;y nhưng vẫn c&oacute; những đơn vị trụ được 7, 8 năm nay, d&ugrave; rằng đ&oacute; l&agrave; h&agrave;nh tr&igrave;nh của &ldquo;phập phồng, lo lắng v&agrave; cũng kh&ocirc;ng biết ng&agrave;y mai sẽ như thế n&agrave;o&rdquo; - &yacute; kiến của l&atilde;nh đạo một c&ocirc;ng ty truyền th&ocirc;ng.</p>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source/TTBN2.jpg\" alt=\"\" width=\"400\" height=\"560\" /></p>\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"background-color: #ffffff; color: #0000ff;\">Chương tr&igrave;nh <em>Thử th&aacute;ch c&ugrave;ng bước nhảy</em> (tr&ecirc;n s&oacute;ng HTV)</span></p>\n<p style=\"text-align: justify;\">Nếu li&ecirc;n kết để sản xuất phim hoặc chương tr&igrave;nh ph&aacute;t s&oacute;ng, đơn vị li&ecirc;n kết phải tự bỏ tiền sản xuất, mua bản quyền chương tr&igrave;nh (hoặc phim) sau đ&oacute; được nh&agrave; đ&agrave;i trả lại bằng c&aacute;c đoạn clip quảng c&aacute;o. Khi đơn vị li&ecirc;n kết nhận cả một k&ecirc;nh, ngo&agrave;i kinh ph&iacute; sản xuất, mua bản quyền c&ograve;n phải trả cho nh&agrave; đ&agrave;i chi ph&iacute; truyền dẫn ph&aacute;t s&oacute;ng (con số n&agrave;y thường ở mức h&agrave;ng chục tỷ đồng/năm). V&igrave; thế, n&oacute;i đầu tư l&agrave;m truyền h&igrave;nh l&agrave; cuộc chơi &ldquo;đốt tiền&rdquo; cũng kh&ocirc;ng ngoa. Với b&agrave;i to&aacute;n sống c&ograve;n trong việc thu hồi vốn, muốn vậy phải l&agrave;m sao thu h&uacute;t được c&agrave;ng nhiều quảng c&aacute;o c&agrave;ng tốt. Bởi vậy, những đơn vị li&ecirc;n kết thường chọn đầu tư sản xuất c&aacute;c chương tr&igrave;nh giải tr&iacute; để thu h&uacute;t kh&aacute;n giả, như chương tr&igrave;nh Thử th&aacute;ch c&ugrave;ng bước nhảy, Người b&iacute; ẩn tr&ecirc;n s&oacute;ng HTV v&agrave; Gương mặt th&acirc;n quen&hellip; tr&ecirc;n s&oacute;ng VTV.</p>\n<p style=\"text-align: justify;\">Khi c&oacute; một, hai chương tr&igrave;nh ăn kh&aacute;ch, c&aacute;c đơn vị kh&aacute;c cũng ồ ạt mua bản quyền, đầu tư sản xuất c&aacute;c chương tr&igrave;nh giải tr&iacute; tương tự. Ch&iacute;nh v&igrave; thế, v&agrave;i năm lại đ&acirc;y, kh&aacute;n giả đ&atilde; bắt đầu bội thực với h&agrave;ng loạt chương tr&igrave;nh giải tr&iacute; na n&aacute; nhau, tr&ecirc;n hầu hết c&aacute;c đ&agrave;i truyền h&igrave;nh từ trung ương đến địa phương: c&aacute;c cuộc thi h&aacute;t, thi nhảy, chương tr&igrave;nh h&agrave;i... V&agrave; trong cuộc cạnh tranh ng&agrave;y c&agrave;ng khắc nghiệt ấy, để tạo sự ch&uacute; &yacute; với người xem - đồng nghĩa với việc sẽ thu h&uacute;t được nhiều quảng c&aacute;o, c&oacute; đơn vị sẵn s&agrave;ng l&agrave;m ẩu, l&agrave;m bừa, cố t&igrave;nh tạo scandal g&acirc;y ảnh hưởng kh&ocirc;ng nhỏ đến uy t&iacute;n của nh&agrave; đ&agrave;i; hoặc thấy phim H&agrave;n, phim Ấn ph&aacute;t tr&ecirc;n k&ecirc;nh n&agrave;y được kh&aacute;n giả th&iacute;ch, thế l&agrave; đơn vị kh&aacute;c cũng nhảy v&ocirc; mua phim c&aacute;c nước ấy mang về ph&aacute;t tr&ecirc;n k&ecirc;nh của m&igrave;nh. Kiểu kinh doanh ấy vừa đẩy gi&aacute; bản quyền l&ecirc;n cao, vừa khiến cho chương tr&igrave;nh truyền h&igrave;nh trở n&ecirc;n mất c&acirc;n đối, đơn điệu v&agrave; hạn chế thẩm mỹ người xem truyền h&igrave;nh.</p>\n<p style=\"text-align: justify;\">&Ocirc;ng Nguyễn Qu&yacute; H&ograve;a, Tổng Gi&aacute;m đốc Đ&agrave;i Truyền h&igrave;nh TPHCM (HTV), chia sẻ: &ldquo;C&aacute;c đơn vị thường tập trung quảng c&aacute;o nhiều v&agrave;o những chương tr&igrave;nh giải tr&iacute;, trong khi c&aacute;c chương tr&igrave;nh ch&iacute;nh luận, phim t&agrave;i liệu v&agrave; ngay cả phim truyện cũng rất &iacute;t, kh&oacute; k&ecirc;u gọi được quảng c&aacute;o. Trong khi đ&oacute;, nhiệm vụ của truyền h&igrave;nh, cũng như c&aacute;c loại h&igrave;nh b&aacute;o ch&iacute; kh&aacute;c, l&agrave; tuy&ecirc;n truyền về chủ trương, ch&iacute;nh s&aacute;ch của Đảng, Nh&agrave; nước đến người d&acirc;n&hellip; nhưng do ảnh hưởng về kinh tế đ&atilde; l&agrave;m giảm thế mạnh ấy của truyền h&igrave;nh&rdquo;.</p>\n<p style=\"text-align: justify;\"><strong>Gồng m&igrave;nh để tồn tại</strong></p>\n<p style=\"text-align: justify;\">&Ocirc;ng L&acirc;m Ch&iacute; Thiện, Chủ tịch Điều h&agrave;nh k&ecirc;nh TodayTV (C&ocirc;ng ty cổ phần Quốc tế Truyền th&ocirc;ng IMC) cho biết: &ldquo;Về tổng thể, x&atilde; hội h&oacute;a truyền h&igrave;nh được cơ quan chức năng tạo điều kiện ph&aacute;t triển. Tuy nhi&ecirc;n, việc x&atilde; hội h&oacute;a truyền h&igrave;nh đang trong giai đoạn ph&aacute;t triển v&agrave; c&ograve;n qu&aacute; mới mẻ. Nguồn nh&acirc;n lực của lĩnh vực n&agrave;y c&ograve;n giới hạn, đặc biệt l&agrave; c&aacute;c c&ocirc;ng việc li&ecirc;n quan, phụ trợ như: bi&ecirc;n tập nội dung, bi&ecirc;n tập kịch bản, kỹ thuật l&agrave;m phim&hellip; chưa ph&aacute;t triển như c&aacute;c nước kh&aacute;c, n&ecirc;n việc sản xuất những bộ phim hay, cũng như t&igrave;m những kịch bản tốt, t&igrave;nh tiết nhanh với nội dung s&acirc;u sắc vẫn c&ograve;n nhiều hạn chế&rdquo;. Chặng đường 7 năm của k&ecirc;nh TodayTV gặp kh&ocirc;ng &iacute;t gian nan. Kh&ocirc;ng &iacute;t lần tưởng chừng k&ecirc;nh phải đ&oacute;ng cửa v&igrave; thiếu kinh ph&iacute;, thiếu nh&acirc;n lực.</p>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//TTBN 1.jpg\" alt=\"\" width=\"500\" height=\"333\" /></p>\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"background-color: #ffffff; color: #0000ff;\"> Chương tr&igrave;nh Thử th&aacute;ch c&ugrave;ng bước nhảy được kh&aacute;n giả y&ecirc;u th&iacute;ch</span></p>\n<p style=\"text-align: justify;\">Với k&ecirc;nh HTV3 th&igrave; nhiều lần tưởng chừng ngưng ph&aacute;t s&oacute;ng v&agrave; thay đổi người điều h&agrave;nh. D&ugrave; đ&atilde; được mang t&ecirc;n l&agrave; k&ecirc;nh thanh thiếu ni&ecirc;n v&agrave; gia đ&igrave;nh th&igrave; hiện nay, với khoản nợ gần 80 tỷ đồng, k&ecirc;nh HTV3 đ&atilde; được chuyển giao cho một đơn vị đầu tư mới l&agrave; C&ocirc;ng ty Truyền th&ocirc;ng Thanh thiếu ni&ecirc;n với hy vọng c&oacute; thể trụ được để trả nợ HTV. Sau khi chuyển ti&ecirc;u ch&iacute; k&ecirc;nh từ k&ecirc;nh thể thao th&agrave;nh k&ecirc;nh giải tr&iacute; tổng hợp, k&ecirc;nh HTV2 (Tập đo&agrave;n Đất Việt VAC) ng&agrave;y c&agrave;ng ph&aacute;t triển v&agrave; đơn vị đầu tư đ&atilde; c&oacute; thể y&ecirc;n t&acirc;m, d&ugrave; rằng họ vẫn phải thừa nhận l&agrave; &ldquo;kh&oacute; khăn vất vả lắm!&rdquo;.</p>\n<p style=\"text-align: justify;\">&Ocirc;ng Trần Minh Tiến, Tổng Gi&aacute;m đốc k&ecirc;nh Let&rsquo;s Viet (C&ocirc;ng ty cổ phần Truyền th&ocirc;ng La Sa Ta), cho biết: &ldquo;L&agrave;m truyền h&igrave;nh, nếu kh&ocirc;ng đầu tư nghi&ecirc;m t&uacute;c l&agrave; chết ngay! Nhiều người cứ nghĩ l&agrave;m k&ecirc;nh truyền h&igrave;nh chỉ việc mua nội dung về ph&aacute;t s&oacute;ng l&agrave; xong, nhưng đ&acirc;u phải vậy. Nếu kh&ocirc;ng x&acirc;y dựng bản sắc ri&ecirc;ng cho k&ecirc;nh, kh&aacute;n giả xem k&ecirc;nh n&agrave;o cũng thấy giống nhau th&igrave; kh&aacute;n giả sẽ ngay lập tức chuyển k&ecirc;nh kh&aacute;c. V&agrave; khi k&ecirc;nh kh&ocirc;ng c&oacute; nhiều người xem, kh&ocirc;ng thu h&uacute;t được quảng c&aacute;o nghĩa l&agrave; kinh doanh kh&ocirc;ng đạt, kh&ocirc;ng đủ tiền để t&aacute;i đầu tư, để &ldquo;nu&ocirc;i&rdquo; k&ecirc;nh m&agrave; chỉ &ldquo;đốt&rdquo; tiền. Số tiền đầu tư ng&agrave;y c&agrave;ng lớn, lợi nhuận kh&ocirc;ng c&oacute;, đương nhi&ecirc;n phải ngưng ph&aacute;t s&oacute;ng&rdquo;.</p>\n<p style=\"text-align: justify;\">Thời gian qua, v&igrave; nhiều l&yacute; do m&agrave; h&agrave;ng loạt k&ecirc;nh đ&atilde; phải đ&oacute;ng cửa, như: VBC (Tập đo&agrave;n T&acirc;n Tạo), Fansipan TV (Le Group), Astro Cảm X&uacute;c (BHD, Astro), M4Me (Quantum Media), MOD (Quantum Media), Real TV (C&ocirc;ng ty cổ phần Truyền th&ocirc;ng Truyền h&igrave;nh thực tế), Saigon Channel (C&ocirc;ng ty Truyền th&ocirc;ng Sắc M&agrave;u - SGC), HTV1 (C&ocirc;ng ty V&acirc;n Thanh Long - VTL), HSV (C&ocirc;ng ty Home Shopping Vietnam)... Với những k&ecirc;nh truyền h&igrave;nh x&atilde; hội h&oacute;a kh&aacute;c, nếu c&aacute;c đơn vị li&ecirc;n kết với nh&agrave; đ&agrave;i kh&ocirc;ng đủ tiềm lực kinh tế, kh&ocirc;ng định hướng nội dung k&ecirc;nh r&otilde; r&agrave;ng v&agrave; chỉ chạy theo tr&agrave;o lưu kiểu ăn xổi ở th&igrave;, chắc chắn kết cục &ldquo;c&aacute;c chủ nợ d&iacute; theo đến phải đ&oacute;ng cửa k&ecirc;nh&rdquo; chỉ l&agrave; vấn đề thời gian!</p>\n<p style=\"text-align: right;\"><em>Theo sggp.org.vn</em></p>','Kênh truyền hình xã hội hóa: Vất vả tồn tại ','','',0,'http://tamdiem.com.vn/sources/images/TTBN%201.jpg','',0,0,0,0,485,0,1441248257,1441941773,1),(116,'Phim Việt Nam \'Thời gian để yêu\'','phim-truyen-hinh-thoi-gian-de-yeu',16,'news_item','Phim truyền hình Thời gian để yêu','Câu chuyện xoay quanh các bạn trẻ trong một nhà máy nước trái cây đóng hộp, với muôn vàn phức tạp trong cuộc sống','Câu chuyện xoay quanh các bạn trẻ trong một nhà máy nước trái cây đóng hộp, với muôn vàn phức tạp trong cuộc sống','<p style=\"text-align: justify;\">C&acirc;u chuyện xoay quanh c&aacute;c bạn trẻ trong một nh&agrave; m&aacute;y nước tr&aacute;i c&acirc;y đ&oacute;ng hộp, với mu&ocirc;n v&agrave;n phức tạp trong cuộc sống. B&ecirc;n cạnh c&aacute;c bạn trẻ cố gắng vươn l&ecirc;n, cũng c&ograve;n một số người đi ngược lại chiều hướng ph&aacute;t triển chỉ để mưu cầu cho lợi &iacute;ch c&aacute; nh&acirc;n, trong đ&oacute; nổi bật nhất l&agrave; Th&aacute;i một ch&agrave;ng trai th&ocirc;ng minh, gi&agrave;u c&oacute;, nhưng lại lắm mưu nhiều kế, chỉ v&igrave; quyền lợi, l&agrave;m ảnh hưởng đến những số phận đau thương của c&aacute;c c&ocirc;ng nh&acirc;n cũng như người th&acirc;n nhất của đời m&igrave;nh.</p>\n<p><span style=\"color: #0000ff;\"><em><img src=\"/tinymce/source//Thoi Gian De Yeu.png\" alt=\"\" width=\"570\" height=\"300\" /></em></span></p>\n<p><span style=\"color: #0000ff;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4 nh&acirc;n vật ch&iacute;nh trong phim</em></span></p>\n<p style=\"text-align: justify;\">Thái l&agrave; con ruột của &ocirc;ng b&agrave; Th&agrave;nh, cha chết, mẹ trở n&ecirc;n đi&ecirc;n loạn n&ecirc;n từ b&eacute; đ&atilde; được &ocirc;ng b&agrave; Sang nhận nu&ocirc;i. Sự cố gắng của Th&aacute;i đạt kết quả khi anh được ba mẹ trao quyền quản l&yacute; to&agrave;n bộ x&iacute; nghiệp. Tuy nhi&ecirc;n, khi đạt được mục đ&iacute;ch đầu ti&ecirc;n th&igrave; mọi chuyện trở n&ecirc;n rối ren khi Th&aacute;i ph&aacute;t hiện m&igrave;nh chỉ l&agrave; con nu&ocirc;i của &ocirc;ng b&agrave; Sang v&agrave; c&agrave;ng tồi tệ hơn khi anh biết được Khải - kẻ th&ugrave; kh&ocirc;ng đội trời chung với m&igrave;nh, mới ch&iacute;nh l&agrave; con ruột của &ocirc;ng b&agrave; Sang. Sợ mất vị thế của m&igrave;nh v&agrave; v&igrave; ghen tức khi kh&ocirc;ng gi&agrave;nh được t&igrave;nh cảm của Hương - người Th&aacute;i y&ecirc;u, Th&aacute;i t&igrave;m mọi c&aacute;ch hất cẳng Khải ra khỏi c&ocirc;ng ty nhưng kế hoạch lu&ocirc;n bất th&agrave;nh. Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, Th&aacute;i đ&atilde; t&igrave;m c&aacute;ch để Khải kh&ocirc;ng thể nhận lai cha mẹ ruột. Dần dần Th&aacute;i bất chấp cả thủ đoạn v&agrave; lương t&acirc;m.</p>\n<p><span style=\"color: #0000ff;\"><em><img src=\"/tinymce/source//Vinh.jpg\" alt=\"\" width=\"550\" height=\"320\" /></em></span></p>\n<p><span style=\"color: #0000ff;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trương Thế Vinh &ldquo;lột x&aacute;c&rdquo; khi h&oacute;a th&acirc;n v&agrave;o nh&acirc;n vật Th&aacute;i</em></span></p>\n<p style=\"text-align: justify;\">Khải l&agrave; con ruột của &ocirc;ng b&agrave; Sang, bị b&agrave; Th&agrave;nh bắt c&oacute;c trong qu&aacute; khứ n&ecirc;n cứ nghĩ b&agrave; Th&agrave;nh l&agrave; mẹ m&igrave;nh v&agrave; lu&ocirc;n quan t&acirc;m chăm s&oacute;c b&agrave; d&ugrave; b&agrave; bị đi&ecirc;n loạn. Khải bỏ qu&ecirc; l&ecirc;n th&agrave;nh phố với mong muốn kiếm tiền chữa bệnh cho mẹ v&agrave; tiếp tục theo đuổi giấc mơ kỹ sư h&oacute;a thực phẩm. Khải bị Th&aacute;i ph&aacute;t hiện ra th&acirc;n phận thật l&agrave; con &ocirc;ng b&agrave; Sang v&agrave; bị cuốn v&agrave;o những &acirc;m mưu của Th&aacute;i m&agrave; Khải kh&ocirc;ng hề hay biết. Tuy nhi&ecirc;n, d&ugrave; gặp nhiều kh&oacute; khăn, Khải vẫn vững v&agrave;ng v&agrave; ki&ecirc;n tr&igrave; với mục ti&ecirc;u l&agrave; chữa bệnh cho mẹ. Cũng tại nơi đ&acirc;y, Khải c&oacute; t&igrave;nh cảm với Hương. Sau bao kh&oacute; khăn v&agrave; trắc trở, Khải đ&atilde; đạt được những mục ti&ecirc;u m&igrave;nh đề ra. B&agrave; Th&agrave;nh khỏi bệnh v&agrave; anh c&ugrave;ng Hương mở một xưởng sản xuất nhỏ.</p>\n<p><span style=\"color: #0000ff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img src=\"/tinymce/source//Vy.jpg\" alt=\"\" width=\"320\" height=\"450\" /></span></p>\n<p><span style=\"color: #0000ff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Diễn vi&ecirc;n T&uacute; Vi đảm nhiệm vai c&ocirc; g&aacute;i trẻ t&ecirc;n Hương c&oacute; ho&agrave;n cảnh đ&aacute;ng thương </em></span></p>\n<p><em><span style=\"color: #0000ff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nhưng lu&ocirc;n lạc quan, nỗ lực trong cuộc sống.</span></em></p>\n<p><em><span style=\"color: #0000ff;\"><img src=\"/tinymce/source//La.JPG\" alt=\"\" width=\"520\" height=\"350\" /></span></em></p>\n<p><span style=\"color: #0000ff;\"><em>Trong khi đ&oacute;, Nguyệt (do L&ecirc; B&ecirc; La đ&oacute;ng) &ndash; bạn của Hương lại l&agrave; một người tham vọng v&agrave; đầy thủ đoạn</em>.</span></p>\n<p>&nbsp;</p>\n<table style=\"height: 164px;\" border=\"1\" width=\"439\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: center;\"><strong>&nbsp; TH&Ocirc;NG TIN TH&Ecirc;M VỀ BỘ PHIM \"THỜI GIAN ĐỂ Y&Ecirc;U\"<br /></strong></p>\n<p><strong>&nbsp; Thể loại&nbsp;&nbsp; :</strong> T&acirc;m l&yacute;</p>\n<p><strong>&nbsp; Số tập&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :</strong> 30 tập x 45 ph&uacute;t</p>\n<p><strong>&nbsp; Diễn vi&ecirc;n :</strong> Trương Thế Vinh, L&ecirc; B&ecirc; La, H&ugrave;ng Thuận, C&ocirc;ng Danh, T&uacute; Vi</p>\n<p><strong>&nbsp; Nước SX &nbsp; :</strong> Việt Nam</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em> T&acirc;m Điểm</em></p>\n<p>&nbsp;</p>','Phim truyền hình \'Thời gian để yêu\'','','',0,'http://tamdiem.com.vn/sources/images/Thoi%20Gian%20De%20Yeu.png','',0,0,0,0,195,0,1441359354,1448245800,1),(117,'Ca sĩ dòng nhạc cách mạng: Sống khỏe với nghề ','ca-si-dong-nhac-cach-mang-song-khoe-voi-nghe-',14,'news_item','Ca sĩ dòng nhạc cách mạng: Sống khỏe với nghề ','Sức sống của dòng nhạc cách mạng, không chỉ ở phía khán giả, mà có cả một lớp nghệ sĩ trẻ cũng hào hứng quay lại dòng nhạc này. ','Sức sống của dòng nhạc cách mạng, không chỉ ở phía khán giả, mà có cả một lớp nghệ sĩ trẻ cũng hào hứng quay lại dòng nhạc này.','<p style=\"text-align: justify;\"><strong><em>Sau thế hệ của c&aacute;c ca sĩ Thanh Hoa, Thu Hiền, Qu&yacute; Dương, Trung Ki&ecirc;n, Kiều Hưng&hellip; l&agrave; buổi v&agrave;ng son của nhạc pop khiến nhiều người từng c&oacute; l&uacute;c đặt c&acirc;u hỏi về sức sống của nhạc c&aacute;ch mạng cũng như những giọng ca theo đuổi d&ograve;ng nhạc n&agrave;y. Nhưng từ khi thế hệ c&aacute;c ca sĩ Trọng Tấn, Đăng Dương, Việt Ho&agrave;n, Anh Thơ, Lan Anh ở ph&iacute;a Bắc cũng như Tạ Minh T&acirc;m, Thanh Th&uacute;y, Anh Bằng, Huỳnh Lợi ở ph&iacute;a Nam&hellip; xuất hiện th&igrave; c&acirc;u hỏi tr&ecirc;n đ&atilde; được trả lời: Sức sống của d&ograve;ng nhạc c&aacute;ch mạng, kh&ocirc;ng chỉ ở ph&iacute;a kh&aacute;n giả, m&agrave; c&oacute; cả một lớp nghệ sĩ trẻ cũng h&agrave;o hứng quay lại d&ograve;ng nhạc n&agrave;y.<br /> <br /> </em></strong><strong>C&aacute;t-s&ecirc; &ldquo;khủng&rdquo;<br /> </strong><br /> Theo chị Ho&agrave;i Anh, C&ocirc;ng ty Tổ chức biểu diễn Đ&ocirc;ng Đ&ocirc;, rất kh&oacute; so s&aacute;nh về thu nhập của c&aacute;c ca sĩ h&aacute;t nhạc c&aacute;ch mạng với c&aacute;c ca sĩ nhạc trẻ. Bởi lẽ, ca sĩ h&aacute;t nhạc trẻ chỉ c&oacute; thời, c&aacute;t-s&ecirc; của mỗi chương tr&igrave;nh c&oacute; thể l&ecirc;n tới mức rất &ldquo;khủng&rdquo; khiến nhiều người phải cho&aacute;ng v&aacute;ng, nhưng với những giọng ca th&agrave;nh danh của d&ograve;ng nhạc c&aacute;ch mạng như Trọng Tấn, Anh Thơ, Việt Ho&agrave;n&hellip; th&igrave; th&ugrave; lao biểu diễn cũng kh&ocirc;ng hề thấp. Mỗi buổi diễn trong c&aacute;c sự kiện của c&aacute;c đơn vị, c&ocirc;ng ty&hellip; th&ocirc;ng thường c&aacute;t-s&ecirc; họ nhận được l&ecirc;n tới 60 triệu đồng (nếu đi tỉnh). <br /> <br /> Ca sĩ Trọng Tấn c&oacute; lần chia sẻ, đợt 8-3 năm ngo&aacute;i, c&oacute; những ng&agrave;y anh phải biểu diễn tới 8 show. Mức thu nhập của c&aacute;c ca sĩ trẻ d&ograve;ng nhạc n&agrave;y như Vũ Thắng Lợi, L&ecirc; Anh Dũng&hellip; tuy c&oacute; thấp hơn ch&uacute;t nhưng kh&ocirc;ng bao giờ dưới 10 triệu đồng. Nh&igrave;n chung, nguồn thu nhập ch&iacute;nh của c&aacute;c ca sĩ h&aacute;t nhạc c&aacute;ch mạng ở thời điểm n&agrave;y vẫn phụ thuộc nhiều v&agrave;o c&aacute;c sự kiện.<br /> <br /> Trước đ&acirc;y, nhiều ca sĩ h&aacute;t d&ograve;ng nhạc n&agrave;y sống kh&aacute; chật vật với nghề, như ca sĩ Việt Ho&agrave;n từng chia sẻ, v&agrave;o đầu những năm 1990, khi đ&atilde; l&agrave; ca sĩ rất nổi tiếng tại Hải Ph&ograve;ng nhưng cuộc sống kh&oacute; khăn đến mức anh phải mở qu&aacute;n karaoke đ&atilde; tăng thu nhập. &ldquo;Hồi đ&oacute; qu&aacute;n của t&ocirc;i rất đ&ocirc;ng kh&aacute;ch, những tưởng t&ocirc;i sẽ rẽ ngang bỏ nghiệp d&ugrave; đang rất y&ecirc;u nghề, nhưng thật may mắn NSND L&ecirc; Dung đ&atilde; gặp, thức tỉnh v&agrave; k&eacute;o t&ocirc;i trở lại với con đường nghệ thuật chuy&ecirc;n nghiệp để c&oacute; ng&agrave;y h&ocirc;m nay. T&ocirc;i cứ nghĩ m&atilde;i, nếu kh&ocirc;ng c&oacute; c&aacute;i duy&ecirc;n đ&oacute;, cuộc đời t&ocirc;i chắc chắn sẽ rẽ theo một ngả đường kh&aacute;c hẳn&rdquo;. Tự cho l&agrave; m&igrave;nh kh&aacute; bảo thủ, song ca sĩ Việt Ho&agrave;n cho rằng nếu ca sĩ kh&ocirc;ng sống được bằng nghề l&agrave; họ n&oacute;i dối hoặc họ qu&aacute; tham lam. <br /> <br /> Đồng cảm với suy nghĩ n&agrave;y, ca sĩ Anh Thơ cũng cho rằng, do nhu cầu ti&ecirc;u d&ugrave;ng của m&igrave;nh kh&ocirc;ng nhiều n&ecirc;n chị c&oacute; thể tho&aacute;i m&aacute;i hơn trong việc dung h&ograve;a giữa việc biểu diễn v&agrave; giảng dạy. Anh Thơ nhiều lần t&acirc;m sự l&yacute; do từ chối h&aacute;t đ&aacute;m tiệc v&igrave; thấy m&igrave;nh kh&ocirc;ng phải l&agrave; tu&yacute;p người năng nổ, s&ocirc;i động v&agrave; việc h&aacute;t đ&aacute;m tiệc kh&ocirc;ng mấy ph&ugrave; hợp. Chị n&oacute;i:&ldquo;T&ocirc;i l&agrave; một người sống kh&aacute; kh&eacute;p k&iacute;n, đơn giản v&agrave; rất &iacute;t khi... ti&ecirc;u tiền. Trong cuộc sống h&agrave;ng ng&agrave;y, t&ocirc;i kh&ocirc;ng đ&ograve;i hỏi qu&aacute; nhiều về vật chất. Hơn nữa, tiền th&igrave; biết thế n&agrave;o l&agrave; đủ n&ecirc;n t&ocirc;i sống kh&aacute; thoải m&aacute;i bằng nghề của m&igrave;nh&rdquo;.</p>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source/Cs Kieu Hung.jpg\" alt=\"\" width=\"500\" height=\"350\" /></p>\n<p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: #0000ff;\">&nbsp; Ca sĩ Kiều Hưng trở lại trong chương tr&igrave;nh <em>Giai điệu tự h&agrave;o</em></span></p>\n<p style=\"text-align: justify;\">Nếu những sự kiện l&agrave; mảnh đất m&agrave;u mỡ để c&aacute;c ca sĩ ph&iacute;a Bắc kiếm tiền th&igrave; c&aacute;c ca sĩ d&ograve;ng nhạc c&aacute;ch mạng ở ph&iacute;a Nam c&oacute; vẻ k&eacute;m cạnh hơn. Trừ một v&agrave;i ca sĩ c&oacute; &ldquo;thương hiệu&rdquo; như ca sĩ Tạ Minh T&acirc;m, Thanh Th&uacute;y, c&ograve;n đa phần kh&ocirc;ng mấy được &ldquo;chuộng&rdquo; ở địa hạt n&agrave;y. V&agrave; c&aacute;t-s&ecirc; của họ cũng thuộc h&agrave;ng mơ ước của nhiều ca sĩ trẻ. Ca sĩ Huỳnh Lợi chia sẻ, kh&ocirc;ng v&igrave; thế m&agrave; ca sĩ theo d&ograve;ng nhạc đỏ ở ph&iacute;a Nam &iacute;t đất sống bởi những chương tr&igrave;nh nghệ thuật mang t&iacute;nh tuy&ecirc;n truyền hay hướng đến những ng&agrave;y kỷ niệm, ng&agrave;y lễ lớn của đất nước hầu như diễn ra li&ecirc;n tục, từ cấp tỉnh th&agrave;nh cho đến quận huyện, phường x&atilde;. <br /> <br /> Ngay như Trung t&acirc;m Tổ chức Biểu diễn v&agrave; điện ảnh TPHCM trong th&aacute;ng 8 vừa qua tổ chức đến 8 chương tr&igrave;nh nghệ thuật quy m&ocirc; hưởng ứng c&aacute;c ng&agrave;y lễ lớn. &ldquo;C&oacute; những th&aacute;ng t&ocirc;i nhận 10 show l&agrave; b&igrave;nh thường. Kh&ocirc;ng đặt nặng c&aacute;t-s&ecirc;, nhưng chỉ cần như thế thu nhập cũng đủ để sống thoải m&aacute;i, nhất l&agrave; với những ca sĩ theo d&ograve;ng nhạc c&aacute;ch mạng vốn dĩ cũng kh&ocirc;ng phải tốn k&eacute;m nhiều thứ để chạy theo sự h&agrave;o nho&aacute;ng như ca sĩ thị trường&rdquo;, ca sĩ Huỳnh Lợi chia sẻ. <br /> <br /> <strong>Mạch chảy th&uacute; vị của nhạc Việt<br /> </strong><br /> Thu nhập tốt, song tại sao ca sĩ nhạc ch&iacute;nh thống kh&ocirc;ng mấy người l&agrave;m liveshow, hay ph&aacute;t h&agrave;nh đĩa v&agrave; c&aacute;c sản phẩm &acirc;m nhạc? Ph&acirc;n t&iacute;ch vấn đề n&agrave;y, một số người hoạt động l&acirc;u năm trong lĩnh vực chỉ r&otilde;: Mỗi d&ograve;ng nhạc c&oacute; đối tượng kh&aacute;n giả kh&aacute;c nhau. Với nhạc trẻ th&igrave; việc b&aacute;n v&eacute;, xin t&agrave;i trợ kh&aacute; dễ d&agrave;ng bởi đối tượng m&agrave; họ hướng tới l&agrave; thanh ni&ecirc;n, những người ham th&iacute;ch v&agrave; sẵn s&agrave;ng chi tiền để đ&oacute;n nhận những d&ograve;ng nhạc tươi trẻ. Với d&ograve;ng nhạc ch&iacute;nh thống, mặc d&ugrave; ch&uacute;ng lu&ocirc;n giữ vị tr&iacute; ri&ecirc;ng trong &acirc;m nhạc, trong mỗi người nhưng lại thiếu đi yếu tố &ldquo;lạ&rdquo; để c&oacute; thể kinh doanh. Do đ&oacute;, mặc d&ugrave; việc xuất hiện trong c&aacute;c chương tr&igrave;nh mang t&iacute;nh ch&iacute;nh trị, hội h&egrave;, lễ lạt lu&ocirc;n được c&aacute;c ca sĩ d&ograve;ng nhạc n&agrave;y ch&uacute; trọng nhằm x&acirc;y dựng h&igrave;nh ảnh, sự nghiệp th&igrave; nguồn thu ch&iacute;nh của họ phụ thuộc nhiều v&agrave;o việc tham gia biểu diễn trong c&aacute;c sự kiện. <br /> <br /> N&oacute;i như vậy kh&ocirc;ng c&oacute; nghĩa l&agrave; c&aacute;c ca sĩ d&ograve;ng nhạc n&agrave;y kh&ocirc;ng thể l&agrave;m liveshow. Thực tế cho thấy sau nhiều năm c&aacute;c ca sĩ thị trường thống trị tr&ecirc;n bảng xếp hạng của c&aacute;c liveshow đắt kh&aacute;ch th&igrave; sự xuất hiện của chương tr&igrave;nh <em>Rừng ta biển bạc đồng xanh</em> của Anh Thơ - Trọng Tấn cuối năm 2014 khiến nhiều bầu show phải c&oacute; c&aacute;ch nh&igrave;n mới. Khi đ&oacute;, kh&aacute;n ph&ograve;ng của Cung hữu nghị Việt X&ocirc; lu&ocirc;n k&iacute;n chỗ v&agrave; nh&agrave; sản xuất th&igrave; tuy&ecirc;n bố đ&oacute; mới chỉ l&agrave; mở đầu. <br /> <br /> &ldquo;Nhạc c&aacute;ch mạng c&oacute; một ph&acirc;n kh&uacute;c thị trường rộng v&agrave; m&agrave;u mỡ, với những giọng h&aacute;t như Trọng Tấn, Anh Thơ, t&ocirc;i b&aacute;n v&eacute; v&agrave; đắt show sự kiện hơn cả ng&ocirc;i sao thị trường. Việc ca sĩ Trọng Tấn tập trung h&aacute;t d&ograve;ng nhạc c&aacute;ch mạng l&agrave; quyết định cực kỳ kh&ocirc;n ngoan&rdquo;, b&agrave; &ldquo;bầu&rdquo; Ho&agrave;i Anh khẳng định. Cho tới thời điểm n&agrave;y, Trọng Tấn - &ldquo;Ho&agrave;ng tử nhạc đỏ&rdquo; - vẫn lu&ocirc;n l&agrave; c&aacute;i t&ecirc;n quyến rũ với c&aacute;c ph&ograve;ng v&eacute;. Ch&iacute;nh v&igrave; thế, kh&ocirc;ng c&oacute; g&igrave; lạ lẫm với th&ocirc;ng tin c&aacute;c ca sĩ d&ograve;ng nhạc n&agrave;y sở hữu những ng&ocirc;i nh&agrave; tiền tỷ, đẹp đẽ sang trọng. V&agrave; thu nhập của họ, tuy kh&ocirc;ng c&ocirc;ng khai như c&aacute;c sao thị trường nhưng đ&oacute; cũng kh&ocirc;ng hề k&eacute;m cạnh - c&aacute;c bầu show khẳng định. <br /> <br /> B&ecirc;n cạnh đ&oacute;, c&aacute;c ca sĩ h&aacute;t d&ograve;ng nhạc c&aacute;ch mạng cũng c&oacute; nguồn thu đ&aacute;ng kể từ việc dạy học. Với những nghệ sĩ gạo cội như NSND Thanh Hoa, NSND Trung Hiếu, NSND Thu Hiền&hellip; th&igrave; ngo&agrave;i việc xuất hiện trong c&aacute;c buổi biểu diễn lớn, sự kiện với c&aacute;t-s&ecirc; kh&ocirc;ng thua k&eacute;m c&aacute;c giọng ca trẻ đang nổi tiếng của d&ograve;ng nhạc n&agrave;y, họ c&ograve;n c&oacute; thu nhập rất tốt từ việc giảng dạy, truyền đạt kinh nghiệm cho c&aacute;c ca sĩ trẻ. Hướng n&agrave;y cũng đang được c&aacute;c ca sĩ thế hệ sau của d&ograve;ng nhạc c&aacute;ch mạng tận dụng tốt. V&agrave; đ&oacute; ch&iacute;nh l&agrave; thế mạnh m&agrave; c&aacute;c ca sĩ thị trường kh&ocirc;ng thể c&oacute; được.</p>\n<table border=\"1\" width=\"100%\">\n<tbody>\n<tr>\n<td>\n<p>&nbsp;D&ograve;ng chảy nhạc c&aacute;ch mạng&nbsp; lu&ocirc;n l&agrave; mạch chảy th&uacute; vị của đời sống ca nhạc nước nh&agrave;. Chẳng thế m&agrave; Giai điệu tự h&agrave;o, chương tr&igrave;nh thuần nhạc c&aacute;ch mạng hiếm hoi tr&ecirc;n s&oacute;ng truyền h&igrave;nh năm qua đ&atilde; rinh giải Cống hiến giữa thời buổi đầy rẫy những chương tr&igrave;nh giải tr&iacute; được đầu tư ho&agrave;nh tr&aacute;ng, c&ocirc;ng phu.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: right;\"><em>Theo sggp.org.vn</em><strong><br /></strong></p>\n<div id=\"stcpDiv\" style=\"position: absolute; top: -1999px; left: -1988px;\">Sau thế hệ của c&aacute;c ca sĩ Thanh Hoa, Thu Hiền, Qu&yacute; Dương, Trung Ki&ecirc;n, Kiều Hưng&hellip; l&agrave; buổi v&agrave;ng son của nhạc pop khiến nhiều người từng c&oacute; l&uacute;c đặt c&acirc;u hỏi về sức sống của nhạc c&aacute;ch mạng cũng như những giọng ca theo đuổi d&ograve;ng nhạc n&agrave;y. Nhưng từ khi thế hệ c&aacute;c ca sĩ Trọng Tấn, Đăng Dương, Việt Ho&agrave;n, Anh Thơ, Lan Anh ở ph&iacute;a Bắc cũng như Tạ Minh T&acirc;m, Thanh Th&uacute;y, Anh Bằng, Huỳnh Lợi ở ph&iacute;a Nam&hellip; xuất hiện th&igrave; c&acirc;u hỏi tr&ecirc;n đ&atilde; được trả lời: Sức sống của d&ograve;ng nhạc c&aacute;ch mạng, kh&ocirc;ng chỉ ở ph&iacute;a kh&aacute;n giả, m&agrave; c&oacute; cả một lớp nghệ sĩ trẻ cũng h&agrave;o hứng quay lại d&ograve;ng nhạc n&agrave;y. - See more at: http://www.sggp.org.vn/amnhac/2015/9/395240/#sthash.kx5XhiI2.dpuf</div>','Ca sĩ dòng nhạc cách mạng: Sống khỏe với nghề ','','',0,'http://tamdiem.com.vn/sources/images/Anh%20Tho.jpg','',0,0,0,0,484,0,1441687344,1442227505,1),(119,'\'Âm Mưu Tình Yêu\' đoạt giải Phim truyền hình hay nhất','Am-muu-tinh-yeu-doat-giai-phim-truyen-hinh-hay-nhat',16,'news_item','Âm Mưu Tình Yêu đoạt giải Phim truyền hình hay nhất','Vào tối ngày 6/9/2015 vừa qua, Lễ trao giải truyền hình Ấn Độ (gọi tắt là ITA Awards) đã diễn ra tại thành phố Mumbai.','Vào tối ngày 6/9/2015 vừa qua, Lễ trao giải truyền hình Ấn Độ (gọi tắt là ITA Awards) đã diễn ra tại thành phố Mumbai.','<p style=\"text-align: justify;\"><strong>V&agrave;o tối ng&agrave;y 6/9/2015 vừa qua, Lễ trao giải truyền h&igrave;nh Ấn Độ (gọi tắt l&agrave; ITA Awards) đ&atilde; diễn ra tại th&agrave;nh phố Mumbai. Đ&acirc;y l&agrave; sự kiện hằng năm nhằm t&ocirc;n vinh những nghệ sĩ ti&ecirc;u biểu v&agrave; những t&aacute;c phẩm truyền h&igrave;nh xuất sắc.</strong></p>\n<p style=\"text-align: justify;\">Trải qua 15 năm tổ chức, Lễ trao giải đ&atilde; thu h&uacute;t được sự quan t&acirc;m v&agrave; tham gia của đ&ocirc;ng đảo diễn vi&ecirc;n nổi tiếng Ấn Độ.</p>\n<p><img src=\"/tinymce/source//Giaa Maanek.jpg\" alt=\"\" width=\"500\" height=\"370\" /></p>\n<p>Giaa Manek-diễn vi&ecirc;n trong phim Saath Nibhana Saathiya (tạm dịch&ldquo;&Acirc;m Mưu T&igrave;nh Y&ecirc;u&rdquo;)&nbsp;</p>\n<p><img src=\"/tinymce/source//karanpatel.jpg\" alt=\"\" width=\"500\" height=\"700\" /></p>\n<p>Karan Patel-diễn vi&ecirc;n trong phim Yeh Hai Mohabbatein (tạm dịch &ldquo;Hạnh Ph&uacute;c Muộn M&agrave;ng&rdquo;)</p>\n<p><img src=\"/tinymce/source//Shakti.jpg\" alt=\"\" width=\"500\" height=\"350\" /></p>\n<p>Shakti Arora&ndash;diễn vi&ecirc;n trong phim Meri Aashiqui Tum Se c&ugrave;ng với vợ đến dự lễ trao giải.</p>\n<table style=\"height: 378px;\" width=\"558\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Hina Khan and Karan Mehra.jpg\" alt=\"\" width=\"550\" height=\"300\" /></p>\n<p style=\"text-align: justify;\">Với vai diễn Akshara &amp; Naitik &ndash;đ&ocirc;i vợ chồng trẻ ở một gia đ&igrave;nh truyền thống nhiều thế hệ trong phim Yeh Rishta Kya Kehlata Hai, Karan Mehra v&agrave; Hina Khan đ&atilde; vượt qua nhiều ứng cử vi&ecirc;n nặng k&yacute;, trở th&agrave;nh Nam v&agrave; Nữ diễn vi&ecirc;n được y&ecirc;u th&iacute;ch năm 2015.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table width=\"560\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Am muu tinh yeu.jpg\" alt=\"\" width=\"550\" height=\"300\" /></p>\n<p style=\"text-align: justify;\">Bộ phim &Acirc;m Mưu T&igrave;nh Y&ecirc;u đầy kịch t&iacute;nh đ&atilde; d&agrave;nh được giải thưởng Phim truyền h&igrave;nh hay nhất tại lễ trao giải.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table width=\"570\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//devoleena.jpg\" alt=\"\" width=\"275\" height=\"300\" /> <img src=\"/tinymce/source//Vishal.jpg\" alt=\"\" width=\"275\" height=\"300\" /></p>\n<p style=\"text-align: justify;\">Devoleena Bhattacharjee v&agrave; Vishal Singh &ndash; hai diễn vi&ecirc;n đ&oacute;ng vai Gopi Bahu v&agrave; Jigar trong &Acirc;m Mưu T&igrave;nh Y&ecirc;u đ&atilde; c&ugrave;ng chia sẻ niềm vui v&agrave; hy vọng rằng bộ phim sẽ đoạt được nhiều giải thưởng hơn trong những năm tới.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table width=\"408\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//anita 2.jpg\" alt=\"\" width=\"400\" height=\"400\" /></p>\n<p style=\"text-align: justify;\">Anita Hassanandani đ&atilde; bất ngờ mang về giải thưởng Nữ diễn vi&ecirc;n phản diện xuất sắc nhất khi h&oacute;a th&acirc;n th&agrave;nh Shagun Arora trong phim Yeh Hai Mohabbatein (Hạnh Ph&uacute;c Muộn M&agrave;ng)</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><strong><em>Một số giải thưởng kh&aacute;c</em></strong>:</p>\n<ul>\n<li>Nam diễn vi&ecirc;n phong c&aacute;ch nhất: Gautam Gulati - Bigg Boss 8</li>\n<li>Nam diễn vi&ecirc;n hấp dẫn nhất: Karan Patel - Yeh Hai Mohabbatein</li>\n<li>Gương mặt trẻ ti&ecirc;u biểu của năm: Shakti Arora</li>\n<li>Nữ diễn vi&ecirc;n mới triển vọng nhất: Radhika Madan - Meri Aashiqui Tumse Hi</li>\n<li>Chương tr&igrave;nh h&agrave;i hay nhất: Taarak Mehta Ka Ooltah Chashmah</li>\n<li>Người dẫn chương tr&igrave;nh xuất sắc nhất: Manish Paul.</li>\n</ul>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Kh&aacute;nh Linh tổng hợp<br /></em></p>','\'Âm Mưu Tình Yêu\' đoạt giải Phim truyền hình hay nhất','','',0,'http://tamdiem.com.vn/sources/images/ITA%20Awards.jpg','',0,0,0,0,194,0,1441771121,1442803737,1),(120,'Hồng Vân: \'Nếu không đủ tiền trang trải, tôi sẽ đóng cửa sân khấu\'','hong-van-neu-khong-du-tien-trang-trai-toi-se-dong-cua-san-khau',14,'news_item','Hồng Vân, sân khấu kịch Phú Nhuận','NSND cho biết sân khấu kịch Phú Nhuận của chị đang hoạt động khá khó khăn trong tình hình bế tắc chung của làng sân khấu TP HCM.','NSND cho biết sân khấu kịch Phú Nhuận của chị đang hoạt động khá khó khăn trong tình hình bế tắc chung của làng sân khấu TP HCM.','<p style=\"text-align: justify;\"><strong>NSND cho biết s&acirc;n khấu kịch Ph&uacute; Nhuận của chị đang hoạt động kh&aacute; kh&oacute; khăn trong t&igrave;nh h&igrave;nh bế tắc chung của l&agrave;ng s&acirc;n khấu TP HCM.</strong></p>\n<div class=\"fck_detail width_common\">\n<p class=\"Normal\" style=\"text-align: justify;\">- <em>Vừa qua kịch Ph&uacute; Nhuận kỷ niệm 15 năm th&agrave;nh lập, dấu mốc n&agrave;y mang đến cho chị cảm x&uacute;c g&igrave;?</em></p>\n<p class=\"Normal\" style=\"text-align: justify;\">- Trước khi tổ chức chương tr&igrave;nh kỷ niệm 15 năm, s&acirc;n khấu kịch Ph&uacute; Nhuận phải dừng diễn hai th&aacute;ng chờ sửa chữa. Ban đầu, t&ocirc;i định cho dấu mốc n&agrave;y tr&ocirc;i qua lu&ocirc;n. Nhưng nghĩ lại<em>,</em> nếu kh&ocirc;ng l&agrave;m kỷ niệm t&ocirc;i sẽ rất &aacute;y n&aacute;y. 15 năm gắn b&oacute; với một s&acirc;n khấu đ&acirc;u phải chuyện đơn giản. Điểm diễn n&agrave;y như ng&ocirc;i nh&agrave; chung của kh&ocirc;ng &iacute;t anh em nghệ sĩ, như \"đứa con\" t&ocirc;i dứt ruột đẻ ra. Bởi vậy, t&ocirc;i phải cố gắng tổ chức sự kiện cho chu đ&aacute;o. L&agrave;m chương tr&igrave;nh xong<em>,&nbsp;</em>t&ocirc;i thấy nhẹ l&ograve;ng v&igrave; ho&agrave;n th&agrave;nh t&acirc;m nguyện tưởng kh&oacute; thực hiện được.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Chỉ c&oacute; điều t&ocirc;i tiếc l&agrave; chương tr&igrave;nh kỷ niệm diễn ra trong thời điểm s&acirc;n khấu Ph&uacute; Nhuận n&oacute;i ri&ecirc;ng v&agrave; s&acirc;n khấu TP HCM n&oacute;i chung suy tho&aacute;i về nhiều mặt. N&oacute;i thật, t&ocirc;i vui v&igrave; kh&eacute;p lại chặng đường 15 năm m&igrave;nh đ&atilde; c&oacute; &iacute;t nhiều đ&oacute;ng g&oacute;p cho mặt bằng nghệ thuật - giải tr&iacute; của TP HCM. Nhưng t&ocirc;i chưa d&aacute;m n&oacute;i m&igrave;nh lạc quan về chặng đường tiếp theo<em>.</em></p>\n<table class=\"tplCaption\" style=\"width: 404px; height: 621px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://c1.f9.img.vnecdn.net/2015/09/08/DSC-0612-copy-1273-1441697570.jpg\" alt=\"Năm 2012, Hồng V&acirc;n được trao tặng danh hiệu Nghệ sĩ Nh&acirc;n d&acirc;n.\" data-pwidth=\"470.4\" data-width=\"400\" data-natural-width=\"400\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\" style=\"text-align: justify;\">Năm 2012, Hồng V&acirc;n được trao tặng danh hiệu Nghệ sĩ Nh&acirc;n d&acirc;n. Ảnh:<em> Minh Ho&agrave;ng</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\"><em>- V&igrave; sao chị kh&ocirc;ng tự tin khi nghĩ về chặng đường hoạt động sắp tới?</em></p>\n<p class=\"Normal\" style=\"text-align: justify;\">- S&acirc;n khấu của TP HCM đang trong giai đoạn rất kh&oacute; khăn. Kh&aacute;n giả ng&agrave;y nay &iacute;t đi xem kịch hơn, một phần cũng v&igrave; sức tấn c&ocirc;ng của c&aacute;c chương tr&igrave;nh truyền h&igrave;nh v&agrave; rất nhiều loại h&igrave;nh giải tr&iacute; kh&aacute;c. Tiền thu&ecirc; rạp tăng l&ecirc;n đỉnh điểm, s&acirc;n khấu thiếu hụt nghệ sĩ \"đinh\" v&igrave; nghệ sĩ giờ bận rất nhiều việc: đ&oacute;ng phim, diễn h&agrave;i, tham gia chương tr&igrave;nh truyền h&igrave;nh thực tế...</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Hiện t&ocirc;i quản l&yacute; hai điểm diễn: s&acirc;n khấu Ph&uacute; Nhuận v&agrave; kịch Superbowl, T&acirc;n B&igrave;nh. Cả hai đều chịu t&aacute;c động của t&igrave;nh h&igrave;nh chung. T&ocirc;i đang chuẩn bị thương thảo k&yacute; tiếp hợp đồng thu&ecirc; s&acirc;n khấu Ph&uacute; Nhuận với đối t&aacute;c. Nhưng nếu họ n&acirc;ng gi&aacute; thu&ecirc; địa điểm l&ecirc;n cao qu&aacute;, chắc t&ocirc;i kh&oacute; l&ograve;ng duy tr&igrave; nổi. T&ocirc;i vẫn đang xoay sở để hoạt động. Nhưng đến khi kh&ocirc;ng c&ograve;n kham nổi th&igrave; chắc phải đ&oacute;ng cửa th&ocirc;i chứ biết l&agrave;m sao? Điểm diễn s&acirc;n khấu Super Bowl ở T&acirc;n B&igrave;nh th&igrave; \"dễ thở\" hơn v&igrave; đối t&aacute;c giao cho t&ocirc;i to&agrave;n bộ s&acirc;n khấu để sử dụng.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Đ&ocirc;i khi người ta phải c&oacute; thần kinh th&eacute;p mới chịu được &aacute;p lực của việc l&agrave;m \"bầu\" s&acirc;n khấu. Nhiều l&uacute;c t&ocirc;i mệt mỏi, sức khỏe cũng kh&ocirc;ng c&ograve;n được như thời xưa năng động. Giờ t&ocirc;i duy tr&igrave; hoạt động được đến bao l&acirc;u th&igrave; sẽ cố gắng. Điều l&agrave;m t&ocirc;i vui l&agrave; vẫn c&oacute; nghệ sĩ trụ lại với nghề. Họ c&oacute; thể l&agrave; nghệ sĩ th&agrave;nh danh nhưng thủy chung với s&agrave;n diễn, hoặc đ&oacute; l&agrave; c&aacute;c học sinh ng&agrave;nh s&acirc;n khấu do t&ocirc;i v&agrave; đồng nghiệp m&igrave;nh đ&agrave;o tạo. Một lần trong tuần, họ tự nguyện giảm phần lương để chia sẻ với t&ocirc;i. Họ đều muốn cố gắng để l&agrave;m sao để s&acirc;n khấu của t&ocirc;i s&aacute;ng đ&egrave;n, v&igrave; nếu t&ocirc;i cứ phải b&ugrave; lỗ ho&agrave;i t&ocirc;i sẽ \"chết\".</p>\n<p class=\"Normal\" style=\"text-align: justify;\">- <em>L&yacute; do g&igrave; chị vẫn chưa bu&ocirc;ng tay với s&acirc;n khấu?</em></p>\n<p style=\"text-align: justify;\">-T&ocirc;i kh&ocirc;ng thể n&agrave;o bu&ocirc;ng một nơi m&igrave;nh xem như \"n&uacute;m ruột\". Ngo&agrave;i ra, c&ograve;n rất nhiều người gắn b&oacute; với nơi n&agrave;y, từ c&aacute;c diễn vi&ecirc;n, nh&acirc;n c&ocirc;ng, hậu đ&agrave;i... Nếu s&acirc;n khấu đ&oacute;ng cửa, họ sẽ mất c&ocirc;ng việc gắn b&oacute; bao l&acirc;u nay. \"Đất\" cho diễn vi&ecirc;n trẻ l&agrave;m nghề cũng bị thu hẹp. Nếu chỉ lo cho m&igrave;nh, t&ocirc;i chỉ cần tập trung đi diễn, chạy show kiếm tiền để đầu &oacute;c nhẹ nh&agrave;ng hơn v&igrave; ri&ecirc;ng hoạt động của một nghệ sĩ, t&ocirc;i đ&atilde; qu&aacute; bận rộn.</p>\n<p style=\"text-align: justify;\">T&ocirc;i cũng từng nghĩ đến chuyện t&igrave;m người kế thừa, thay m&igrave;nh quản l&yacute; s&acirc;n khấu. Nhưng chưa t&igrave;m được. T&igrave;m đ&acirc;u ra những <a href=\"http://giaitri.vnexpress.net/tin-tuc/san-khau-my-thuat/san-khau-hoang-thai-thanh-dung-truoc-nguy-co-dong-cua-2973020.html\" target=\"_blank\">&Aacute;i Như - Th&agrave;nh Hội</a>, những <a href=\"http://giaitri.vnexpress.net/tin-tuc/san-khau-my-thuat/san-khau/thanh-loc-dong-bon-vai-trong-kich-nang-cong-chua-di-lac-3212257.html\" target=\"_blank\">Th&agrave;nh Lộc</a>, Huỳnh Anh Tuấn mới? Những người thuộc thế hệ đi trước đ&atilde; v&agrave; đang cố hết sức giữ cho s&acirc;n khấu s&aacute;ng đ&egrave;n với tất cả tấm l&ograve;ng v&agrave; t&acirc;m huyết của họ rồi.</p>\n<table class=\"tplCaption\" style=\"width: 405px; height: 621px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://c1.f9.img.vnecdn.net/2015/09/08/hong-van-1-9238-1441697570.jpg\" alt=\"Hồng V&acirc;n được xem l&agrave; b&agrave; bầu\" data-pwidth=\"470.4\" data-width=\"400\" data-natural-width=\"400\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\" style=\"text-align: justify;\">Hồng V&acirc;n được xem l&agrave; b&agrave; bầu \"m&aacute;t tay\" v&agrave; th&agrave;nh c&ocirc;ng trong l&agrave;ng kịch S&agrave;i G&ograve;n 15 năm qua. Chị đang l&agrave; Đại biểu Hội đồng nh&acirc;n d&acirc;n TP HCM (nhiệm kỳ 2012-2016). Ảnh: <em>Minh Ho&agrave;ng</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">- <em>Theo chị, để vực dậy hoạt động của s&acirc;n khấu, giải ph&aacute;p nằm ở đ&acirc;u?</em></p>\n<p class=\"Normal\" style=\"text-align: justify;\">- Nếu xem thị phần nhu cầu giải tr&iacute; của người d&acirc;n TP HCM l&agrave; một \"miếng b&aacute;nh\" th&igrave; \"miếng b&aacute;nh\" đ&oacute; đang ng&agrave;y c&agrave;ng nhỏ đi so với sự ph&aacute;t triển của qu&aacute; nhiều loại h&igrave;nh. B&acirc;y giờ người người l&agrave;m phim, l&agrave;m s&acirc;n khấu, băng đĩa, giải tr&iacute; tr&ecirc;n mạng Internet \"b&ugrave;ng nổ\", chưa kể rất nhiều loại h&igrave;nh giải tr&iacute; tự ph&aacute;t khiến gi&aacute; trị thực ảo lẫn lộn... S&acirc;n khấu phải gồng m&igrave;nh trước rất nhiều cạnh tranh.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Ngo&agrave;i sự nỗ lực, tự th&acirc;n vận động vươn l&ecirc;n của mỗi đơn vị, để giải quyết t&igrave;nh trạng kh&oacute; khăn hiện nay, loại h&igrave;nh n&agrave;y rất cần một sự hỗ trợ mang t&iacute;nh thiết thực, tổng thể v&agrave; c&oacute; trọng điểm hơn từ cơ quan chức năng ng&agrave;nh văn h&oacute;a. L&agrave; Đại biểu Hội đồng Nh&acirc;n d&acirc;n TP HCM, t&ocirc;i hiểu kinh ph&iacute; d&agrave;nh cho ph&aacute;t triển văn h&oacute;a - văn nghệ của th&agrave;nh phố đang eo hẹp. Nhưng nếu để mười năm sau nữa mới nghĩ đến chuyện vực dậy văn h&oacute;a th&igrave; e sẽ trễ. Hiện nay, để một s&acirc;n khấu tạo ra sản phẩm chất lượng, n&acirc;ng cao thị hiếu thẩm mỹ của kh&aacute;n giả th&igrave; nơi đ&oacute; rất cần một điểm tựa, cần được \"nu&ocirc;i\" một thời gian để họ y&ecirc;n t&acirc;m l&agrave;m nghề.</p>\n<p style=\"text-align: justify;\">- <em>Ở tuổi 50, dấu ấn tuổi t&aacute;c t&aacute;c động đến đam m&ecirc; nghề nghiệp của chị ra sao?</em></p>\n<p class=\"Normal\" style=\"text-align: justify;\">- T&ocirc;i thấy m&igrave;nh phải chăm lo sức khỏe bản th&acirc;n nhiều hơn, điều tiết giữa l&agrave;m việc v&agrave; nghỉ ngơi, l&agrave;m g&igrave; cũng cần ph&agrave;i lượng sức chứ kh&ocirc;ng lao v&agrave;o như \"thi&ecirc;u th&acirc;n\". C&ograve;n trăn trở v&agrave; ấp ủ với nghề của t&ocirc;i vẫn đầy ắp. T&ocirc;i thường n&oacute;i với c&aacute;c bạn ở s&acirc;n khấu của m&igrave;nh nếu t&ocirc;i l&agrave; đại gia, lu&ocirc;n c&oacute; tiền tỷ trong người th&igrave; t&ocirc;i sẽ d&agrave;n dựng, biểu diễn thường xuy&ecirc;n c&aacute;c t&aacute;c phẩm s&acirc;n khấu như: <a href=\"http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/hong-van-dem-quan-chinh-phuc-san-khau-bac-1912613.html\" target=\"_blank\"><em>Nỏ thần</em></a>, <a href=\"http://giaitri.vnexpress.net/tin-tuc/san-khau-my-thuat/san-khau/dien-vien-nguoi-dan-ba-uong-ruou-ngat-xiu-tren-san-khau-3250697.html\" target=\"_blank\"><em>Người đ&agrave;n b&agrave; uống rượu</em></a> hoặc những vở diễn chuyển thể từ c&aacute;c t&aacute;c phẩm văn học nổi tiếng... T&ocirc;i sẽ mời kh&aacute;n giả đến s&acirc;n khấu xem miễn ph&iacute; định kỳ h&agrave;ng tuần hoặc h&agrave;ng th&aacute;ng, nhất l&agrave; đối tượng học sinh. C&oacute; thể ban đầu họ chưa th&iacute;ch xem kịch nhưng với c&aacute;c vở chất lượng, dần dần họ sẽ th&iacute;ch v&agrave; c&oacute; th&oacute;i quen đến với s&acirc;n khấu.</p>\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"1\">\n<tbody>\n<tr>\n<td>\n<p class=\"Normal\" style=\"text-align: justify;\">S&acirc;n khấu kịch Ph&uacute; Nhuận của nghệ sĩ Hồng V&acirc;n th&agrave;nh lập khoảng cuối năm 2000. 15 năm qua, đơn vị s&acirc;n khấu x&atilde; hội h&oacute;a n&agrave;y được nh&igrave;n nhận l&agrave; một trong số đơn vị ph&aacute;t triển mạnh của TP HCM. Kịch Ph&uacute; Nhuận d&agrave;n dựng hơn 75 t&aacute;c phẩm, trong đ&oacute; c&oacute; nhiều vở diễn giải tr&iacute; đạt doanh thu tốt, song song với c&aacute;c vở đạt chất lượng nghệ thuật được giới chuy&ecirc;n m&ocirc;n đ&aacute;nh gi&aacute; cao, như: <em>Nỏ thần, Số đỏ, Ch&iacute; Ph&egrave;o, Kỹ nghệ lấy T&acirc;y&hellip; </em></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Kịch Ph&uacute; Nhuận c&ograve;n l&agrave; nơi g&oacute;p phần đ&agrave;o tạo đội ngũ diễn vi&ecirc;n, đạo diễn giỏi trong nước như: Đức Thịnh, Th&aacute;i H&ograve;a, C&aacute;t Phượng, Thanh Th&uacute;y, Xu&acirc;n Trang, Diệp Ti&ecirc;n, Mạnh Ph&uacute;c, Quốc Nam, Kim Huyền, H&ograve;a Hiệp...</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: right;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: right;\"><em>Theo&nbsp;vnexpress.net</em></p>\n</div>','Hồng Vân: \'Nếu không đủ tiền trang trải, tôi sẽ đóng cửa sân khấu\'','','',0,'http://tamdiem.com.vn/sources/images/hongvan.jpg','',0,0,0,0,483,0,1441860871,1442375128,1),(122,'Khánh Ly mừng nghệ sĩ Thanh Thủy ra mắt phim đầu tay','khanh-ly-mung-nghe-si-thanh-thuy-ra-mat-phim-dau-tay',15,'news_item','Khánh Ly, nghệ sĩ Thanh Thủy, Ôsin nổi loạn','Nữ danh ca dành thời gian dự buổi họp mặt đoàn phim \"Ôsin nổi loạn\" và chia sẻ cảm nhận của bà về kịch bản phim về người lao động bình dân.','Nữ danh ca dành thời gian dự buổi họp mặt đoàn phim \"Ôsin nổi loạn\" và chia sẻ cảm nhận của bà về kịch bản phim về người lao động bình dân.','<p class=\"Normal\" style=\"text-align: justify;\"><strong>Tối 13/9, diễn vi&ecirc;n Thanh Thủy c&oacute; buổi họp mặt đo&agrave;n phim truyền h&igrave;nh <a href=\"http://giaitri.vnexpress.net/tin-tuc/phim/sau-man-anh/ngoc-giau-dien-bi-hai-trong-phim-oshin-noi-loan-3161231.html\" target=\"_blank\"><em>&Ocirc;sin nổi loạn</em></a>. Đ&acirc;y l&agrave; phim đầu tay do Thanh Thủy giữ vai tr&ograve; sản xuất. Chị cũng đ&oacute;ng một vai trong phim.</strong></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Đ&acirc;y cũng l&agrave; dự &aacute;n phim ảnh đầu ti&ecirc;n ca sĩ hải ngoại Quang Th&agrave;nh. Anh từ l&acirc;u l&agrave; cậu em th&acirc;n thiết, gắn b&oacute; với Kh&aacute;nh Ly trong nhiều hoạt động. V&igrave; thế, b&agrave; d&agrave;nh thời gian đến buổi họp mặt đo&agrave;n phim chia sẻ niềm vui ng&agrave;y phim đ&oacute;ng m&aacute;y. Đồng h&agrave;nh với b&agrave; c&ograve;n c&oacute; ca sĩ L&ecirc; Uy&ecirc;n.</p>\n<table class=\"tplCaption\" style=\"width: 429px; height: 415px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 491px;\" src=\"http://c1.f9.img.vnecdn.net/2015/09/14/quang-thanh-1-1465-1442204997.jpg\" alt=\"Từ tr&aacute;i qua: ca sĩ L&ecirc; Uy&ecirc;n Phương, diễn vi&ecirc;n Thanh Thủy, ca sĩ Kh&aacute;nh Ly\" height=\"328\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Từ tr&aacute;i qua: ca sĩ L&ecirc; Uy&ecirc;n, diễn vi&ecirc;n Thanh Thủy, ca sĩ Kh&aacute;nh Ly, Nghệ sĩ Nh&acirc;n d&acirc;n Ngọc Gi&agrave;u v&agrave; Quang Th&agrave;nh.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">Kh&aacute;nh Ly t&acirc;m sự ngo&agrave;i c&ocirc;ng việc ca h&aacute;t, b&agrave; lu&ocirc;n d&agrave;nh thời gian quan t&acirc;m t&igrave;m hiểu hoạt động văn nghệ, giải tr&iacute; trong v&agrave; ngo&agrave;i nước. Phim ảnh cũng l&agrave; lĩnh vực b&agrave; quan t&acirc;m. \"T&ocirc;i rất th&iacute;ch th&uacute; với bộ phim c&oacute; nội dung về những người lao động b&igrave;nh d&acirc;n. Đạo diễn L&ecirc; Ngọc Linh từng chia sẻ qua phim n&agrave;y anh gửi lời tri &acirc;n về những người phụ nữ Việt Nam vốn sinh ra đời đ&atilde; c&oacute; m&aacute;u \'&ocirc; sin\' trong người. Họ d&agrave;nh cả đời để chăm s&oacute;c chồng con, chu to&agrave;n gia đ&igrave;nh. Bản th&acirc;n t&ocirc;i cũng l&agrave; một \'&ocirc; sin\' như thế v&agrave; rất hạnh ph&uacute;c khi m&igrave;nh được l&agrave;m \'nghề\' n&agrave;y\".</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Phim truyền h&igrave;nh <em>&Ocirc;sin nổi loạn</em> d&agrave;i 38 tập do t&aacute;c giả Nguyễn Vũ bi&ecirc;n kịch, quy tụ gần 40 diễn vi&ecirc;n như NSND Ngọc Gi&agrave;u, NSƯT L&ecirc; Thiện, NSƯT Diệu Đức, Phi Phụng, ca sĩ Phương Thanh, Tường Vi, Ho&agrave;ng Anh, Minh Dũng, ca sĩ Long Nhật, Lương Duy&ecirc;n, nh&agrave; thiết kế Sĩ Ho&agrave;ng... Trong đ&oacute;, vai Tom Mũ Ni của ca sĩ Quang Th&agrave;nh được đạo diễn \"đo ni đ&oacute;ng gi&agrave;y\" cho anh, khắc họa một ch&agrave;ng trai c&oacute; t&iacute;nh c&aacute;ch n&aacute;o nhiệt, kh&ocirc;i h&agrave;i v&agrave; đam m&ecirc; ca h&aacute;t.</p>\n<table class=\"tplCaption\" style=\"width: 493px; height: 386px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 484px;\" src=\"http://c1.f9.img.vnecdn.net/2015/09/14/ngoc-giau-6-8747-1427084057-2243-1442204997.jpg\" alt=\"Trong phim, Ngọc Gi&agrave;u c&ograve;n ph&aacute;t huy thế mạnh ca vọng cổ, cải lương.\" height=\"324\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Trong phim, Nghệ sĩ Nh&acirc;n d&acirc;n Ngọc Gi&agrave;u (giữa) c&ograve;n ph&aacute;t huy thế mạnh ca vọng cổ, cải lương.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">Tại buổi họp mặt, nghệ sĩ Thanh Thủy kh&ocirc;ng giấu được sự x&uacute;c động khi lần đầu giữ vai tr&ograve; nh&agrave; sản xuất phim. Hơn nửa năm theo đuổi dự &aacute;n n&agrave;y tr&ecirc;n phim trường trong m&ocirc;i trường l&agrave;m việc tập thể lớn, Thanh Thủy c&agrave;ng thấm th&iacute;a vất vả, cực nhọc của nghề l&agrave;m phim. C&oacute; những ng&agrave;y trăn trở một chi tiết trong kịch bản hoặc th&ecirc;m thắt m&agrave;u sắc cho vai diễn n&agrave;o đ&oacute;, chị kh&ocirc;ng ngủ được, gọi điện thoại ngay trong đ&ecirc;m với anh em đo&agrave;n phim, diễn vi&ecirc;n để b&agrave;n bạc.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">\"Phim c&oacute; rất nhiều diễn vi&ecirc;n nhưng mỗi người đều c&oacute; đất diễn ri&ecirc;ng. T&ocirc;i v&agrave;o vai &Yacute; Lạ, nằm trong tuyến nh&acirc;n vật g&acirc;y cười nhưng cũng để lại nhiều suy nghĩ về c&aacute;ch nh&igrave;n nhận của mọi người d&agrave;nh cho người chuyển giới. Họ cũng l&agrave; những người b&igrave;nh thường v&agrave; ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m những c&ocirc;ng việc b&igrave;nh thường để kiếm sống\", ca sĩ Long Nhật chia sẻ.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Chất h&agrave;i v&agrave; bi l&agrave; hai yếu tố đạo diễn Nguyễn Vũ gửi gắm trong nội dung bộ phim n&oacute;i về c&aacute;c c&acirc;u chuyện xoay quanh đời sống gia đ&igrave;nh, giữ g&igrave;n nếp nh&agrave; n&agrave;y.&nbsp;Phim ph&aacute;t s&oacute;ng tr&ecirc;n truyền h&igrave;nh v&agrave;o giữa th&aacute;ng 9.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><em>Theo vnexpress.net</em></p>','Khánh Ly mừng nghệ sĩ Thanh Thủy ra mắt phim đầu tay','','',0,'http://tamdiem.com.vn/sources/images/hop%20bao.jpg','',0,0,0,0,199,0,1442228037,1444016065,1),(123,'Long Nhật gây tò mò khi diện váy cưới trắng tinh','long-nhat-gay-to-mo-khi-dien-vay-cuoi-trang-tinh',15,'news_item','Long Nhật, Ô Sin nổi loạn, NSND Ngọc Giàu, NSƯT Thanh Thủy','Nam ca sỹ Long Nhật hóa trang thành cô dâu mặc váy trắng, trang điểm đậm trong buổi họp báo ra mắt phim truyền hình Ô Sin nổi loạn vào chiều ngày 13.9 tại một phòng trà ở TP.HCM. Đây là một bộ phim tâm lý hài hước, kể về cuộc đời của những người giúp việc biết vượt lên số phận và sống vui nhộn.','Nam ca sỹ Long Nhật hóa trang thành cô dâu mặc váy trắng, trang điểm đậm trong buổi họp báo ra mắt phim truyền hình Ô Sin nổi loạn vào chiều ngày 13.9 tại một phòng trà ở TP.HCM. Đây là một bộ phim tâm lý hài hước, kể về cuộc đời của những người giúp việc biết vượt lên số phận và sống vui nhộn.','<p><strong>Tạo h&igrave;nh ấn tượng của Long Nhật khi ra mắt vai diễn &ocirc; sin chuyển giới.</strong></p>\n<div class=\"contentbaiviet\">\n<p style=\"text-align: justify;\">Nam ca sỹ Long Nhật h&oacute;a trang th&agrave;nh c&ocirc; d&acirc;u mặc v&aacute;y trắng, trang điểm đậm trong buổi họp b&aacute;o ra mắt phim truyền h&igrave;nh <em>&Ocirc; Sin nổi loạn v</em>&agrave;o chiều ng&agrave;y 13.9 tại một ph&ograve;ng tr&agrave; ở TP.HCM. Đ&acirc;y l&agrave; một bộ phim t&acirc;m l&yacute; h&agrave;i hước, kể về cuộc đời của những người gi&uacute;p việc biết vượt l&ecirc;n số phận v&agrave; sống vui nhộn.</p>\n<p align=\"center\"><img id=\"news-image-id-0\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 1\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--9-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 1\" width=\"411\" height=\"654\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Long Nhật h&oacute;a trang th&agrave;nh c&ocirc; d&acirc;u trong buổi ra mắt phim &Ocirc; Sin nổi loạn</p>\n<p style=\"text-align: justify;\">Trong phim, Long Nhật v&agrave;o vai c&oacute; c&aacute;i t&ecirc;n đặc biệt &Yacute; Lạ. Đ&acirc;y l&agrave; nh&acirc;n vật một ca sỹ kh&ocirc;ng gặp thời, mang t&ecirc;n thật l&agrave; Hinh, h&aacute;t hay, y&ecirc;u nghề nhưng kh&ocirc;ng được kh&aacute;n giả quan t&acirc;m. Từ sau khi Hinh chuyển giới v&agrave; lấy t&ecirc;n &Yacute; Lạ, anh bỗng nổi như cồn với những&nbsp; chi&ecirc;u tr&ograve; lạ đời.</p>\n<p style=\"text-align: justify;\">&Yacute; Lạ c&ugrave;ng với bạn g&aacute;i Thi&ecirc;n Tơ (Tường Vy thủ vai) v&agrave; đại gia Biếu (Vương Bảo Tuấn) th&agrave;nh lập c&ocirc;ng ty đ&agrave;o tạo v&agrave; cho thu&ecirc; &ocirc; sin &ldquo;xuy&ecirc;n quốc gia&rdquo;. Nhiều t&igrave;nh huống hỷ nộ &aacute;i ố diễn ra từ khi c&ocirc;ng ty &ocirc; sin ra đời. Trong phim, nh&acirc;n vật chuyển giới của Long Nhật ngo&agrave;i việc c&oacute; tạo h&igrave;nh xinh đẹp c&ograve;n mang trong l&ograve;ng mối t&igrave;nh duy nhất từ thời thơ ấu đến khi trưởng th&agrave;nh.</p>\n<p style=\"text-align: justify;\">B&ecirc;n cạnh Long Nhật, bộ phim c&ograve;n c&oacute; sự g&oacute;p mặt của d&agrave;n sao t&ecirc;n tuổi như NSND Ngọc Gi&agrave;u,&nbsp;NSƯT L&ecirc; Thiện, NSƯT Thanh Thủy, NSƯT Diệu Đức, diễn vi&ecirc;n điện ảnh&nbsp;Ho&agrave;ng Anh, ca sĩ Phương Thanh, Phi Phụng, Xu&acirc;n Hiệp , Minh Dũng, L&ecirc; Như, ca sỹ&nbsp;hải ngoại Quang Th&agrave;nh...</p>\n<p style=\"text-align: justify;\">Trong buổi họp b&aacute;o, Long Nhật h&eacute; lộ một phần tạo h&igrave;nh nh&acirc;n vật chuyển giới khi h&oacute;a trang th&agrave;nh c&ocirc; d&acirc;u l&ecirc;n h&aacute;t song ca với ca sỹ Vương Bảo Tuấn &ndash; người đ&oacute;ng vai đại gia Biếu.</p>\n<p style=\"text-align: justify;\">Phim sẽ được ph&aacute;t s&oacute;ng rộng r&atilde;i tr&ecirc;n đ&agrave;i truyền h&igrave;nh cả nước v&agrave;o cuối th&aacute;ng 9.2015.</p>\n<p style=\"text-align: justify;\">Một số h&igrave;nh ảnh Long Nhật v&agrave; d&agrave;n diễn vi&ecirc;n phim<em> &Ocirc; Sin nổi loạn:</em></p>\n<p align=\"center\"><img id=\"news-image-id-1\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 2\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--1-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 2\" width=\"508\" height=\"348\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Long Nhật c&ugrave;ng d&agrave;n diễn vi&ecirc;n ra mắt phim</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-2\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 3\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--11-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 3\" width=\"513\" height=\"342\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Tr&ecirc;n s&acirc;n khấu ph&ograve;ng tr&agrave; l&agrave; nơi tổ chức họp b&aacute;o phim, Long Nhật thể hiện&nbsp;diễn</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">t&agrave;i năng ca h&aacute;t với ca kh&uacute;c \"T&igrave;nh ngh&egrave;o c&oacute; nhau\"</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-3\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 4\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--12-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 4\" width=\"514\" height=\"329\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">M&agrave;n kết hợp kiểu \"t&igrave;nh tay ba\" giữa Long Nhật v&agrave; hai người bạn: Việt kiều Minh Ly</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">v&agrave; nam ca sỹ Vương Bảo Tuấn.</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-4\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 5\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--8-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 5\" width=\"516\" height=\"344\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Nam ca sỹ độn ngực, h&oacute;a trang đậm v&agrave; mặc v&aacute;y c&ocirc; d&acirc;u.</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\"><img id=\"news-image-id-5\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 6\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--11-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 6\" width=\"512\" height=\"341\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Long Nhật c&ugrave;ng \"người t&igrave;nh tin đồn\" biểu diễn t&igrave;nh cảm.</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-6\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 7\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--9-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 7\" width=\"510\" height=\"810\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><span style=\"text-align: justify;\">​\"C&ocirc; d&acirc;u\" Long Nhật hứa hẹn mang đến một vai diễn h&agrave;i hước độc đ&aacute;o.</span></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-7\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 8\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--13-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 8\" width=\"508\" height=\"525\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Chụp ảnh t&igrave;nh cảm với \"bạn g&aacute;i Thi&ecirc;n Tơ\" do Tường Vy thủ vai</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-8\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 9\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--2-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 9\" width=\"509\" height=\"438\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Một trang phục giả g&aacute;i kh&aacute;c của nam ca sỹ trong buổi họp b&aacute;o</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-9\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 10\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--7-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 10\" width=\"513\" height=\"356\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Ngồi trong ph&ograve;ng tr&agrave;, Long Nhật tự tin với bộ t&oacute;c giả v&agrave; phục trang nữ giới.</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-10\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 11\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--6-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 11\" width=\"504\" height=\"616\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Giọng ca \"T&igrave;nh ngh&egrave;o c&oacute; nhau\" c&ugrave;ng&nbsp;NSND Ngọc Gi&agrave;u</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-11\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 12\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--5-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 12\" width=\"503\" height=\"492\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\">Diện &aacute;o d&agrave;i c&aacute;ch điệu, Long Nhật ng&agrave;y c&agrave;ng chứng tỏ sở th&iacute;ch đ&oacute;ng&nbsp;giả g&aacute;i</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-12\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 13\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--4-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 13\" width=\"508\" height=\"709\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\">Pha chụp h&igrave;nh t&igrave;nh cảm c&ugrave;ng&nbsp;ca sỹ Vương Bảo Tuấn &ndash; người đ&oacute;ng vai đại gia Biếu trong phim.</p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><img id=\"news-image-id-13\" class=\"news-image\" title=\"Long Nhật g&acirc;y t&ograve; m&ograve; khi diện v&aacute;y cưới trắng tinh - 14\" src=\"http://streaming1.danviet.vn/upload/3-2015/images/2015-09-14/1442215256-long-nhat--3-.jpg\" alt=\"long nhat gay to mo khi dien vay cuoi trang tinh - 14\" width=\"502\" height=\"693\" data-pagespeed-loaded=\"1\" data-pagespeed-onload=\"pagespeed.CriticalImages.checkImageForCriticality(this);\" /></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: center;\" align=\"center\"><span style=\"text-align: justify;\">​Hai nam ca sỹ &ocirc;m eo t&igrave;nh tứ trước b&aacute;o giới.</span></p>\n<p style=\"color: #0000ff; font-style: italic; text-align: right;\" align=\"center\"><span style=\"text-align: justify; color: #000000;\">Theo D&acirc;n Việt</span></p>\n</div>','Long Nhật gây tò mò khi diện váy cưới trắng tinh','','',0,'http://tamdiem.com.vn/sources/images/long-nhat.jpg','',0,0,0,0,198,0,1442287656,1449288814,1),(124,' Dân truyền hình lấn sân phim chiếu rạp ','-dan-truyen-hinh-lan-san-phim-chieu-rap-',14,'news_item',' Dân truyền hình lấn sân phim chiếu rạp ','Những con số doanh thu từ mấy chục tỉ đến cả trăm tỉ đồng của phim chiếu rạp đã và đang là một động lực lớn để các nhà làm phim truyền hình nhảy qua phim chiếu rạp.','Những con số doanh thu từ mấy chục tỉ đến cả trăm tỉ đồng của phim chiếu rạp đã và đang là một động lực lớn để các nhà làm phim truyền hình nhảy qua phim chiếu rạp.','<div class=\"detail-content\">\n<div class=\"left-side\">\n<div class=\"block-feature block-feature-1\">\n<p class=\"txt-head\"><strong>Những con số doanh thu từ mấy chục tỉ đến cả trăm tỉ đồng của phim chiếu rạp đ&atilde; v&agrave; đang l&agrave; một động lực lớn để c&aacute;c nh&agrave; l&agrave;m phim truyền h&igrave;nh nhảy qua phim chiếu rạp.</strong></p>\n</div>\n<div class=\"fck\">\n<table class=\"desc_image slide_content\" style=\"height: 367px; background-color: #ece9e9;\" title=\"D&acirc;n truyền h&igrave;nh lấn s&acirc;n phim chiếu rạp \" border=\"0\" width=\"492\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"Nh&agrave; c&oacute; năm n&agrave;ng ti&ecirc;n l&agrave; những phim điện ảnh m&agrave; người l&agrave;m phim đa số l&agrave; &ldquo;d&acirc;n truyền h&igrave;nh&rdquo; - Ảnh: ĐPCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 516px;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/09/16/bf7b4310.jpg\" alt=\"Nh&agrave; c&oacute; năm n&agrave;ng ti&ecirc;n l&agrave; những phim điện ảnh m&agrave; người l&agrave;m phim đa số l&agrave; &ldquo;d&acirc;n truyền h&igrave;nh&rdquo; - Ảnh: ĐPCC\" height=\"334\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\" style=\"text-align: justify;\">\n<p><span style=\"color: #808080;\">Nh&agrave; c&oacute; năm n&agrave;ng ti&ecirc;n l&agrave; những phim điện ảnh m&agrave; người l&agrave;m phim đa số l&agrave; &ldquo;d&acirc;n truyền h&igrave;nh&rdquo; - Ảnh: ĐPCC</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">C&oacute; thể n&oacute;i việc thay đổi chất liệu ghi h&igrave;nh (từ phim nhựa sang kỹ thuật số) cộng với sự ph&aacute;t triển qu&aacute; mạnh của truyền h&igrave;nh Việt khoảng 10 năm qua l&agrave; những yếu tố ch&iacute;nh khiến khoảng c&aacute;ch giữa hai lĩnh vực n&agrave;y &iacute;t nhiều bị x&oacute;a mờ.</p>\n<p style=\"text-align: justify;\">G&oacute;p sức cho sự ph&aacute;t triển số lượng phim chiếu rạp thời gian vừa qua c&oacute; sự g&oacute;p mặt của c&aacute;c c&ocirc;ng ty chuy&ecirc;n sản xuất phim truyền h&igrave;nh.&nbsp;&nbsp;</p>\n<p class=\"ck_inner_title\" style=\"text-align: justify;\"><strong>Ng&aacute;n ngẩm giờ v&agrave;ng th&agrave;nh giờ... đất s&eacute;t</strong></p>\n<p style=\"text-align: justify;\">Năm 2005, việc HTV7 mở giờ v&agrave;ng phim Việt đầu ti&ecirc;n được xem l&agrave; bước ngoặt lớn của truyền h&igrave;nh Việt. Một loạt phim ph&aacute;t s&oacute;ng&nbsp;thắng lớn cả về phương diện người xem lẫn quảng c&aacute;o.</p>\n<p style=\"text-align: justify;\">Thừa thắng x&ocirc;ng l&ecirc;n, rất nhiều k&ecirc;nh tiếp tục mở giờ v&agrave;ng phim Việt. Thế nhưng sau 10 năm, t&igrave;nh h&igrave;nh sản xuất phim truyền h&igrave;nh hiện đang trong giai đoạn mu&ocirc;n v&agrave;n kh&oacute; khăn. &Iacute;t kh&aacute;n giả, quảng c&aacute;o l&uacute;c trồi l&uacute;c sụt, d&acirc;n trong nghề đ&ugrave;a vui rằng giờ v&agrave;ng phim Việt hiện nay đang trở th&agrave;nh giờ... đất s&eacute;t.</p>\n<p style=\"text-align: justify;\">Thậm ch&iacute; giờ phim Việt trước đ&acirc;y to&agrave;n ph&aacute;t phim mới th&igrave; nay ph&aacute;t s&oacute;ng lại phim cũ. Cụ thể như l&uacute;c 17g30 tr&ecirc;n HTV9 hiện ph&aacute;t s&oacute;ng hai phim cũ l&agrave; <em>Trường nội tr&uacute; </em>v&agrave; <em>Những mảnh đời gi&ocirc;ng b&atilde;o</em>. Tr&ecirc;n truyền h&igrave;nh Vĩnh Long 1 l&uacute;c 20g cũng vừa ph&aacute;t s&oacute;ng xong<em> Ra gi&ecirc;ng anh cưới em </em>- phim từng ph&aacute;t s&oacute;ng dịp đầu năm mới.</p>\n<p style=\"text-align: justify;\">V&agrave; cả ngay những phim c&oacute; chất lượng tốt, lượng người xem cao cũng kh&oacute; thu h&uacute;t quảng c&aacute;o. Đến như phim <em>Cha rơi</em> (huy chương v&agrave;ng tại Li&ecirc;n hoan truyền h&igrave;nh to&agrave;n quốc v&agrave; giải C&aacute;nh diều v&agrave;ng năm 2014) với c&acirc;u chuyện hấp dẫn, đậm t&iacute;nh nh&acirc;n văn v&agrave; quy tụ lực lượng diễn vi&ecirc;n kh&aacute; h&ugrave;ng hậu nhưng khi ph&aacute;t s&oacute;ng tr&ecirc;n VTV9&nbsp;kh&ocirc;ng thu đủ quảng c&aacute;o như trong hợp đồng với nh&agrave; đ&agrave;i.</p>\n<p style=\"text-align: justify;\">B&agrave; B&iacute;ch Li&ecirc;n - gi&aacute;m đốc H&atilde;ng phim S&oacute;ng V&agrave;ng (nh&agrave; sản xuất<em> Cha rơi</em>) - chua ch&aacute;t: &ldquo;Thời kinh tế kh&oacute; khăn m&agrave;, kiếm quảng c&aacute;o đ&acirc;u dễ&rdquo;.</p>\n<p style=\"text-align: justify;\">B&agrave; B&iacute;ch Thủy - gi&aacute;m đốc H&atilde;ng phim Sena - cũng nhận thấy như vậy: &ldquo;Thời nay kh&aacute;n giả qu&aacute; no n&ecirc;, thậm ch&iacute; ch&aacute;n ng&aacute;n với phim truyền h&igrave;nh. Trong khi đ&oacute; game show được xem nhiều bởi đầu tư ho&agrave;nh tr&aacute;ng, h&igrave;nh ảnh đẹp, vui vẻ.</p>\n<p style=\"text-align: justify;\">Kinh tế hiện đang kh&oacute; khăn n&ecirc;n c&aacute;c c&ocirc;ng ty quảng c&aacute;o tiết kiệm ng&acirc;n s&aacute;ch, chỉ đầu tư v&agrave;o những chương tr&igrave;nh đ&ocirc;ng người xem&rdquo;.</p>\n<table class=\"desc_image slide_content\" style=\"background-color: #ece9e9; height: 383px;\" title=\"D&acirc;n truyền h&igrave;nh lấn s&acirc;n phim chiếu rạp \" border=\"0\" width=\"492\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"Bộ ba rắc rối &nbsp;- Ảnh: ĐPCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 531px;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/09/16/c171824a.jpg\" alt=\"Bộ ba rắc rối &nbsp;- Ảnh: ĐPCC\" height=\"355\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\">\n<p><span style=\"color: #808080;\">Bộ ba rắc rối &nbsp;- Ảnh: ĐPCC</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<table class=\"trichdan\" style=\"height: 80px; background-color: #ece9e7; float: left;\" border=\"0\" width=\"535\" cellspacing=\"4\" cellpadding=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><strong>&ldquo;Phim chiếu rạp hiện đang bị truyền h&igrave;nh h&oacute;a l&agrave; một thực tế kh&aacute; r&otilde; r&agrave;ng. Cụ thể những phim c&oacute; doanh thu đều l&agrave; phim quy tụ c&aacute;c nghệ sĩ h&agrave;i nổi tiếng tr&ecirc;n truyền h&igrave;nh</strong></td>\n</tr>\n<tr>\n<td class=\"quote_author\" style=\"text-align: right;\">\n<p><span style=\"color: #808080;\"><strong>Đạo diễn trẻ&nbsp;TRƯƠNG  QUANG THỊNH</strong></span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\"><strong>Phim chiếu rạp: cơ hội l&agrave;m ăn mới</strong></p>\n<p style=\"text-align: justify;\">Ngược lại với những kh&oacute; khăn của phim truyền h&igrave;nh, phim chiếu rạp đang c&oacute; vẻ như l&agrave; m&oacute;n hời để đầu tư khi hệ thống rạp chiếu phim ở VN hiện đ&atilde; tăng đến gần 100 cụm rạp ti&ecirc;u chuẩn, lượng phim Việt từ khoảng 10 phim/năm tăng l&ecirc;n đến 30 phim/năm v&agrave; kh&ocirc;ng chỉ c&oacute; m&ugrave;a tết mới c&oacute; doanh thu lớn.</p>\n<p style=\"text-align: justify;\">Kh&ocirc;ng phải vừa lo sản xuất phim vừa phải chạy quảng c&aacute;o để đủ doanh số với nh&agrave; đ&agrave;i, ng&agrave;y c&agrave;ng c&oacute; nhiều nh&agrave; sản xuất phim truyền h&igrave;nh lấn s&acirc;n sang phim điện ảnh như t&igrave;m một cơ hội l&agrave;m ăn mới.</p>\n<p style=\"text-align: justify;\">Mang t&acirc;m trạng hồ hởi của những người mới bước v&agrave;o s&acirc;n chơi mới, b&agrave; B&iacute;ch Thủy cho biết: &ldquo;Theo t&ocirc;i, chiếu rạp đang được xem l&agrave; cuộc chơi s&ograve;ng phẳng. Nh&agrave; sản xuất chỉ ch&uacute; t&acirc;m sản xuất sao cho ph&ugrave; hợp với thị hiếu kh&aacute;n giả.</p>\n<p style=\"text-align: justify;\">C&ograve;n phương thức thanh to&aacute;n c&agrave;o bằng gi&aacute; phim truyền h&igrave;nh như hiện nay đang khiến nhiều nh&agrave; sản xuất kh&ocirc;ng d&aacute;m đầu tư cao v&igrave; sợ lỗ&rdquo;.</p>\n<p style=\"text-align: justify;\">Hiện đơn vị sản xuất phim truyền h&igrave;nh tham gia phim chiếu rạp nổi bật nhất l&agrave; H&atilde;ng phim S&oacute;ng V&agrave;ng. Mỗi năm h&atilde;ng phim n&agrave;y sản xuất mấy trăm tập phim truyền h&igrave;nh cung cấp cho cả HTV lẫn VTV. Đều đặn từ năm 2013 đến nay, S&oacute;ng V&agrave;ng l&agrave;m phim chiếu rạp m&ugrave;a tết v&agrave; c&ograve;n hợp t&aacute;c với c&aacute;c h&atilde;ng phim kh&aacute;c để sản xuất phim chiếu rạp khắp m&ugrave;a trong năm.</p>\n<p style=\"text-align: justify;\">Sau một thời gian chung sống với phim truyền h&igrave;nh, H&atilde;ng phim Sena cũng vừa quyết định đầu tư sang phim chiếu rạp.</p>\n<p style=\"text-align: justify;\">Bộ phim đầu ti&ecirc;n của h&atilde;ng n&agrave;y <em>H&atilde;y ẵm em ra biển</em> đang thực hiện phần hậu kỳ. &Aacute;t chủ b&agrave;i của bộ phim n&agrave;y l&agrave; sự trở lại của &ldquo;nữ ho&agrave;ng nước mắt&rdquo; Việt Trinh với c&acirc;u chuyện kh&aacute; l&acirc;m li ướt &aacute;t về cuộc sống vợ chồng.</p>\n<p style=\"text-align: justify;\">C&ocirc;ng ty Leo Media sau khi sản xuất bốn phim truyền h&igrave;nh giờ cũng bắt đầu &ldquo;dấn th&acirc;n&rdquo; với bộ phim đầu tay<em> Cầu vồng kh&ocirc;ng sắc</em>. Sắp tới đ&acirc;y, h&atilde;ng sẽ ra rạp một bộ phim h&agrave;i hước, h&agrave;nh động mang t&ecirc;n <em>Điệp vụ ch&acirc;n d&agrave;i</em>.</p>\n<p style=\"text-align: justify;\">Ho&agrave;ng Thần T&agrave;i - h&atilde;ng phim của diễn vi&ecirc;n h&agrave;i Ho&agrave;ng Mập - sau khi thực hiện một số phim truyền h&igrave;nh cũng nhảy v&agrave;o lĩnh vực phim chiếu rạp với c&aacute;c bộ phim <em>Cưới chạy, Đời như &yacute;, Mỹ nam kế... </em></p>\n<p style=\"text-align: justify;\">Một h&atilde;ng phim truyền h&igrave;nh kh&aacute;c l&agrave; Vietcom cũng đang nhắm nhe một kịch bản phim chiếu rạp v&agrave; chuẩn bị sản xuất trong nay mai.</p>\n<table class=\"desc_image slide_content\" style=\"background-color: #ece9e9; height: 382px;\" title=\"D&acirc;n truyền h&igrave;nh lấn s&acirc;n phim chiếu rạp \" border=\"0\" width=\"492\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"Sơn đẹp trai - Ảnh: ĐPCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 490px;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/09/16/321c4e59.jpg\" alt=\"Sơn đẹp trai - Ảnh: ĐPCC\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\">\n<p><span style=\"color: #808080;\">Sơn đẹp trai - Ảnh: ĐPCC</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\"><strong>Kh&aacute;c nhau đ&acirc;u chỉ  c&aacute;i m&agrave;n h&igrave;nh</strong></p>\n<p style=\"text-align: justify;\">B&agrave; B&iacute;ch Li&ecirc;n kh&ocirc;ng giấu giếm hiện doanh thu phim truyền h&igrave;nh của H&atilde;ng phim S&oacute;ng V&agrave;ng đ&atilde; giảm nhiều so với trước đ&acirc;y.</p>\n<p style=\"text-align: justify;\">Trong khi đ&oacute; phim chiếu rạp lại đem về nguồn thu đ&aacute;ng kể: &ldquo;Tuy nhi&ecirc;n l&agrave;m phim thể loại n&agrave;o cũng c&oacute; c&aacute;i kh&oacute; của n&oacute;. L&agrave;m phim truyền h&igrave;nh nếu lỗ cũng lỗ &iacute;t, c&ograve;n phim chiếu rạp m&agrave; kh&ocirc;ng c&oacute; kh&aacute;n giả th&igrave; coi như mất trắng. M&agrave; vốn sản xuất to&agrave;n t&iacute;nh bằng tiền tỉ n&ecirc;n kh&ocirc;ng đ&ugrave;a được&rdquo;.</p>\n<p style=\"text-align: justify;\">N&oacute;i về ưu thế của sản xuất phim truyền h&igrave;nh chuyển sang phim chiếu rạp, &ocirc;ng Nguyễn Quang Tuyến - gi&aacute;m đốc H&atilde;ng phim Leo Media - cho rằng:</p>\n<p style=\"text-align: justify;\">&ldquo;Chịu &aacute;p lực về thời gian v&agrave; tiền bạc n&ecirc;n c&aacute;c đo&agrave;n phim truyền h&igrave;nh khi ra phim trường thường đ&aacute;nh nhanh, gọn, lẹ. Một &ecirc;kip l&agrave;m phim truyền h&igrave;nh đ&atilde; quen với cường độ l&agrave;m việc căng thẳng n&ecirc;n khi chuyển qua l&agrave;m phim chiếu rạp tốc độ thực hiện cũng nhanh v&agrave; chuy&ecirc;n nghiệp hơn&rdquo;.</p>\n<p style=\"text-align: justify;\">Thế nhưng truyền h&igrave;nh v&agrave; điện ảnh vốn l&agrave; hai lĩnh vực kh&aacute;c nhau, ch&iacute; &iacute;t cũng kh&aacute;c nhau bởi c&aacute;i m&agrave;n ảnh. Phim tr&ecirc;n m&agrave;n ảnh nhỏ kh&aacute;n giả c&oacute; thể vừa xem vừa nấu cơm, lau nh&agrave;... Những hạt sạn đ&ocirc;i khi v&igrave; thế m&agrave; dễ d&agrave;ng bỏ qua. C&ograve;n phim chiếu rạp th&igrave; m&agrave;n ảnh to đ&ugrave;ng, &acirc;m thanh sắc n&eacute;t.</p>\n<p style=\"text-align: justify;\">Kh&aacute;n giả tập trung coi trong 90 ph&uacute;t n&ecirc;n c&aacute;i dở, c&aacute;i ph&ocirc;, c&aacute;i ngớ ngẩn cứ hiện r&otilde; mồn một. Nhiều đạo diễn dạn d&agrave;y kinh nghiệm trong phim truyền h&igrave;nh nhưng chỉ l&agrave; l&iacute;nh mới của phim chiếu rạp như V&otilde; Tấn B&igrave;nh, Phương Điền, Minh Cao... đ&atilde; phải nếm m&ugrave;i thương đau với <em>N&agrave;ng men ch&agrave;ng b&oacute;ng, Bộ ba rắc rối, Bay v&agrave;o c&otilde;i mộng, Thi&ecirc;n sứ 99</em>...</p>\n<p style=\"text-align: justify;\">V&agrave; c&acirc;u hỏi đặt ra: liệu với sự tham gia của c&aacute;c nh&agrave; sản xuất phim truyền h&igrave;nh, phim chiếu rạp đang ng&agrave;y một truyền h&igrave;nh h&oacute;a?</p>\n<p style=\"text-align: justify;\">Đạo diễn trẻ Trương Quang Thịnh (đạo diễn phim truyền h&igrave;nh <em>Quyến rũ, Đ&aacute;nh thức tr&aacute;i tim</em> v&agrave; mới đ&acirc;y l&agrave; phim điện ảnh <em>Sơn đẹp trai)</em> thừa nhận:</p>\n<p style=\"text-align: justify;\">&ldquo;Phim chiếu rạp hiện đang bị truyền h&igrave;nh h&oacute;a l&agrave; một thực tế kh&aacute; r&otilde; r&agrave;ng. Cụ thể những phim c&oacute; doanh thu đều l&agrave; phim quy tụ c&aacute;c nghệ sĩ h&agrave;i nổi tiếng tr&ecirc;n truyền h&igrave;nh.</p>\n<p style=\"text-align: justify;\">Kh&aacute;n giả mua v&eacute; xem phim để người ta coi danh h&agrave;i Ho&agrave;i Linh, Việt Hương hay Trấn Th&agrave;nh... chọc kh&aacute;n giả cười điều g&igrave;, chứ họ chẳng quan t&acirc;m lắm c&acirc;u chuyện phim bởi nội dung rất dở. V&agrave; để đ&aacute;p ứng nhu cầu ấy, h&agrave;i trong phim Việt được nghi&ecirc;n cứu sao cho ng&agrave;y c&agrave;ng d&agrave;y hơn để giữ ch&acirc;n kh&aacute;n giả.</p>\n<p style=\"text-align: justify;\">Nhưng đến một l&uacute;c n&agrave;o đ&oacute; tiếng cười nh&agrave;m dần, kh&ocirc;ng c&ograve;n nặng k&yacute;, phim Việt sẽ lấy g&igrave; để h&uacute;t kh&aacute;n giả đ&acirc;y?&rdquo;.</p>\n<table class=\"dbox\" style=\"width: 488px; height: 221px; background-color: #ece9e9; margin-left: auto; margin-right: auto;\" border=\"0\" cellspacing=\"4\" cellpadding=\"1\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: left;\">Từ năm 2013, H&atilde;ng phim S&oacute;ng V&agrave;ng chuy&ecirc;n sản xuất phim truyền h&igrave;nh bắt đầu cuộc chơi mới bằng phim chiếu rạp m&ugrave;a tết. Thị trường phim chiếu rạp c&ograve;n ghi dấu son về doanh thu của <em>Nh&agrave; c&oacute; năm n&agrave;ng ti&ecirc;n</em> m&agrave; S&oacute;ng V&agrave;ng sản xuất khi l&agrave; phim tết nhưng đến tận th&aacute;ng 4 kh&aacute;n giả vẫn c&ograve;n đi xem phim n&agrave;y ở cụm rạp Megastar v&agrave; thu về gần 60 tỉ đồng (con số khủng thời điểm đ&oacute;).</p>\n<p style=\"text-align: left;\">Giữ &aacute;t chủ b&agrave;i v&agrave; độc quyền bạc tỉ Ho&agrave;i Linh, S&oacute;ng V&agrave;ng tiếp tục thắng với <em>Năm sau con lại về</em> (2014) v&agrave; <em>Qu&yacute; tử bất đắc dĩ</em> (2015). Kh&ocirc;ng chỉ tập trung m&ugrave;a phim tết, S&oacute;ng V&agrave;ng c&ograve;n hợp t&aacute;c, bỏ vốn đầu tư tham gia sản xuất nhiều phim chiếu rạp kh&aacute;c rải r&aacute;c trong năm v&agrave; mới đ&acirc;y kinh doanh th&ecirc;m việc ph&aacute;t h&agrave;nh đồng thời l&agrave;m rạp chiếu phim (Mega GS).</p>\n<p class=\"text-right\" style=\"text-align: left;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C.Khu&ecirc;</strong></p>\n</td>\n</tr>\n</tbody>\n</table>\n<div class=\"wrapper-qt\" style=\"text-align: right;\">\n<div class=\"qtam_social btn-qt\">\n<div id=\"like_thread\" style=\"float: left;\">&nbsp;</div>\n</div>\n<em>Theo tuoitre.com.vn</em></div>\n</div>\n</div>\n</div>',' Dân truyền hình lấn sân phim chiếu rạp ','','',0,'http://tamdiem.com.vn/sources/images/phim%20chieu%20rap.jpg','',0,0,0,0,481,0,1442375898,1443683028,1),(125,'Làm phim độc lập - Nhọc nhằn, đơn độc ','lam-phim-doc-lap-nhoc-nhan-don-doc-',14,'news_item','Phim độc lập, liên hoan phim quốc tế, Bi! Đừng sợ, Đập cánh giữa không trung','Dù còn ít về số lượng và gặp nhiều khó khăn khi thực hiện, nhưng vài năm trở lại đây, những bộ phim điện ảnh Việt trong dòng phim độc lập đã đến với các liên hoan phim quốc tế và gặt hái ít nhiều thành công, tạo được sự chú ý nhất định với bạn bè quốc tế. ','Dù còn ít về số lượng và gặp nhiều khó khăn khi thực hiện, nhưng vài năm trở lại đây, những bộ phim điện ảnh Việt trong dòng phim độc lập đã đến với các liên hoan phim quốc tế và gặt hái ít nhiều thành công, tạo được sự chú ý nhất định với bạn bè quốc tế. ','<p style=\"text-align: justify;\"><strong><em>D&ugrave; c&ograve;n &iacute;t về số lượng v&agrave; gặp nhiều kh&oacute; khăn khi thực hiện, nhưng v&agrave;i năm trở lại đ&acirc;y, những bộ phim điện ảnh Việt trong d&ograve;ng phim độc lập đ&atilde; đến với c&aacute;c li&ecirc;n hoan phim quốc tế v&agrave; gặt h&aacute;i &iacute;t nhiều th&agrave;nh c&ocirc;ng, tạo được sự ch&uacute; &yacute; nhất định với bạn b&egrave; quốc tế. </em></strong></p>\n<p style=\"text-align: justify;\">Những th&agrave;nh c&ocirc;ng ban đầu ấy l&agrave; động lực để c&aacute;c bạn trẻ y&ecirc;u điện ảnh, c&oacute; c&aacute; t&iacute;nh, tự tin t&igrave;m lấy cơ hội thực hiện dự &aacute;n phim độc lập cho m&igrave;nh. Tuy nhi&ecirc;n, việc t&igrave;m được kinh ph&iacute; cho d&ograve;ng phim n&agrave;y v&agrave; đưa phim ra rạp lại đầy thử th&aacute;ch.</p>\n<p style=\"text-align: justify;\"><strong>H&agrave;nh tr&igrave;nh t&igrave;m kinh ph&iacute;</strong></p>\n<p style=\"text-align: justify;\">Một bộ phim độc lập l&agrave; t&aacute;c phẩm được sản xuất phần lớn hoặc to&agrave;n bộ bởi c&aacute;c c&ocirc;ng ty kh&ocirc;ng nằm trong hệ thống c&aacute;c nh&agrave; sản xuất phim ch&iacute;nh thống. Trong một số trường hợp, c&aacute;c phim độc lập l&agrave; phim được thực hiện với kinh ph&iacute; thấp v&agrave; đ&acirc;y được xem l&agrave; d&ograve;ng phim mang đậm dấu ấn, tư duy, c&aacute; t&iacute;nh của t&aacute;c giả. Trước đ&acirc;y đ&atilde; c&oacute; phim <em>Bi! Đừng sợ</em>, gần đ&acirc;y nhất c&oacute; <em>Đập c&aacute;nh giữa kh&ocirc;ng trung</em>, <em>Chuyến đi cuối c&ugrave;ng của chị Phụng</em> (phim t&agrave;i liệu) được coi l&agrave; những t&aacute;c phẩm của c&aacute;c nh&agrave; l&agrave;m phim độc lập từng nhận một số giải thưởng tại c&aacute;c li&ecirc;n hoan phim (LHP) quốc tế v&agrave; tạo nhiều ch&uacute; &yacute; với kh&aacute;n giả trong nước.</p>\n<table style=\"height: 424px; margin-left: auto; margin-right: auto;\" width=\"472\">\n<tbody>\n<tr>\n<td>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//Hai Yen.jpg\" alt=\"\" width=\"463\" height=\"347\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #0000ff;\">Đạo diễn Phan Đăng Di (đeo k&iacute;nh) v&agrave; c&aacute;c diễn vi&ecirc;n trong phim Cha v&agrave; con v&agrave;&hellip; tại buổi họp b&aacute;o v&agrave; ra mắt phim tại LHP Berlin 2015</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Tại Việt Nam, c&aacute;c bộ phim độc lập đều c&oacute; chung một phương thức thực hiện: Dự &aacute;n phim được t&aacute;c giả hoặc nh&agrave; sản xuất gửi đến c&aacute;c LHP hay c&aacute;c chợ dự &aacute;n. Sau khi dự &aacute;n được chọn, ban tổ chức sẽ t&igrave;m v&agrave; giới thiệu c&aacute;c nh&agrave; đầu tư hoặc c&aacute;c quỹ hỗ trợ điện ảnh để đưa dự &aacute;n v&agrave;o sản xuất. Sau khi ho&agrave;n th&agrave;nh, phim bắt đầu tham dự c&aacute;c LHP quốc tế v&agrave; c&oacute; một số giải thưởng nhất định mới ph&aacute;t h&agrave;nh trong nước.</p>\n<p style=\"text-align: justify;\">Qu&aacute; tr&igrave;nh t&igrave;m nh&agrave; đầu tư hoặc quỹ hỗ trợ điện ảnh cũng gặp rất nhiều kh&oacute; khăn, trở ngại. &ldquo;Kinh ph&iacute; sản xuất một bộ phim độc lập thường khoảng 600.000 - 700.000 USD, nhưng rất hiếm dự &aacute;n n&agrave;o được đầu tư to&agrave;n bộ. Ch&uacute;ng t&ocirc;i phải chia dự &aacute;n ra để lấy tiền từ 3, 4 quỹ đi quay. Khi quay xong, lại xin tiền từ nh&agrave; đầu tư hoặc quỹ kh&aacute;c để l&agrave;m hậu kỳ. Tiền từ c&aacute;c quỹ n&agrave;y r&oacute;t cho dự &aacute;n cao nhất l&agrave; 200.000 USD v&agrave; thấp nhất l&agrave; 10.000 USD&rdquo;, anh chia sẻ. Theo đạo diễn Phan Đăng Di, việc gửi dự &aacute;n đến c&aacute;c chợ phim hay LHP cũng kh&ocirc;ng dễ d&agrave;ng được &ldquo;để mắt&rdquo; tới, v&igrave; mỗi lần c&oacute; đến h&agrave;ng trăm dự &aacute;n đến từ h&agrave;ng trăm quốc gia kh&aacute;c nhau tr&ecirc;n thế giới. Thường th&igrave; dự &aacute;n ấy nếu được gửi từ một t&aacute;c giả, nh&agrave; sản xuất đ&atilde; c&oacute; &iacute;t nhiều tiếng tăm, uy t&iacute;n hoặc từng nhận giải thưởng tại c&aacute;c LHP, khả năng được ch&uacute; &yacute; sẽ cao hơn.</p>\n<table style=\"height: 385px; margin-left: auto; margin-right: auto;\" width=\"481\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//phim Cha va Con.jpg\" alt=\"\" width=\"471\" height=\"330\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #0000ff;\">Đỗ Thị Hải Yến, Trương Thế Vinh v&agrave; L&ecirc; C&ocirc;ng Ho&agrave;ng trong phim Cha v&agrave; con v&agrave;&hellip; của Phan Dăng Di</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Với kinh nghiệm của người đi trước, từ sau th&agrave;nh c&ocirc;ng của bộ phim <em>Bi! Đừng sợ</em> - từng đoạt giải thưởng Dự &aacute;n ch&acirc;u &Aacute; nổi bật tại LHP quốc tế Busan năm 2007, trước khi ra mắt kh&aacute;n giả trong nước năm 2011, đạo diễn Phan Đăng Di l&agrave; một trong số c&aacute;c đạo diễn trẻ đ&atilde; s&aacute;ng lập v&agrave; điều h&agrave;nh chương tr&igrave;nh &ldquo;Gặp gỡ m&ugrave;a thu&rdquo; - một chương tr&igrave;nh hỗ trợ c&aacute;c nh&agrave; l&agrave;m phim trẻ c&oacute; cơ hội đưa những c&acirc;u chuyện của m&igrave;nh l&ecirc;n m&agrave;n ảnh rộng. Chương tr&igrave;nh n&agrave;y được khởi xướng từ năm 2013 v&agrave; diễn ra h&agrave;ng năm tại Đ&agrave; Nẵng. Bằng kinh nghiệm v&agrave; c&aacute;c mối quan hệ của c&aacute; nh&acirc;n m&igrave;nh, đạo diễn Phan Đăng Di v&agrave; đồng nghiệp đ&atilde; chia sẻ v&agrave; gi&uacute;p đỡ kh&aacute; nhiều bạn trẻ y&ecirc;u điện ảnh được l&agrave;m phim v&agrave; từng bước tiếp cận, giới thiệu m&igrave;nh với bạn b&egrave; thế giới. Th&aacute;ng 10 tới, đạo diễn Phan Đăng Di v&agrave; Nguyễn Ho&agrave;ng Điệp sẽ giới thiệu hai dự &aacute;n phim đ&atilde; đoạt giải tại &ldquo;Gặp gỡ m&ugrave;a thu&rdquo; lần 2-2014 của hai bạn trẻ Việt Nam tới LHP quốc tế Busan, l&agrave;: <em>Cha, cha, cha</em> của Đỗ Quốc Trung v&agrave; <em>Thằng r&ograve;m</em> của Trần Dũng Thanh Huy.</p>\n<p style=\"text-align: justify;\"><strong>Kh&oacute; khăn ra rạp</strong></p>\n<p style=\"text-align: justify;\">Thị trường ph&aacute;t h&agrave;nh của phim độc lập ở cả Việt Nam lẫn tr&ecirc;n thế giới thường kh&aacute; hạn chế. C&aacute;c phim n&agrave;y thường được tr&igrave;nh chiếu tại c&aacute;c LHP địa phương, quốc gia hay quốc tế, trước khi được ph&aacute;t h&agrave;nh hoặc b&aacute;n lẻ cho c&aacute;c nh&agrave; ph&aacute;t h&agrave;nh lớn. Sự th&agrave;nh c&ocirc;ng lớn về lượng người xem đến với bộ phim t&agrave;i liệu <em>Chuyến đi cuối c&ugrave;ng của chị Phụng</em>, c&oacute; lẽ l&agrave; trường hợp đặc biệt ngoại lệ v&agrave; ngo&agrave;i sức tưởng tượng của cả người l&agrave;m phim lẫn kh&aacute;n giả. Bởi v&igrave;, d&ograve;ng phim độc lập thường được xem l&agrave; những t&aacute;c phẩm thuần nghệ thuật, đậm dấu ấn c&aacute; nh&acirc;n v&agrave; kh&ocirc;ng d&agrave;nh cho số đ&ocirc;ng kh&aacute;n giả. Trong khi c&aacute;c nh&agrave; ph&aacute;t h&agrave;nh rất d&egrave; dặt, th&igrave; nh&agrave; sản xuất - t&aacute;c giả phim phải kh&aacute; vất vả trong việc thuyết phục họ đồng &yacute; ph&aacute;t h&agrave;nh ra rạp. <br /> <br /> Với phim truyện, v&igrave; d&ograve;ng phim độc lập đề cao sự s&aacute;ng tạo, phong c&aacute;ch v&agrave; tư duy mới mẻ n&ecirc;n c&aacute;c phim độc lập thường ở dạng &ldquo;phim c&aacute; t&iacute;nh&rdquo;. Để được ra rạp, c&aacute;c bộ phim n&agrave;y vẫn phải chịu sự kiểm duyệt của hội đồng duyệt v&agrave; chịu sự chế t&agrave;i của c&aacute;c cơ quan quản l&yacute; v&agrave; v&igrave; thế, bao giờ cũng bị cắt bỏ những cảnh qu&aacute; nhạy cảm hoặc kh&ocirc;ng ph&ugrave; hợp với văn h&oacute;a, ứng xử của người Việt. Kh&aacute;n giả được xem một bản phim kh&ocirc;ng ho&agrave;n chỉnh khi ra rạp, c&ograve;n t&aacute;c giả &iacute;t nhiều ấm ức cho rằng, phim kh&ocirc;ng c&ograve;n trọn vẹn như bản gốc! C&oacute; thể thấy r&otilde; điều n&agrave;y khi hai phim <em>Bi! Đừng sợ</em> v&agrave; <em>Đập c&aacute;nh giữa kh&ocirc;ng trung</em> ra rạp. C&oacute; vẻ như kh&aacute;n giả kh&ocirc;ng mặn m&agrave; đến rạp, bằng việc t&igrave;m xem bản gốc của phim bằng mọi gi&aacute;. C&oacute; &yacute; kiến cho rằng, đ&atilde; đến l&uacute;c những người l&agrave;m điện ảnh, quản l&yacute; điện ảnh n&ecirc;n cập nhật tư duy, để gi&uacute;p c&aacute;c nh&agrave; l&agrave;m phim trẻ khẳng định c&aacute; t&iacute;nh v&agrave; tiếp cận được với tr&agrave;o lưu l&agrave;m phim của thế giới. Nhưng một số nh&agrave; l&agrave;m phim kỳ cựu của điện ảnh Việt lại cho rằng, c&aacute;i mới d&ugrave; c&oacute; ph&aacute;t triển cũng n&ecirc;n dựa tr&ecirc;n nền tảng, văn h&oacute;a của d&acirc;n tộc. Bộ phim Người truyền giống bị cấm chiếu vĩnh viễn l&agrave; một v&iacute; dụ cho tư duy &ldquo;tho&aacute;ng&rdquo;, &ldquo;mới&rdquo; nhưng kh&ocirc;ng được chấp nhận v&igrave; qu&aacute; xa lạ v&agrave; đi ngược với văn h&oacute;a truyền thống.</p>\n<table style=\"height: 557px; margin-left: auto; margin-right: auto;\" width=\"463\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Dap canh giua khong trung.jpg\" alt=\"\" width=\"460\" height=\"460\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #0000ff;\">Cảnh trong phim Đập c&aacute;nh giữa kh&ocirc;ng trung - dự &aacute;n phim độc lập được ch&uacute; &yacute; thời gian qua</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Chị Nguyễn Thị Thắm, đạo diễn phim t&agrave;i liệu <em>Chuyến đi cuối c&ugrave;ng của chị Phụng</em>, chia sẻ: &ldquo;Bộ phim được thực hiện trong v&ograve;ng 5 năm. Khởi đầu t&ocirc;i tự bỏ tiền t&uacute;i để l&agrave;m. Sau đ&oacute;, t&ocirc;i nhận được một phần kinh ph&iacute; từ Varan Việt Nam - quỹ của c&aacute;c nh&agrave; l&agrave;m phim t&agrave;i liệu độc lập của Việt Nam. Mặc d&ugrave; số tiền quỹ của nh&oacute;m kh&ocirc;ng nhiều, nhưng mỗi th&aacute;ng họ hỗ trợ t&ocirc;i một phần để c&oacute; kinh ph&iacute; thực hiện để qu&aacute; tr&igrave;nh quay kh&ocirc;ng bị gi&aacute;n đoạn. Sau n&agrave;y, Viện Goethe v&agrave; quỹ l&agrave;m phim ở Ph&aacute;p: Atelier Varen, Andr&eacute; Van In v&agrave; Ina-Sylvie Blum&hellip; cũng đ&atilde; gi&uacute;p đỡ t&ocirc;i rất nhiều trong c&ocirc;ng đoạn l&agrave;m &acirc;m thanh cho bộ phim, in ấn th&agrave;nh băng, đĩa. Đặc biệt, nh&oacute;m l&agrave;m phim ở Paris, Ph&aacute;p đ&atilde; hỗ trợ miễn ph&iacute;. Nếu n&oacute;i c&oacute; đủ tiền để l&agrave;m một mạch cho ho&agrave;n th&agrave;nh phim l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute;&rdquo;.<br /> <br /> Đạo diễn trẻ Đỗ Quốc Trung đ&atilde; gặt h&aacute;i th&agrave;nh c&ocirc;ng với những bộ phim C&aacute; chuối (giải phim Đ&ocirc;ng Nam &Aacute; xuất sắc nhất cuộc thi phim ngắn Chatomuk - Campuchia 2013 v&agrave; nhiều giải kh&aacute;c), Trực nhật với Thư Kỳ (giải Tr&aacute;i tim hồng YxineFF 2012), Đ&oacute;ng v&agrave;o, Mở ra... (giải Golden Award - LHP ngắn quốc tế REC Berlin, Đức 2014)..., cho biết: &ldquo;Để c&oacute; được t&agrave;i trợ từ c&aacute;c quỹ điện ảnh, c&aacute;i kh&oacute; l&agrave; phải thuyết phục được họ đồng &yacute; với dự &aacute;n của m&igrave;nh. Điều n&agrave;y y&ecirc;u cầu phải c&oacute; sự chuẩn bị kỹ lưỡng v&igrave; lu&ocirc;n lu&ocirc;n c&oacute; nhiều đối thủ c&oacute; c&ugrave;ng chung mong muốn như m&igrave;nh. Hầu hết c&aacute;c phim độc lập của ch&uacute;ng t&ocirc;i cũng như nhiều đồng nghiệp kh&aacute;c đều kh&oacute; c&oacute; cửa ra rạp. Phim ngắn Trực nhật với Thư Kỳ từng được mua bản quyền ph&aacute;t s&oacute;ng tr&ecirc;n truyền h&igrave;nh, nhưng kh&ocirc;ng nhiều, c&oacute; khi chỉ v&agrave;i triệu đồng&rdquo;. N&oacute;i về việc dự &aacute;n <em>Cha, cha, cha</em> tham gia LHP quốc tế Busan, đạo diễn trẻ Đỗ Quốc Trung thẳng thắn: &ldquo;T&ocirc;i kh&ocirc;ng đặt nặng việc m&igrave;nh c&oacute; được giải hay kh&ocirc;ng. Việc được chọn v&agrave;o tốp 30 đ&atilde; l&agrave; một may mắn&rdquo;.</p>\n<table style=\"height: 105px; margin-left: auto; margin-right: auto;\" border=\"1\" width=\"532\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\">Đạo diễn B&ugrave;i Thạc Chuy&ecirc;n, người d&agrave;nh nhiều sự quan t&acirc;m, ủng hộ cho d&ograve;ng phim độc lập, phim t&aacute;c giả, cho biết: &ldquo;Hiện nay, c&aacute;c quỹ đầu tư, ph&aacute;t triển điện ảnh của thế giới ng&agrave;y c&agrave;ng b&oacute; hẹp do khủng hoảng kinh tế v&agrave; v&igrave; thế con đường d&agrave;nh cho d&ograve;ng phim độc lập, phim t&aacute;c giả vốn rất gập ghềnh, phức tạp lại c&agrave;ng kh&oacute; khăn hơn. Nh&agrave; nước cần phải c&oacute; quỹ d&agrave;nh cho điện ảnh, v&igrave; hiện nay đ&atilde; c&oacute; một số anh em trẻ bắt đầu tham gia v&agrave;o d&ograve;ng phim n&agrave;y&rdquo;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: right;\"><em>Theo sggp.org.vn<strong><br /></strong></em></p>\n<p>&nbsp;</p>','Làm phim độc lập - Nhọc nhằn, đơn độc ','','',0,'http://tamdiem.com.vn/sources/images/Hai%20Yen.jpg','',0,0,0,0,480,0,1442804350,1444962127,1),(126,'Vì sao Vietnam\'s Next Top Model 2015 giảm sức hút','vi-sao-vietnams-next-top-model-2015-giam-suc-hut',14,'news_item','Vietnam\'s Next Top Model 2015','Chương trình Người mẫu Việt Nam mùa thứ sáu bị đánh giá kém chất lượng cả về thí sinh, tính thời trang trong các bộ ảnh cùng dàn giám khảo nhạt nhòa.','Chương trình Người mẫu Việt Nam mùa thứ sáu bị đánh giá kém chất lượng cả về thí sinh, tính thời trang trong các bộ ảnh cùng dàn giám khảo nhạt nhòa.','<p style=\"text-align: justify;\"><strong>Chương tr&igrave;nh Người mẫu Việt Nam m&ugrave;a thứ s&aacute;u bị đ&aacute;nh gi&aacute; k&eacute;m chất lượng cả về th&iacute; sinh, t&iacute;nh thời trang trong c&aacute;c bộ ảnh c&ugrave;ng d&agrave;n gi&aacute;m khảo nhạt nh&ograve;a.</strong></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Sau ch&iacute;n tập ph&aacute;t s&oacute;ng, Vietnam\'s Next Top Model 2015 đi gần hết chặng đường để tiến về đ&ecirc;m chung kết &nbsp;v&agrave;o 11/10. Từ l&uacute;c tổ chức casting, nh&agrave; sản xuất hứa hẹn m&ugrave;a giải năm nay c&oacute; nhiều điểm mới th&uacute; vị v&agrave; kh&aacute;c&nbsp;biệt so với mọi năm, song thực tế, chương tr&igrave;nh ng&agrave;y c&agrave;ng giảm sức h&uacute;t.&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Nội dung kịch bản m&ugrave;a thứ s&aacute;u kh&ocirc;ng l&agrave;m thỏa m&atilde;n người xem v&igrave; thử th&aacute;ch thiếu kịch t&iacute;nh, sa đ&agrave; chuyện b&ecirc;n lề, chất lượng ảnh k&eacute;m thời trang thể hiện qua c&aacute;c&nbsp;thử th&aacute;ch chụp h&igrave;nh. Một trong những thử th&aacute;ch được mong đợi nhất l&agrave; chụp h&igrave;nh với động vật. Nếu những m&ugrave;a trước, th&iacute; sinh kh&oacute;c th&eacute;t khi phải chụp h&igrave;nh với trăn, rắn lục, kỳ đ&agrave;, nhện, chuột, voi th&igrave; năm nay, họ đồng h&agrave;nh với bạn diễn l&agrave; những ch&uacute; khỉ tinh nghịch. Thử th&aacute;ch n&agrave;y được đ&aacute;nh gi&aacute; l&agrave; kh&aacute; dễ d&agrave;ng, từ đ&oacute; kh&ocirc;ng gi&uacute;p th&iacute; sinh bộc lộ c&aacute; t&iacute;nh cũng như khả năng điều khiển cảm x&uacute;c bản th&acirc;n khi l&agrave;m mẫu.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Tương tự, buổi&nbsp;chụp h&igrave;nh tr&ecirc;n cao từng l&agrave; nỗi khiếp đảm của nhiều th&iacute; sinh th&igrave; đến tập n&agrave;y, kh&oacute; khăn đ&atilde; được giảm đi nhiều với chủ đề chụp h&igrave;nh tr&ecirc;n <a href=\"http://giaitri.vnexpress.net/photo/truyen-hinh/co-gai-cao-19-m-lot-xac-voi-anh-mat-bieu-cam-3264762.html\">v&aacute;ch đ&aacute;</a> nh&acirc;n tạo. Tận dụng điểm tựa l&agrave; đường mấp m&ocirc; tr&ecirc;n v&aacute;ch, đồng thời được mặc trang phục kh&ocirc;ng cồng kềnh như những năm trước, họ dễ d&agrave;ng tạo d&aacute;ng. Trong khi t&iacute;nh từ 2011 đến 2014, người chơi lần lượt phải treo m&igrave;nh tr&ecirc;n cần cẩu, tạo d&aacute;ng tr&ecirc;n đỉnh một t&ograve;a th&aacute;p cao ngất ngưởng, đứng tr&ecirc;n sợi d&acirc;y thừng lửng lơ giữa kh&ocirc;ng trung, hay&nbsp;chụp h&igrave;nh với to&agrave; nh&agrave; c&oacute; mặt k&iacute;nh trơn trượt.&nbsp;</p>\n<table class=\"tplCaption\" style=\"width: 425px; height: 596px; background-color: #e6e6ec;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://c1.f9.img.vnecdn.net/2015/09/30/3-1414-1443581997.jpg\" alt=\"3-1414-1443581997.jpg\" width=\"423\" height=\"582\" data-pwidth=\"470.4\" data-width=\"400\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\" style=\"text-align: center;\">Chất lượng c&aacute;c&nbsp;bộ h&igrave;nh&nbsp;năm nay.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Kh&ocirc;ng chỉ giảm độ kh&oacute; của phần thi, gần một nửa số thử th&aacute;ch bị ch&ecirc; l&agrave; xem nhẹ t&iacute;nh chuy&ecirc;n m&ocirc;n, đề cao t&iacute;nh giải tr&iacute;. Catwalk, chụp h&igrave;nh - những kỹ năng cơ bản của một người mẫu - đ&aacute;ng lẽ cần được đầu tư nhiều hơn cả về chất lượng lẫn số lượng th&igrave; chương tr&igrave;nh lại d&agrave;nh thời gian cho những kỹ năng b&ecirc;n lề.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Trong tập mới nhất, c&aacute;c ch&agrave;ng trai c&ocirc; g&aacute;i&nbsp;phải trổ t&agrave;i l&agrave;m tiế<span class=\"s2\">p vi&ecirc;n h&agrave;ng kh&ocirc;ng. Trước đ&oacute;,</span>&nbsp;th&iacute; sinh diệ<span class=\"s2\">n đ</span>ồ<span class=\"s2\">&nbsp;cosplay đi <a href=\"http://giaitri.vnexpress.net/photo/truyen-hinh/co-gai-cao-19-m-cua-next-top-doi-toc-gia-ban-hang-rong-3269847.html\">b&aacute;n h&agrave;ng rong</a>, ch&agrave;o m</span>ờ<span class=\"s2\">i kh&aacute;ch mua đ</span>ồ từ thiện, thử t&agrave;i l&agrave;m MC.&nbsp;<span class=\"s2\">Đ&acirc;y đư</span>ợc xem những phần thi kh&ocirc;ng li&ecirc;n quan đến nghề mẫu chuy&ecirc;n nghiệp. Thậm ch&iacute;, trong tập 4, ngo&agrave;i việc l&agrave;m stylist cho bản th&acirc;n, họ phải chạ<span class=\"s2\">y bộ 27 t</span>ầng cầu thang, khiến th&iacute; sinh Đinh Đức Th&agrave;nh&nbsp;kiệt sức <a href=\"http://giaitri.vnexpress.net/photo/truyen-hinh/hot-boy-next-top-ngat-xiu-sau-khi-chay-bo-27-tang-lau-3265807.html\">ngất xỉ</a><span class=\"s2\"><a href=\"http://giaitri.vnexpress.net/photo/truyen-hinh/hot-boy-next-top-ngat-xiu-sau-khi-chay-bo-27-tang-lau-3265807.html\">u</a>.&nbsp;</span></p>\n<p class=\"Normal\" style=\"text-align: justify;\">C&aacute;c bộ ảnh thời trang năm nay được coi l&agrave; một bước thụt l&ugrave;i kh&ocirc;ng chỉ so với những m&ugrave;a trước m&agrave; c&ograve;n với xu hướng hiện đại. Thay v&igrave; chụp h&igrave;nh trong khung cảnh tự nhi&ecirc;n, c&aacute;c th&iacute; sinh chụp <span class=\"s2\">c&ugrave;ng <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/vietnam-s-next-top-model-gay-kho-hieu-khi-khong-loai-thi-sinh-kem-3275126.html\">x&iacute;ch đu</a> tr&ecirc;n</span>&nbsp;ph&ocirc;ng nền l&agrave; h&agrave;ng c&acirc;y l&aacute; v&agrave;ng kh&aacute; \"sến\", gợi nhớ phong c&aacute;ch chụp ảnh của thập ni&ecirc;n trước. Buổi <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/thanh-hang-khien-khan-gia-day-song-vi-uu-ai-thi-sinh-kem-3282325.html\">chụp </a><a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/thanh-hang-khien-khan-gia-day-song-vi-uu-ai-thi-sinh-kem-3282325.html\"><span class=\"s2\">dư</span>ớ<span class=\"s2\">i nư</span></a><a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/thanh-hang-khien-khan-gia-day-song-vi-uu-ai-thi-sinh-kem-3282325.html\">ớc</a> cũng nhận lời ch&ecirc; của kh&aacute;n giả v&igrave; bối cảnh đại dương được sắp đặt tẻ nhạt, m&agrave;u sắc ảm đạm. Ở tập 9, người thi&nbsp;chụp h&igrave;nh trong ph&ograve;ng kh&ocirc;ng trọng lự<span class=\"s2\">c</span>. Bộ ảnh bị ch&ecirc; <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/hot-boy-next-top-co-y-bo-thi-khien-giam-khao-that-vong-3286228.html\">sơ s&agrave;i</a> giống như chụp ảnh lưu niệm, thiếu t&iacute;nh thời trang khi để người mẫu mặc nguy&ecirc;n trang phục của tr&ograve; chơi, giữ nguy&ecirc;n huấn luyện vi&ecirc;n ph&iacute;a sau m&agrave; kh&ocirc;ng chỉnh sửa.</p>\n<p class=\"Normal\" style=\"text-align: justify;\"><span style=\"color: #222222;\">Chia sẻ với&nbsp;</span><em style=\"color: #222222;\">VnExpress</em><span style=\"color: #222222;\">, một chuy&ecirc;n gia trong l&agrave;ng mốt Việt nhận định: \"Chưa bao giờ h&igrave;nh ảnh của th&iacute; sinh năm nay lại được đưa ra l&agrave;m tr&ograve; cười nhiều như vậy, c&aacute;c bộ ảnh bị chế giễu tr&ecirc;n mạng nhiều như thế. L&agrave; người l&agrave;m thời trang, t&ocirc;i kh&ocirc;ng chấp nhận được t&iacute;nh thiếu chuy&ecirc;n m&ocirc;n trong c&aacute;c bộ h&igrave;nh năm nay\".&nbsp;</span></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Trước đ&oacute;,&nbsp;<span style=\"color: #222222;\">T&ugrave;ng Leo - người thuộc &ecirc;k&iacute;p s&aacute;ng tạo của chương tr&igrave;nh - cho rằng bản chất của truyền h&igrave;nh thực tế kh&ocirc;ng phải l&agrave; ph&oacute;ng sự chuy&ecirc;n m&ocirc;n. Ph&aacute;t biểu của T&ugrave;ng Leo vấp phải phản ứng của nhiều người trong nghề, khi một chương tr&igrave;nh t&igrave;m kiếm người mẫu lại kh&ocirc;ng lấy t&iacute;nh thời trang, nghề nghiệp để l&agrave;m ti&ecirc;u ch&iacute; đ&agrave;o tạo,&nbsp;</span>đi ngược lại mục đ&iacute;ch ban đầu của chương tr&igrave;nh.</p>\n<p class=\"Normal\" style=\"text-align: justify;\"><span style=\"color: #333333;\"><strong>Quyết định loại th&iacute; sinh thiếu thuyết phục của ban gi&aacute;m khảo cũng l&agrave; một yếu tố khiến kh&aacute;n giả quay lưng với chương tr&igrave;nh. &nbsp;</strong></span></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Phần đ&aacute;nh gi&aacute; v&agrave; loại th&iacute; sinh của Thanh Hằng, Samuel Ho&agrave;ng v&agrave; Adrian Anh Tuấn thường m&acirc;u thuẫn v&agrave; g&acirc;y <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/vietnam-s-next-top-model-gay-kho-hieu-khi-khong-loai-thi-sinh-kem-3275126.html\">kh&oacute; hiểu</a>. Ở tập 6, bộ ba quyền lực li&ecirc;n tiếp ch&ecirc; th&iacute; sinh Hồng Xu&acirc;n, Đức Th&agrave;nh... tạo d&aacute;ng xấu, Hồng V&acirc;n thiếu nghi&ecirc;m t&uacute;c khi chụp h&igrave;nh, tiếp thu chậm chạp. Nhưng tất cả th&iacute; sinh k&eacute;m trong tập n&agrave;y đều kh&ocirc;ng bị loại.&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Samuel Ho&agrave;ng từng cho biết chủ<span class=\"s2\">&nbsp;đ</span>ề n<span class=\"s2\">ăm nay l&agrave; \"Kh&ocirc;ng ng</span>ừng chuyể<span class=\"s2\">n đ</span>ộng\", v&igrave; vậy muốn chạm tới th&agrave;nh c&ocirc;ng, c&aacute;c th&iacute; sinh phải li&ecirc;n tục s&aacute;ng tạo, quyết liệt tiến về<span class=\"s2\">&nbsp;ph&iacute;a trư</span>ớ<span class=\"s2\">c. Nh</span>ững th&iacute; sinh nhiều lầ<span class=\"s2\">n chưa đ</span>ảm bả<span class=\"s2\">o đư</span>ợ<span class=\"s2\">c ti&ecirc;u ch&iacute; n&agrave;y như Đ</span>ức Th&agrave;nh, Nguyễn Hợp, Hồng V&acirc;n, Hồ<span class=\"s2\">ng Xu&acirc;n, Hương Ly... đơn đi</span>ệu mộ<span class=\"s2\">t m&agrave;u như V</span>&otilde; Th&agrave;nh An, Ho&agrave;ng Anh T&uacute; tiếp tụ<span class=\"s2\">c đư</span>ợc cứu hết tậ<span class=\"s2\">p n&agrave;y đ</span>ến tập kh&aacute;c m&agrave; kh&ocirc;ng c&oacute;&nbsp;lời giải th&iacute;ch thỏa đ&aacute;ng.</p>\n<table class=\"tplCaption\" style=\"width: 550px; height: 390px; background-color: #e6e6ec;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 548px;\" src=\"http://c1.f9.img.vnecdn.net/2015/09/30/2-7896-1443581997.jpg\" alt=\"[Caption]\" height=\"365\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\" style=\"text-align: justify;\">Năng lực c&oacute; hạn nhưng Nguyễn Hợp (giữa) li&ecirc;n tục được cứu ở lại. Việc loại H\'Hen Ni&ecirc; (phải) l&agrave; trường hợp&nbsp;đ&aacute;ng tiếc của Next Top năm nay khiến kh&aacute;n giả bức x&uacute;c.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Đặc biệt l&agrave; trường hợp của Nguyễn Hợp. V&igrave; kỹ năng k&eacute;m, c&ocirc; g&aacute;i n&agrave;y từng bốn lần li&ecirc;n tiế<span class=\"s2\">p rơi v&agrave;o top nguy hi</span>ểm <span class=\"s2\">nhưng </span>kh&ocirc;ng bị<span class=\"s2\">&nbsp;đ&aacute;nh trư</span>ợt. Trong <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/thanh-hang-khien-khan-gia-day-song-vi-uu-ai-thi-sinh-kem-3282325.html\">tập 8</a>, quyết định loại H\'Hen Ni&ecirc; v&agrave; giữ Nguyễn Hợp khiến kh&aacute;n giả bức x&uacute;c.&nbsp;X&eacute;t cả qu&aacute; tr&igrave;nh, H\'Hen Ni&ecirc; l&agrave; th&iacute; sinh c&oacute; tiề<span class=\"s2\">m năng khi nhi</span>ều lầ<span class=\"s2\">n vư</span>ợt qua thử th&aacute;ch v&agrave; ch<span class=\"s2\">ưa bao gi</span>ờ<span class=\"s2\">&nbsp;rơi v&agrave;o nh&oacute;m c&oacute; nguy cơ bị loại. </span><span class=\"s2\">Trong khi Nguy</span>ễn Hợ<span class=\"s2\">p chưa l</span>ần n&agrave;o sở hữu bức ả<span class=\"s2\">nh đ</span>ẹp nhất tuần, bốn lầ<span class=\"s2\">n rơi v&agrave;o nh&oacute;m nguy hi</span>ểm v&agrave; lu&ocirc;n<span class=\"s2\"> đư</span>ợc cứu v&agrave;o ph&uacute;t ch&oacute;t.&nbsp;L&yacute; giải về việc cứu Nguyễn Hợp, ban gi&aacute;m khảo chỉ n&oacute;i ngắn gọn họ muố<span class=\"s2\">n cho c&ocirc; g&aacute;i n&agrave;y cơ h</span>ộ<span class=\"s2\">i đ</span>ể sử<span class=\"s2\">a đ</span>ổi bản th&acirc;n.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Ngo&agrave;i quyết định thiếu thuyết phục, bộ ba gi&aacute;m khảo năm nay c&ograve;n bị ch&ecirc; mờ nhạt v&igrave; chỉ thể hiện sự gần gũi hơn l&agrave; đưa ra c&aacute;c g&oacute;p &yacute; mạnh mẽ, thẳng thắn mang t&iacute;nh đ&agrave;o tạo.&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\"><strong><span style=\"color: #333333;\">Yếu tố quan trọng tạo th&agrave;nh c&ocirc;ng v&agrave; sức hấp dẫn của một m&ugrave;a giải l&agrave; chất lượng th&iacute; sinh. Năm nay, nh&acirc;n tố xứng đ&aacute;ng cho ng&ocirc;i vị Qu&aacute;n qu&acirc;n vẫn l&agrave; một c&acirc;u hỏi lớn d&ugrave; đường đua đ&atilde; gần kết th&uacute;c.</span></strong></p>\n<p class=\"Normal\" style=\"text-align: justify;\">Trong 14 th&iacute; sinh v&agrave;o nh&agrave; chung m&ugrave;a thứ s&aacute;u, kh&ocirc;ng c&aacute; nh&acirc;n n&agrave;o xuất sắc nổi trội. Hiện tại trong top 6, Hương Ly được gi&aacute;m khảo đ&aacute;nh gi&aacute; cao. Nhiều kh&aacute;n giả dự b&aacute;o c&ocirc; c&oacute; khả năng gi&agrave;nh vị tr&iacute; cao nhất. Tuy nhi&ecirc;n, so với những &Aacute; qu&acirc;n, Qu&aacute;n qu&acirc;n của c&aacute;c m&ugrave;a trước, c&ocirc; g&aacute;i n&agrave;y chưa c&oacute; thể hiện n&agrave;o g&acirc;y ấn tượng hay bứt ph&aacute; trong cả m&ugrave;a giải, phong độ trồi sụt thất thường với một lần chiến thắng duy nhất ở thử th&aacute;ch chụp ảnh tr&ecirc;n s&acirc;n băng.&nbsp;Những th&iacute; sinh c&ograve;n lại l&agrave; V&otilde; Th&agrave;nh An, Hồng Xu&acirc;n, Nguyễn Hợp, Đinh Đức Th&agrave;nh, K\'Brơi thường c&oacute; những tấm h&igrave;nh đơn điệu, biểu cảm một m&agrave;u, tạo d&aacute;ng ngh&egrave;o n&agrave;n.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Ở phần thi chụp h&igrave;nh dưới nước, gần như to&agrave;n bộ ảnh của c&aacute;c th&iacute; sinh đều nhận lời ch&ecirc; bai của kh&aacute;n giả. Nguyễn Hợp tạo d&aacute;ng như người đuối nước, Hồng V&acirc;n k&eacute;o ngắn người lại với tư thế xấu, biểu cảm của Đ&agrave;o Thị Thu c&ograve;n bộc lộ n&eacute;t sợ h&atilde;i tr&ecirc;n khu&ocirc;n mặt. Ngay cả bức ảnh đẹp nhất của Hồng Xu&acirc;n cũng kh&ocirc;ng l&agrave;m thỏa m&atilde;n số đ&ocirc;ng v&agrave; được đưa ra so s&aacute;nh với nhiều bộ ảnh chụp dưới nước của c&aacute;c phi&ecirc;n bản Next Top kh&aacute;c.</p>\n<table class=\"tplCaption\" style=\"width: 549px; height: 426px; background-color: #e6e6ec;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 547px;\" src=\"http://c1.f9.img.vnecdn.net/2015/09/30/1-9198-1443581997.jpg\" alt=\"1-9198-1443581997.jpg\" height=\"383\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\" style=\"text-align: justify;\">Chiến thắng trong thử th&aacute;ch chụp ảnh dưới nước,&nbsp;Hồng Xu&acirc;n (tr&aacute;i) được đem ra so s&aacute;nh với Nguyễn Tuyết của m&ugrave;a giải năm 2011 (phải, tr&ecirc;n) v&agrave; một phần thi c&oacute; c&ugrave;ng concept&nbsp;của th&iacute; sinh Next Top Phần Lan.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Số lượng th&iacute; sinh k&eacute;m trụ lại trong top 6 kh&aacute; nhiều. Điều n&agrave;y bị xem l&agrave; ti&ecirc;u ch&iacute; ưu ti&ecirc;n th&iacute; sinh c&oacute; yếu tố đặc biệt để c&acirc;u k&eacute;o kh&aacute;n giả. Thay v&igrave; giữ th&iacute; sinh c&oacute; nhiều tiềm năng như H\'Hen Ni&ecirc;, gi&aacute;m khảo lại nhiều lần ưu &aacute;i cho Nguyễn Hợp, Hồng Xu&acirc;n.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Sở hữu chiều <a href=\"http://giaitri.vnexpress.net/tin-tuc/truyen-hinh/co-gai-cao-1-9-m-ngat-xiu-truoc-giam-khao-next-top-3261305.html\">cao 1,9 m</a>, từ v&ograve;ng casting, Hồng Xu&acirc;n nhận được sự ch&uacute; &yacute; d&ugrave; ch&acirc;n d&agrave;i catwalk kh&ocirc;ng vững, chụp h&igrave;nh hay bặm m&ocirc;i, cơ mặt căng thẳng, biểu cảm tệ. Tương tự, Nguyễn Hợp l&agrave; c&ocirc; g&aacute;i c&aacute; t&iacute;nh mạnh, nhiều chi&ecirc;u tr&ograve;, th&aacute;i độ k&ecirc;nh kiệu. Nhưng điều n&agrave;y gi&uacute;p c&ocirc;&nbsp;g&acirc;y ch&uacute; &yacute; v&agrave; trụ lại với chương tr&igrave;nh.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Kh&aacute;n giả Nguyễn Ngọc Phương bức x&uacute;c: \"Hai th&iacute; sinh li&ecirc;n tục được cứu vớt (Xu&acirc;n, Hợp) kh&ocirc;ng kh&eacute;o lại đoạt lu&ocirc;n ng&ocirc;i qu&aacute;n qu&acirc;n, &aacute; qu&acirc;n. Những th&iacute; sinh tiềm năng vừa lọt nh&oacute;m nguy hiểm l&agrave; x&aacute;ch vali về lu&ocirc;n. Mấy m&ugrave;a trước l&agrave;m chuy&ecirc;n nghiệp bao nhi&ecirc;u th&igrave; m&ugrave;a n&agrave;y nghiệp dư bấy nhi&ecirc;u, từ concept chụp h&igrave;nh cho đến quần &aacute;o, chẳng thấy c&aacute;i n&agrave;o ra d&aacute;ng model high fashion\".</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Đỗ Mạnh Cường - gi&aacute;m khảo cuộc thi năm 2011 v&agrave; 2013 - cho hay trong số c&aacute;c th&iacute; sinh năm nay, anh đ&aacute;nh gi&aacute; cao H\'Hen Ni&ecirc;, Đ&agrave;o Thu, Kim Phương v&agrave; b&agrave;y tỏ sự tiếc nuối khi cả ba c&ocirc; g&aacute;i n&agrave;y đều đ&atilde; bị loại. Nh&agrave; thiết kế c&ograve;n tiết lộ nhiều th&iacute; sinh c&oacute; tiềm năng ở v&ograve;ng loại bikini bị bỏ qua đ&aacute;ng tiếc v&igrave; ti&ecirc;u ch&iacute; chương tr&igrave;nh năm nay thay đổi.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Trước những bất cập về t&iacute;nh chuy&ecirc;n m&ocirc;n, nội dung cũng như chất lượng th&iacute; sinh, đại diện nh&agrave; sản xuất khẳng định: \"Đ&acirc;y kh&ocirc;ng phải l&agrave; cuộc thi của những người mẫu chuy&ecirc;n nghiệp, những Top Model, m&agrave; l&agrave; cuộc thi d&agrave;nh cho những bạn trẻ c&ograve;n chưa biết nhiều về thời trang. Ch&uacute;ng t&ocirc;i t&igrave;m kiếm những gương mặt tiềm năng để đ&agrave;o tạo trở th&agrave;nh những người mẫu chuy&ecirc;n nghiệp trong tương lai - những Next Top Model\".</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Theo b&agrave; Quỳnh Trang - gi&aacute;m đốc sản xuất, họ kh&ocirc;ng kỳ vọng th&iacute; sinh lập tức tỏa s&aacute;ng v&agrave; trở th&agrave;nh người mẫu chuy&ecirc;n nghiệp ngay sau cuộc thi. \"Từ c&aacute;c m&ugrave;a đầu, ch&uacute;ng t&ocirc;i nhiều lần phải nhận \'gạch đ&aacute;\' của dư luận về những th&iacute; sinh đi tới v&ograve;ng cuối chương tr&igrave;nh, nhưng giờ h&atilde;y nh&igrave;n lại họ. Ho&agrave;ng Th&ugrave;y, Kha V&acirc;n, Ch&agrave; Mi, M&acirc;u Thủy... đều mất 1-2 năm r&egrave;n luyện để c&oacute; thể tự tin l&agrave;m việc v&agrave; gặt h&aacute;i th&agrave;nh c&ocirc;ng tại c&aacute;c Tuần thời trang lớn của thế giới\", b&agrave; Trang n&oacute;i.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>&Yacute; Ly</strong> (vnexpress.net)</p>','Vì sao Vietnam\'s Next Top Model 2015 giảm sức hút','','',0,'http://tamdiem.com.vn/sources/images/VN%20Model.jpg','',0,0,0,0,479,0,1443682989,1446529448,1),(127,' Đạo diễn trẻ làm MV xịn cho các ngôi sao ca nhạc ','-dao-dien-tre-lam-mv-xin-cho-cac-ngoi-sao-ca-nhac-',14,'news_item',' Đạo diễn trẻ, MV ca nhạc, Đàm Vĩnh Hưng, Hồ Ngọc Hà, Noo Phước Thịnh, Tóc Tiên, Issac.','MV (music video / video âm nhạc) ngày càng trở thành cụm từ quá quen thuộc, hầu như mỗi tuần (thậm chí mỗi ngày) đều có ca sĩ giới thiệu MV mới! ','MV (music video / video âm nhạc) ngày càng trở thành cụm từ quá quen thuộc, hầu như mỗi tuần (thậm chí mỗi ngày) đều có ca sĩ giới thiệu MV mới! ','<p><strong>MV (music video / video &acirc;m nhạc) ng&agrave;y c&agrave;ng trở th&agrave;nh cụm từ qu&aacute; quen thuộc, hầu như mỗi tuần (thậm ch&iacute; mỗi ng&agrave;y) đều c&oacute; ca sĩ giới thiệu MV mới!&nbsp;</strong></p>\n<div class=\"detail-content\">\n<div class=\"left-side\">\n<div class=\"fck\">\n<table class=\"desc_image slide_content\" style=\"height: 374px; background-color: #e6e6ec;\" title=\"Đạo diễn trẻ l&agrave;m MV xịn cho c&aacute;c ng&ocirc;i sao ca nhạc \" border=\"0\" width=\"492\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"Đạo diễn trẻ Nguyễn Tuấn Anh đ&atilde; mang đến cho Minh Hằng (đứng giữa) một diện mạo mới mẻ s&ocirc;i động trong MV Yolo mới nhất của c&ocirc; Ảnh: NVCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 490px; display: block; margin-left: auto; margin-right: auto;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/10/04/e1c7dfa1.jpg\" alt=\"Đạo diễn trẻ Nguyễn Tuấn Anh đ&atilde; mang đến cho Minh Hằng (đứng giữa) một diện mạo mới mẻ s&ocirc;i động trong MV Yolo mới nhất của c&ocirc; Ảnh: NVCC\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\">\n<p>&nbsp;Đạo diễn trẻ Nguyễn Tuấn Anh đ&atilde; mang đến cho Minh Hằng (đứng giữa) một diện mạo mới mẻ s&ocirc;i động trong MV Yolo mới nhất của c&ocirc; Ảnh: NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Đ&aacute;p ứng nhu cầu n&agrave;y, một lực lượng trẻ trung, năng động đ&atilde; v&agrave;o cuộc! Họ l&agrave; những 8X đời cuối, 9X đời đầu nhưng đ&atilde; tạo được nhiều dấu ấn ri&ecirc;ng với vai tr&ograve; đạo diễn trong lĩnh vực ca nhạc.</p>\n<p style=\"text-align: justify;\">Thời thượng, chịu kh&oacute; lăn xả, tự học nghề (nhiều người trong số họ vừa l&agrave; đạo diễn vừa ki&ecirc;m lu&ocirc;n dựng phim cho c&aacute;c MV) đến mức kh&ocirc;ng c&oacute; thời gian để... y&ecirc;u, nhưng hầu hết đều thừa nhận: đ&acirc;y l&agrave; c&ocirc;ng việc c&oacute; sức h&uacute;t kh&oacute; cưỡng.</p>\n<p class=\"ck_inner_title\" style=\"text-align: justify;\"><strong>V&igrave; họ biết r&otilde; người trẻ th&iacute;ch g&igrave;!</strong></p>\n<p style=\"text-align: justify;\">&Iacute;t ai biết đạo diễn &ldquo;cứng cựa&rdquo; đứng sau hơn cả trăm MV cho c&aacute;c ca sĩ ng&ocirc;i sao hạng A hiện nay như Đ&agrave;m Vĩnh Hưng, Hồ Ngọc H&agrave;, Noo Phước Thịnh, T&oacute;c Ti&ecirc;n, Issac... l&agrave; một c&ocirc; g&aacute;i trẻ kh&aacute; nh&uacute;t nh&aacute;t: Đinh H&agrave; Uy&ecirc;n Thư.</p>\n<p style=\"text-align: justify;\">Hơi sốc khi biết Thư mới sinh năm 1987, nhưng đ&atilde; c&oacute; gần bốn năm trải nghiệm trong vai tr&ograve; đạo diễn, dựng phim cho c&aacute;c MV được đầu tư khủng.</p>\n<p style=\"text-align: justify;\">Tốt nghiệp kh&oacute;a 7 khoa đạo diễn điện ảnh của ĐH S&acirc;n khấu - điện ảnh TP.HCM nhưng trước khi được đi đ&uacute;ng con đường đ&atilde; chọn lựa từ đầu, Uy&ecirc;n Thư lại b&eacute;n duy&ecirc;n trở th&agrave;nh một &ldquo;người h&aacute;t underground&rdquo; (Thư n&oacute;i c&ocirc; kh&ocirc;ng muốn d&ugrave;ng từ ca sĩ) bởi c&ocirc; đến với &acirc;m nhạc chỉ với l&ograve;ng đam m&ecirc; cuồng nhiệt.</p>\n<p style=\"text-align: justify;\">&ldquo;T&ocirc;i nghĩ việc m&ecirc; h&aacute;t v&agrave; y&ecirc;u nhạc đ&atilde; tạo cho t&ocirc;i một nhạc cảm tốt để khi đứng sau m&aacute;y quay, ngồi sau m&agrave;n h&igrave;nh t&ocirc;i lu&ocirc;n cảm được tiết tấu, giai điệu của b&agrave;i h&aacute;t, từ đ&oacute; c&oacute; những &yacute; tưởng mới, s&aacute;ng tạo cho MV của mỗi ca sĩ đặt h&agrave;ng&rdquo;.</p>\n<p style=\"text-align: justify;\">Cũng sinh năm 1987, Khương Vũ (Vũ Huy Khương) - đạo diễn đứng đằng sau h&agrave;ng loạt MV của Hiền Thục, Minh Hằng, Tr&agrave; My Idol, &Aacute;i Phương, Hari Won, Chi Pu, Gil L&ecirc;... th&igrave; chỉ c&oacute; một l&yacute; do để anh được tin tưởng, đ&oacute; l&agrave; khả năng s&aacute;ng tạo kh&ocirc;ng giới hạn với những video kh&aacute;... đi&ecirc;n rồ m&agrave; kh&ocirc;ng phải ai cũng dễ d&agrave;ng chấp nhận.</p>\n<p style=\"text-align: justify;\">Anh l&yacute; giải: &ldquo;Sản phẩm từ c&aacute;c b&agrave;n tay trẻ l&agrave;m ra sẽ dễ được giới trẻ đ&oacute;n nhận, v&igrave; họ biết r&otilde; người trẻ đang th&iacute;ch g&igrave;. Đ&acirc;y cũng l&agrave; đối tượng m&agrave; phần đ&ocirc;ng nghệ sĩ muốn nhắm tới&rdquo;.</p>\n<p style=\"text-align: justify;\">Khởi đầu l&agrave; một motion graphic (người l&agrave;m c&aacute;c h&igrave;nh hiệu opening cho c&aacute;c chương tr&igrave;nh tr&ecirc;n tivi), Khương Vũ dần chuyển qua c&ocirc;ng việc s&aacute;ng tạo, l&ecirc;n &yacute; tưởng cho c&aacute;c video bằng c&aacute;ch m&agrave;y m&ograve; tự học.</p>\n<p style=\"text-align: justify;\">Đến nay, c&aacute;i t&ecirc;n Khương Vũ đ&atilde; xuất hiện trong vai tr&ograve; đạo diễn hơn 30 MV chất lượng tốt, đặc biệt l&agrave; đạo diễn cho c&aacute;c Vlog của Phở Đặc Biệt - một vlogger c&oacute; h&agrave;ng triệu lượt xem mỗi clip tr&ecirc;n YouTube.</p>\n<p style=\"text-align: justify;\">Trong khi đ&oacute; Huỳnh Dũng Thanh Nh&acirc;n - c&ocirc; g&aacute;i lu&ocirc;n được nhắc đến với vai tr&ograve; l&agrave; em g&aacute;i ruột của cố đạo diễn Huỳnh Ph&uacute;c Điền - lại kh&ocirc;ng ngừng chứng tỏ năng lực của m&igrave;nh với vai tr&ograve; đạo diễn s&acirc;n khấu, khởi điểm ban đầu l&agrave; việc thực hiện c&aacute;c MV ca nhạc d&agrave;nh cho... thiếu nhi.</p>\n<p style=\"text-align: justify;\">Với MV <em>X&uacute;c xắc x&uacute;c sẻ</em> của b&eacute; Bảo An giữ kỷ lục 75 triệu lượt xem tr&ecirc;n YouTube (đến giờ vẫn chưa c&oacute; MV của ca sĩ n&agrave;o vượt qua nổi cột mốc n&agrave;y), Thanh Nh&acirc;n nhanh ch&oacute;ng trở th&agrave;nh đạo diễn c&oacute; nghề được săn đ&oacute;n.</p>\n<p style=\"text-align: justify;\">Hiện nay, c&ocirc; đang được giao trọng tr&aacute;ch mới l&agrave; đạo diễn game show <em>Người h&ugrave;ng t&iacute; hon </em>- một game show về t&igrave;m kiếm t&agrave;i năng ca nhạc nh&iacute; sẽ ph&aacute;t s&oacute;ng thời gian tới. Ri&ecirc;ng v&agrave;o đầu th&aacute;ng 10, c&ocirc; đạo diễn trẻ n&agrave;y được một nh&atilde;n h&agrave;ng c&ocirc;ng nghệ chọn lựa để thực hiện c&aacute;c tour ca nhạc d&agrave;nh cho sinh vi&ecirc;n của năm tỉnh th&agrave;nh, đưa những ca sĩ đang được y&ecirc;u th&iacute;ch v&agrave; c&aacute;c loại h&igrave;nh biểu diễn DJ đặc sắc đến với sinh vi&ecirc;n...</p>\n<p style=\"text-align: justify;\">V&agrave; c&ograve;n rất nhiều c&aacute;i t&ecirc;n ng&agrave;y c&agrave;ng quen thuộc hơn với giới l&agrave;m nhạc ở Việt Nam trong vai tr&ograve; đạo diễn - sản xuất c&aacute;c MV, c&aacute;c chương tr&igrave;nh ca nhạc c&oacute; dấu ấn ri&ecirc;ng như Nguyễn Tuấn Anh với dự &aacute;n mới ho&agrave;n th&agrave;nh l&agrave; đạo diễn cho album <em>Yolo </em>của ca sĩ Minh Hằng; Nguyễn Chung - đạo diễn cho những dự &aacute;n của C&ocirc;ng ty Tiếng h&aacute;t Việt - với sản phẩm mới nhất l&agrave; single <em>T&igrave;nh hoang phế </em>của th&iacute; sinh vừa bước ra khỏi cuộc thi <em>Giọng h&aacute;t Việt 2015</em> Phượng Vũ; hay Nguyễn Duy Đức (Aiden Nguyễn) - ch&agrave;ng trai sinh năm 1992 n&agrave;y l&agrave; người &ldquo;nh&agrave;o nặn&rdquo; n&ecirc;n c&aacute;c MV long lanh cho nh&oacute;m St319, đặc biệt MV <em>Lu&ocirc;n b&ecirc;n anh </em>của ca sĩ Min (nh&oacute;m St319) do anh thực hiện đ&atilde; c&aacute;n mốc 4 triệu lượt xem v&agrave;o giữa th&aacute;ng 9, d&ugrave; MV n&agrave;y chỉ được sản xuất với kinh ph&iacute; cực kỳ thấp!</p>\n<table class=\"desc_image slide_content\" style=\"height: 348px; background-color: #e6e6ec;\" title=\"Đạo diễn trẻ l&agrave;m MV xịn cho c&aacute;c ng&ocirc;i sao ca nhạc \" border=\"0\" width=\"495\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"H&igrave;nh ảnh trẻ trung gợi cảm của Hồ Ngọc H&agrave; trong MV What is love do Đinh H&agrave; Uy&ecirc;n Thư l&agrave;m đạo diễn - Ảnh: NVCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 490px;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/10/04/1f3976fe.jpg\" alt=\"H&igrave;nh ảnh trẻ trung gợi cảm của Hồ Ngọc H&agrave; trong MV What is love do Đinh H&agrave; Uy&ecirc;n Thư l&agrave;m đạo diễn - Ảnh: NVCC\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\">\n<p>&nbsp;H&igrave;nh ảnh trẻ trung gợi cảm của Hồ Ngọc H&agrave; trong MV What is love do Đinh H&agrave; Uy&ecirc;n Thư l&agrave;m đạo diễn - Ảnh: NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"ck_inner_title\">&nbsp;</p>\n<p class=\"ck_inner_title\"><strong>Chọn mặt gửi... MV</strong></p>\n<p style=\"text-align: justify;\">Trong giai đoạn hiện nay của l&agrave;ng nhạc, khi album l&agrave;m ra ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; lời v&agrave; kh&ocirc;ng phải lựa chọn th&ocirc;ng minh để tiếp thị một ca kh&uacute;c hay sản phẩm &acirc;m nhạc mới, việc ra mắt c&aacute;c MV một c&aacute;ch thường xuy&ecirc;n thật sự l&agrave; giải ph&aacute;p để nghệ sĩ kh&ocirc;ng bị l&atilde;ng qu&ecirc;n, đồng thời giới thiệu những h&igrave;nh ảnh mới của họ để từ đ&oacute; c&oacute; c&aacute;c hợp đồng quảng c&aacute;o, biểu diễn mới.</p>\n<p style=\"text-align: justify;\">Do vậy, chuyện phải &ldquo;chọn mặt gửi v&agrave;ng&rdquo; cho những tay m&aacute;y cừ l&agrave; đương nhi&ecirc;n. Hợp t&aacute;c gần đ&acirc;y với một v&agrave;i đạo diễn trẻ, nữ ca sĩ Hiền Thục nhận x&eacute;t:</p>\n<p style=\"text-align: justify;\">&ldquo;C&aacute;c tay m&aacute;y l&atilde;o th&agrave;nh cho t&ocirc;i kinh nghiệm, c&ograve;n c&aacute;c đạo diễn trẻ cho t&ocirc;i năng lượng khi l&agrave;m việc với họ. Họ vừa linh động, vừa hiện đại, vừa biết c&aacute;ch sắp xếp hợp l&yacute;, nhất l&agrave; &yacute; tưởng lu&ocirc;n tr&agrave;n trề. Đương nhi&ecirc;n khi c&ograve;n trẻ họ c&oacute; thể thiếu s&oacute;t kinh nghiệm l&agrave;m việc nhưng kh&ocirc;ng vấn đề g&igrave;, v&igrave; họ c&oacute; quyền được trải nghiệm&rdquo;.</p>\n<p style=\"text-align: justify;\">Tiếp tục hợp t&aacute;c với Đinh H&agrave; Uy&ecirc;n Thư trong một sản phẩm &acirc;m nhạc mới tinh sẽ ra mắt thời gian tới, ca sĩ Hồ Ngọc H&agrave; lại chia sẻ:</p>\n<p style=\"text-align: justify;\">&ldquo;Uy&ecirc;n Thư khi&ecirc;́n H&agrave; qu&yacute; m&ecirc;́n v&agrave; tin tưởng kh&ocirc;ng chỉ bởi Thư l&agrave; đạo di&ecirc;̃n trẻ, m&agrave; c&ograve;n v&igrave; Thư l&agrave; nữ nhi nhưng kh&ocirc;ng thua k&eacute;m b&acirc;́t kỳ đạo di&ecirc;̃n nam n&agrave;o. Ch&iacute;nh sự bản lĩnh của Thư v&agrave; c&ugrave;ng l&agrave; phụ nữ, lu&ocirc;n hi&ecirc;̉u v&agrave; đ&ocirc;̀ng cảm trong những &yacute; tưởng n&ecirc;n Thư l&agrave; đạo di&ecirc;̃n khi&ecirc;́n H&agrave; tin tưởng v&agrave; hợp t&aacute;c trong nhi&ecirc;̀u sản ph&acirc;̉m &acirc;m nhạc thời gian g&acirc;̀n đ&acirc;y&rdquo;.<br /> Quả thật Hồ Ngọc H&agrave; đ&atilde; c&oacute; một diện mạo vừa trẻ trung gợi cảm vừa thể hiện được sự mong manh, nữ t&iacute;nh của một c&ocirc; g&aacute;i đang y&ecirc;u khi xuất hiện trong MV <em>What is love </em>do Uy&ecirc;n Thư đảm nhiệm c&aacute;ch đ&acirc;y chưa l&acirc;u.</p>\n<p style=\"text-align: justify;\">G&oacute;p th&ecirc;m một &yacute; kiến về điều n&agrave;y, anh Tuấn Khanh - người thực hiện c&aacute;c dự &aacute;n cho ca sĩ Noo Phước Thịnh, gần nhất l&agrave; một dự &aacute;n thuộc h&agrave;ng khủng khi đưa cả &ecirc;kip sang đảo thi&ecirc;n đường Maldives ghi h&igrave;nh - cho biết:</p>\n<p style=\"text-align: justify;\">&ldquo;Lựa chọn đạo diễn trẻ cho c&aacute;c dự &aacute;n giờ đ&acirc;y l&agrave; xu thế chung v&igrave; họ c&oacute; c&aacute;ch l&agrave;m việc ăn rơ, hợp &yacute; với ca sĩ. Th&iacute; dụ như với &ecirc;kip thực hiện MV <em>Chuyện t&igrave;nh Maldives </em>lần n&agrave;y, với y&ecirc;u cầu thời gian chỉ quay trong một ng&agrave;y để tiết kiệm chi ph&iacute;, giữa đạo diễn v&agrave; ca sĩ phải l&agrave; người rất hợp v&agrave; hiểu &yacute; nhau, v&agrave; kh&ocirc;ng ai hiểu những người trẻ bằng ch&iacute;nh những người trẻ.</p>\n<p style=\"text-align: justify;\">Ngo&agrave;i ra tư duy hợp thời, hiện đại, nắm r&otilde; thị hiếu người xem của những đạo diễn n&agrave;y cũng l&agrave; một lợi thế, v&igrave; thế kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi nhiều nghệ sĩ với c&aacute;c sản phẩm &acirc;m nhạc &ldquo;khủng&rdquo; sẵn s&agrave;ng hợp t&aacute;c với những người trẻ ở thời điểm n&agrave;y&rdquo;.</p>\n<table class=\"desc_image slide_content\" style=\"height: 513px; background-color: #e6e6ec;\" title=\"Đạo diễn trẻ l&agrave;m MV xịn cho c&aacute;c ng&ocirc;i sao ca nhạc \" border=\"0\" width=\"492\" cellspacing=\"0\" cellpadding=\"0\" data-fancybox-group=\"gallery\" data-loading-text=\"Đạo diễn trẻ Khương Vũ chỉ đạo diễn xuất cho ca sĩ &Aacute;i Phương - Ảnh: NVCC\">\n<tbody>\n<tr>\n<td><img class=\"ck_image\" style=\"width: 490px;\" src=\"http://static.new.tuoitre.vn/tto/i/s626//2015/10/04/67345ee5.jpg\" alt=\"Đạo diễn trẻ Khương Vũ chỉ đạo diễn xuất cho ca sĩ &Aacute;i Phương - Ảnh: NVCC\" /></td>\n</tr>\n<tr>\n<td class=\"ck_legend caption\">\n<p>&nbsp;Đạo diễn trẻ Khương Vũ chỉ đạo diễn xuất cho ca sĩ &Aacute;i Phương - Ảnh: NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<br /><br />\n<table style=\"background-color: #e6e6ec; height: 179px;\" width=\"501\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><strong>T&ocirc;i c&oacute; hơi giật m&igrave;nh!</strong></p>\n<p style=\"text-align: justify;\">Với con mắt của một đạo diễn, quay phim từng l&agrave;m n&ecirc;n &ldquo;thương hiệu&rdquo; với nhiều ca sĩ trong thập ni&ecirc;n trước, đạo diễn Nguyễn Tranh th&iacute;ch th&uacute; chia sẻ:</p>\n<p style=\"text-align: justify;\">&ldquo;Thỉnh thoảng t&ocirc;i cũng c&oacute; xem qua một v&agrave;i MV mới do c&aacute;c bạn đạo diễn trẻ l&agrave;m, v&agrave; n&oacute;i thật l&agrave; c&oacute; hơi... giật m&igrave;nh về c&aacute;ch dựng h&igrave;nh rất tốt. C&oacute; thể l&acirc;u rồi t&ocirc;i kh&ocirc;ng l&agrave;m MV, &iacute;t tiếp cận với c&ocirc;ng nghệ v&agrave; những phần mềm hỗ trợ h&igrave;nh ảnh khi quay. Điều n&agrave;y g&oacute;p phần cho c&aacute;c bạn trẻ c&oacute; th&ecirc;m nhiều c&ocirc;ng cụ để hiện thực h&oacute;a &yacute; tưởng.</p>\n<p style=\"text-align: justify;\">Tuy nhi&ecirc;n, c&ocirc;ng cụ d&ugrave; t&acirc;n tiến cũng chỉ chiếm một phần, quan trọng t&ocirc;i thấy phần thẩm mỹ v&agrave; thẩm &acirc;m của c&aacute;c bạn đ&aacute;ng nể. Dựng MV th&igrave; tiết tấu l&agrave; quan trọng, nhất l&agrave; với những MV c&oacute; vũ đạo.</p>\n<p style=\"text-align: justify;\">T&ocirc;i cũng mong c&oacute; th&ecirc;m nhiều ca sĩ &ldquo;chịu chơi&rdquo;, thỉnh thoảng đầu tư cho MV của m&igrave;nh để c&aacute;c đạo diễn trẻ 8X - 9X kh&ocirc;ng bị hạn chế về &yacute; tưởng v&agrave; khả năng s&aacute;ng tạo&rdquo;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 386px; background-color: #e6e6ec;\" width=\"508\" data-fancybox-group=\"gallery\" data-loading-text=\"Đạo diễn trẻ Nguyễn Chung (tr&aacute;i) l&agrave;m việc c&ugrave;ng ca sĩ Phượng Vũ trong Mv đầu tay của c&ocirc; (1) - Ảnh NVCC\">\n<tbody>\n<tr>\n<td><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Nguyen Chung.jpg\" alt=\"\" width=\"500\" height=\"333\" /></td>\n</tr>\n<tr>\n<td>\n<p>&nbsp;Đạo diễn trẻ Nguyễn Chung (tr&aacute;i) l&agrave;m việc c&ugrave;ng ca sĩ Phượng Vũ trong Mv đầu tay của c&ocirc; (1) - Ảnh NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"background-color: #e6e6ec; height: 373px;\" width=\"508\" data-fancybox-group=\"gallery\" data-loading-text=\"Khương Vũ (cầm m&aacute;y quay) hợp t&aacute;c c&ugrave;ng ca sĩ Kimmese - Ảnh NVCC\">\n<tbody>\n<tr>\n<td><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Khuong Vu.jpg\" alt=\"\" width=\"500\" height=\"333\" /></td>\n</tr>\n<tr>\n<td>\n<p>&nbsp; Khương Vũ (cầm m&aacute;y quay) hợp t&aacute;c c&ugrave;ng ca sĩ Kimmese - Ảnh NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 377px; background-color: #e6e6e6;\" width=\"508\" data-fancybox-group=\"gallery\" data-loading-text=\"Đinh H&agrave; Uy&ecirc;n Thư (đội mũ) tr&ecirc;n phim trường c&ugrave;ng c&aacute;c diễn vi&ecirc;n trẻ của Chiến dich chống ế phần 2 - Ảnh NVCC\">\n<tbody>\n<tr>\n<td><img id=\"__mcenew\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Uyen Thu.jpg\" alt=\"\" width=\"500\" height=\"333\" /></td>\n</tr>\n<tr>\n<td>\n<p>&nbsp; Đinh H&agrave; Uy&ecirc;n Thư (đội mũ) tr&ecirc;n phim trường c&ugrave;ng c&aacute;c diễn vi&ecirc;n trẻ của Chiến dich chống ế phần 2 - Ảnh NVCC</p>\n</td>\n</tr>\n</tbody>\n</table>\n<br class=\"ck_inner_title\" />\n<div class=\"wrapper-qt\">\n<div class=\"qtam_social btn-qt\">\n<div style=\"float: left;\">&nbsp;</div>\n<div id=\"like_thread\" style=\"float: left;\">&nbsp;</div>\n</div>\n</div>\n<div class=\"wrapper-qt\" style=\"text-align: right;\"><strong>MINH TRANG </strong>(tuoitre.com.vn)</div>\n</div>\n</div>\n</div>',' Đạo diễn trẻ làm MV xịn cho các ngôi sao ca nhạc ','','',0,'http://tamdiem.com.vn/sources/images/Minh%20Hang.jpg','',0,0,0,0,478,0,1444014591,1445925850,1),(128,'Đạo diễn phim - Nghề “hốt bạc”?','dao-dien-phim-nghe-hot-bac',14,'news_item','Đạo diễn phim, nghề hốt bạc, Trương Dũng (Sóng Vàng), Nhâm Minh Hiền, Đặng Lưu Việt Bảo, Võ Việt Hùng (M&T Pictures)','Thu nhập hấp dẫn của nghề đạo diễn đã khiến nhiều người đổ xô đi học làm “vua trường quay” hoặc không có chuyên môn, kinh nghiệm cũng gia nhập “cuộc chơi” hái ra tiền này. ','Thu nhập hấp dẫn của nghề đạo diễn đã khiến nhiều người đổ xô đi học làm “vua trường quay” hoặc không có chuyên môn, kinh nghiệm cũng gia nhập “cuộc chơi” hái ra tiền này.','<p><strong>Thu nhập hấp dẫn của nghề đạo diễn đ&atilde; khiến nhiều người đổ x&ocirc; đi học l&agrave;m &ldquo;vua trường quay&rdquo; hoặc kh&ocirc;ng c&oacute; chuy&ecirc;n m&ocirc;n, kinh nghiệm cũng gia nhập &ldquo;cuộc chơi&rdquo; h&aacute;i ra tiền n&agrave;y.</strong></p>\n<p style=\"text-align: justify;\">Qua rồi c&aacute;i thời đạo diễn nằm d&agrave;i chờ phim, phải sống bằng lương hoặc nhận số tiền c&aacute;t-s&ecirc; &iacute;t ỏi kh&ocirc;ng đủ sống với nghề. Thị trường sản xuất v&agrave; kinh doanh phim truyền h&igrave;nh đang ng&agrave;y c&agrave;ng s&ocirc;i động khiến đạo diễn ng&agrave;y c&agrave;ng đắt s&ocirc;, được trọng dụng, chọn lựa những dự &aacute;n theo &yacute; muốn (đạo diễn c&oacute; t&ecirc;n tuổi) v&agrave; c&oacute; cơ hội l&agrave;m nghề dễ d&agrave;ng (đạo diễn trẻ).</p>\n<p style=\"text-align: justify;\"><strong>1 tỉ đồng/năm l&agrave; chuyện thường</strong></p>\n<p style=\"text-align: justify;\">Hiện nay, d&ugrave; nh&agrave; nước hay tư nh&acirc;n, đa số đều &aacute;p dụng phương thức trả c&aacute;t-s&ecirc; theo thỏa thuận từng tập phim. Tr&ecirc;n mặt bằng chung hiện nay, c&aacute;t-s&ecirc; cho đạo diễn trung b&igrave;nh dao động từ 8-12 triệu đồng/tập. Nếu t&iacute;nh một phim khoảng 30 tập, quay trong v&ograve;ng 2 th&aacute;ng theo kế hoạch của nh&agrave; sản xuất (NSX) để đ&aacute;p ứng tiến độ ph&aacute;t s&oacute;ng, đạo diễn c&oacute; thể bỏ t&uacute;i khoảng 300 triệu đồng/phim. Những đạo diễn c&oacute; t&ecirc;n tuổi, ăn kh&aacute;ch hiện giờ như Xu&acirc;n Phước, V&otilde; Việt H&ugrave;ng, Hồ Ngọc Xum, Trương Dũng, Nh&acirc;m Minh Hiền&hellip; thường l&agrave;m 3 phim/năm, thu nhập 1 tỉ đồng mỗi năm l&agrave; b&igrave;nh thường.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_250443\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:2015/8-chot-8-1444835444526/dao-dien-phim-nghe-hot-bac.jpg\" alt=\"Đạo diễn Xu&acirc;n Phước chỉ đạo diễn xuất tr&ecirc;n trường quay. (Ảnh do nh&acirc;n vật cung cấp)\" /></div>\n<div class=\"PhotoCMS_Caption\"><em><span style=\"background-color: #ffffff; color: #999999;\">Đạo diễn Xu&acirc;n Phước chỉ đạo diễn xuất tr&ecirc;n trường quay. (Ảnh do nh&acirc;n vật cung cấp)</span></em></div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Trong thời buổi l&agrave;m phim truyền h&igrave;nh &ldquo;đ&aacute;nh nhanh, thắng nhanh&rdquo; để tiết kiệm chi ph&iacute; của NSX, một đạo diễn ăn kh&aacute;ch tiết lộ anh l&agrave;m tới 4-5 phim/năm. Như vậy, thu nhập sẽ cao hơn con số trung b&igrave;nh 1 tỉ đồng rất nhiều. Vị đạo diễn n&agrave;y cho biết khi cơ hội đến, anh kh&ocirc;ng thể bỏ qua d&ugrave; l&agrave;m xong c&oacute; thể &ldquo;tắt thở&rdquo; v&agrave; chất lượng phim kh&ocirc;ng tốt. Cũng theo tiết lộ của c&aacute;c NSX, t&ugrave;y t&ecirc;n tuổi, đẳng cấp của đạo diễn, mức độ &ldquo;ho&agrave;nh tr&aacute;ng&rdquo; của phim v&agrave; để cạnh tranh với c&aacute;c h&atilde;ng kh&aacute;c, họ kh&ocirc;ng ngại chi nhiều tiền để mời đạo diễn. Hiện nay, tuy kh&ocirc;ng độc quyền nhưng c&oacute; những đạo diễn lu&ocirc;n được c&aacute;c NSX &ldquo;chọn mặt gửi v&agrave;ng&rdquo; như: Trương Dũng (S&oacute;ng V&agrave;ng), Nh&acirc;m Minh Hiền, Đặng Lưu Việt Bảo, V&otilde; Việt H&ugrave;ng (M&amp;T Pictures)&hellip; Đ&atilde; c&oacute; những đạo diễn được trả đến 15 triệu đồng/tập phim. Đại diện một NSX cho rằng với những đạo diễn c&oacute; t&ecirc;n tuổi, l&agrave;m nghề nghi&ecirc;m t&uacute;c, bảo đảm chất lượng phim th&igrave; mức c&aacute;t-s&ecirc; tr&ecirc;n l&agrave; xứng đ&aacute;ng. Tất nhi&ecirc;n, với những đạo diễn trẻ, &iacute;t t&ecirc;n tuổi, con số n&agrave;y chỉ ở mức 6-7 triệu đồng/tập, thậm ch&iacute; 4-5 triệu đồng/tập k&egrave;m theo những lời hứa thưởng th&ecirc;m nếu phim đạt chỉ số người xem theo y&ecirc;u cầu của nh&agrave; đ&agrave;i.</p>\n<p style=\"text-align: justify;\">Theo Xu&acirc;n Phước, trước đ&acirc;y một đạo diễn l&agrave;m được 2 phim/năm l&agrave; mơ ước. Đa phần c&aacute;c h&atilde;ng phim nh&agrave; nước kho&aacute;n từ A - Z cho đạo diễn nhưng với kinh ph&iacute; cho một bộ phim khi&ecirc;m tốn như l&acirc;u nay, đạo diễn kh&oacute; hưởng lợi được số tiền dư. Hiện nay, một v&agrave;i h&atilde;ng phim tư nh&acirc;n vẫn c&ograve;n trả c&aacute;t-s&ecirc; cho đạo diễn theo h&igrave;nh thức kho&aacute;n. Nhờ kinh ph&iacute; kho&aacute;n cho một phim tăng l&ecirc;n nhiều, những đạo diễn biết t&iacute;nh to&aacute;n, l&agrave;m nhanh, gọn, rẻ th&igrave; c&oacute; thể hưởng được số tiền dư đ&aacute;ng kể. Kh&ocirc;ng ai phủ nhận rằng v&agrave;i năm trở lại đ&acirc;y, th&ugrave; lao của đạo diễn phim truyền h&igrave;nh ng&agrave;y c&agrave;ng được cải thiện d&ugrave; kh&ocirc;ng tăng vọt hay đạt những con số ngất ngưởng như ở c&aacute;c nước tr&ecirc;n thế giới. Thế nhưng, thu nhập h&agrave;ng trăm triệu đồng mỗi phim v&agrave; cả tỉ đồng hoặc hơn thế mỗi năm kh&ocirc;ng c&ograve;n l&agrave; giấc mơ của c&aacute;c đạo diễn.</p>\n<p style=\"text-align: justify;\"><strong>Cầu cao hơn cung</strong></p>\n<p style=\"text-align: justify;\">V&agrave;i năm trở lại đ&acirc;y, nghề đạo diễn trở th&agrave;nh nghề &ldquo;hot&rdquo; khi số lượng phim truyền h&igrave;nh tăng, nhu cầu đạo diễn cũng tăng vọt. Song, lượng phim sản xuất ng&agrave;y một nhiều nhưng lực lượng l&agrave;m phim, nhất l&agrave; đạo diễn c&oacute; tay nghề cao, bao năm nay vẫn kh&ocirc;ng tăng. Khi cung - cầu nh&acirc;n lực mất c&acirc;n đối, những gương mặt mới từ c&aacute;c vai tr&ograve; kh&aacute;c như &nbsp;kịch vụ, diễn vi&ecirc;n, quay phim, &aacute;nh s&aacute;ng&hellip; chuyển qua l&agrave;m đạo diễn cũng kh&aacute; nhiều. Tuy kh&ocirc;ng đảm nhận những dự &aacute;n lớn của c&aacute;c NSX uy t&iacute;n nhưng lực lượng n&agrave;y đang l&agrave;m phim đều đặn bởi hiện nay c&oacute; h&agrave;ng chục NSX tư nh&acirc;n tham gia sản xuất phim.</p>\n<p style=\"text-align: justify;\">Trong số đ&oacute;, c&oacute; người được học qua c&aacute;c lớp đạo diễn nhưng cũng c&oacute; kh&ocirc;ng &iacute;t người l&agrave;m nghề theo kiểu &ldquo;nghề truyền nghề&rdquo;. Những c&aacute;i t&ecirc;n như Qu&aacute;ch Khoa Nam, L&ecirc; Minh, Hạnh Th&uacute;y, Tấn Beo, L&yacute; Hải&hellip; đều từ diễn vi&ecirc;n chuyển qua l&agrave;m đạo diễn. Hiện nay, rất nhiều diễn vi&ecirc;n c&oacute; &yacute; định sẽ l&agrave;m đạo diễn.</p>\n<p style=\"text-align: justify;\">Đạo diễn V&otilde; Việt H&ugrave;ng cho rằng lực lượng đạo diễn hiện nay kh&ocirc;ng c&ograve;n khan hiếm v&agrave; dự b&aacute;o v&agrave;i năm nữa sẽ &ldquo;nở nồi&rdquo;. Trong đ&oacute;, nhiều gương mặt nữ sẽ l&agrave;m đạo diễn b&ecirc;n cạnh Hạnh Th&uacute;y, Việt Trinh, Hồng Ng&acirc;n, Trần Quế Ngọc, Đặng Th&aacute;i Huyền, Nguyễn Ho&agrave;ng Điệp&hellip;</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<table style=\"border-color: #000000; padding: 10px; overflow: hidden; background-color: #c0d6ff;\" border=\"1\" cellspacing=\"10\" cellpadding=\"10\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: center;\"><strong>Đạo diễn cần c&oacute; năng lực</strong></p>\n<p style=\"text-align: justify;\">Một đạo diễn kỳ cựu thốt l&ecirc;n: &ldquo;Thời của t&ocirc;i, muốn l&agrave;m đạo diễn kh&oacute; lắm! Phải học h&agrave;nh đ&agrave;ng ho&agrave;ng, ra trường cọ x&aacute;t với thực tế v&agrave;i ba năm cũng chưa chắc được đứng vai tr&ograve; đạo diễn. C&ograve;n b&acirc;y giờ, c&oacute; cảm gi&aacute;c ai cũng l&agrave;m đạo diễn được&rdquo;. Đạo diễn Xu&acirc;n Phước cho rằng c&oacute; thể ở một kh&iacute;a cạnh n&agrave;o đ&oacute;, nghề đạo diễn đang &ldquo;h&aacute;i ra tiền&rdquo;, trở th&agrave;nh một miếng b&aacute;nh ngon m&agrave; ai cũng muốn nhảy v&agrave;o để chia phần. Song, cũng kh&ocirc;ng &iacute;t người theo nghề v&igrave; đam m&ecirc; thực sự, v&igrave; muốn khẳng định t&agrave;i năng, t&ecirc;n tuổi ở một vai tr&ograve; kh&aacute;c. Theo khẳng định của người l&agrave;m nghề, đạo diễn kh&ocirc;ng phải chỉ c&oacute; đam m&ecirc; m&agrave; cần phải c&oacute; năng lực.</p>\n<p style=\"text-align: justify;\">Mặc kệ l&agrave; c&oacute; năng lực hay kh&ocirc;ng, c&aacute;c đạo diễn kh&ocirc;ng chuy&ecirc;n cho rằng đ&atilde; thấy được khả năng kiếm tiền v&agrave; h&agrave;nh nghề dễ d&agrave;ng. &ldquo;T&ocirc;i kh&ocirc;ng sợ cạnh tranh, thất nghiệp. Bởi lẽ, c&aacute;c NSX hiện nay phải chạy đua với số lượng phim để kịp đ&aacute;p ứng lịch ph&aacute;t s&oacute;ng của nh&agrave; đ&agrave;i, v&igrave; vậy họ rất cần đạo diễn. Nhiều NSX kh&ocirc;ng đ&ograve;i hỏi đạo diễn l&agrave;m phim c&oacute; chất lượng hay kh&ocirc;ng, miễn c&oacute; l&agrave; mừng rồi&rdquo; - một đạo diễn đi l&ecirc;n từ diễn vi&ecirc;n chia sẻ.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><strong>HẠ NGUY&Ecirc;N</strong> (nld.com.vn)</div>','Đạo diễn phim - Nghề hốt bạc','','',0,'http://tamdiem.com.vn/sources/images/dao%20dien%20Xuan%20Phuoc.jpg','',0,0,0,0,477,0,1444962069,1453191251,1),(129,'America\'s Next Top Model bị khai tử','americas-next-top-model-bi-khai-tu',14,'news_item','America\'s Next Top Model bị khai tử','Thông tin chương trình America\'s Next Top Model sẽ bị khai tử sau mùa 22 vừa được đại diện kênh CW xác nhận là thật','Thông tin chương trình America\'s Next Top Model sẽ bị khai tử sau mùa 22 vừa được đại diện kênh CW xác nhận là thật','<p style=\"text-align: justify;\"><strong>Trong khi cuộc đua năm nay đang tới hồi gay cấn, si&ecirc;u mẫu Tyra Banks bất ngờ th&ocirc;ng b&aacute;o tr&ecirc;n trang c&aacute; nh&acirc;n: \"T&ocirc;i nghĩ cuộc thi n&ecirc;n dừng lại sau m&ugrave;a 22\".</strong></p>\n<div class=\"fck_detail width_common\">\n<p class=\"Normal\" style=\"text-align: justify;\">Th&ocirc;ng tin chương tr&igrave;nh <em>America\'s Next Top Model</em> sẽ bị khai tử sau m&ugrave;a 22 vừa được đại diện k&ecirc;nh <em>CW</em> x&aacute;c nhận l&agrave; thật. &Ocirc;ng Mark Pedowitz, chủ tịch của <em>CW</em>, cho biết: \"Đ&acirc;y l&agrave; một chương tr&igrave;nh th&agrave;nh c&ocirc;ng. Kh&ocirc;ng chỉ c&oacute; tỷ lệ người xem cao, n&oacute; c&ograve;n c&oacute; sức ảnh hưởng to&agrave;n cầu. T&ocirc;i muốn cảm ơn Tyra v&agrave; nh&agrave; đồng s&aacute;ng lập Ken Mok v&igrave; những nỗ lực suốt nhiều năm qua\".</p>\n<table class=\"tplCaption\" style=\"width: 409px; height: 374px; background-color: #e6e6ec; margin-left: auto; margin-right: auto;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td style=\"text-align: right;\"><img style=\"width: 477px; display: block; margin-left: auto; margin-right: auto;\" src=\"http://c1.f9.img.vnecdn.net/2015/10/15/b-9849-1444905454.jpg\" alt=\"americas-next-top-model-bi-khai-tu\" height=\"318\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Tyra Banks l&agrave; người đồng s&aacute;ng lập chương tr&igrave;nh t&igrave;m kiếm người mẫu đ&igrave;nh đ&aacute;m tr&ecirc;n truyền h&igrave;nh Mỹ.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Tyra Banks t&acirc;m sự: \"T&ocirc;i tin đ&acirc;y l&agrave; thời điểm th&iacute;ch hợp. D&ugrave; sao t&ocirc;i cũng rất tự h&agrave;o với những g&igrave; chương tr&igrave;nh đ&atilde; l&agrave;m được. Đ&acirc;y l&agrave; chương tr&igrave;nh truyền h&igrave;nh thực tế đầu ti&ecirc;n về thời trang. Trước đ&oacute;, t&ocirc;i kh&ocirc;ng bao giờ nghĩ &yacute; tưởng nhỏ b&eacute; của m&igrave;nh lại c&oacute; sức ảnh hưởng đến to&agrave;n thế giới. V&agrave; cũng kh&ocirc;ng thể tin ch&uacute;ng t&ocirc;i c&oacute; thể k&eacute;o d&agrave;i cuộc thi đến m&ugrave;a thứ 22. Chương tr&igrave;nh c&oacute; được th&agrave;nh c&ocirc;ng như ng&agrave;y h&ocirc;m nay l&agrave; nhờ v&agrave;o kh&aacute;n giả. Điều đ&oacute; thật tuyệt vời v&agrave; vượt xa những g&igrave; t&ocirc;i từng mơ\".</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Si&ecirc;u mẫu cho biết c&ocirc; chuẩn bị ra show mới n&oacute;i về sự nh&agrave;m ch&aacute;n của việc trở n&ecirc;n ho&agrave;n hảo. C&ocirc; mong nhận được sự ủng hộ nhiệt t&igrave;nh từ người h&acirc;m mộ. \"T&ocirc;i hy vọng c&aacute;c bạn lu&ocirc;n y&ecirc;u những đốm t&agrave;n nhang tr&ecirc;n mặt, đ&ocirc;i mắt to, vầng tr&aacute;n rộng hay v&ograve;ng một l&eacute;p... H&atilde;y nhớ lấy một điều, c&aacute;c bạn lu&ocirc;n xinh đẹp theo c&aacute;ch của ri&ecirc;ng m&igrave;nh\", Tyra n&oacute;i th&ecirc;m.</p>\n<table class=\"tplCaption\" style=\"width: 432px; height: 382px; background-color: #e6e6ec;\" border=\"0\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 494px; display: block; margin-left: auto; margin-right: auto;\" src=\"http://c1.f9.img.vnecdn.net/2015/10/15/a-5382-1444905454.jpg\" alt=\"americas-next-top-model-bi-khai-tu-1\" height=\"329\" data-pwidth=\"470.4\" data-width=\"500\" data-natural-width=\"500\" /></td>\n</tr>\n<tr>\n<td>\n<p class=\"Image\">Cuộc thi t&igrave;m kiếm người mẫu của Mỹ sẽ ngưng ph&aacute;t s&oacute;ng sau khi kết th&uacute;c m&ugrave;a 22. Ảnh: <em>Variety.</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: justify;\">&nbsp;</p>\n<p class=\"Normal\" style=\"text-align: justify;\"><em>America\'s Next Top Model</em> lần đầu ra mắt kh&aacute;n giả Mỹ v&agrave;o 2003. Sau hơn 12 năm tổ chức, chương tr&igrave;nh t&igrave;m được nhiều gương mặt s&aacute;ng gi&aacute; cho l&agrave;ng mẫu như Analeigh Tipton, Adrianne Curry, CariDee English, Whitney Thompson, Dani Evans, Eva Marcille... Ngo&agrave;i những lời khen, cuộc thi gần đ&acirc;y cũng nhận kh&ocirc;ng &iacute;t b&igrave;nh luận tr&aacute;i chiều, từ việc ngừng hợp t&aacute;c với gi&aacute;m khảo l&acirc;u năm như Nigel Barker, J. Alexander, Jay Manuel v&agrave;o 2012 cho đến chuyện tăng kịch t&iacute;nh bằng c&aacute;ch đưa những th&iacute; sinh g&acirc;y tranh c&atilde;i v&agrave;o v&ograve;ng trong.</p>\n<p class=\"Normal\" style=\"text-align: justify;\">Chung kết&nbsp;<em>America\'s Next Top Model</em> m&ugrave;a 22 dự kiến l&ecirc;n s&oacute;ng v&agrave;o ng&agrave;y 4/12 tr&ecirc;n k&ecirc;nh CW. Tuy vậy, nh&agrave; sản xuất đang b&agrave;n bạc để dời lịch ph&aacute;t s&oacute;ng v&agrave;o năm sau.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đức Tr&iacute; </strong>(vnexpress.net)<strong><br /></strong></p>\n</div>','America\'s Next Top Model bị khai tử','','',0,'http://tamdiem.com.vn/sources/images/Tyra%20Banks.jpg','',0,0,0,0,476,0,1444963059,1446529620,1),(130,'Diễn viên nhí: Hiếm hoi và lãng phí ','dien-vien-nhi-hiem-hoi-va-lang-phi-',14,'news_item','Diễn viên nhí, Đạo diễn Victor Vũ, Tôi thấy hoa vàng trên cỏ xanh','Tìm được diễn viên nhí tài năng không dễ nhưng chẳng có chiến lược đào tạo nguồn nhân lực từ những tài năng này ','Tìm được diễn viên nhí tài năng không dễ nhưng chẳng có chiến lược đào tạo nguồn nhân lực từ những tài năng này ','<p><strong><span style=\"color: #333333;\"><em>T&igrave;m được diễn vi&ecirc;n nh&iacute; t&agrave;i năng kh&ocirc;ng dễ nhưng chẳng c&oacute; chiến lược đ&agrave;o tạo nguồn nh&acirc;n lực từ những t&agrave;i năng n&agrave;y.</em></span></strong></p>\n<div id=\"divNewsContent\" class=\"contentdetail\" data-field=\"body\">\n<p style=\"text-align: justify;\">Để truyền tải những cảm x&uacute;c đẹp đẽ về tuổi thơ trong s&aacute;ng, hồn nhi&ecirc;n trong phim &ldquo;T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh&rdquo; c&oacute; sự g&oacute;p c&ocirc;ng rất lớn của 3 diễn vi&ecirc;n nh&iacute;: Thịnh Vinh, Trọng Khang, Thanh Mỹ thể hiện bộ ba vai diễn: Thiều - Tường - Mận. &ldquo;Những thi&ecirc;n thần&rdquo; của đạo diễn Victor Vũ đ&atilde; h&oacute;a th&acirc;n th&agrave;nh những c&ocirc;, cậu b&eacute; th&ocirc;n qu&ecirc; ngh&egrave;o v&ugrave;ng ven biển bằng tất cả sự tự nhi&ecirc;n, ng&acirc;y thơ v&agrave; cảm x&uacute;c tinh kh&ocirc;i sẵn c&oacute;. Đ&atilde; l&acirc;u lắm rồi sau những H&ugrave;ng Thuận hay Lan H&agrave;, ch&uacute;ng ta mới c&oacute; lại những diễn vi&ecirc;n nh&iacute; nổi bật v&agrave; ấn tượng như vậy. Tuy nhi&ecirc;n, việc t&igrave;m kiếm v&agrave; đ&agrave;o tạo t&agrave;i năng diễn vi&ecirc;n nh&iacute; đang l&agrave; vấn đề quan t&acirc;m của người trong giới.</p>\n<p style=\"text-align: justify;\"><strong>T&igrave;m kiếm kh&ocirc;ng dễ</strong></p>\n<p style=\"text-align: justify;\">Đạo diễn Victor Vũ cho biết khi bắt tay thực hiện bộ phim &ldquo;T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh&rdquo;, một trong những điều anh lo lắng, trăn trở nhất l&agrave; l&agrave;m sao t&igrave;m được 3 diễn vi&ecirc;n nh&iacute; ph&ugrave; hợp với nh&acirc;n vật. Bởi những đứa trẻ l&agrave; &ldquo;linh hồn&rdquo; của bộ phim, nếu diễn vi&ecirc;n kh&ocirc;ng tốt sẽ dễ&hellip; thất bại. Phải mất một thời gian d&agrave;i từ việc lựa chọn ra 50 bức ảnh từ h&agrave;ng trăm bức ảnh được gửi về cho đến kh&acirc;u catsting (thử vai), Victor Vũ mới chọn được 3 gương mặt l&agrave; Thanh Mỹ, Trọng Khang v&agrave; Thịnh Vinh. Cả 3 gương mặt n&agrave;y đều đ&atilde; tham gia đ&oacute;ng phim trước đ&oacute;. C&oacute; lẽ trong số c&aacute;c diễn vi&ecirc;n nh&iacute; n&agrave;y, kh&ocirc;ng c&oacute; nhiều gương mặt s&aacute;ng gi&aacute;, vừa hợp vai lại vừa c&oacute; khả năng diễn xuất đ&atilde; buộc đạo diễn phải chọn 3 gương mặt quen thuộc. Thế mới thấy để t&igrave;m được diễn vi&ecirc;n nh&iacute; mới, mang t&iacute;nh ph&aacute;t hiện thật sự kh&ocirc;ng dễ d&agrave;ng.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_254705\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/chot-8-1445873208329/dien-vien-nhi-hiem-hoi-va-lang-phi.jpg\" alt=\"Đạo diễn Victor Vũ (tr&aacute;i) hướng dẫn diễn xuất cho diễn vi&ecirc;n nh&iacute; tr&ecirc;n trường quay phim &ldquo;T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh&rdquo; (Ảnh do nh&agrave; sản xuất cung cấp)\" /></div>\n<div class=\"PhotoCMS_Caption\"><span style=\"color: #999999;\"><em>Đạo diễn Victor Vũ (tr&aacute;i) hướng dẫn diễn xuất cho diễn vi&ecirc;n nh&iacute; tr&ecirc;n trường quay phim </em></span></div>\n<div class=\"PhotoCMS_Caption\"><span style=\"color: #999999;\"><em>&ldquo;T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh&rdquo; (Ảnh do nh&agrave; sản xuất cung cấp)</em></span></div>\n</div>\n<p style=\"text-align: justify;\">L&agrave; người c&oacute; kinh nghiệm l&agrave;m phim thiếu nhi gần 10 năm nay nhưng đạo diễn L&ecirc; Bảo Trung mới đ&acirc;y khi l&agrave;m phim &ldquo;Bảo mẫu si&ecirc;u quậy&rdquo; đ&atilde; chia sẻ: &ldquo;T&ocirc;i phải trằn trọc rất nhiều đ&ecirc;m v&igrave; kh&ocirc;ng biết m&igrave;nh sẽ t&igrave;m diễn vi&ecirc;n ở đ&acirc;u v&agrave; liệu c&oacute; t&igrave;m ra gương mặt ph&ugrave; hợp với &yacute; muốn ban đầu hay kh&ocirc;ng&rdquo;. Cuối c&ugrave;ng, anh cũng phải chạy đ&ocirc;n chạy đ&aacute;o đi l&ugrave;ng sục khắp mọi nơi, từ trường học đến c&aacute;c nh&agrave; văn h&oacute;a trong th&agrave;nh phố v&agrave; c&aacute;c tỉnh l&acirc;n cận, nhờ bạn b&egrave;, người th&acirc;n giới thiệu. Đạo diễn Tường Phương từng phải &ldquo;trần ai&rdquo; đi khắp nơi suốt mấy th&aacute;ng liền m&agrave; kh&ocirc;ng t&igrave;m được 3 diễn vi&ecirc;n nh&iacute; ph&ugrave; hợp cho phim của m&igrave;nh, cuối c&ugrave;ng phải l&ecirc;n tận Ch&acirc;u Đốc mới thấy. &ldquo;Đ&acirc;y l&agrave; c&ocirc;ng việc rất gian nan, mất qu&aacute; nhiều thời gian v&agrave; c&ocirc;ng sức. Đ&atilde; kh&ocirc;ng &iacute;t lần t&ocirc;i định bỏ cuộc&rdquo; - đạo diễn L&ecirc; Bảo Trung n&oacute;i. Đạo diễn Nhuệ Giang kh&ocirc;ng &iacute;t lần nản ch&iacute; khi cứ phải t&igrave;m kiếm diễn vi&ecirc;n nh&iacute; r&ograve;ng r&atilde; suốt 6 th&aacute;ng cho vai Thu trong phim &lsquo;T&acirc;m hồn mẹ&rdquo;.</p>\n<p style=\"text-align: justify;\">Đạo diễn n&agrave;o cũng mong muốn diễn vi&ecirc;n nh&iacute; phải đ&aacute;p ứng được 2 ti&ecirc;u ch&iacute; l&agrave; ngoại h&igrave;nh hợp vai v&agrave; biết diễn xuất. Song, n&oacute;i như đạo diễn Nguyễn Minh Chung, để t&igrave;m được diễn vi&ecirc;n như vậy kh&oacute; như &ldquo;m&ograve; kim đ&aacute;y biển&rdquo; bởi thường diễn vi&ecirc;n c&oacute; ngoại h&igrave;nh th&igrave; lại kh&ocirc;ng biết diễn xuất v&agrave; ngược lại. Nhiều đạo diễn đ&atilde; ưu ti&ecirc;n chọn yếu tố hợp vai trước, c&ograve;n chuyện diễn được hay kh&ocirc;ng sẽ&hellip; t&iacute;nh sau. Đ&oacute; cũng l&agrave; l&yacute; do sau khi t&igrave;m được 3 diễn vi&ecirc;n nh&iacute;, đạo diễn Tường Phương phải nhờ c&aacute;c giảng vi&ecirc;n của Trường ĐH S&acirc;n khấu v&agrave; Điện ảnh TP HCM dạy diễn xuất. Đạo diễn Quốc Thịnh cũng từng đưa c&aacute;c nữ diễn vi&ecirc;n nh&iacute; trong bộ phim &ldquo;Hương cỏ dại&rdquo; đến một người c&oacute; kinh nghiệm dạy diễn xuất để c&aacute;c em học hỏi v&igrave; c&aacute;c c&ocirc; b&eacute; n&agrave;y kh&ocirc;ng hề biết diễn xuất l&agrave; g&igrave;. Học xong mấy th&aacute;ng trời, c&aacute;c diễn vi&ecirc;n nh&iacute; n&agrave;y mới v&agrave;o vai được nhưng diễn xuất cũng chỉ ở mức b&igrave;nh thường.</p>\n<p><strong>Diễn xong rồi th&ocirc;i</strong></p>\n<p style=\"text-align: justify;\">Đạo diễn Nguyễn Minh Chung cho rằng kh&ocirc;ng thể đ&ograve;i hỏi c&aacute;c diễn vi&ecirc;n nh&iacute; diễn xuất chuy&ecirc;n nghiệp v&igrave; hiện nay, việc đ&agrave;o tạo, ph&aacute;t triển những &ldquo;măng non&rdquo; n&agrave;y chưa được quan t&acirc;m. &ldquo;Khi l&agrave;m phim, c&aacute;c đạo diễn đều đi tới c&aacute;c nh&agrave; văn h&oacute;a, đội kịch để t&igrave;m. Đ&acirc;y l&agrave; nơi cung cấp diễn vi&ecirc;n nh&iacute; chủ yếu&rdquo; - đạo diễn của &ldquo;K&iacute;nh vạn hoa&rdquo; n&oacute;i th&ecirc;m. Song theo nhiều đạo diễn, c&aacute;c diễn vi&ecirc;n nh&iacute; nơi đ&acirc;y c&oacute; kinh nghiệm đứng trước ống k&iacute;nh nhưng kh&ocirc;ng phải bao giờ cũng hợp với vai diễn trong phim của họ. Vậy n&ecirc;n, nhiều người đ&atilde; phải t&igrave;m những &ldquo;t&agrave;i năng&rdquo; mới rồi đ&agrave;o tạo. Thế nhưng, việc dạy diễn xuất chỉ l&agrave; tạm thời, mang t&iacute;nh&hellip; chữa ch&aacute;y để đ&aacute;p ứng cho một vai diễn trong phim n&agrave;o đ&oacute; rồi th&ocirc;i.</p>\n<p style=\"text-align: justify;\">Hiện nay, cũng c&oacute; c&aacute;c lớp học, c&acirc;u lạc bộ đ&agrave;o tạo diễn vi&ecirc;n nh&iacute; tại c&aacute;c nh&agrave; văn h&oacute;a, trường học hoặc ở nh&agrave; nhưng kh&ocirc;ng thường xuy&ecirc;n. N&oacute;i như một đạo diễn rằng sự cố gắng n&agrave;y rất đ&aacute;ng ghi nhận nhưng vẫn chưa đ&aacute;p ứng được nhu cầu do sự thiếu chuy&ecirc;n nghiệp trong kh&acirc;u giảng dạy, đ&agrave;o tạo v&agrave; số lượng c&ograve;n &iacute;t ỏi. Đ&oacute; l&agrave; l&yacute; do khi t&igrave;m vai An cho phim &ldquo;Đất phương Nam&rdquo;, đạo diễn Vinh Sơn cũng kiếm diễn vi&ecirc;n nh&iacute; từ nh&agrave; văn h&oacute;a, đội kịch thiếu nhi nhưng kết quả vẫn kh&ocirc;ng c&oacute; em n&agrave;o hợp với vai diễn. H&ugrave;ng Thuận l&agrave; diễn vi&ecirc;n được ph&aacute;t hiện t&igrave;nh cờ v&agrave; diễn xuất bằng bản năng. Nhưng bản năng trời ph&uacute; đ&acirc;u thể n&agrave;o tồn tại l&acirc;u d&agrave;i m&atilde;i, cần phải được nu&ocirc;i dưỡng, đ&agrave;o tạo. N&oacute;i như đạo diễn Vinh Sơn: &ldquo;Vấn đề l&agrave; khi đ&atilde; được ph&aacute;t hiện v&agrave; tỏa s&aacute;ng như H&ugrave;ng Thuận, ch&uacute;ng ta cần c&oacute; chiến lược ph&aacute;t triển ngay sau đ&oacute;&rdquo;. Kh&ocirc;ng c&oacute; nơi đ&agrave;o tạo khởi nguồn, cũng kh&ocirc;ng c&oacute; chiến lược đ&agrave;o tạo, ph&aacute;t triển sau khi t&agrave;i năng tỏa s&aacute;ng ch&iacute;nh l&agrave; l&yacute; do khiến điện ảnh Việt đ&aacute;nh rơi nhiều t&agrave;i năng.</p>\n<div id=\"ObjectBoxContent_1445873235801\" class=\"VCSortableInPreviewMode\" dir=\"center\" style=\"border: solid 1px #999; background: #E6E6FA; float: none; width: 90%; margin: 0 auto;\" data-back=\"E6E6FA\" data-border=\"999\">\n<p style=\"text-align: justify;\"><strong>L&atilde;ng ph&iacute; t&agrave;i năng</strong></p>\n<p style=\"text-align: justify;\">Ở c&aacute;c nước phương T&acirc;y, diễn vi&ecirc;n nh&iacute; sau khi được ph&aacute;t hiện sẽ được chuyển v&agrave;o sống ngay tại trường đ&agrave;o tạo nghệ thuật chuy&ecirc;n nghiệp, vừa được học về văn h&oacute;a vừa được học về diễn xuất. Nền điện ảnh c&aacute;c nước cũng ch&uacute; trọng đầu tư, &aacute;p dụng c&aacute;c ch&iacute;nh s&aacute;ch, chiến lược bảo đảm quyền lợi v&agrave; cơ hội ph&aacute;t triển l&acirc;u d&agrave;i cho c&aacute;c em. C&ograve;n ở Việt Nam, mong muốn một nơi đ&agrave;o tạo diễn vi&ecirc;n nh&iacute; thật sự chuy&ecirc;n nghiệp để c&oacute; một nguồn diễn vi&ecirc;n nh&iacute; ổn định v&agrave; chất lượng cho c&aacute;c dự &aacute;n phim xem ra c&ograve;n qu&aacute; xa vời. Một đạo diễn thở d&agrave;i: &ldquo;T&ocirc;i rất tiếc khi từng thấy H&ugrave;ng Thuận, Lan H&agrave;&hellip; tỏa s&aacute;ng rồi vụt tắt. Diễn vi&ecirc;n nh&iacute; t&agrave;i năng đ&atilde; &iacute;t ỏi m&agrave; ch&uacute;ng ta con qu&aacute; l&atilde;ng ph&iacute;&rdquo;. Đạo diễn Nguyễn Minh Chung bức x&uacute;c: &ldquo;Số lượng phim thiếu nhi qu&aacute; &iacute;t n&ecirc;n c&aacute;c t&agrave;i năng nh&iacute; đ&oacute;ng xong 1 phim rồi&hellip; th&ocirc;i. Kh&ocirc;ng ai mặn m&agrave; với việc l&agrave;m phim thiếu nhi dẫn đến chuyện đ&agrave;o tạo cũng bị bỏ qu&ecirc;n&rdquo;.</p>\n</div>\n<p>&nbsp;</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><strong>NGỌC PHƯƠNG</strong> (nld.com.vn)</div>\n</div>','Diễn viên nhí: Hiếm hoi và lãng phí ','','',0,'http://tamdiem.com.vn/sources/images/dien-vien-nhi-hiem-hoi-va-lang-phi.jpg','',0,0,0,0,475,0,1445926214,1447209291,1),(131,' VTV sẽ bán vốn tại 3 nhà đài ','-vtv-se-ban-von-tai-3-nha-dai-',14,'news_item',' VTV sẽ bán vốn tại 3 nhà đài , VTVcab (Truyền hình cáp VN), K+ (Truyền hình số vệ tinh VN), SCTV (Truyền hình cáp Saigontourist).','VTV sẽ tổ chức đấu giá công khai khi bán cổ phần tại ba đơn vị nêu trên và số tiền thu được chuyển về Tổng công ty Đầu tư và kinh doanh vốn nhà nước (SCIC). ','VTV sẽ tổ chức đấu giá công khai khi bán cổ phần tại ba đơn vị nêu trên và số tiền thu được chuyển về Tổng công ty Đầu tư và kinh doanh vốn nhà nước (SCIC). ','<p style=\"text-align: justify;\"><strong>VTV sẽ tổ chức đấu gi&aacute; c&ocirc;ng khai khi b&aacute;n cổ phần tại ba đơn vị n&ecirc;u tr&ecirc;n v&agrave; số tiền thu được chuyển về Tổng c&ocirc;ng ty Đầu tư v&agrave; kinh doanh vốn nh&agrave; nước (SCIC).</strong></p>\n<table style=\"height: 317px; background-color: #e6e6e6; margin-left: auto; margin-right: auto;\" width=\"433\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//VTV ban von.jpg\" alt=\"\" width=\"518\" height=\"362\" /></p>\n<p><span style=\"color: #808080;\">Đ&agrave;i truyền h&igrave;nh Việt Nam (VTV) sẽ tho&aacute;i vốn ở ba đơn vị truyền h&igrave;nh trả tiền: VTVcab, SCTV v&agrave; K+ - Ảnh: Nguyễn Kh&aacute;nh</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: left;\">Tổng gi&aacute;m đốc Đ&agrave;i truyền h&igrave;nh VN (VTV) Trần B&igrave;nh Minh cho biết VTV sẽ tho&aacute;i vốn khỏi ba đơn vị truyền h&igrave;nh trả tiền m&agrave; VTV đang sở hữu hoặc đồng sở hữu l&agrave;: VTVcab (Truyền h&igrave;nh c&aacute;p VN), K+ (Truyền h&igrave;nh số vệ tinh VN), SCTV (Truyền h&igrave;nh c&aacute;p Saigontourist).</p>\n<p style=\"text-align: left;\">Theo đ&oacute;, trong tương lai VTV sẽ chỉ quản l&yacute; nội dung, kh&ocirc;ng c&oacute; vai tr&ograve; trong việc kinh doanh của c&aacute;c đơn vị n&agrave;y.</p>\n<p style=\"text-align: left;\">Người xem truyền h&igrave;nh, nhất l&agrave; truyền h&igrave;nh thể thao, nhiều năm qua kh&ocirc;ng &iacute;t lần phản ứng với VTV khi K+ đứng ra mua độc quyền bản quyền truyền h&igrave;nh Giải b&oacute;ng đ&aacute; ngoại hạng Anh.</p>\n<p style=\"text-align: left;\">Tuy nhi&ecirc;n, những t&igrave;nh huống như thế n&agrave;y trong tương lai sẽ kh&ocirc;ng xảy ra khi VTV sẽ b&aacute;n phần lớn số cổ phần tại K+ thay v&igrave; giữ cổ phần chi phối tới 51% như hiện nay. Ngo&agrave;i ra, VTV cũng sẽ b&aacute;n khoảng 49% cổ phần tại VTVcab v&agrave; phần lớn cổ phần tại SCTV.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/Capture.JPG\" alt=\"\" width=\"497\" height=\"164\" /></p>\n<p style=\"text-align: justify;\"><strong>B&aacute;n xong trong năm 2016</strong></p>\n<p style=\"text-align: left;\">Cổ phần h&oacute;a (CPH) c&aacute;c doanh nghiệp trực thuộc VTV l&agrave; một trong những chủ trương VTV thực hiện theo chỉ đạo của Ch&iacute;nh phủ, bắt đầu triển khai từ nay đến năm 2016.</p>\n<p style=\"text-align: left;\">Hiện nay, VTV đang sở hữu, đồng sở hữu ba đơn vị truyền h&igrave;nh trả tiền (THTT), chi phối to&agrave;n bộ thị trường THTT tại VN. Theo b&aacute;o c&aacute;o của Hiệp hội THTT VN (VNPayTV), t&iacute;nh đến th&aacute;ng 6-2014 tổng số thu&ecirc; bao THTT tại VN v&agrave;o khoảng 7 triệu.</p>\n<p style=\"text-align: left;\">Trong số đ&oacute;, ba đơn vị THTT trực thuộc VTV đ&atilde; chiếm khoảng 5,1 triệu thu&ecirc; bao bao gồm: SCTV 2,3 triệu thu&ecirc; bao, VTVcab 2 triệu thu&ecirc; bao, K+ 800.000 thu&ecirc; bao.</p>\n<p style=\"text-align: left;\">SCTV được th&agrave;nh lập từ năm 1992, l&agrave; doanh nghiệp 100% vốn nh&agrave; nước tr&ecirc;n cơ sở li&ecirc;n doanh giữa VTV v&agrave; Tổng c&ocirc;ng ty Du lịch S&agrave;i G&ograve;n (Saigontourist).</p>\n<p style=\"text-align: left;\">Sau 23 năm ph&aacute;t triển, SCTV l&agrave; đơn vị THTT đứng đầu VN về số lượng thu&ecirc; bao, phủ s&oacute;ng 53 tỉnh th&agrave;nh tr&ecirc;n cả nước.</p>\n<p style=\"text-align: left;\">Theo website của SCTV, đơn vị n&agrave;y hiện c&oacute; đội ngũ lao động l&ecirc;n tới 3.200 nh&acirc;n vi&ecirc;n. Trong khi đ&oacute; VTVcab (100% vốn của VTV, th&agrave;nh lập từ năm 1995) hiện đang &ldquo;b&aacute; chủ&rdquo; thị trường THTT khu vực ph&iacute;a Bắc.</p>\n<p style=\"text-align: left;\">Sinh sau đẻ muộn nhất (th&agrave;nh lập v&agrave;o th&aacute;ng 5-2009), K+ l&agrave; c&ocirc;ng ty li&ecirc;n doanh giữa VTV v&agrave; Tập đo&agrave;n truyền th&ocirc;ng Canal+ (Ph&aacute;p). Trong đ&oacute; VTV nắm giữ 51% cổ phần, Canal+ nắm giữ 49%. Sau khi ra đời, K+ đ&atilde; g&acirc;y &ldquo;sốc&rdquo; với thị trường THTT v&agrave; người h&acirc;m mộ thể thao khi đứng ra mua g&oacute;i độc quyền bản quyền ph&aacute;t s&oacute;ng Giải b&oacute;ng đ&aacute; ngoại hạng Anh ng&agrave;y chủ nhật trong ba m&ugrave;a 2010 - 2013,&nbsp;&nbsp;&nbsp;&nbsp; 2013 - 2016.</p>\n<p style=\"text-align: left;\">Hiện nay, &ocirc;ng Nguyễn Th&agrave;nh Lương - ph&oacute; tổng gi&aacute;m đốc VTV - đang l&agrave; chủ tịch hội đồng th&agrave;nh vi&ecirc;n K+, tổng gi&aacute;m đốc của K+ l&agrave; &ocirc;ng L&ecirc; Ch&iacute; C&ocirc;ng (nguy&ecirc;n ph&oacute; ch&aacute;nh văn ph&ograve;ng VTV).</p>\n<p style=\"text-align: left;\">Trong buổi lễ kỷ niệm 20 năm th&agrave;nh lập VTVcab diễn ra v&agrave;o th&aacute;ng 9, &ocirc;ng Trần B&igrave;nh Minh cho biết bước đầu VTV sẽ tho&aacute;i vốn khỏi VTVcab, sau đ&oacute; đến lượt SCTV v&agrave; K+.</p>\n<p style=\"text-align: left;\">Việc tho&aacute;i vốn, r&uacute;t dần vai tr&ograve; của VTV tại c&aacute;c doanh nghiệp THTT, theo &ocirc;ng Minh, để c&aacute;c doanh nghiệp hoạt động hiệu quả hơn, mang lại lợi &iacute;ch cho cổ đ&ocirc;ng v&agrave; người sử dụng thu&ecirc; bao. Sau khi b&aacute;n cổ phần khỏi c&aacute;c đơn vị n&agrave;y, VTV sẽ chỉ đ&oacute;ng vai tr&ograve; l&agrave; đơn vị kiểm so&aacute;t về nội dung.</p>\n<p style=\"text-align: justify;\"><strong>Đấu gi&aacute; c&ocirc;ng khai, tiền thu được nộp về SCIC</strong></p>\n<p style=\"text-align: left;\">Trao đổi với Tuổi Trẻ, &ocirc;ng Nguyễn Th&agrave;nh Lương khẳng định VTV sẽ tổ chức đấu gi&aacute; c&ocirc;ng khai khi b&aacute;n cổ phần tại ba đơn vị n&ecirc;u tr&ecirc;n v&agrave; số tiền thu được chuyển về Tổng c&ocirc;ng ty Đầu tư v&agrave; kinh doanh vốn nh&agrave; nước (SCIC).</p>\n<p style=\"text-align: left;\">Theo &ocirc;ng Lương, việc CPH sẽ tạo điều kiện cho những doanh nghiệp n&agrave;y quản l&yacute; v&agrave; kinh doanh tốt hơn. Sau n&agrave;y khi CPH, VTV sẽ chỉ đ&oacute;ng vai tr&ograve; đơn vị kiểm so&aacute;t nội dung cho c&aacute;c đơn vị THTT.</p>\n<p style=\"text-align: left;\">&ldquo;VTV sẽ chọn những nh&agrave; đầu tư mạnh về t&agrave;i ch&iacute;nh, quản l&yacute; điều h&agrave;nh tốt v&agrave; đặc biệt phải c&oacute; kinh nghiệm trong lĩnh vực THTT.</p>\n<p style=\"text-align: left;\">VTV kh&ocirc;ng chỉ t&iacute;nh đến việc b&aacute;n cổ phần l&agrave;m sao được nhiều tiền nhất m&agrave; c&ograve;n phải t&iacute;nh đến việc khi đối t&aacute;c mua cổ phần c&oacute; mang lại gi&aacute; trị, đem hiệu quả cho doanh nghiệp về sau n&agrave;y. Sau CPH, hội bảo vệ người ti&ecirc;u d&ugrave;ng sẽ gi&aacute;m s&aacute;t bảo vệ quyền lợi của kh&aacute;ch h&agrave;ng l&agrave; thu&ecirc; bao của c&aacute;c đơn vị THTT&rdquo; - &ocirc;ng Lương n&oacute;i.</p>\n<p style=\"text-align: left;\">Trả lời Tuổi Trẻ, &ocirc;ng Ho&agrave;ng Ngọc Huấn - tổng gi&aacute;m đốc VTVcab - cũng cho rằng CPH l&agrave; xu hướng của thị trường THTT v&agrave; VTVcab đ&atilde; chuẩn bị việc n&agrave;y. &ldquo;Dự kiến VTV sẽ b&aacute;n 49% cổ phần tại VTVcab, 51% c&ograve;n lại VTV sẽ vẫn nắm để giữ vai tr&ograve; chi phối&rdquo; - &ocirc;ng Huấn cho biết.</p>\n<p style=\"text-align: left;\">Theo &ocirc;ng Huấn, việc VTV vẫn giữ cổ phần chi phối bởi VTVcab kh&ocirc;ng chỉ l&agrave; đơn vị THTT m&agrave; c&ograve;n c&oacute; tr&aacute;ch nhiệm truyền dẫn một số k&ecirc;nh s&oacute;ng thiết yếu của truyền h&igrave;nh quốc gia.</p>\n<p style=\"text-align: left;\">Việc VTV tiếp tục chi phối sẽ gi&uacute;p VTVcab ph&aacute;t triển theo định hướng, trong khi CPH gi&uacute;p VTVcab c&oacute; đủ nguồn lực từ những đối t&aacute;c chiến lược, c&oacute; kinh nghiệm trong ph&aacute;t triển THTT.</p>\n<p style=\"text-align: left;\">&ldquo;T&ocirc;i cho rằng c&aacute;c thu&ecirc; bao của VTVcab sẽ được hưởng nhiều tiện &iacute;ch như dịch vụ tốt hơn, chương tr&igrave;nh hay hơn, nhiều g&oacute;i bản quyền hấp dẫn hơn khi VTVcab được CPH&rdquo; - &ocirc;ng Huấn nhận định.</p>\n<p style=\"text-align: left;\">Trong khi đ&oacute;, &ocirc;ng L&ecirc; Ch&iacute; C&ocirc;ng cho biết dự kiến VTV sẽ b&aacute;n phần lớn cổ phần tại K+ v&agrave; chỉ giữ lại một phần vốn nhỏ, đ&oacute;ng vai tr&ograve; kiểm so&aacute;t nội dung của K+ l&agrave; ch&iacute;nh. Theo &ocirc;ng C&ocirc;ng, khi đến VN kinh doanh, Canal+ muốn kết hợp với VTV để l&agrave;m việc.</p>\n<p style=\"text-align: left;\">Tuy nhi&ecirc;n, đối t&aacute;c n&agrave;y cũng ủng hộ chủ trương CPH K+ của VTV. &ldquo;CPH nhằm thu h&uacute;t th&ecirc;m nguồn lực, gia tăng sức mạnh cho sự ph&aacute;t triển của doanh nghiệp.</p>\n<p style=\"text-align: left;\">Hiện nay K+ đ&atilde; l&agrave; li&ecirc;n doanh VN với nước ngo&agrave;i, v&igrave; thế khi b&aacute;n phần vốn của m&igrave;nh tại K+, VTV nhiều khả năng sẽ b&aacute;n cho một đối t&aacute;c c&oacute; tiềm lực trong nước&rdquo; - &ocirc;ng C&ocirc;ng cho biết.</p>\n<p style=\"text-align: justify;\"><strong>Để thị trường quyết định</strong></p>\n<p style=\"text-align: left;\">Trao đổi với Tuổi Trẻ, l&atilde;nh đạo một đơn vị THTT tại VN cho biết trong c&aacute;c lĩnh vực kinh doanh hiện nay, đầu tư v&agrave;o THTT l&agrave; &ldquo;si&ecirc;u&rdquo; lợi nhuận. Vị n&agrave;y cho biết tăng trưởng của một đơn vị THTT trong một năm v&agrave;o khoảng 130 - 140%. Đầu tư v&agrave;o THTT c&oacute; thể đem lại lợi nhuận l&ecirc;n tới 30 - 40 %/năm, hiếm c&oacute; lĩnh vực kinh doanh n&agrave;o thời điểm n&agrave;y kiếm nhiều tiền đến vậy.</p>\n<p style=\"text-align: left;\">&ldquo;Sau khi biết một số đơn vị THTT của VTV sẽ được CPH, tr&ecirc;n thị trường THTT thời gian qua x&ocirc;n xao với th&ocirc;ng tin một tập đo&agrave;n viễn th&ocirc;ng lớn của VN sẽ sớm nhảy v&agrave;o VTVcab. Với số thu&ecirc; bao lớn, lợi nhuận cao, chắc chắn cả VTVcab, SCTV, K+ sẽ thu h&uacute;t rất nhiều nh&agrave; đầu tư mạnh nhảy v&agrave;o khi VTV b&aacute;n cổ phần&rdquo; - vị n&agrave;y nhận định.</p>\n<p style=\"text-align: left;\">B&aacute;n cổ phần tại một đơn vị THTT th&igrave; những g&igrave; sẽ được mang ra b&aacute;n l&agrave; c&acirc;u hỏi nhiều người đặt ra đối với VTV.</p>\n<p style=\"text-align: left;\">Chia sẻ về điều n&agrave;y, tổng gi&aacute;m đốc K+ L&ecirc; Ch&iacute; C&ocirc;ng cho biết với THTT, người ta sẽ t&iacute;nh gi&aacute; trị của đơn vị đ&oacute; dựa tr&ecirc;n: trang thiết bị đầu tư cho hệ thống truyền h&igrave;nh đ&oacute; (v&iacute; dụ như ở K+ l&agrave; trạm thu vệ tinh gần 20 triệu USD đ&atilde; được đầu tư); định gi&aacute; số lượng v&agrave; gi&aacute; trị của c&aacute;c thu&ecirc; bao (hiện nay K+ c&oacute; 800.000 thu&ecirc; bao, mỗi thu&ecirc; bao sẽ được t&iacute;nh ra một số tiền nhất định). T&ocirc;i cho rằng VTV chỉ b&aacute;n một phần cổ phần tại K+ cũng đ&atilde; thu được h&agrave;ng ng&agrave;n tỉ đồng.</p>\n<p style=\"text-align: left;\">C&ograve;n &ocirc;ng L&ecirc; Đ&igrave;nh Cường, tổng thư k&yacute; VNPayTV, cho biết việc VTV CPH c&aacute;c đơn vị THTT thuộc VTV l&agrave; t&iacute;n hiệu rất đ&aacute;ng mừng đối với thị trường THTT VN.</p>\n<p style=\"text-align: left;\">&ldquo;CPH l&agrave; xu hướng tất yếu của THTT, để thị trường điều chỉnh việc kinh doanh của c&aacute;c doanh nghiệp. C&aacute;c doanh nghiệp c&oacute; tiền th&igrave; mua c&aacute;i hay về ph&aacute;t, &iacute;t tiền th&igrave; mua c&aacute;i vừa. Người ti&ecirc;u d&ugrave;ng cũng c&oacute; thể lựa chọn việc mua m&oacute;n h&agrave;ng n&agrave;o để phục vụ việc giải tr&iacute; của gia đ&igrave;nh m&igrave;nh&rdquo; - &ocirc;ng Cường n&oacute;i.</p>\n<table style=\"height: 256px; background-color: #e6e6e6; margin-left: auto; margin-right: auto;\" width=\"524\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: left;\">&nbsp;</p>\n<p style=\"text-align: left;\"><strong>VTV c&oacute; l&atilde;i khi b&aacute;n cổ phần ở K+</strong></p>\n<p style=\"text-align: left;\">Khi được th&agrave;nh lập v&agrave;o năm 2009, vốn điều lệ của K+ l&agrave; 20 triệu USD, trong đ&oacute; ph&iacute;a VTV g&oacute;p hơn 10 triệu USD (chiếm tỉ lệ 51% cổ phần), c&ograve;n lại l&agrave; Canal+ International Development g&oacute;p 9,78 triệu USD.</p>\n<p style=\"text-align: left;\">Tuy nhi&ecirc;n, VTV kh&ocirc;ng g&oacute;p bằng tiền m&agrave; bằng khoảng 100.000 thu&ecirc; bao DTH từ VTVcab (khi đ&oacute; l&agrave; VCTV) chuyển sang c&ugrave;ng hệ thống kỹ thuật trạm thu ph&aacute;t s&oacute;ng tại Vĩnh Ph&uacute;c.</p>\n<p style=\"text-align: left;\">Để c&oacute; đủ vốn đầu tư sau khi ra đời, ngo&agrave;i 20 triệu USD vốn điều lệ, K+ đ&atilde; vay th&ecirc;m 34 triệu USD từ c&ocirc;ng ty mẹ Canal+ (Ph&aacute;p). Sau s&aacute;u năm ra đời, th&aacute;ng 6-2015 K+ th&ocirc;ng b&aacute;o đ&atilde; đạt điểm h&ograve;a vốn.</p>\n<p style=\"text-align: left;\">Tăng trưởng thu&ecirc; bao của K+ đạt b&igrave;nh qu&acirc;n 140%/năm, doanh thu tăng 150%/năm, tổng doanh thu đạt tr&ecirc;n 1.200 tỉ đồng/năm. Với việc g&oacute;p vốn chỉ bằng thu&ecirc; bao của kh&aacute;ch h&agrave;ng v&agrave; hạ tầng đ&atilde; khấu hao, một đại diện K+ cho biết VTV sẽ l&atilde;i lớn khi b&aacute;n cổ phần ở K+.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<table style=\"height: 174px; background-color: #e6e6e6; margin-left: auto; margin-right: auto;\" width=\"520\">\n<tbody>\n<tr>\n<td>\n<p>&nbsp;</p>\n<p><strong> *&Ocirc;ng L&ecirc; Đ&igrave;nh Cường </strong><em>(tổng thư k&yacute; VNPayTV):</em></p>\n<p style=\"text-align: justify;\"><strong>Người ti&ecirc;u d&ugrave;ng sẽ hưởng lợi</strong></p>\n<p style=\"text-align: left;\">Việc VTV b&aacute;n vốn tại c&aacute;c doanh nghiệp n&agrave;y l&agrave; t&iacute;n hiệu tốt cho thị trường THTT v&agrave; người ti&ecirc;u d&ugrave;ng cũng được hưởng lợi. Một khi những nh&agrave; đầu tư c&oacute; năng lực t&agrave;i ch&iacute;nh mạnh tham gia, thị trường n&agrave;y sẽ cạnh tranh hơn. C&aacute;c doanh nghiệp muốn tồn tại phải kh&ocirc;ng ngừng n&acirc;ng cao chất lượng dịch vụ, đ&aacute;p ứng y&ecirc;u cầu của người ti&ecirc;u d&ugrave;ng.</p>\n<p style=\"text-align: left;\">Người ti&ecirc;u d&ugrave;ng cũng kh&ocirc;ng qu&aacute; lo lắng việc tăng gi&aacute; thu&ecirc; bao, bởi hiện thị trường THTT VN c&oacute; đến 40 - 50 nh&agrave; cung cấp tham gia, cạnh tranh rất khốc liệt.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><strong>KHƯƠNG XU&Acirc;N</strong> (khuongxuan@tuoitre.com.vn)</p>\n<p style=\"text-align: justify;\">&nbsp;</p>',' VTV sẽ bán vốn tại 3 nhà đài ','','',0,'http://tamdiem.com.vn/sources/images/VTV%20ban%20von.jpg','',0,0,0,0,474,0,1446001223,1449287958,1),(132,'Phim Ấn Độ \'Hạnh Phúc Muộn Màng\'','phim-truyen-hinh-hanh-phuc-muon-mang',16,'news_item','Hạnh Phúc Muộn Màng, THVL, phim ấn độ, Ishita, Ra-man,Ru-hi','Nội dung chính xoay quanh “cuộc hôn nhân bất đắc dĩ” của cô nha sĩ xinh đẹp Ishita và anh chàng CEO Ra-man kiêu ngạo luôn tỏ ra lạnh lùng. Cả 2 người với 2 tính cách hoàn toàn đối lập, khác biệt về cả tôn giáo, phong tục tập quán của 2 miền Nam và Bắc Ấn nhưng họ lại thỏa thuận trở thành vợ chồng vì tình yêu dành cho cô bé Ru-hi mà cả 2 đều hết lòng yêu thương.','Nội dung chính xoay quanh “cuộc hôn nhân bất đắc dĩ” của cô nha sĩ xinh đẹp Ishita và anh chàng CEO Ra-man kiêu ngạo luôn tỏ ra lạnh lùng. Cả 2 người với 2 tính cách hoàn toàn đối lập, khác biệt về cả tôn giáo, phong tục tập quán của 2 miền Nam và Bắc Ấn nhưng họ lại thỏa thuận trở thành vợ chồng vì tình yêu dành cho cô bé Ru-hi mà cả 2 đều hết lòng yêu thương.','<p class=\"tin-van--pv--bai-chuyen-mucpara-style-override-9\" style=\"text-align: justify;\"><strong>Hạnh ph&uacute;c muộn m&agrave;ng- bộ phim t&igrave;nh cảm t&acirc;m l&yacute; x&atilde; hội đặc sắc với c&acirc;u chuyện t&igrave;nh đầy l&atilde;ng mạn hấp dẫn, nhưng rất hiện thực v&agrave; gần gũi bởi nội dung xo&aacute;y s&acirc;u v&agrave;o mối quan hệ t&igrave;nh cảm gia đ&igrave;nh, cuốn h&uacute;t với nhiều t&igrave;nh tiết đan xen bất ngờ dẫn dắt người xem phải d&otilde;i theo từng tập phim.</strong></p>\n<table class=\"image center\" width=\"420\" align=\"center\">\n<tbody>\n<tr>\n<td><img style=\"width: 493px; height: 394px;\" src=\"http://www.baovinhlong.com.vn/dataimages/201510/original/images1404126_Z032_Sequence_03.jpg\" alt=\"\" /></td>\n</tr>\n</tbody>\n</table>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Nội dung ch&iacute;nh xoay quanh &ldquo;cuộc h&ocirc;n nh&acirc;n bất đắc dĩ&rdquo; của c&ocirc; nha sĩ xinh đẹp Ishita v&agrave; anh ch&agrave;ng CEO Ra-man ki&ecirc;u ngạo lu&ocirc;n tỏ ra lạnh l&ugrave;ng. Cả 2 người với 2 t&iacute;nh c&aacute;ch ho&agrave;n to&agrave;n đối lập, kh&aacute;c biệt về cả t&ocirc;n gi&aacute;o, phong tục tập qu&aacute;n của 2 miền Nam v&agrave; Bắc Ấn nhưng họ lại thỏa thuận trở th&agrave;nh vợ chồng v&igrave; t&igrave;nh y&ecirc;u d&agrave;nh cho c&ocirc; b&eacute; Ru-hi m&agrave; cả 2 đều hết l&ograve;ng y&ecirc;u thương.</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Ishita đ&atilde; từng nếm trải những nỗi đau mất m&aacute;t khi bị từ h&ocirc;n ngay trong ng&agrave;y lễ th&agrave;nh h&ocirc;n v&igrave; c&ocirc; kh&ocirc;ng c&oacute; khả năng l&agrave;m mẹ. Nỗi mặc cảm v&agrave; kh&aacute;t khao trở th&agrave;nh một người mẹ khiến Ishita lu&ocirc;n d&agrave;nh t&igrave;nh y&ecirc;u thương cho c&ocirc; b&eacute; h&agrave;ng x&oacute;m Ru-hi tội nghiệp.</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Ru-hi l&agrave; con g&aacute;i của Ra-man, đ&atilde; bị mẹ bỏ rơi khi mới vừa tr&ograve;n 6 th&aacute;ng tuổi, lại lu&ocirc;n bị người cha đối xử lạnh l&ugrave;ng v&igrave; nỗi hận th&ugrave; khi bị vợ phản bội.</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Ishita v&agrave; Ra-man l&agrave; h&agrave;ng x&oacute;m của nhau, cả 2 gia đ&igrave;nh lu&ocirc;n bất h&ograve;a v&agrave; m&acirc;u thuẫn v&igrave; sự kh&aacute;c biệt về t&ocirc;n gi&aacute;o, v&agrave; phong tục tập qu&aacute;n của 2 v&ugrave;ng miền Nam- Bắc Ấn. Ra-man v&agrave; Ishita cũng kh&ocirc;ng ngoại lệ- họ lu&ocirc;n tranh c&atilde;i về bất cứ điều g&igrave; khi c&oacute; những x&iacute;ch m&iacute;ch, va chạm giữa 2 gia đ&igrave;nh&hellip;</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Nhưng họ lại kh&ocirc;ng ngờ, 2 người cha đ&atilde; &acirc;m thầm theo d&otilde;i v&agrave; biết được cả Ra-man v&agrave; Ishita lại c&oacute; c&ugrave;ng một mối quan t&acirc;m- đ&oacute; ch&iacute;nh l&agrave; Ru- hi&hellip; V&igrave; vậy &ocirc;ng Om-Ba v&agrave; &ocirc;ng Vich-qua c&ugrave;ng bắt tay nhau để tiến h&agrave;nh lễ ra mắt&hellip;</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Bất ngờ đến sửng sốt nhưng họ lại bị thuyết phục bởi h&igrave;nh ảnh đ&aacute;ng y&ecirc;u của c&ocirc; b&eacute; Ru-hi đang rất cần một gia đ&igrave;nh- nhất l&agrave; khi đứng trước t&ograve;a &aacute;n, c&ocirc; b&eacute; lại chọn phương &aacute;n về sống c&ugrave;ng c&ocirc; Ishita&hellip; V&agrave; mọi rắc rối, m&acirc;u thuẫn mới chỉ l&agrave; bắt đầu trong cuộc sống mới của họ- cuộc sống của &ldquo;vợ chồng Ra-man- Ishita&rdquo;&hellip;</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">D&ugrave; lu&ocirc;n thể hiện những c&aacute;i t&ocirc;i của m&igrave;nh, d&ugrave; lu&ocirc;n tỏ ra mạnh mẽ, cao ngạo nhưng s&acirc;u thẳm trong tim họ l&agrave; sự quan t&acirc;m lặng lẽ d&agrave;nh cho nhau v&agrave; họ đ&atilde; nh&igrave;n thấy vết thương l&ograve;ng ở mỗi người đ&atilde; l&agrave;nh lại nhường chỗ cho một khởi đầu mới&hellip; Nhưng họ đều nghĩ rằng họ l&agrave;m tất cả l&agrave; v&igrave; Ru-hi, đến bao giờ họ mới nhận ra t&igrave;nh y&ecirc;u đ&iacute;ch thực của ch&iacute;nh m&igrave;nh?</p>\n<p class=\"tin-van--pv--bai-chuyen-muc\" style=\"text-align: justify;\">Với lối x&acirc;y dựng nhanh, giải quyết dứt kho&aacute;t những t&igrave;nh tiết phụ trước khi đưa ra một t&igrave;nh huống mới khiến cho bộ phim truyền h&igrave;nh nhiều tập của Ấn Độ đ&atilde; tạo được sức h&uacute;t ri&ecirc;ng, kh&ocirc;ng g&acirc;y nh&agrave;m ch&aacute;n cho người xem, cộng th&ecirc;m diễn xuất tuyệt vời của diễn vi&ecirc;n nh&iacute; Ruhanika Dhawan trong vai c&ocirc; b&eacute; Ru- hi đ&atilde; thật sự l&agrave;m lay động tr&aacute;i tim của những kh&aacute;n giả kh&oacute; t&iacute;nh nhất. Phim sẽ ra mắt qu&yacute; kh&aacute;n giả trong khung giờ v&agrave;ng 17 giờ tr&ecirc;n k&ecirc;nh THVL1, bắt đầu từ ng&agrave;y 4/11/2015.</p>\n<p class=\"tgia\" style=\"text-align: right;\"><strong>B&Aacute;CH HỢP<span class=\"apple-converted-space\">&nbsp;</span><span class=\"char-style-override-6\">(TP Vĩnh Long)</span></strong></p>','Phim truyền hình \'Hạnh Phúc Muộn Màng\'','','',0,'http://tamdiem.com.vn/sources/images/1_3.jpg','',0,0,0,0,193,1,1446529242,1448770135,1),(133,'PR đủ trò ranh ma: Chi phối truyền thông ','pr-du-tro-ranh-ma-chi-phoi-truyen-thong-',14,'news_item','PR, truyền thông, Vũ Khắc Tiệp, người mẫu Ngọc Trinh','Chẳng phải chuyện tiền nong to tát gì ngoài vài món quà vặt nhưng những người làm PR dễ dàng điều khiển nhiều phóng viên, nhất là của một số trang báo mạng, chịu đưa tin ','Chẳng phải chuyện tiền nong to tát gì ngoài vài món quà vặt nhưng những người làm PR dễ dàng điều khiển nhiều phóng viên, nhất là của một số trang báo mạng, chịu đưa tin ','<p style=\"text-align: justify;\"><strong>Chẳng phải chuyện tiền nong to t&aacute;t g&igrave; ngo&agrave;i v&agrave;i m&oacute;n qu&agrave; vặt nhưng những người l&agrave;m PR dễ d&agrave;ng điều khiển nhiều ph&oacute;ng vi&ecirc;n, nhất l&agrave; của một số trang b&aacute;o mạng, chịu đưa tin</strong></p>\n<p style=\"text-align: justify;\">Khi c&aacute;c phương tiện truyền th&ocirc;ng trở th&agrave;nh c&ocirc;ng cụ lợi hại trong hoạt động PR (quảng b&aacute;, tiếp thị, quan hệ c&ocirc;ng ch&uacute;ng&hellip;), giới showbiz c&agrave;ng cần phải nắm trong tay nhiều người trong giới truyền th&ocirc;ng để thực hiện hiệu quả những chi&ecirc;u tr&ograve; của m&igrave;nh. Thực tế, những người l&agrave;m c&ocirc;ng việc PR lu&ocirc;n c&oacute; mối quan hệ mật thiết với một lực lượng kh&aacute; mạnh đang hoạt động trong lĩnh vực truyền th&ocirc;ng.</p>\n<p style=\"text-align: justify;\"><strong>Kiểu n&agrave;o cũng l&ecirc;n b&aacute;o được</strong></p>\n<p style=\"text-align: justify;\">Chuyện Th&uacute;y Vi (người thứ 3 trong cuộc t&igrave;nh giữa diễn vi&ecirc;n Mi Du với đại gia Phan Th&agrave;nh) gặp Vũ Khắc Tiệp (Gi&aacute;m đốc C&ocirc;ng ty Quản l&yacute; người mẫu Venus, cũng l&agrave; quản l&yacute; của người mẫu Ngọc Trinh) được anh n&agrave;y chia sẻ tr&ecirc;n trang mạng c&aacute; nh&acirc;n đại &yacute; rằng mới 17 tuổi nhưng c&ocirc; đ&atilde; ma m&atilde;nh đến mức k&eacute;o theo cả ph&oacute;ng vi&ecirc;n ảnh để chụp h&igrave;nh. Tuy nhi&ecirc;n, khi th&ocirc;ng tin n&agrave;y được đăng tải tr&ecirc;n một số trang tin điện tử v&agrave; b&aacute;o mạng, kh&ocirc;ng &iacute;t người nghi ngờ: &ldquo;Th&uacute;y Vi &ldquo;ma m&atilde;nh k&eacute;o theo ph&oacute;ng vi&ecirc;n&rdquo; nhưng c&ocirc; ta đ&acirc;u đủ khả năng để đưa những tấm ảnh gặp gỡ Vũ Khắc Tiệp hay c&aacute;c th&ocirc;ng tin li&ecirc;n quan đến anh n&agrave;y l&ecirc;n mặt b&aacute;o dễ d&agrave;ng như vậy?&rdquo;.</p>\n<p style=\"text-align: justify;\">L&acirc;u nay, ai cũng biết những người như Vũ Khắc Tiệp rất giỏi &ldquo;l&egrave;o l&aacute;i&rdquo; truyền th&ocirc;ng đưa tin theo &yacute; muốn của m&igrave;nh. Minh chứng cho điều n&agrave;y l&agrave; những th&ocirc;ng tin li&ecirc;n quan đến &ldquo;g&agrave;&rdquo; của C&ocirc;ng ty Venus, đặc biệt l&agrave; người mẫu Ngọc Trinh, lu&ocirc;n xuất hiện đều đặn tr&ecirc;n mặt b&aacute;o, kể cả sinh hoạt đời thường nhỏ nhặt nhất của c&ocirc;: đi thăm mộ mẹ, về qu&ecirc;, t&acirc;m trạng bất an, đi dạo tr&ecirc;n phố&hellip; Gần đ&acirc;y, Ngọc Trinh bắt đầu hoạt động nghệ thuật đ&uacute;ng nghĩa khi tham gia bộ phim do ch&iacute;nh c&ocirc; đứng t&ecirc;n sản xuất. Điều n&agrave;y c&agrave;ng gi&uacute;p Ngọc Trinh xuất hiện d&agrave;y đặc hơn tr&ecirc;n nhiều trang tin, b&aacute;o mạng - từ b&agrave;i phỏng vấn nh&acirc;n vật đến bộ ảnh hậu trường của phim; thậm ch&iacute;, b&agrave;i phỏng vấn nghệ sĩ kh&aacute;c c&ugrave;ng đ&oacute;ng chung phim cũng d&agrave;nh cho c&ocirc; những lời khen hết lời.</p>\n<p style=\"text-align: justify;\">Bi&ecirc;n tập vi&ecirc;n c&aacute;c trang b&aacute;o mạng giải th&iacute;ch sự xuất hiện nhiều đến bất thường của người mẫu Ngọc Trinh l&agrave; v&igrave; c&ocirc; &ldquo;hot&rdquo; v&agrave; b&aacute;o cần view (lượng người xem) cao. Song, ai cũng thừa hiểu showbiz c&ograve;n rất nhiều gương mặt xứng đ&aacute;ng được l&ecirc;n mặt b&aacute;o. Người mẫu Ngọc Trinh kh&ocirc;ng phải l&agrave; nh&acirc;n vật &ldquo;hot&rdquo; duy nhất của showbiz Việt. Người trong nghề l&acirc;u nay vẫn b&aacute;i phục Vũ Khắc Tiệp quan hệ giỏi với một bộ phận của giới truyền th&ocirc;ng. Anh n&agrave;y nổi tiếng trong giới l&agrave; &ocirc;ng bầu rất chịu &ldquo;chi&rdquo; để sử dụng c&ocirc;ng cụ PR cho mục đ&iacute;ch của m&igrave;nh.</p>\n<p style=\"text-align: justify;\">Một trong những ti&ecirc;u ch&iacute; quan trọng để người l&agrave;m PR được c&aacute;c sao trong giới showbiz tuyển dụng l&agrave; phải k&ecirc; khai cho được danh s&aacute;ch ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n c&aacute;c trang b&aacute;o mạng n&agrave;o m&igrave;nh quen biết, th&acirc;n thiết. V&igrave; thế, nhiều ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n v&agrave;i trang tin điện tử, thậm ch&iacute; một số tờ b&aacute;o, đ&atilde; ki&ecirc;m th&ecirc;m c&ocirc;ng việc PR cho c&aacute;c ng&ocirc;i sao. Do đ&oacute;, những th&ocirc;ng tin m&agrave; họ đưa l&ecirc;n mặt b&aacute;o về &ldquo;chủ nh&acirc;n&rdquo; của m&igrave;nh kh&ocirc;ng c&ograve;n kh&aacute;ch quan.</p>\n<p style=\"text-align: justify;\">C&oacute; rất nhiều h&igrave;nh ảnh của giới văn nghệ sĩ xuất hiện tr&ecirc;n mặt b&aacute;o dưới dạng bị &ldquo;săn&rdquo; nhưng thật ra, hầu hết đều được d&agrave;n dựng. Nghệ sĩ cố t&igrave;nh d&agrave;n dựng hiện trường v&agrave; cho người quen (hoặc gọi ph&oacute;ng vi&ecirc;n ruột đến) chụp theo kiểu lấp l&oacute;, mờ ảo rồi đăng b&aacute;o. Tất nhi&ecirc;n, kh&ocirc;ng phải tất cả h&igrave;nh ảnh dạng săn t&igrave;m đều l&agrave; d&agrave;n dựng v&igrave; c&oacute; sự việc, ph&oacute;ng vi&ecirc;n đ&atilde; mất cả th&aacute;ng theo d&otilde;i nh&acirc;n vật để c&oacute; ảnh độc (phanh phui mối quan hệ giữa ng&ocirc;i sao với đại gia chẳng hạn) nhưng số n&agrave;y chỉ chiếm rất &iacute;t, c&ograve;n lại đều l&agrave; tr&ograve; d&agrave;n dựng của nghệ sĩ v&agrave; c&oacute; hỗ trợ của truyền th&ocirc;ng.</p>\n<p style=\"text-align: justify;\"><strong>C&ocirc; lập ph&oacute;ng vi&ecirc;n &ldquo;kh&oacute; bảo&rdquo;</strong></p>\n<p style=\"text-align: justify;\">Kh&ocirc;ng phải ph&oacute;ng vi&ecirc;n n&agrave;o cũng chấp nhận trở th&agrave;nh c&ocirc;ng cụ PR cho giới showbiz nhưng từ chối hợp t&aacute;c đồng nghĩa ph&oacute;ng vi&ecirc;n đ&oacute; sẽ bị c&ocirc; lập. Họ kh&ocirc;ng được mời tham dự họp b&aacute;o c&aacute;c sự kiện, kh&ocirc;ng được cung cấp th&ocirc;ng tin li&ecirc;n quan.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_259631\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/8-nld-pr-2-1447081119983/pr-du-tro-ranh-ma-chi-phoi-truyen-thong.jpg\" alt=\"Minh họa: KHỀU\" /></div>\n<div class=\"PhotoCMS_Caption\">Minh họa: KHỀU</div>\n</div>\n<p style=\"text-align: justify;\">Trong một vụ tranh chấp giữa 2 nh&agrave; thiết kế, người đảm tr&aacute;ch truyền th&ocirc;ng của nh&agrave; thiết kế A gửi gắm: &ldquo;Sự thật l&agrave; nh&agrave; thiết kế B cố t&igrave;nh ăn cắp mẫu của nh&agrave; thiết kế A. Bằng chứng đ&atilde; được chuẩn bị sẵn, nhờ ph&oacute;ng vi&ecirc;n đưa th&ocirc;ng tin l&ecirc;n b&aacute;o gi&uacute;p để đập lại nh&agrave; thiết kế B&rdquo;. Khi c&oacute; ph&oacute;ng vi&ecirc;n kh&ocirc;ng đồng t&igrave;nh với l&yacute; do đợi xem phản hồi của đ&ocirc;i b&ecirc;n, lập tức người n&agrave;y được đưa v&agrave;o &ldquo;danh s&aacute;ch đen&rdquo;, tuyệt đối kh&ocirc;ng được tham dự bất cứ sự kiện n&agrave;o do người đảm tr&aacute;ch truyền th&ocirc;ng của nh&agrave; thiết kế A thực hiện.</p>\n<p style=\"text-align: justify;\">Một số tay phụ tr&aacute;ch PR ở những c&ocirc;ng ty sản xuất chương tr&igrave;nh giải tr&iacute; truyền h&igrave;nh cũng chơi tr&ograve; c&ocirc; lập ph&oacute;ng vi&ecirc;n dạng &ldquo;kh&oacute; bảo&rdquo; n&agrave;y. Ph&oacute;ng vi&ecirc;n được mời họp b&aacute;o chương tr&igrave;nh nhiều lần m&agrave; kh&ocirc;ng thấy đưa tin sẽ bị đưa v&agrave;o nh&oacute;m kh&ocirc;ng th&acirc;n thiện. Khi sự cố trong chương tr&igrave;nh giải tr&iacute; xảy ra, ph&oacute;ng vi&ecirc;n kh&ocirc;ng th&ocirc;ng tin theo th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; của người phụ tr&aacute;ch PR gửi đến m&agrave; khai th&aacute;c theo &yacute; m&igrave;nh, g&acirc;y bất lợi cho họ th&igrave; bị liệt v&agrave;o danh s&aacute;ch nguy hiểm, kh&ocirc;ng quan hệ. Với số đầu b&aacute;o v&agrave; trang tin điện tử l&ecirc;n đến h&agrave;ng trăm như hiện nay, sự vắng mặt v&agrave;i ph&oacute;ng vi&ecirc;n hoặc th&ocirc;ng tin kh&ocirc;ng đăng tr&ecirc;n một số tờ b&aacute;o n&agrave;o đ&oacute; cũng chẳng phải l&agrave; điều g&igrave; đ&aacute;ng bận t&acirc;m với họ.</p>\n<p style=\"text-align: justify;\">Thực tế hiện nay, một nh&oacute;m những người từng l&agrave;m hoặc đang l&agrave;m b&aacute;o đ&atilde; nhận l&agrave;m truyền th&ocirc;ng cho giới showbiz. Để &ldquo;được việc&rdquo;, họ phải kiểm so&aacute;t th&ocirc;ng tin n&oacute;i về c&aacute;c &ldquo;sao&rdquo; tr&ecirc;n mặt b&aacute;o, tất nhi&ecirc;n theo hướng c&oacute; lợi. Thế lực của những người l&agrave;m PR c&agrave;ng lớn mạnh khi họ c&oacute; trong tay ph&oacute;ng vi&ecirc;n, bi&ecirc;n tập vi&ecirc;n ở nhiều cơ quan truyền th&ocirc;ng.</p>\n<p style=\"text-align: justify;\">V&igrave; tự cho m&igrave;nh c&aacute;i quyền rất lớn n&ecirc;n v&agrave;i người phụ tr&aacute;ch truyền th&ocirc;ng cho một số ca sĩ ng&ocirc;i sao đ&atilde; mạnh miệng tuy&ecirc;n bố khi họ phụ tr&aacute;ch c&ocirc;ng việc n&agrave;y th&igrave; c&aacute;c ph&oacute;ng vi&ecirc;n A, B, C, D... n&agrave;o đ&oacute; sẽ kh&ocirc;ng c&oacute; cửa tham gia họp b&aacute;o, l&yacute; do chỉ l&agrave; kh&ocirc;ng th&ocirc;ng tin theo y&ecirc;u cầu của họ. B&ecirc;n cạnh những người l&agrave;m b&aacute;o &ldquo;kh&oacute; bảo&rdquo; vẫn c&oacute; những ph&oacute;ng vi&ecirc;n rất &ldquo;dễ thương&rdquo;, như nhận x&eacute;t của mấy tay l&agrave;m PR cho giới showbiz. C&oacute; khi chẳng phải chuyện tiền nong to t&aacute;t g&igrave; ở đ&acirc;y - ngo&agrave;i những m&oacute;n qu&agrave; như đ&ocirc;i gi&agrave;y, chai nước hoa, v&agrave;i m&oacute;n lặt vặt rẻ tiền&hellip; - nhưng c&aacute;c tay l&agrave;m PR n&agrave;y đủ sức điều khiển nhiều ph&oacute;ng vi&ecirc;n, nhất l&agrave; của một số trang b&aacute;o mạng, chịu đưa tin.</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><strong>Lam Anh</strong> (nld.com.vn)</div>','PR đủ trò ranh ma: Chi phối truyền thông ','','',0,'http://tamdiem.com.vn/sources/images/PR.jpg','',0,0,0,0,473,0,1447209662,1451545034,1),(134,'Đám cưới truyền thống ở Ấn Độ','dam-cuoi-truyen-thong-o-an-do',16,'news_item','Đám cưới truyền thống ở Ấn Độ','Trên thế giới ít có đám cưới nào có thể kéo dài tới tận 5 ngày và toàn bộ chi phí đám cưới do gia đình cô dâu lo liệu nhưng đám cưới truyền thống của người Ấn Độ là một đám cưới như thế.','Trên thế giới ít có đám cưới nào có thể kéo dài tới tận 5 ngày và toàn bộ chi phí đám cưới do gia đình cô dâu lo liệu nhưng đám cưới truyền thống của người Ấn Độ là một đám cưới như thế.','<h5 class=\"icon-vn white-t text-capitalize\"><strong style=\"line-height: 1.5em;\">Tr&ecirc;n thế giới &iacute;t c&oacute; đ&aacute;m cưới n&agrave;o c&oacute; thể k&eacute;o d&agrave;i tới tận 5 ng&agrave;y v&agrave; to&agrave;n bộ chi ph&iacute; đ&aacute;m cưới do gia đ&igrave;nh c&ocirc; d&acirc;u lo liệu nhưng đ&aacute;m cưới truyền thống của người Ấn Độ l&agrave; một đ&aacute;m cưới như thế.</strong></h5>\n<div class=\"panel\">\n<div class=\"p-10 white-t\">\n<div class=\"panel-body\">\n<div class=\"center white-b\"><img class=\"img-responsive img-thumbnail\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819893674905.jpg\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"519\" height=\"346\" /></div>\n<div class=\"info-detail\">\n<p style=\"text-align: justify;\">Đ&aacute;m cưới thường l&agrave; ng&agrave;y trọng đại của c&aacute;c cặp đ&ocirc;i v&agrave; bất cứ quốc gia n&agrave;o cũng c&oacute; những tập tục cưới truyền thống kh&aacute;c nhau, nhưng đ&aacute;m cưới truyền thống của người Ấn Độ l&agrave; một đ&aacute;m cưới đặc biệt. N&oacute; kh&ocirc;ng chỉ đặc biệt ở trang phục, m&agrave;u sắc, thời gian tổ chức m&agrave; c&ograve;n l&agrave; n&eacute;t truyền thống văn h&oacute;a với nghi lễ v&agrave; biểu tượng, c&aacute;c nghi lễ trong đ&aacute;m cưới Ấn l&agrave; biểu trưng cao nhất cho truyền thống v&agrave; sắc m&agrave;u của d&acirc;n tộc n&agrave;y.<br /><br /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819898674905.jpg\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"515\" height=\"342\" />&nbsp;</p>\n<p style=\"text-align: justify;\">V&agrave; chắc chắn bạn sẽ kh&ocirc;ng t&igrave;m thấy một đ&aacute;m cưới n&agrave;o đặc biệt như đ&aacute;m cưới truyền thống của người Ấn Độ. Đầu ti&ecirc;n l&agrave; thời gian, đ&aacute;m cưới c&oacute; thể k&eacute;o d&agrave;i tới 5 ng&agrave;y, thậm ch&iacute; hơn v&agrave; đặc biệt kh&aacute;c hẳn với bất cứ đ&aacute;m cưới theo kiểu phương T&acirc;y n&agrave;o m&agrave; bạn từng biết.</p>\n<p style=\"text-align: justify;\">Từ khi đ&iacute;nh h&ocirc;n tới trước đ&aacute;m cưới, người Ấn c&oacute; rất nhiều nghi lễ quan trọng. Lễ đầu ti&ecirc;n l&agrave; Misri &ndash; lễ trao nhẫn. Nghi lễ n&agrave;y diễn ra trong v&agrave;i ng&agrave;y trước đ&aacute;m cưới. 7 người phụ nữ đ&atilde; c&oacute; gia đ&igrave;nh sẽ đến v&agrave; vẽ những dấu hiệu của thần Ganesha bằng bột đỏ l&ecirc;n một chiếc b&aacute;t đựng đường bằng đ&aacute;.&nbsp; <br /><br /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819914914905.JPG\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"521\" height=\"346\" />&nbsp;</p>\n<p style=\"text-align: justify;\">C&ocirc; d&acirc;u ch&uacute; rể v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh sẽ c&ugrave;ng cầu nguyện v&agrave; trao nhau v&ograve;ng hoa v&agrave; chiếc nhẫn cưới bằng v&agrave;ng trước sự chứng kiến của người l&agrave;m lễ. Gia đ&igrave;nh ch&uacute; rể sẽ đặt v&agrave;o tay c&ocirc; d&acirc;u một giỏ hoa quả v&agrave; những m&oacute;n qu&agrave; kh&aacute;c v&agrave; c&ocirc; d&acirc;u sẽ lấy hoa quả trong đ&oacute; thả v&agrave;o chiếc b&aacute;t đường ban đầu. Đ&acirc;y l&agrave; dấu hiệu cho sự đ&iacute;nh ước v&agrave; hứa hẹn một cuộc sống ngọt ng&agrave;o ph&iacute;a trước.&nbsp; <br /><br /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819908904905.jpg\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"530\" height=\"408\" />&nbsp;</p>\n<p style=\"text-align: justify;\">Nghi lễ tiếp theo l&agrave; Mehendi, diễn ra v&agrave;o ng&agrave;y trước đ&aacute;m cưới, trong buổi tr&agrave; chiều của phụ nữ (đ&agrave;n &ocirc;ng kh&ocirc;ng được ph&eacute;p tham gia). Đ&acirc;y l&agrave; l&uacute;c c&ocirc; d&acirc;u được vẽ henna l&ecirc;n b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n để thể hiện sự phụ thuộc của người phụ nữ đối với người chồng từ nay về sau. Lễ hội n&agrave;y thường diễn ra c&ugrave;ng l&uacute;c với lễ Sagri, khi gia đ&igrave;nh ch&uacute; rể mang hoa v&agrave; qu&agrave; tới đặt ở cửa nh&agrave; c&ocirc; d&acirc;u.</p>\n<p style=\"text-align: justify;\">Cũng v&agrave;o ng&agrave;y n&agrave;y, người l&agrave;m lễ sẽ thực hiện nghi thức Nav-Graha Puja ở nh&agrave; của c&ocirc; d&acirc;u v&agrave; ch&uacute; rể. Th&agrave;y l&agrave;m lễ cầu nguyện tới th&aacute;nh thần của 9 h&agrave;nh tinh, xin họ ban phước l&agrave;nh cho đ&ocirc;i uy&ecirc;n ương. Lễ tiếp theo l&agrave; lễ Ghari Puja, khi th&agrave;y l&agrave;m lễ mời kh&aacute;ch bột, dừa, hạt dẻ, gạo v&agrave; gia vị thể hiện sự thịnh vượng.<br /><br /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819920284905.jpg\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"537\" height=\"358\" />&nbsp;</p>\n<p style=\"text-align: justify;\">Cả 2 b&agrave; mẹ sẽ mặc đồ cưới cho con m&igrave;nh v&agrave; tới nh&agrave; của th&ocirc;ng gia với một chiếc b&igrave;nh nước đội tr&ecirc;n đầu. Họ sẽ d&ugrave;ng dao để ch&eacute;m v&agrave;o nước để xua đuổi t&agrave; kh&iacute; sau đ&oacute; sẽ tặng th&ecirc;m cho th&ocirc;ng gia tiền v&agrave; hoa.</p>\n<p style=\"text-align: justify;\">Đến l&uacute;c n&agrave;y, c&ocirc; d&acirc;u v&agrave; ch&uacute; rể sẽ mặc một bộ đồ cũ. Bộ đồ n&agrave;y sau đ&oacute; sẽ bị c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh x&eacute; ra, thể hiện sự kết th&uacute;c của cuộc đời cũ, chuyển sang cuộc sống mới.Tiếp đến sẽ l&agrave; lễ Sangeet, cả gia đ&igrave;nh sẽ nhảy m&uacute;a, h&aacute;t v&agrave; ăn uống linh đ&igrave;nh, chuẩn bị cho ng&agrave;y trọng đại h&ocirc;m sau.</p>\n<p style=\"text-align: justify;\">V&agrave;o ng&agrave;y cưới, nghi lễ đầu ti&ecirc;n l&agrave; lễ Haldi, trong đ&oacute; c&ocirc; d&acirc;u sẽ được được tẩy rửa bằng củ nghệ ở nh&agrave;. Nghi lễ Swagatam l&agrave; l&uacute;c những họ h&agrave;ng gi&uacute;p c&ocirc; d&acirc;u mặc chiếc sari cưới. Khi ch&uacute; rể đến, anh ta phải bước ch&acirc;n phải nhẹ nh&agrave;ng v&agrave;o nh&agrave; v&agrave; rửa ch&acirc;n bằng sữa v&agrave; nước. Lễ cưới bắt đầu bằng việc ch&uacute; rể trao qu&agrave; cho bố vợ v&agrave; bố vợ sẽ dẫn c&ocirc; d&acirc;u ra trao v&agrave;o tay ch&uacute; rể.<br /><br /><img class=\"img-responsive\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" src=\"http://vietnamairlines.hanoi.vn/Img.ashx?635398819903364905.jpg\" alt=\"Đ&aacute;m cưới truyền thống ở Ấn Độ\" width=\"539\" height=\"359\" /></p>\n<p style=\"text-align: justify;\">Đến l&uacute;c n&agrave;y, c&ocirc; d&acirc;u sẽ nhặt gạo v&agrave; n&eacute;m v&agrave; ngọn lửa thi&ecirc;ng để được ch&iacute;nh thức c&ocirc;ng nhận quan hệ vợ chồng. C&ocirc; d&acirc;u cũng sẽ phải đi quanh ngọn lửa n&agrave;y 4 lần, sờ v&agrave;o một h&ograve;n đ&aacute; sau mỗi v&ograve;ng để thể hiện sẵn s&agrave;ng đương đầu với mọi kh&oacute; khăn m&agrave; c&ocirc; d&acirc;u bắt gặp.&nbsp; Vạt &aacute;o sari của c&ocirc; d&acirc;u sẽ được buộc v&agrave;o khăn của ch&uacute; rể, họ l&agrave;m th&ecirc;m một v&agrave;i nghi lễ nữa trước khi chuyển sang phần hội. Đ&acirc;y l&agrave; l&uacute;c tất cả c&aacute;c th&agrave;nh vi&ecirc;n c&ugrave;ng nhảy m&uacute;a, h&aacute;t ca trong tiếng nhạc vui của ng&agrave;y trọng đại. Rất nhiều người khi đọc xong về c&aacute;c nghi lễ n&agrave;y b&agrave;y tỏ lo ngại về t&agrave;i ch&iacute;nh khi l&agrave;m rể người Ấn. Mối lo n&agrave;y l&agrave; ho&agrave;n to&agrave;n v&ocirc; nghĩa. Phần lớn chi ph&iacute; cho đ&aacute;m cưới Ấn l&agrave; do gia đ&igrave;nh c&ocirc; d&acirc;u lo liệu.</p>\n<p style=\"text-align: right;\"><em><span class=\"kaio-italic\">Theo vietnamairlines.hanoi.vn</span></em></p>\n</div>\n</div>\n</div>\n</div>','Đám cưới truyền thống ở Ấn Độ','','',0,'http://tamdiem.com.vn/sources/images/dam%20cuoi%20An%20Do.jpg','',0,0,0,0,192,0,1447813351,1447814733,1),(135,'Divyanka Tripathi : Sau khi chia tay với Ssharad, đóng phim trở nên khó khăn','divyanka-tripathi',16,'news_item','Divyanka Tripathi,Ssharad Malhotra, chia tay','Divyanka Tripathi – nữ diễn viên nổi tiếng trong phim Hạnh Phúc Muộn Màng đã có cuộc phỏng vấn độc quyền với báo Indian Express, chia sẻ về cuộc sống của cô sau khi chia tay với bạn trai lâu năm Ssharad Malhotra, những bài học kinh nghiệm cũng như khả năng hợp tác với bạn trai cũ. ','Divyanka Tripathi – nữ diễn viên nổi tiếng trong phim Hạnh Phúc Muộn Màng đã có cuộc phỏng vấn độc quyền với báo Indian Express, chia sẻ về cuộc sống của cô sau khi chia tay với bạn trai lâu năm Ssharad Malhotra, những bài học kinh nghiệm cũng như khả năng hợp tác với bạn trai cũ. ','<p style=\"text-align: justify;\"><strong>Divyanka Tripathi &ndash; nữ diễn vi&ecirc;n nổi tiếng trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng đ&atilde; c&oacute; cuộc phỏng vấn độc quyền với b&aacute;o Indian Express, chia sẻ về cuộc sống của c&ocirc; sau khi chia tay với bạn trai l&acirc;u năm Ssharad Malhotra, những b&agrave;i học kinh nghiệm cũng như khả năng hợp t&aacute;c với bạn trai cũ.</strong></p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source/divyankatripathi-ssharadmalhotra1.jpg\" alt=\"\" width=\"500\" height=\"350\" /></p>\n<p><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Divyanka Tripathi v&agrave;&nbsp;Ssharad Malhotra thuở c&ograve;n mặn nồng.</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>B&acirc;y giờ bạn đ&atilde; trở lại b&igrave;nh thường rồi chứ, Divyanka?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i kh&ocirc;ng thể giống như trước đ&acirc;y. Sau những thăng trầm của cuộc sống, 1 người sẽ thay đổi v&agrave; t&ocirc;i đ&atilde; thay đổi để tốt hơn. B&acirc;y giờ t&ocirc;i đ&atilde; trở n&ecirc;n độc lập hơn nhiều. T&ocirc;i đ&atilde; kh&aacute;m ph&aacute; ra cuộc sống của ch&iacute;nh m&igrave;nh v&agrave; gi&aacute; trị bản th&acirc;n m&igrave;nh.</p>\n<p style=\"text-align: justify;\"><strong>Bạn c&oacute; hối tiếc g&igrave; kh&ocirc;ng khi nh&igrave;n lại mối quan hệ với Ssharad (Malhotra)?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i nghĩ l&agrave; kh&ocirc;ng. T&ocirc;i đ&atilde; c&oacute; 1 mối t&igrave;nh tuyệt đẹp. Mặc d&ugrave; l&agrave; một diễn vi&ecirc;n nhưng bản chất t&ocirc;i l&agrave; người giản dị. T&ocirc;i đ&atilde; chọn cho ch&iacute;nh m&igrave;nh cuộc sống n&agrave;y. Kh&ocirc;ng ai &aacute;p đặt n&oacute; cả. Thời điểm đ&oacute;, t&ocirc;i thật sự cảm thấy rất hạnh ph&uacute;c nhưng &hellip; rồi mọi thứ đ&atilde; đổ vỡ. T&ocirc;i bị sốc v&agrave; rất đau l&ograve;ng.</p>\n<p style=\"text-align: justify;\"><strong>C&oacute; sự li&ecirc;n hệ n&agrave;o giữa việc chia tay v&agrave; tai nạn g&atilde;y ch&acirc;n?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Sau khi chia tay, việc đ&oacute;ng phim trở n&ecirc;n kh&oacute; khăn. T&ocirc;i kh&oacute;c nhưng t&ocirc;i kh&ocirc;ng thể để lộ điều đ&oacute; khi quay phim. T&ocirc;i đ&atilde; bị ốm v&agrave; thậm ch&iacute; bị ngộ độc thực phẩm. Mọi người xung quanh t&ocirc;i bắt đầu n&oacute;i rằng c&aacute;i g&igrave; đ&oacute; bị xui xẻo. Rất may tai nạn g&atilde;y ch&acirc;n đ&atilde; xảy ra v&agrave; tất cả chuyện phiền n&atilde;o đều qua đi. T&ocirc;i đ&atilde; xử l&yacute; được tai nạn của m&igrave;nh v&agrave; rất nhiều điều kh&aacute;c. T&ocirc;i nhận ra rằng nếu t&ocirc;i lưu t&acirc;m đến tất cả những điều n&agrave;y th&igrave; sự tan vỡ kh&ocirc;ng l&agrave; vấn đề nghi&ecirc;m trọng.</p>\n<p style=\"text-align: justify;\"><strong>Cuộc sống của bạn thay đổi thế n&agrave;o sau khi chia tay?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Trước đ&acirc;y, cuộc sống của t&ocirc;i lu&ocirc;n c&oacute; Ssharad. Giờ n&oacute; đ&atilde; kh&aacute;c hẳn. Sau khi chia tay, cuộc sống trở n&ecirc;n kh&oacute; khăn hơn v&igrave; t&ocirc;i kh&ocirc;ng gặp anh ấy nữa. Sự thay đổi đột ngột n&agrave;y khiến t&ocirc;i đau khổ v&agrave; l&agrave;m t&ocirc;i kh&oacute;c. T&ocirc;i kh&ocirc;ng chia sẻ điều n&agrave;y với ba mẹ v&igrave; t&ocirc;i kh&ocirc;ng muốn tạo g&aacute;nh nặng cho họ. Rất may, t&ocirc;i bị g&atilde;y ch&acirc;n, v&agrave; khi đ&oacute;, mẹ t&ocirc;i đ&atilde; đến an ủi. T&ocirc;i nhận ra rằng nhờ những người lớn ở cạnh, bạn sẽ cố gắng t&igrave;m c&aacute;ch khiến bản th&acirc;n vui vẻ bởi v&igrave; bạn biết nếu bạn vui th&igrave; họ cũng sẽ vui. V&agrave; v&igrave; thế, b&acirc;y giờ t&ocirc;i t&igrave;m c&aacute;ch trở n&ecirc;n vui vẻ.</p>\n<table style=\"height: 480px; margin-left: auto; margin-right: auto;\" width=\"458\">\n<tbody>\n<tr>\n<td>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyankasolodinner.jpg\" alt=\"\" width=\"450\" height=\"450\" /></p>\n<p style=\"text-align: center;\"><em>Divyanka ăn tối một m&igrave;nh sau giờ đ&oacute;ng phim. </em></p>\n<p style=\"text-align: center;\"><em>(Nguồn: Instagram của&nbsp;Divyanka Tripathi)</em>&nbsp;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>B&acirc;y giờ bạn đang l&agrave;m g&igrave;?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i vừa mua chiếc nhẫn đ&iacute;nh h&ocirc;n n&agrave;y cho ch&iacute;nh m&igrave;nh. Việc đeo n&oacute; khiến t&ocirc;i cảm thấy vui. T&ocirc;i cũng đ&atilde; một m&igrave;nh đi ăn tối.&nbsp; Divyanka lu&ocirc;n sống v&igrave; người kh&aacute;c. B&acirc;y giờ t&ocirc;i đang tận hưởng thời gian của ch&iacute;nh m&igrave;nh v&agrave; t&ocirc;i đ&atilde; ph&aacute;t hiện ra một Divyanka mới mẻ, v&ocirc; tư v&agrave; mạo hiểm.</p>\n<table style=\"height: 515px; margin-left: auto; margin-right: auto;\" width=\"478\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//divyanka21.jpg\" alt=\"\" width=\"470\" height=\"500\" /></p>\n<p style=\"text-align: center;\"><em>Divyanka trong 1 chuyến đi du lịch sau khi chia tay&nbsp;</em></p>\n<p style=\"text-align: center;\"><em>(Nguồn: Instagram của Divyanka Tripathi)</em></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>Bạn đ&atilde; c&oacute; được sức mạnh từ đ&acirc;u nữa để vượt qua sự chia tay?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Từ những người h&acirc;m mộ của t&ocirc;i. Khi chia tay, t&ocirc;i cảm thấy rất c&ocirc; đơn. T&igrave;nh y&ecirc;u của t&ocirc;i đ&atilde; ra đi nhưng tr&aacute;i lại, t&ocirc;i đ&atilde; nhận được t&igrave;nh y&ecirc;u lớn của c&aacute;c fans, điều đ&oacute; thật sự khiến t&ocirc;i rất vui v&agrave; cảm động.</p>\n<p style=\"text-align: justify;\"><strong>Bạn c&oacute; r&uacute;t ra được điều g&igrave; từ mối t&igrave;nh n&agrave;y?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: C&oacute; chứ. Ch&uacute;ng ta kh&ocirc;ng bao giờ được đ&aacute;nh mất bản th&acirc;n. H&atilde;y lu&ocirc;n lu&ocirc;n vui vẻ v&igrave; điều n&agrave;y sẽ l&agrave;m người kh&aacute;c vui. Đừng bao giờ hy vọng qu&aacute; nhiều v&igrave; c&agrave;ng hy vọng nhiều, bạn sẽ c&agrave;ng đau khổ. T&ocirc;i nhận ra rằng t&ocirc;i phải l&agrave;m l&agrave;m mọi thứ để trở n&ecirc;n vui vẻ.</p>\n<p style=\"text-align: justify;\"><strong>Trước đ&acirc;y bạn rất gần gũi với ba mẹ Ssharad. Hiện giờ bạn c&ograve;n giữ li&ecirc;n hệ với họ kh&ocirc;ng?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Hiện giờ t&ocirc;i kh&ocirc;ng n&oacute;i chuyện với họ, nhưng t&ocirc;i thực sự t&ocirc;n trọng họ. T&ocirc;i d&agrave;nh t&igrave;nh cảm đặc biệt cho mẹ của anh ấy.</p>\n<p style=\"text-align: justify;\"><strong>Trong tương lai bạn sẽ mở l&ograve;ng để l&agrave;m việc chung với Ssharad chứ?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i sẵn s&agrave;ng l&agrave;m việc chung với anh ấy. C&ograve;n về t&igrave;nh cảm c&aacute; nh&acirc;n th&igrave; điều đ&oacute; sẽ kh&ocirc;ng xảy ra lần nữa đ&acirc;u.</p>\n<p style=\"text-align: justify;\"><strong>Bạn cảm thấy ổn với cuộc sống độc th&acirc;n sau thời gian d&agrave;i kh&ocirc;ng?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i đang t&igrave;m hiểu về những điều m&agrave; m&ocirc;t c&ocirc; g&aacute;i trải qua khi độc th&acirc;n. Trong x&atilde; hội của ch&uacute;ng ta, những c&ocirc; g&aacute;i đơn th&acirc;n kh&ocirc;ng được t&ocirc;n trọng lắm. T&ocirc;i phải cẩn thận với người ở b&ecirc;n cạnh t&ocirc;i, điều n&agrave;y rất dễ để người kh&aacute;c b&agrave;n t&aacute;n v&igrave; t&ocirc;i b&acirc;y giờ l&agrave; người độc th&acirc;n.</p>\n<p style=\"text-align: justify;\"><strong>Một c&acirc;u hỏi cuối c&ugrave;ng đ&oacute; l&agrave; bạn sẽ chấp nhận một cuộc h&ocirc;n nh&acirc;n được sắp đặt?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: (Cười) Hiện giờ t&ocirc;i đang mở l&ograve;ng với mọi thứ đ&acirc;y, kể cả điều đ&oacute; được sắp đặt. Tuy nhi&ecirc;n cần c&oacute; sự đ&aacute;p trả từ người ấy. T&igrave;nh y&ecirc;u kh&ocirc;ng thể 1 chiều được. T&ocirc;i muốn người đ&agrave;n &ocirc;ng của t&ocirc;i sẽ y&ecirc;u t&ocirc;i nhiều như t&ocirc;i y&ecirc;u anh ấy. Nếu kh&ocirc;ng, t&ocirc;i sẽ kh&ocirc;ng duy tr&igrave; mối quan hệ. T&ocirc;i muốn đi nhiều nơi v&agrave; t&ocirc;i cũng muốn người ấy đi nhiều nơi. Ch&uacute;ng t&ocirc;i cần gi&uacute;p đỡ nhau để c&ugrave;ng ph&aacute;t triển chứ kh&ocirc;ng phải l&agrave; giới hạn nhau.</p>\n<p style=\"text-align: right;\"><em>Kh&aacute;nh Linh</em> (<em>indianexpress.com)</em></p>','Divyanka Tripathi','','',0,'http://tamdiem.com.vn/sources/images/JNgaR.jpg','',0,0,0,0,191,0,1447891846,1450310286,1),(136,'Phim Việt Nam \'Một Thời Ngang Dọc\'','phim-viet-nam-mot-thoi-ngang-doc',16,'news_item','phim Việt Nam, Một thời ngang dọc, HTV9, Quý Bình','Một Thời Ngang Dọc là  câu chuyện hiện đại được xây dựng dựa trên hai yếu tố chủ đạo: Hành động đường phố và tình cảm lãng mạn.','Một Thời Ngang Dọc là câu chuyện hiện đại được xây dựng dựa trên hai yếu tố chủ đạo: Hành động đường phố và tình cảm lãng mạn','<p style=\"text-align: justify;\"><strong>Một Thời Ngang Dọc l&agrave; c&acirc;u chuyện hiện đại được x&acirc;y dựng dựa tr&ecirc;n hai yếu tố chủ đạo: H&agrave;nh động đường phố v&agrave; t&igrave;nh cảm l&atilde;ng mạn</strong></p>\n<p style=\"text-align: justify;\">Quốc Qu&acirc;n (23 tuổi) đang l&agrave; sinh vi&ecirc;n trường Đại học Cảnh S&aacute;t, được nhận nhiệm vụ gia nhập đường d&acirc;y chống tội phạm. Số phận đưa đẩy anh đến một x&oacute;m lao động ngh&egrave;o với đầy đủ c&aacute;c th&agrave;nh phần dưới đ&aacute;y x&atilde; hội, nằm ngo&agrave;i v&ograve;ng ph&aacute;p luật. Quốc Qu&acirc;n quen biết với Mộc L&acirc;m (22 tuổi), c&ocirc; g&aacute;i cầm đầu nh&oacute;m chuy&ecirc;n m&oacute;c t&uacute;i.</p>\n<table style=\"margin-left: auto; margin-right: auto; height: 370px;\" width=\"562\">\n<tbody>\n<tr>\n<td>\n<p>&nbsp;<img src=\"/tinymce/source/Quy Binh (MTND).jpg\" alt=\"\" width=\"550\" height=\"330\" /></p>\n<h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: #808080;\"> <em>Qu&yacute; B&igrave;nh trong vai Quốc Qu&acirc;n - ch&agrave;ng sinh vi&ecirc;n trường Đại học Cảnh S&aacute;t.</em></span></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Cuộc sống ở khu x&oacute;m trọ ngh&egrave;o n&agrave;n, nhơ nhớp với đầy đủ những mặt tr&aacute;i của x&atilde; hội. Quốc Qu&acirc;n v&agrave; Mộc L&acirc;m lu&ocirc;n phải sống thường trực trong những nỗi lo &acirc;u mưa sinh thường ng&agrave;y. Tuy thế dần dần Quốc Qu&acirc;n lại ng&agrave;y một thấm nhuần tinh thần giang hồ v&agrave; anh cũng được dịp ngộ ra những n&eacute;t đẹp, những ch&acirc;n l&yacute; của thế giới n&agrave;y. Ch&iacute;nh Quốc Qu&acirc;n một c&aacute;ch ho&agrave;n to&agrave;n v&ocirc; thức đ&atilde; trở th&agrave;nh &ldquo;thủ lĩnh&rdquo; của x&oacute;m trọ. Anh lu&ocirc;n c&oacute; mặt kịp thời để giải quyết những vướng mắc, những vụ x&ocirc; x&aacute;t hay những kh&oacute; khăn cho người d&acirc;n nơi đ&acirc;y ở một giới hạn n&agrave;o đ&oacute;.</p>\n<table style=\"margin-left: auto; margin-right: auto; height: 366px;\" width=\"558\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//MTND-3.jpg\" alt=\"\" width=\"550\" height=\"330\" /></p>\n<h6><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: #808080;\">&nbsp; Anh Thư h&oacute;a th&acirc;n th&agrave;nh c&ocirc; g&aacute;i cầm đầu nh&oacute;m chuy&ecirc;n m&oacute;c t&uacute;i Mộc L&acirc;m.</span></em></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Đ&acirc;y cũng ch&iacute;nh l&agrave; thời điểm Quốc Qu&acirc;n l&agrave;m quen với Long - một tay giang hồ c&oacute; &ldquo;số m&aacute;&rdquo;, vừa mới ra t&ugrave; &ndash; thực chất l&agrave; một nguồn tin được c&agrave;i v&agrave;o giới giang hồ từ nhiều năm nay trong một chuy&ecirc;n &aacute;n d&agrave;i hơi của trung tướng &Acirc;n, gi&aacute;m đốc c&ocirc;ng an th&agrave;nh phố.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//MTND-7.jpg\" alt=\"\" width=\"550\" height=\"330\" /></p>\n<h6 style=\"text-align: center;\"><span style=\"color: #808080;\"><em>Long Điền với tạo h&igrave;nh Long giang hồ.</em></span></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Sự đồng điệu về t&acirc;m hồn, những sẻ chia trong cuộc sống đ&atilde; khiến Long v&agrave; Quốc Qu&acirc;n dần trở th&agrave;nh đ&ocirc;i bạn t&acirc;m giao. Long bản lĩnh, c&oacute; học thức nhưng sớm phải mưu sinh bởi ba mẹ mất sớm, một tay phải nu&ocirc;i em g&aacute;i ăn học. Đ&ocirc;ng Nghi (em g&aacute;i Long) - c&ocirc; sinh vi&ecirc;n đại học dễ thương, nh&iacute; nhảnh đem l&ograve;ng y&ecirc;u Quốc Qu&acirc;n. V&agrave; trớ tr&ecirc;u thay Long lại thầm y&ecirc;u Mộc L&acirc;m. Mối quan hệ t&igrave;nh y&ecirc;u giữa bốn con người trẻ tuổi n&agrave;y h&ograve;a quyện trong d&ograve;ng chảy của cuộc sống mưu sinh nơi x&oacute;m trọ, c&ugrave;ng những &acirc;n o&aacute;n giang hồ trong thế giới ngầm giữa đất S&agrave;i G&ograve;n với sự xuất hiện của h&agrave;ng loạt &ldquo;qu&aacute;i kiệt&rdquo; như A Mẫn cầm đầu đường d&acirc;y x&atilde; hội đen người Hoa, Bốn &ldquo;lơ xe&rdquo; tay anh chị lẫy lừng ra t&ugrave; với mối hận th&ugrave; chất chứa một phần tư thế kỷ&hellip; ,Luận lươn _một giang hồ kh&eacute;t tiếng. Tất cả g&oacute;p phần tạo th&agrave;nh một bản giao hưởng cuộc sống mang m&agrave;u sắc hết sức ri&ecirc;ng biệt đầy ấn tượng.</p>\n<table style=\"margin-left: auto; margin-right: auto; height: 290px;\" width=\"558\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//MTND-6.jpg\" alt=\"\" width=\"550\" height=\"330\" /></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">Khi tinh y&ecirc;u v&agrave; những gi&aacute; trị cao đẹp trong cuộc sống dường như đ&atilde; cảm h&oacute;a tất cả gi&uacute;p con người ta ho&agrave;n lương v&agrave; tạo động lực để vươn l&ecirc;n th&igrave; Mộc L&acirc;m ph&aacute;t hiện ra căn bệnh hiểm ngh&egrave;o của m&igrave;nh. C&ocirc; trốn tr&aacute;nh tất cả v&agrave; bỏ đi. H&agrave;nh tr&igrave;nh m&agrave; Quốc Qu&acirc;n phải trải qua để đi t&igrave;m Mộc L&acirc;m, động vi&ecirc;n c&ocirc; trong những gi&acirc;y ph&uacute;t hạnh ph&uacute;c ngắn ngủi c&ograve;n s&oacute;t lại thực sự l&agrave; bản t&igrave;nh ca đầy thương cảm nhưng cũng chứa chan hơi ấm t&igrave;nh y&ecirc;u thương v&agrave; ngập tr&agrave;n những khoảnh khắc thăng hoa tuyệt diệu.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td>\n<p><iframe src=\"http://www.youtube.com/embed/yQPveXeXFdw\" width=\"500\" height=\"300\"></iframe></p>\n<h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: #808080;\">&nbsp; <em>Trailer phim \"Một Thời Ngang Dọc\"</em></span></h6>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: right;\"><em>T&acirc;m Điểm</em></p>','Phim Việt Nam \'Một Thời Ngang Dọc\'','','',0,'http://tamdiem.com.vn/sources/images/Mot%20thoi%20ngang%20doc.jpg','',0,0,0,0,190,0,1448247737,1449202035,0),(137,'Chương trình truyền hình nào được đề cử Mai Vàng 2015? ','mai-vang-2015',14,'news_item','chương trình truyền hình, giải mai vàng 2015','Năm 2015, các chương trình truyền hình thực tế tiếp tục nở rộ, đặc biệt là lĩnh vực hài nhưng để tìm ra một chương trình đột phá, có sức hút như mọi năm thì rất khó. ','Năm 2015, các chương trình truyền hình thực tế tiếp tục nở rộ, đặc biệt là lĩnh vực hài nhưng để tìm ra một chương trình đột phá, có sức hút như mọi năm thì rất khó. ','<p><strong><em><span style=\"color: #808080;\">Năm 2015, c&aacute;c chương tr&igrave;nh truyền h&igrave;nh thực tế tiếp tục nở rộ, đặc biệt l&agrave; lĩnh vực h&agrave;i nhưng để t&igrave;m ra một chương tr&igrave;nh đột ph&aacute;, c&oacute; sức h&uacute;t như mọi năm th&igrave; rất kh&oacute;.</span></em></strong></p>\n<p style=\"text-align: justify;\">Hiện đa phần c&aacute;c chương tr&igrave;nh truyền h&igrave;nh thực tế l&ecirc;n s&oacute;ng tập trung chủ yếu v&agrave;o tối thứ 7, chủ nhật. B&ecirc;n cạnh c&aacute;c chương tr&igrave;nh nội dung h&agrave;i v&agrave;o m&ugrave;a 2, m&ugrave;a 3, một số chương tr&igrave;nh mới về lĩnh vực ca h&aacute;t, nhảy m&uacute;a xuất hiện. Tuy nhi&ecirc;n, điều khiến c&aacute;c chương tr&igrave;nh cả mới lẫn cũ kh&oacute; nổi bật l&agrave; lực lượng danh h&agrave;i tham gia vẫn chỉ l&agrave; c&aacute;c gương mặt quen thuộc: Ho&agrave;i Linh, Việt Hương, Trấn Th&agrave;nh, Trường Giang&hellip;.</p>\n<p style=\"text-align: justify;\"><strong>H&agrave;i tiếp tục \"nở nồi\"</strong></p>\n<p style=\"text-align: justify;\">Tiếp nối chương tr&igrave;nh &ldquo;Người b&iacute; ẩn&rdquo; v&agrave; &ldquo;Ơn giời, cậu đ&acirc;y rồi&rdquo;, năm nay, Đ&ocirc;ng T&acirc;y Promotion ra mắt chương tr&igrave;nh &ldquo;B&iacute; mật đ&ecirc;m chủ nhật&rdquo; (Việt h&oacute;a từ format &lsquo;Whose line is it anyway&rsquo; của h&atilde;ng Hattrick International). Đ&acirc;y l&agrave; một chương tr&igrave;nh thuộc thể loại h&agrave;i t&igrave;nh huống, khai th&aacute;c khả năng ứng biến của người chơi trước c&aacute;c thử th&aacute;ch được chương tr&igrave;nh giữ b&iacute; mật. Ở mỗi tập ph&aacute;t s&oacute;ng, chương tr&igrave;nh mời một vị kh&aacute;ch đặc biệt l&agrave; ca sĩ, diễn vi&ecirc;n, người mẫu hoặc nghệ sĩ h&agrave;i xuất hiện để c&ugrave;ng Việt Hương, Trường Giang, Trấn Th&agrave;nh v&agrave; một danh h&agrave;i kh&aacute;ch mời khi l&agrave; Ch&iacute; T&agrave;i, l&uacute;c l&agrave; Nam Kh&aacute;nh vượt qua c&aacute;c thử th&aacute;ch của người nắm giữ b&iacute; mật l&agrave; nghệ sĩ Ho&agrave;i Linh.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264770\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/img-2016-1448265890420/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.JPG\" alt=\"\nC&aacute;c nghệ sĩ trong chương tr&igrave;nh B&iacute; mật đ&ecirc;m chủ nhật\n\" /></div>\n<div class=\"PhotoCMS_Caption\">\n<p style=\"text-align: center; margin: 0px;\"><em><span style=\"color: #999999;\">C&aacute;c nghệ sĩ trong chương tr&igrave;nh \"B&iacute; mật đ&ecirc;m chủ nhật\"</span></em></p>\n</div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Tương tự,&nbsp;&ldquo;Hội qu&aacute;n tiếu l&acirc;m&rdquo; cũng l&agrave; chương tr&igrave;nh mới, tương t&aacute;c dựa v&agrave;o khả năng ứng biến, giải quyết c&aacute;c t&igrave;nh huống của c&aacute;c diễn vi&ecirc;n h&agrave;i c&ugrave;ng với sự g&oacute;p mặt của kh&aacute;ch mời l&agrave; diễn vi&ecirc;n, người mẫu, ca sĩ nổi tiếng.</p>\n<p style=\"text-align: justify;\">Với nội dung, h&igrave;nh thức kh&aacute; giống c&aacute;c chương tr&igrave;nh cũ &ldquo;Ơn giời cậu đ&acirc;y rồi&rdquo; (nghệ sĩ kh&ocirc;ng biết trước kịch bản), &ldquo;B&iacute; mật đ&ecirc;m chủ nhật&rdquo;, &ldquo;Hội qu&aacute;n tiếu l&acirc;m&rdquo; kh&ocirc;ng c&oacute; nhiều n&eacute;t mới lạ, hấp dẫn nhưng c&oacute; rating (tỉ suất người xem) cao l&agrave; nhờ sự c&oacute; mặt Ho&agrave;i Linh, Việt Hương, Trấn Th&agrave;nh, Trường Giang&hellip;.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264773\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/bo-mac-nha-phuong-truong-giang-om-ghi-dien-vien-kim-tuyen-1448266142458/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.jpg\" alt=\"\nChương tr&igrave;nh Ơn giời cậu đ&acirc;y rồi m&ugrave;a 2\n\" /></div>\n<div class=\"PhotoCMS_Caption\">\n<p style=\"text-align: center; margin: 0px;\"><em><span style=\"color: #999999;\">Chương tr&igrave;nh \"Ơn giời cậu đ&acirc;y rồi\" m&ugrave;a 2</span></em></p>\n</div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">D&ugrave; l&ecirc;n s&oacute;ng m&ugrave;a đầu ti&ecirc;n nhưng 2 chương tr&igrave;nh truyền h&igrave;nh kh&aacute;c l&agrave; &ldquo;Ho&aacute;n đổi&rdquo; v&agrave; &ldquo;Bước nhảy ng&agrave;n c&acirc;n&rdquo; tạo ch&uacute; &yacute; v&igrave; mới lạ. &ldquo;Ho&aacute;n đổi&rdquo; (do Việt Nam sản xuất) l&agrave; chương tr&igrave;nh giải tr&iacute;, tạp kỹ h&agrave;i, lấy &yacute; tưởng từ h&agrave;nh tr&igrave;nh theo đuổi đam m&ecirc; v&agrave; thực hiện ước mơ nghề nghiệp của mỗi người. Theo đ&oacute;, sự bất ngờ v&agrave; tiếng cười sẽ đến từ những m&agrave;n &ldquo;đổi vai&rdquo; ngoạn mục, vui nhộn của c&aacute;c nghệ sĩ.</p>\n<p style=\"text-align: justify;\">&ldquo;Bước nhảy ng&agrave;n c&acirc;n&rdquo; (Việt h&oacute;a từ định dạng truyền h&igrave;nh của NBC Universal &ldquo;Dance your fat off&rdquo;) lại l&agrave; chương tr&igrave;nh nhảy m&uacute;a đầu ti&ecirc;n d&agrave;nh cho người thừa c&acirc;n. Qua cuộc thi, kh&aacute;n giả sẽ được chứng kiến những thay đổi v&agrave; nỗ lực kh&ocirc;ng ngừng của 12 th&iacute; sinh trong h&agrave;nh tr&igrave;nh &ldquo;giảm c&acirc;n để chiến thắng&rdquo; của họ.&nbsp; Tương tự, điều l&agrave;m n&ecirc;n sức h&uacute;t của c&aacute;c chương tr&igrave;nh n&agrave;y kh&ocirc;ng thể thiếu c&aacute;c nghệ sĩ h&agrave;i, MC nổi tiếng như Ho&agrave;i Linh, Trấn Th&agrave;nh, Trường Giang, Thanh Bạch&hellip;.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264775\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/hoan-doi-tap-1-dspl-9-1448265890310/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.jpg\" alt=\"Bộ ba MC của chương tr&igrave;nh Ho&aacute;n đổi\" /></div>\n<div class=\"PhotoCMS_Caption\"><em><span style=\"color: #999999;\">Bộ ba MC của chương tr&igrave;nh \"Ho&aacute;n đổi\"</span></em></div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><strong>Ca h&aacute;t, nhảy m&uacute;a nh&agrave;m ch&aacute;n</strong></p>\n<p style=\"text-align: justify;\">Kh&ocirc;ng c&oacute; nhiều chương tr&igrave;nh mới xuất hiện trong khi c&aacute;i cũ lại nhạt nhẽo dần khiến cho c&aacute;c chương tr&igrave;nh truyền h&igrave;nh thực tế mảng ca h&aacute;t, nhảy m&uacute;a năm nay trầm lắng. Nếu Vietnam Idol 2015 được xem l&agrave; m&ugrave;a th&agrave;nh c&ocirc;ng với chất lượng th&iacute; sinh g&acirc;y được sức h&uacute;t từ kh&aacute;n giả v&agrave; sự quan t&acirc;m của giới chuy&ecirc;n m&ocirc;n th&igrave; &ldquo;Giọng h&aacute;t Việt&rdquo; (người lớn) được kh&aacute;n giả ch&uacute; &yacute; từ c&aacute;c gi&aacute;m khảo danh tiếng (đặc biệt l&agrave; Mỹ T&acirc;m) chứ kh&ocirc;ng phải từ chất lượng th&iacute; sinh. Nhưng đi đến những v&ograve;ng thi cuối, chương tr&igrave;nh cũng nhạt dần v&igrave; những &ldquo;chi&ecirc;u tr&ograve;&rdquo; của c&aacute;c HLV kh&ocirc;ng c&ograve;n nhiều th&uacute; vị, mới lạ. Qu&aacute;n qu&acirc;n chương tr&igrave;nh năm nay cũng chưa phải l&agrave; gương mặt xuất sắc, nhận được sự đồng t&igrave;nh của phần đ&ocirc;ng kh&aacute;n giả, nhất l&agrave; giới chuy&ecirc;n m&ocirc;n.</p>\n<p style=\"text-align: justify;\">&ldquo;Thử th&aacute;ch c&ugrave;ng bước nhảy&rdquo; qua m&ugrave;a thứ tư vẫn khẳng định được một s&acirc;n chơi chất lượng, chuy&ecirc;n nghiệp d&agrave;nh cho những người c&oacute; đam m&ecirc; nhảy m&uacute;a thật sự nhưng độ n&oacute;ng đ&atilde; giảm r&otilde; rệt so với m&ugrave;a đầu ti&ecirc;n.</p>\n<div class=\"VCSortableInPreviewMode IMSCurrentEditorEditObject\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264776\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/duc-phuc-giong-hat-viet-bi-to-khong-xung-dang-my-tam-noi-gi-2-1448266266435/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.jpg\" alt=\"\nHLV Mỹ T&acirc;m v&agrave; học tr&ograve; Đức Ph&uacute;c biểu diễn trong đ&ecirc;m chung kết chương tr&igrave;nh giọng h&aacute;t Việt\n\" /></div>\n<div class=\"PhotoCMS_Caption\">\n<p style=\"text-align: center; margin: 0px;\"><span style=\"color: #999999;\"><em>HLV Mỹ T&acirc;m v&agrave; học tr&ograve; Đức Ph&uacute;c biểu diễn trong đ&ecirc;m chung kết chương tr&igrave;nh \"giọng h&aacute;t Việt\"</em></span></p>\n</div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">C&aacute;c chương tr&igrave;nh ca h&aacute;t, nhảy m&uacute;a d&agrave;nh cho thiếu nhi cũng nở rộ nhưng kh&ocirc;ng hiệu quả v&igrave; chủ yếu mang t&iacute;nh giải tr&iacute;. Chương tr&igrave;nh &ldquo;Giọng h&aacute;t Việt nh&iacute;&rdquo; qua m&ugrave;a thứ 3 đ&atilde; mất dần sức h&uacute;t, kh&ocirc;ng c&ograve;n những giọng ca nh&iacute; tiềm năng đủ sức lan tỏa như Phương Mỹ Chi hay Thiện Nh&acirc;n của 2 năm trước. Chương tr&igrave;nh &ldquo;Giọng ca nh&iacute; - H&ograve;, xự, xang, x&ecirc;, cống&rdquo; hay chương tr&igrave;nh \"Người h&ugrave;ng t&iacute; hon\" ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y được ấn tượng. Trong khi đ&oacute;, &ldquo;Bước nhảy ho&agrave;n vũ&rdquo; nh&iacute; tiếp tục c&oacute; một m&ugrave;a giải &ldquo;thất&nbsp; bại&rdquo; khi kh&ocirc;ng được kh&aacute;n giả quan t&acirc;m.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264777\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:2015/1445914349-ku-tin-42-1448266360711/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.jpg\" alt=\"Một th&iacute; sinh nh&iacute; trong chương tr&igrave;nh Người h&ugrave;ng t&iacute; hon\" /></div>\n<div class=\"PhotoCMS_Caption\"><span style=\"color: #999999;\"><em>Một th&iacute; sinh nh&iacute; trong chương tr&igrave;nh \"Người h&ugrave;ng t&iacute; hon\"</em></span></div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Ri&ecirc;ng chương tr&igrave;nh &ldquo;Solo c&ugrave;ng bolero&rdquo; qua m&ugrave;a thứ 2 vẫn giữ được &ldquo;sức n&oacute;ng&rdquo; nhất định, với sự tham dự của đ&ocirc;ng đảo th&iacute; sinh từ khắp mọi miền, lứa tuổi đ&atilde; chứng minh được rằng d&ograve;ng nhạc n&agrave;y vẫn c&oacute; sức sống. &ldquo;Thần tượng c&ugrave;ng bolero&rdquo; l&agrave; một chương tr&igrave;nh kh&aacute;c cũng mang đến cơ hội biểu diễn tr&ecirc;n một s&acirc;n khấu chuy&ecirc;n nghiệp cho những th&iacute; sinh c&oacute; niềm đam m&ecirc; với c&aacute;c d&ograve;ng nhạc qu&ecirc; hương, trữ t&igrave;nh v&agrave; d&acirc;n ca.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;\">\n<div><img id=\"img_264778\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:thumb_w/540/2015/753812-1448266495833/chuong-trinh-truyen-hinh-nao-duoc-de-cu-mai-vang-2015.JPG\" alt=\"Th&iacute; sinh của chương tr&igrave;nh Solo c&ugrave;ng bolero\" /></div>\n<div class=\"PhotoCMS_Caption\"><span style=\"color: #999999;\"><em>Th&iacute; sinh của chương tr&igrave;nh \"Solo c&ugrave;ng bolero\"</em></span></div>\n</div>\n<p>&nbsp;</p>\n<div id=\"ObjectBoxContent_1448330066622\" class=\"VCSortableInPreviewMode\" dir=\"center\" style=\"border: solid 1px #999; background: #E6E6FA; float: none; width: 70%; margin: 0 auto;\" data-back=\"E6E6FA\" data-border=\"999\">\n<p><strong>Cũ m&agrave; kh&ocirc;ng ch&aacute;n</strong></p>\n<p>&nbsp;&nbsp; C&aacute;c chương tr&igrave;nh cũ mang yếu tố nh&acirc;n văn, nh&acirc;n &aacute;i như \"Vượt l&ecirc;n ch&iacute;nh m&igrave;nh\", \"Ng&ocirc;i nh&agrave; mơ ước\", \"Lục lạc v&agrave;ng\", \"Bếp y&ecirc;u thương\", \"C&ugrave;ng x&acirc;y tương lai\", \"Nhịp cầu ước mơ\"&hellip;vẫn giữ được &ldquo;phong độ&rdquo; ổn định v&agrave; thu h&uacute;t kh&aacute;n giả đều đặn.</p>\n<p>&nbsp;&nbsp; Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c chương tr&igrave;nh t&ocirc;n vinh gi&aacute; trị gia đ&igrave;nh, t&igrave;nh y&ecirc;u như \"Vợ chồng son\", \"Bạn muốn hẹn h&ograve;\", \"Ngh&igrave;n lẻ một chuyện\", &nbsp;\"&Yacute; ph&aacute;i đẹp, lời ph&aacute;i mạnh\"...lu&ocirc;n c&oacute; một lượng kh&aacute;n giả nhất định.</p>\n</div>\n<p>&nbsp;</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><em>Theo nld.com.vn</em></div>','Mai Vàng 2015','','',0,'http://tamdiem.com.vn/sources/images/%C6%A0n%20gi%E1%BB%9Di.jpg','',0,0,0,0,472,0,1448350399,1449289454,1),(138,'Cô bé đáng yêu trong Hạnh Phúc Muộn Màng','co-be-dang-yeu-trong-hanh-phuc-muon-mang',16,'news_item','Hạnh Phúc Muộn Màng, Ruhi, Raman, Ishita, Ruhanika Dhawan','Trong phim truyền hình dài tập Hạnh phúc muộn màng, Ruhi là một cô bé rất đặc biệt. Mặc dù còn nhỏ tuổi nhưng Ruhi đã có những suy nghĩ trưởng thành hơn so với các bạn đồng trang lứa.','Trong phim truyền hình dài tập Hạnh phúc muộn màng, Ruhi là một cô bé rất đặc biệt. Mặc dù còn nhỏ tuổi nhưng Ruhi đã có những suy nghĩ trưởng thành hơn so với các bạn đồng trang lứa.','<p style=\"text-align: justify;\">Trong phim truyền h&igrave;nh d&agrave;i tập Hạnh ph&uacute;c muộn m&agrave;ng, Ruhi l&agrave; một c&ocirc; b&eacute; rất đặc biệt. Mặc d&ugrave; c&ograve;n nhỏ tuổi nhưng Ruhi đ&atilde; c&oacute; những suy nghĩ trưởng th&agrave;nh hơn so với c&aacute;c bạn đồng trang lứa.</p>\n<p style=\"text-align: justify;\">Sinh ra trong một gia đ&igrave;nh đổ vỡ, từ nhỏ đ&atilde; kh&ocirc;ng c&oacute; được sự chăm s&oacute;c của mẹ ruột, thường bị cha đối xử lạnh l&ugrave;ng v&igrave; nỗi hận th&ugrave; khi bị vợ phản bội n&ecirc;n trong s&acirc;u thẳm tr&aacute;i tim, c&ocirc; b&eacute; rất muốn c&oacute; một người mẹ quan t&acirc;m m&igrave;nh. May mắn gặp được một người phụ nữ thương y&ecirc;u, Ruhi trở th&agrave;nh &ldquo;b&agrave; nguyệt&rdquo; se duy&ecirc;n cho&nbsp;ba Raman v&agrave; mẹ Ishita. V&igrave; Ruhi, một cuộc h&ocirc;n nh&acirc;n bất đắc dĩ đ&atilde; xảy ra với rất nhiều m&acirc;u thuẫn, bất đồng trong suy nghĩ, t&iacute;nh c&aacute;ch v&agrave; lối sống giữa Raman v&agrave; Ishita. Nhưng cũng nhờ Ruhi, cả hai người dần dần hiểu nhau hơn v&agrave; t&igrave;m thấy được t&igrave;nh y&ecirc;u đ&iacute;ch thực của đời m&igrave;nh.</p>\n<p style=\"text-align: justify;\">Ruhi thật sự l&agrave;m lay động tr&aacute;i tim của nhiều kh&aacute;n giả bởi t&igrave;nh cảm đặc biệt m&agrave; c&ocirc; b&eacute; d&agrave;nh cho mẹ kế Ishita. Nhờ diễn xuất rất tự nhi&ecirc;n của Ruhanika Dhawan, người xem cũng bị cuốn h&uacute;t bởi n&eacute;t hồn nhi&ecirc;n, dễ thương nhưng cũng rất l&eacute;m lĩnh, t&acirc;m l&yacute; v&agrave; những biểu lộ cảm x&uacute;c v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u của Ruhi trong phim.</p>\n<table style=\"height: 332px;\" width=\"508\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source/Ruhi 1.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #808080;\">Vui mừng hớn hở khi tr&ocirc;ng thấy ba đến trường</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 2.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #808080;\">Mếu kh&oacute;c khi nghe tin c&ocirc; Ishita sắp lấy chồng</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 3.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #808080;\">Khi năn nỉ ba trả lời c&acirc;u hỏi</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 4.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\"><span style=\"color: #808080;\">Khi được ba h&ocirc;n</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 5.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\">Lần đ&acirc;u ti&ecirc;n gọi mẹ Ishita</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 6.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\">Khi giận mẹ Ishita</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ruhi 7.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\n<p style=\"text-align: center;\">Khi l&agrave;m &ldquo;qu&acirc;n sư&rdquo; t&igrave;nh y&ecirc;u cho ba</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em> T&acirc;m Điểm</em></p>','BÀ CỤ NON TRONG HẠNH PHÚC MUỘN MÀNG','','',0,'http://tamdiem.com.vn/sources/images/Ruhi%204.jpg','',0,0,0,0,189,0,1449202248,1449455331,1),(139,'Xây dựng thương hiệu phim Việt: Chất lượng và bản sắc dân tộc','xay-dung-thuong-hieu-phim-viet-chat-luong-va-ban-sac-dan-toc',14,'news_item','Liên hoan phim Việt Nam lần thứ 19, Chủ tịch Hãng Fortissimo Films, thương hiệu phim Việt, chất lượng , bản sắc dân tộc ','Việc xây dựng thương hiệu và vị thế của phim Việt Nam trước hết phải bắt đầu từ chất lượng tác phẩm, trong đó yếu tố tiên quyết là phải kết tinh và thể hiện được bản sắc dân tộc, thông qua điện ảnh nhằm quảng bá thương hiệu quốc gia. ','Việc xây dựng thương hiệu và vị thế của phim Việt Nam trước hết phải bắt đầu từ chất lượng tác phẩm, trong đó yếu tố tiên quyết là phải kết tinh và thể hiện được bản sắc dân tộc, thông qua điện ảnh nhằm quảng bá thương hiệu quốc gia. ','<p style=\"text-align: justify;\"><strong><em>Trong bối cảnh hội nhập với nền điện ảnh khu vực v&agrave; thế giới, theo b&agrave; Ng&ocirc; Phương Lan, Cục trưởng Cục Điện ảnh, việc x&acirc;y dựng thương hiệu v&agrave; vị thế của phim Việt Nam trước hết phải bắt đầu từ chất lượng t&aacute;c phẩm, trong đ&oacute; yếu tố ti&ecirc;n quyết l&agrave; phải kết tinh v&agrave; thể hiện được bản sắc d&acirc;n tộc, th&ocirc;ng qua điện ảnh nhằm quảng b&aacute; thương hiệu quốc gia.&nbsp;&nbsp;&nbsp;&nbsp; </em></strong></p>\n<p style=\"text-align: justify;\"><strong>Một bước tiến mới</strong></p>\n<p style=\"text-align: justify;\">Trong khu&ocirc;n khổ Li&ecirc;n hoan phim (LHP) Việt Nam lần thứ 19, tại hội thảo quốc tế với chủ đề: &ldquo;C&aacute;c ch&iacute;nh s&aacute;ch v&agrave; biện ph&aacute;p ưu đ&atilde;i ph&aacute;t triển c&ocirc;ng nghiệp điện ảnh Việt Nam&rdquo; tổ chức s&aacute;ng 3-12 tại TPHCM, &ocirc;ng Đỗ Duy Anh, Ph&oacute; Cục trưởng Cục Điện ảnh Việt Nam đ&atilde; th&ocirc;ng b&aacute;o h&agrave;ng loạt số liệu mới nhất về t&igrave;nh h&igrave;nh ph&aacute;t triển của điện ảnh Việt trong năm 2015.</p>\n<p style=\"text-align: justify;\">T&iacute;nh đến th&aacute;ng 12-2015, điện ảnh Việt Nam đ&atilde; c&oacute; những bước tiến vượt bậc với 143 phim được sản xuất v&agrave; ph&aacute;t h&agrave;nh, trong đ&oacute; ri&ecirc;ng phim truyện điện ảnh l&agrave; 40 phim, chiếm tỷ lệ 23,5% so với phim ngoại. Hệ thống rạp chiếu phim của Việt Nam cũng ph&aacute;t triển rất nhanh, hiện c&oacute; tổng số 140 rạp chiếu với 470 ph&ograve;ng chiếu v&agrave; 82.470 chỗ ngồi. Sự tăng trưởng đ&oacute; c&ugrave;ng với số lượng phim ph&aacute;t h&agrave;nh rất cao l&agrave; 210 phim (170 phim ngoại nhập) đ&atilde; thu h&uacute;t 36 triệu lượt kh&aacute;n giả đến rạp v&agrave; lần đầu ti&ecirc;n đạt doanh thu kỷ lục 110 triệu USD. Kinh ph&iacute; sản xuất phim c&ograve;n kh&aacute; hạn chế, trung b&igrave;nh ở mức 6-7 tỷ đồng/phim truyện điện ảnh (cao nhất l&agrave; 24 đến 26 tỷ đồng), khoảng 600 triệu đồng/phim t&agrave;i liệu, hoạt h&igrave;nh.&nbsp;</p>\n<table style=\"height: 409px; margin-left: auto; margin-right: auto;\" width=\"500\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source/LHP VN.jpg\" alt=\"\" width=\"500\" height=\"350\" /></p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"color: #0000ff;\">Kh&aacute;n giả xếp h&agrave;ng đến xem c&aacute;c phim Việt tại LHP Việt Nam lần thứ 19</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&Ocirc;ng Michael Werner, Chủ tịch H&atilde;ng Fortissimo Films (Hong Kong - Trung Quốc) cũng l&agrave; c&ocirc;ng ty ph&aacute;t h&agrave;nh bộ phim T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh tại thị trường quốc tế, tin tưởng bộ phim sẽ th&agrave;nh c&ocirc;ng. &Ocirc;ng cũng nhấn mạnh, điện ảnh Việt c&oacute; nhiều &yacute; tưởng để c&oacute; thể sản xuất c&aacute;c bộ phim chất lượng nếu l&agrave;m trong khả năng cho ph&eacute;p v&agrave; ph&ugrave; hợp với điều kiện, ho&agrave;n cảnh thực tế. &ldquo;Khi c&aacute;c bạn đ&atilde; c&oacute; c&aacute;c bộ phim tốt, khả năng b&aacute;n được ra thị trường quốc tế ho&agrave;n to&agrave;n rộng mở&rdquo;, &ocirc;ng Michael nhấn mạnh.&nbsp;</p>\n<p style=\"text-align: justify;\">Bước tiến của phim Việt c&ograve;n thể hiện ở xu hướng quốc tế h&oacute;a khi ng&agrave;y c&agrave;ng c&oacute; nhiều nh&agrave; l&agrave;m phim gốc Việt, nh&agrave; l&agrave;m phim ngoại chọn Việt Nam l&agrave; điểm đến để thực hiện c&aacute;c t&aacute;c phẩm của m&igrave;nh. Theo thống k&ecirc; của Cục Điện ảnh, trong năm 2015, c&oacute; tổng số 25 đo&agrave;n phim (20 phim truyện điện ảnh), bao gồm c&aacute;c phim của đạo diễn Việt kiều, phim hợp t&aacute;c với c&aacute;c nh&agrave; l&agrave;m phim nước ngo&agrave;i được thực hiện tại Việt Nam.</p>\n<p style=\"text-align: justify;\">Trước đ&oacute;, cũng trong khu&ocirc;n khổ LHP Việt Nam 19, tại hội thảo &ldquo;X&acirc;y dựng thương hiệu v&agrave; vị thế của phim Việt Nam&rdquo; ng&agrave;y 2-12, &yacute; kiến của c&aacute;c đại biểu tham dự đều cho rằng muốn đạt được mục ti&ecirc;u n&oacute;i tr&ecirc;n, trước hết cần x&acirc;y dựng thương hiệu phim Việt ngay tại thị trường trong nước, phục vụ kh&aacute;n giả nội địa. Trong b&agrave;i chia sẻ của m&igrave;nh, NSND Đặng Nhật Minh khẳng định, muốn hội nhập thế giới, phim Việt phải c&oacute; bản sắc ri&ecirc;ng; c&oacute; những phim Việt chiếm lĩnh thị trường trong nước trước khi được xuất khẩu ra nước ngo&agrave;i hay tham dự c&aacute;c LHP lớn tr&ecirc;n thế giới.</p>\n<p style=\"text-align: justify;\"><strong>Cần khẳng định chất lượng&nbsp; </strong></p>\n<p style=\"text-align: justify;\">B&agrave; Nguyễn Phương H&ograve;a, Ph&oacute; Cục trưởng Cục Hợp t&aacute;c quốc tế, Bộ VHTT-DL nhấn mạnh: &ldquo;Điện ảnh l&agrave; phương thức hữu hiệu để quảng b&aacute; thương hiệu quốc gia v&agrave; điều quan trọng nhất phải nhấn mạnh v&agrave;o thế mạnh, đặc trưng của quốc gia đ&oacute;. Theo t&ocirc;i, muốn điện ảnh Việt lớn mạnh phải bắt đầu từ những bộ phim chất lượng, việc kể c&acirc;u chuyện về h&igrave;nh ảnh đất nước, con người Việt Nam như thế n&agrave;o mới l&agrave; yếu tố quan trọng&rdquo;. B&agrave; Nguyễn Thế Thanh, nguy&ecirc;n Ph&oacute; Gi&aacute;m đốc Sở VHTT TPHCM cũng chung quan điểm, đồng thời chia sẻ, việc quan t&acirc;m đến thương hiệu phim Việt n&ecirc;n bắt đầu từ những h&agrave;nh động cụ thể như: phối hợp với c&aacute;c đo&agrave;n ngoại giao tổ chức chiếu phim, l&agrave;m hội chợ, tổ chức ng&agrave;y phim Việt Nam... tại c&aacute;c quốc gia kh&aacute;c nhau tr&ecirc;n thế giới.</p>\n<p style=\"text-align: justify;\">Thuyết tr&igrave;nh của &ocirc;ng Jacob Kirstein Hogel, Ủy vi&ecirc;n Quỹ điện ảnh T&acirc;y Đan Mạch về c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ v&agrave; ưu đ&atilde;i để bảo vệ v&agrave; ph&aacute;t triển nền điện ảnh quốc gia cũng chỉ ra rằng, việc x&acirc;y dựng bản sắc phải được nghi&ecirc;n cứu trong mối tương quan với 4 yếu tố: sản xuất, ti&ecirc;u thụ, quản l&yacute; v&agrave; đại diện. &ldquo;Phim ảnh c&oacute; thể đề cập đến c&aacute;c vấn đề quan trọng của quốc gia v&agrave; do vậy c&oacute; vai tr&ograve; trong việc n&acirc;ng cao nhận thức về c&aacute;c vấn đề đ&oacute;. C&aacute;c phim được ưa th&iacute;ch c&oacute; thể trở th&agrave;nh một phần của đời sống x&atilde; hội rộng lớn&rdquo;. Th&ocirc;ng qua nghi&ecirc;n cứu kỹ lưỡng về nền điện ảnh Việt, &ocirc;ng Jacob nhấn mạnh: &ldquo;Nếu ch&iacute;nh s&aacute;ch v&agrave; ưu đ&atilde;i của nh&agrave; nước được &aacute;p dụng ph&ugrave; hợp với năng lực của cộng đồng c&aacute;c nh&agrave; l&agrave;m phim, ph&ugrave; hợp với thị hiếu của kh&aacute;n giả v&agrave; h&igrave;nh thức ph&aacute;t h&agrave;nh sẽ h&igrave;nh th&agrave;nh v&agrave; củng cố vững chắc bản sắc của một nền điện ảnh quốc gia&rdquo;.</p>\n<p style=\"text-align: right;\"><em>Theo sggp.org.vn</em></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<div id=\"stcpDiv\" style=\"position: absolute; top: -1999px; left: -1988px;\">\n<p align=\"justify\"><span style=\"font-family: arial;\"><strong><em>Trong bối cảnh hội nhập với nền điện ảnh khu vực v&agrave; thế giới, theo b&agrave; Ng&ocirc; Phương Lan, Cục trưởng Cục Điện ảnh, việc x&acirc;y dựng thương hiệu v&agrave; vị thế của phim Việt Nam trước hết phải bắt đầu từ chất lượng t&aacute;c phẩm, trong đ&oacute; yếu tố ti&ecirc;n quyết l&agrave; phải kết tinh v&agrave; thể hiện được bản sắc d&acirc;n tộc, th&ocirc;ng qua điện ảnh nhằm quảng b&aacute; thương hiệu quốc gia.&nbsp;&nbsp;&nbsp;&nbsp; </em></strong></span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\"><strong>Một bước tiến mới</strong></span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">Trong khu&ocirc;n khổ Li&ecirc;n hoan phim (LHP) Việt Nam lần thứ 19, tại hội thảo quốc tế với chủ đề: &ldquo;C&aacute;c ch&iacute;nh s&aacute;ch v&agrave; biện ph&aacute;p ưu đ&atilde;i ph&aacute;t triển c&ocirc;ng nghiệp điện ảnh Việt Nam&rdquo; tổ chức s&aacute;ng 3-12 tại TPHCM, &ocirc;ng Đỗ Duy Anh, Ph&oacute; Cục trưởng Cục Điện ảnh Việt Nam đ&atilde; th&ocirc;ng b&aacute;o h&agrave;ng loạt số liệu mới nhất về t&igrave;nh h&igrave;nh ph&aacute;t triển của điện ảnh Việt trong năm 2015. </span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">T&iacute;nh đến th&aacute;ng 12-2015, điện ảnh Việt Nam đ&atilde; c&oacute; những bước tiến vượt bậc với 143 phim được sản xuất v&agrave; ph&aacute;t h&agrave;nh, trong đ&oacute; ri&ecirc;ng phim truyện điện ảnh l&agrave; 40 phim, chiếm tỷ lệ 23,5% so với phim ngoại. Hệ thống rạp chiếu phim của Việt Nam cũng ph&aacute;t triển rất nhanh, hiện c&oacute; tổng số 140 rạp chiếu với 470 ph&ograve;ng chiếu v&agrave; 82.470 chỗ ngồi. Sự tăng trưởng đ&oacute; c&ugrave;ng với số lượng phim ph&aacute;t h&agrave;nh rất cao l&agrave; 210 phim (170 phim ngoại nhập) đ&atilde; thu h&uacute;t 36 triệu lượt kh&aacute;n giả đến rạp v&agrave; lần đầu ti&ecirc;n đạt doanh thu kỷ lục 110 triệu USD. Kinh ph&iacute; sản xuất phim c&ograve;n kh&aacute; hạn chế, trung b&igrave;nh ở mức 6-7 tỷ đồng/phim truyện điện ảnh (cao nhất l&agrave; 24 đến 26 tỷ đồng), khoảng 600 triệu đồng/phim t&agrave;i liệu, hoạt h&igrave;nh.&nbsp; </span></p>\n<div class=\"img-full\" align=\"center\"><img src=\"http://sggp.org.vn/dataimages/original/2015/12/images597403_D5b.jpg\" alt=\"\" width=\"540\" name=\"imagePhoto\" border=\"0\" />\n<p class=\"img-name\"><span style=\"color: #0000ff;\">Kh&aacute;n giả xếp h&agrave;ng đến xem c&aacute;c phim Việt tại LHP Việt Nam lần thứ 19</span></p>\n</div>\n<p align=\"justify\"><span style=\"font-family: arial;\">&Ocirc;ng Michael Werner, Chủ tịch H&atilde;ng Fortissimo Films (Hong Kong - Trung Quốc) cũng l&agrave; c&ocirc;ng ty ph&aacute;t h&agrave;nh bộ phim T&ocirc;i thấy hoa v&agrave;ng tr&ecirc;n cỏ xanh tại thị trường quốc tế, tin tưởng bộ phim sẽ th&agrave;nh c&ocirc;ng. &Ocirc;ng cũng nhấn mạnh, điện ảnh Việt c&oacute; nhiều &yacute; tưởng để c&oacute; thể sản xuất c&aacute;c bộ phim chất lượng nếu l&agrave;m trong khả năng cho ph&eacute;p v&agrave; ph&ugrave; hợp với điều kiện, ho&agrave;n cảnh thực tế. &ldquo;Khi c&aacute;c bạn đ&atilde; c&oacute; c&aacute;c bộ phim tốt, khả năng b&aacute;n được ra thị trường quốc tế ho&agrave;n to&agrave;n rộng mở&rdquo;, &ocirc;ng Michael nhấn mạnh.&nbsp; </span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">Bước tiến của phim Việt c&ograve;n thể hiện ở xu hướng quốc tế h&oacute;a khi ng&agrave;y c&agrave;ng c&oacute; nhiều nh&agrave; l&agrave;m phim gốc Việt, nh&agrave; l&agrave;m phim ngoại chọn Việt Nam l&agrave; điểm đến để thực hiện c&aacute;c t&aacute;c phẩm của m&igrave;nh. Theo thống k&ecirc; của Cục Điện ảnh, trong năm 2015, c&oacute; tổng số 25 đo&agrave;n phim (20 phim truyện điện ảnh), bao gồm c&aacute;c phim của đạo diễn Việt kiều, phim hợp t&aacute;c với c&aacute;c nh&agrave; l&agrave;m phim nước ngo&agrave;i được thực hiện tại Việt Nam. </span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">Trước đ&oacute;, cũng trong khu&ocirc;n khổ LHP Việt Nam 19, tại hội thảo &ldquo;X&acirc;y dựng thương hiệu v&agrave; vị thế của phim Việt Nam&rdquo; ng&agrave;y 2-12, &yacute; kiến của c&aacute;c đại biểu tham dự đều cho rằng muốn đạt được mục ti&ecirc;u n&oacute;i tr&ecirc;n, trước hết cần x&acirc;y dựng thương hiệu phim Việt ngay tại thị trường trong nước, phục vụ kh&aacute;n giả nội địa. Trong b&agrave;i chia sẻ của m&igrave;nh, NSND Đặng Nhật Minh khẳng định, muốn hội nhập thế giới, phim Việt phải c&oacute; bản sắc ri&ecirc;ng; c&oacute; những phim Việt chiếm lĩnh thị trường trong nước trước khi được xuất khẩu ra nước ngo&agrave;i hay tham dự c&aacute;c LHP lớn tr&ecirc;n thế giới.</span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\"><strong>Cần khẳng định chất lượng&nbsp; </strong></span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">B&agrave; Nguyễn Phương H&ograve;a, Ph&oacute; Cục trưởng Cục Hợp t&aacute;c quốc tế, Bộ VHTT-DL nhấn mạnh: &ldquo;Điện ảnh l&agrave; phương thức hữu hiệu để quảng b&aacute; thương hiệu quốc gia v&agrave; điều quan trọng nhất phải nhấn mạnh v&agrave;o thế mạnh, đặc trưng của quốc gia đ&oacute;. Theo t&ocirc;i, muốn điện ảnh Việt lớn mạnh phải bắt đầu từ những bộ phim chất lượng, việc kể c&acirc;u chuyện về h&igrave;nh ảnh đất nước, con người Việt Nam như thế n&agrave;o mới l&agrave; yếu tố quan trọng&rdquo;. B&agrave; Nguyễn Thế Thanh, nguy&ecirc;n Ph&oacute; Gi&aacute;m đốc Sở VHTT TPHCM cũng chung quan điểm, đồng thời chia sẻ, việc quan t&acirc;m đến thương hiệu phim Việt n&ecirc;n bắt đầu từ những h&agrave;nh động cụ thể như: phối hợp với c&aacute;c đo&agrave;n ngoại giao tổ chức chiếu phim, l&agrave;m hội chợ, tổ chức ng&agrave;y phim Việt Nam... tại c&aacute;c quốc gia kh&aacute;c nhau tr&ecirc;n thế giới. </span></p>\n<p align=\"justify\"><span style=\"font-family: arial;\">Thuyết tr&igrave;nh của &ocirc;ng Jacob Kirstein Hogel, Ủy vi&ecirc;n Quỹ điện ảnh T&acirc;y Đan Mạch về c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ v&agrave; ưu đ&atilde;i để bảo vệ v&agrave; ph&aacute;t triển nền điện ảnh quốc gia cũng chỉ ra rằng, việc x&acirc;y dựng bản sắc phải được nghi&ecirc;n cứu trong mối tương quan với 4 yếu tố: sản xuất, ti&ecirc;u thụ, quản l&yacute; v&agrave; đại diện. &ldquo;Phim ảnh c&oacute; thể đề cập đến c&aacute;c vấn đề quan trọng của quốc gia v&agrave; do vậy c&oacute; vai tr&ograve; trong việc n&acirc;ng cao nhận thức về c&aacute;c vấn đề đ&oacute;. C&aacute;c phim được ưa th&iacute;ch c&oacute; thể trở th&agrave;nh một phần của đời sống x&atilde; hội rộng lớn&rdquo;. Th&ocirc;ng qua nghi&ecirc;n cứu kỹ lưỡng về nền điện ảnh Việt, &ocirc;ng Jacob nhấn mạnh: &ldquo;Nếu ch&iacute;nh s&aacute;ch v&agrave; ưu đ&atilde;i của nh&agrave; nước được &aacute;p dụng ph&ugrave; hợp với năng lực của cộng đồng c&aacute;c nh&agrave; l&agrave;m phim, ph&ugrave; hợp với thị hiếu của kh&aacute;n giả v&agrave; h&igrave;nh thức ph&aacute;t h&agrave;nh sẽ h&igrave;nh th&agrave;nh v&agrave; củng cố vững chắc bản sắc của một nền điện ảnh quốc gia&rdquo;.</span></p>\n- See more at: http://sggp.org.vn/dienanh/2015/12/404908/#sthash.iCAUwvCC.dpuf</div>','Xây dựng thương hiệu phim Việt: Chất lượng và bản sắc dân tộc','','',0,'http://tamdiem.com.vn/sources/images/2.jpg','',0,0,0,0,471,0,1449288688,1452485313,1),(140,'Có đáng tự hào?','co-dang-tu-hao',14,'news_item','Mỹ Tâm, Thu Minh, Hồ Quỳnh Hương, giải thưởng Mnet Asian Music Awards 2015 (MAMA 2015), Đông Nhi','Sau ca sĩ Mỹ Tâm, Thu Minh, Hồ Quỳnh Hương, giải thưởng Mnet Asian Music Awards 2015 (MAMA 2015) , do một công ty Hàn Quốc tổ chức, diễn ra tại Hồng Kông vào đêm 2-12 đã vinh danh nữ ca sĩ Đông Nhi của Việt Nam ở hạng mục Nghệ sĩ châu Á xuất sắc. ','Sau ca sĩ Mỹ Tâm, Thu Minh, Hồ Quỳnh Hương, giải thưởng Mnet Asian Music Awards 2015 (MAMA 2015) , do một công ty Hàn Quốc tổ chức, diễn ra tại Hồng Kông vào đêm 2-12 đã vinh danh nữ ca sĩ Đông Nhi của Việt Nam ở hạng mục Nghệ sĩ châu Á xuất sắc. ','<h5 class=\"sapo\" style=\"text-align: justify;\" data-field=\"sapo\"><span style=\"color: #808080;\"><em><strong>Sau ca sĩ Mỹ T&acirc;m, Thu Minh, Hồ Quỳnh Hương, giải thưởng Mnet Asian Music Awards 2015 (MAMA 2015), do một c&ocirc;ng ty H&agrave;n Quốc tổ chức, diễn ra tại Hồng K&ocirc;ng v&agrave;o đ&ecirc;m 2-12 đ&atilde; vinh danh nữ ca sĩ Đ&ocirc;ng Nhi của Việt Nam ở hạng mục Nghệ sĩ ch&acirc;u &Aacute; xuất sắc.</strong></em></span></h5>\n<div id=\"divNewsContent\" class=\"contentdetail\" data-field=\"body\">\n<p style=\"text-align: justify;\">Đ&ocirc;ng Nhi l&agrave; ca sĩ thứ 4 của Việt Nam c&oacute; t&ecirc;n ở giải thưởng n&agrave;y với danh hiệu lớn lao tr&ecirc;n. Kh&ocirc;ng được mời tham dự, Đ&ocirc;ng Nhi chỉ xuất hiện trong video clip ph&aacute;t tại lễ trao giải MAMA 2015.</p>\n<div class=\"VCSortableInPreviewMode\" style=\"display: inline-block; width: 100%; text-align: center; box-sizing: border-box;\">\n<div><img id=\"img_268958\" style=\"max-width: 100% ! important;\" src=\"http://nld.vcmedia.vn/k:2015/8-dongnhi-1449245465853/co-dang-tu-hao.jpg\" alt=\"Đ&ocirc;ng Nhi nhận giải &ldquo;Nghệ sĩ ch&acirc;u &Aacute; xuất sắc&rdquo; của MAMA 2015. (Ảnh do nghệ sĩ cung cấp)\" width=\"579\" height=\"417\" /></div>\n<div class=\"PhotoCMS_Caption\">&nbsp;</div>\n<div class=\"PhotoCMS_Caption\"><em><span style=\"color: #808080;\">Đ&ocirc;ng Nhi nhận giải &ldquo;Nghệ sĩ ch&acirc;u &Aacute; xuất sắc&rdquo; của MAMA 2015. (Ảnh do nghệ sĩ cung cấp)</span></em></div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">D&ugrave; c&oacute; nhiều &yacute; kiến b&agrave;n luận tr&aacute;i chiều tr&ecirc;n mạng về giải thưởng của m&igrave;nh, Đ&ocirc;ng Nhi vẫn vui mừng n&oacute;i với b&aacute;o ch&iacute; trong nước rằng c&ocirc; rất bất ngờ v&agrave; hạnh ph&uacute;c khi nhận được th&ocirc;ng b&aacute;o từ ban tổ chức về giải thưởng v&agrave; xem đ&acirc;y l&agrave; th&agrave;nh quả đ&aacute;ng tự h&agrave;o trong sự nghiệp ca h&aacute;t của m&igrave;nh.</p>\n<p style=\"text-align: justify;\">Ca sĩ Đ&agrave;m Vĩnh Hưng &nbsp;trước đ&oacute; cũng tổ chức một cuộc họp b&aacute;o c&ocirc;ng bố m&igrave;nh nhận được giải thưởng Asia Superstar (tạm dịch Si&ecirc;u sao ch&acirc;u &Aacute;), một hạng mục giải thưởng nằm trong Asia Model Awards thuộc khu&ocirc;n khổ Li&ecirc;n hoan Người mẫu ch&acirc;u &Aacute; (Asia model festival) do Hiệp hội Người mẫu ch&acirc;u &Aacute; phối hợp với C&ocirc;ng ty EMN (H&agrave;n Quốc) tổ chức. &nbsp;Cũng tại giải thưởng n&agrave;y, Ngọc Trinh được trao giải Nghệ sĩ trẻ ch&acirc;u &Aacute; xuất sắc.</p>\n<p style=\"text-align: justify;\">V&igrave; kh&ocirc;ng thể đến H&agrave;n Quốc nhận giải trực tiếp n&ecirc;n ca sĩ Đ&agrave;m Vĩnh Hưng gửi video clip thu h&igrave;nh v&agrave; lời ph&aacute;t biểu của m&igrave;nh &nbsp;đến chương tr&igrave;nh trao giải. Theo &ocirc;ng Lee Sun Ki, đại diện ban tổ chức, giải thưởng Asia Superstar trao cho Đ&agrave;m Vĩnh Hưng v&igrave; sự b&igrave;nh chọn của cộng đồng người Việt đang sinh sống v&agrave; l&agrave;m việc tại H&agrave;n Quốc v&agrave; những đ&oacute;ng g&oacute;p của anh trong gần 20 năm ca h&aacute;t. Ngo&agrave;i Đ&agrave;m Vĩnh Hưng, giải thưởng n&agrave;y c&ograve;n trao cho 4 ng&ocirc;i sao H&agrave;n Quốc: Kim Soo Huyn, Kim Woo Bin, Jun Ji Huyn v&agrave; Kim Tae Hee.</p>\n<p style=\"text-align: justify;\">Đ&acirc;y kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n nghệ sĩ Việt Nam được nhận c&aacute;c giải thưởng của nước ngo&agrave;i v&agrave; đến nay, Mỹ T&acirc;m c&oacute; lẽ l&agrave; nghệ sĩ Việt Nam sở hữu nhiều giải thưởng quốc tế nhất.</p>\n<p style=\"text-align: justify;\">Kể ra, mọi sự t&ocirc;n vinh, d&ugrave; lớn nhỏ, đều đ&aacute;ng tự h&agrave;o v&agrave; tr&acirc;n trọng. Đặc biệt l&agrave; c&aacute;c giải thưởng quốc tế d&agrave;nh cho nghệ sĩ Việt Nam. Tuy nhi&ecirc;n, giải thưởng quốc tế cũng c&oacute; h&agrave;ng ng&agrave;n giải v&agrave; kh&ocirc;ng phải giải thưởng n&agrave;o cũng gi&aacute; trị.</p>\n<p style=\"text-align: justify;\">B&aacute;o ch&iacute; H&agrave;n Quốc (trong đ&oacute; c&oacute; nhật b&aacute;o H&agrave;n Quốc &nbsp;Korea Time) thẳng thắn gọi t&ecirc;n sự kiện Asia models festival l&agrave; &ldquo;lễ hội về lĩnh vực người mẫu đơn thuần, kh&ocirc;ng phải một giải thưởng&rdquo;. Theo đ&oacute;, sự kiện n&agrave;y được giới thiệu l&agrave; &ldquo;sự kiện giải tr&iacute; tổng hợp bao gồm: những người mẫu nổi tiếng tr&igrave;nh diễn thời trang cho c&aacute;c thương hiệu, một đ&ecirc;m gala, biểu diễn Kpop v&agrave; beauty show (s&ocirc; diễn cho lĩnh vực l&agrave;m đẹp). N&oacute; cũng l&agrave; nơi tạo ra những kỷ niệm kh&oacute; qu&ecirc;n v&agrave; trao c&aacute;c giải thưởng phong c&aacute;ch cho những nghệ sĩ ch&acirc;u &Aacute;. Đ&acirc;y cũng l&agrave; dịp giới thiệu người mẫu H&agrave;n Quốc v&agrave; ch&acirc;u &Aacute; để truyền b&aacute; v&agrave; ph&aacute;t triển kinh doanh li&ecirc;n quan đến thời trang cũng như vẻ đẹp văn h&oacute;a. &nbsp;Danh hiệu Si&ecirc;u sao ch&acirc;u &Aacute; m&agrave; ca sĩ Đ&agrave;m Vĩnh Hưng được trao nghe hơi to t&aacute;t &nbsp;nhưng vẫn xứng đ&aacute;ng với những g&igrave; anh đ&oacute;ng g&oacute;p ở thị trường nhạc Việt, c&ograve;n người mẫu Ngọc Trinh được trao giải &ldquo;Nghệ sĩ mới xuất sắc nhất ch&acirc;u &Aacute;&rdquo; th&igrave; kh&ocirc;ng tr&aacute;nh khỏi những e ngại từ c&ocirc;ng ch&uacute;ng. Người ta đặt c&acirc;u hỏi: &ldquo;Phải chăng c&aacute;i t&ecirc;n Đ&agrave;m Vĩnh Hưng đặt cạnh người mẫu Ngọc Trinh để bảo chứng chất lượng cho ch&iacute;nh giải thưởng của Ngọc Trinh?&rdquo;. Chưa kể, những giải thưởng n&agrave;y kh&ocirc;ng c&oacute; trong hệ thống b&igrave;nh chọn ch&iacute;nh thức tr&ecirc;n website của li&ecirc;n hoan n&agrave;y.</p>\n<p style=\"text-align: justify;\">Đến nay, những giải thưởng như &nbsp;MAMA hay MTV ch&acirc;u &Acirc;u c&oacute; mặt tại Việt Nam chỉ mang t&iacute;nh giao lưu l&agrave; ch&iacute;nh. Được t&ocirc;n vinh nhưng vẫn chưa c&oacute; ca sĩ n&agrave;o được mời đ&iacute;ch danh đến s&acirc;n khấu lễ trao giải để nhận giải. Giải thưởng MAMA d&agrave;nh cho &ldquo;Nghệ sĩ ch&acirc;u &Aacute; xuất sắc nhất&rdquo; của mỗi nước thường chỉ được quay video clip trước rồi ph&aacute;t l&ecirc;n m&agrave;n h&igrave;nh tại lễ trao giải.</p>\n<p style=\"text-align: justify;\">Những giải thưởng quốc tế được trao cho nghệ sĩ Việt Nam vừa qua l&agrave; rất cảm t&iacute;nh v&agrave; chỉ phục vụ cho lợi &iacute;ch của từng giải thưởng. Ban tổ chức giải MAMA hay MTV ch&acirc;u &Acirc;u đều t&igrave;m c&aacute;ch l&ocirc;i k&eacute;o c&ocirc;ng ch&uacute;ng ở nhiều quốc gia th&ocirc;ng qua việc tham gia b&igrave;nh chọn của họ cho ca sĩ quốc gia đ&oacute; để l&agrave;m tăng lượng theo d&otilde;i của c&ocirc;ng ch&uacute;ng đến giải thưởng của m&igrave;nh.</p>\n<p style=\"text-align: right;\"><em>Theo nld.com.vn</em></p>\n</div>','Có đáng tự hào?','','',0,'http://tamdiem.com.vn/sources/images/cs%20Dong%20Nhi.jpg','',0,0,0,0,470,0,1449289718,1453866248,1),(141,'Karan Patel kết hôn với Ankita Bhargava','karan-patel-ket-hon-voi-ankita-bhargava',16,'news_item','Karan Patel kết hôn với Ankita Bhargava, Abhay Bhargava , Hạnh Phúc Muộn Màng','Cặp đôi bất ngờ tuyên bố kế hoạch đám cưới của họ khiến các phương tiện truyền thông xôn xao. Karan đã chia tay với nữ diễn viên Kamya Punjabi vài tháng trước khi anh yêu Ankita Bhargava. Lễ đính hôn của họ diễn ra khá riêng tư, chỉ gia đình và những người bạn thân thiết được mời tham gia.','Cặp đôi bất ngờ tuyên bố kế hoạch đám cưới của họ khiến các phương tiện truyền thông xôn xao. Karan đã chia tay với nữ diễn viên Kamya Punjabi vài tháng trước khi anh yêu Ankita Bhargava. Lễ đính hôn của họ diễn ra khá riêng tư, chỉ gia đình và những người bạn thân thiết được mời tham gia.','<table style=\"height: 68px;\" width=\"730\">\n<tbody>\n<tr>\n<td style=\"text-align: justify;\">Sau khi th&ocirc;ng tin Karan Patel chia tay với Kamya Punjabi lan truyền, mối quan hệ giữa Karan Patel v&agrave; Ankita Bhargava đ&atilde; trở th&agrave;nh t&acirc;m điểm ch&uacute; &yacute;. Nhiều người h&acirc;m mộ Karan v&agrave; Kamya nghĩ rằng Ankita ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n đằng sau việc chia tay của 2 người. Tuy nhi&ecirc;n Ankita đ&atilde; phủ định điều n&agrave;y. Kh&ocirc;ng l&acirc;u sau đ&oacute;, Karan v&agrave; Ankita đ&atilde; g&acirc;y sốc khi th&ocirc;ng b&aacute;o về kế hoạch đ&aacute;m cưới của họ.</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 356px;\" width=\"578\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source/1.jpg\" alt=\"\" width=\"570\" height=\"340\" />V&agrave;o ng&agrave;y 3/5/2015, cặp đ&ocirc;i đ&atilde; ch&iacute;nh thức kết h&ocirc;n trong một buổi tiệc xa hoa v&agrave; sang trọng. Nhiều kh&aacute;ch mời th&acirc;n thiết đ&atilde; đăng những h&igrave;nh ảnh v&agrave; video về lễ cưới tr&ecirc;n mạng x&atilde; hội.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 414px;\" width=\"573\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//3-1.jpg\" alt=\"\" width=\"570\" height=\"340\" />Việc chuẩn bị cho ng&agrave;y trọng đại của Karan v&agrave; Ankita rất kỹ c&agrave;ng. V&agrave; cặp vợ chồng son cũng đ&atilde; l&ecirc;n kế hoạch cho tuần trăng mật sau đ&aacute;m cưới. Họ đến Singapore để tận hưởng khoảng thời gian b&ecirc;n nhau.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 414px;\" width=\"579\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source/4-1.jpg\" alt=\"\" width=\"570\" height=\"340\" />Bạn diễn c&ugrave;ng với Karan Patel trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng &ndash; Divyanka Tripathi kh&ocirc;ng thể nhảy m&uacute;a tại h&ocirc;n lễ của Karan-Ankita bởi c&ocirc; đ&atilde; bị g&atilde;y ch&acirc;n v&agrave; b&aacute;c sĩ khuy&ecirc;n kh&ocirc;ng được đi lại trong 2 tuần. Divyanka rất tiếc bởi Abhayji (Bhargava) v&agrave; Karan đ&atilde; mời c&ocirc; nhảy trong tiệc cưới.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 399px;\" width=\"578\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//5.jpg\" alt=\"\" width=\"570\" height=\"340\" />Trước h&ocirc;n lễ v&agrave;i ng&agrave;y, những người bạn th&acirc;n của Ankita đ&atilde; tổ chức 1 buổi tiệc chia tay cuộc sống độc th&acirc;n d&agrave;nh cho c&ocirc; ấy.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//6-1.jpg\" alt=\"\" width=\"570\" height=\"340\" /></p>\n<p>Lễ sangeet của Karan Patel v&agrave; Ankita Bhargava diễn ra v&agrave;o ng&agrave;y 1/5/2015.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 444px;\" width=\"577\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//7.jpg\" alt=\"\" width=\"570\" height=\"340\" />Anh ch&agrave;ng độc th&acirc;n hot nhất truyền h&igrave;nh Karan Patel đ&atilde; g&acirc;y sốc khi th&ocirc;ng b&aacute;o kết h&ocirc;n với Ankita Bhargava, con g&aacute;i của Abhay Bhargava &ndash; người đ&oacute;ng vai cha vợ của Karan trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng. Khi được hỏi rằng liệu cha vợ của anh c&oacute; phải l&agrave; người t&aacute;c hợp hay kh&ocirc;ng, anh trả lời rằng &ldquo;Thực sự kh&ocirc;ng phải. T&ocirc;i đ&atilde; l&agrave;m việc với &ocirc;ng Abhayji trong nhiều năm, nhưng kh&ocirc;ng bao giờ nghĩ rằng m&igrave;nh sẽ cưới con g&aacute;i &ocirc;ng ấy. Aly (Goni), người em, người bạn v&agrave; đồng nghiệp của t&ocirc;i đ&atilde; gi&uacute;p hai gia đ&igrave;nh x&iacute;ch lại gần nhau. T&ocirc;i cảm thấy rất vui v&igrave; cuối c&ugrave;ng t&ocirc;i đ&atilde; t&igrave;m được người tri kỷ của m&igrave;nh&rdquo;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 429px;\" width=\"576\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//8-1.jpg\" alt=\"\" width=\"570\" height=\"340\" />Karan and Ankita đ&atilde; tổ chức lễ đ&iacute;nh h&ocirc;n v&agrave;o th&aacute;ng 3, năm 2015. Cặp đ&ocirc;i bất ngờ tuy&ecirc;n bố kế hoạch đ&aacute;m cưới của họ khiến truyền th&ocirc;ng x&ocirc;n xao. Karan đ&atilde; chia tay với nữ diễn vi&ecirc;n Kamya Punjabi v&agrave;i th&aacute;ng trước khi anh y&ecirc;u Ankita Bhargava. Lễ đ&iacute;nh h&ocirc;n của họ diễn ra kh&aacute; ri&ecirc;ng tư, chỉ gia đ&igrave;nh v&agrave; những người bạn th&acirc;n thiết được mời tham dự.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 489px;\" width=\"573\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//9.jpg\" alt=\"\" width=\"570\" height=\"400\" />Ankita hết lời khen ngợi Karan. C&ocirc; ấy n&oacute;i &ldquo;Karan l&agrave; một người rất đẹp trai. Ba t&ocirc;i đ&atilde; từng thường xuy&ecirc;n đề cập đến việc đ&oacute;ng phim v&agrave; c&ocirc;ng việc của anh ấy ở nh&agrave;, nhưng t&ocirc;i kh&ocirc;ng bao giờ nghĩ rằng ch&uacute;ng t&ocirc;i sẽ đến với nhau. T&ocirc;i rất vui v&igrave; gia đ&igrave;nh m&igrave;nh đ&atilde; t&igrave;m thấy 1 người con trai như anh ấy. Mặc d&ugrave; ch&uacute;ng t&ocirc;i l&agrave; 2 người rất kh&aacute;c nhau nhưng ch&uacute;ng t&ocirc;i bổ sung cho nhau v&agrave; kh&aacute; hợp &yacute;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 399px;\" width=\"574\">\n<tbody>\n<tr>\n<td>\n<p style=\"text-align: justify;\"><img src=\"/tinymce/source//10.jpg\" alt=\"\" width=\"570\" height=\"340\" />Ankita Bhargava cũng n&oacute;i rằng c&ocirc; ấy v&agrave; Karan đều c&oacute; qu&aacute; khứ, nhưng họ chấp nhận sai lầm của nhau v&agrave; thay đổi.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em> Kh&aacute;nh Linh (The Times of India &amp; Indiatoday)</em></p>','Karan Patel kết hôn với Ankita Bhargava','','',0,'http://tamdiem.com.vn/sources/images/L%E1%BB%85%20c%C6%B0%E1%BB%9Bi%20Raman.jpg','',0,0,0,0,188,0,1449568773,1451090349,1),(142,'Thói quen các ngôi sao truyền hình Ấn muốn từ bỏ','thoi-quen-cac-ngoi-sao-truyen-hinh-an-muon-tu-bo',16,'news_item','ngôi sao truyền hình Ấn, Divyanka Tripathi, Ssharad Malhotraa ','Divyanka Tripathi: Sở thích đồ ngọt là thứ mà tôi rất muốn từ bỏ vì tôi muốn có cuộc sống khỏe mạnh hơn. ','Divyanka Tripathi: Sở thích đồ ngọt là thứ mà tôi rất muốn từ bỏ vì tôi muốn có cuộc sống khỏe mạnh hơn. ','<table style=\"height: 409px;\" width=\"556\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source/Ssharad-Malhotraa.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Ssharad Malhotraa</strong> : T&ocirc;i thật sự buồn khi để mọi người c&oacute; ấn tượng sai về m&igrave;nh. T&ocirc;i sẽ cố gắng thay đổi n&oacute;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 424px;\" width=\"552\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Divyanka-Tripathi.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Divyanka Tripathi</strong>: Sở th&iacute;ch đồ ngọt l&agrave; thứ m&agrave; t&ocirc;i rất muốn từ bỏ v&igrave; t&ocirc;i muốn c&oacute; cuộc sống khỏe mạnh hơn.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 424px;\" width=\"556\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Shashank-Vyas.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Shashank Vyas</strong>: T&ocirc;i kh&ocirc;ng phải l&agrave; người ho&agrave;n hảo. T&ocirc;i nắm giữ nhiều điều. Qu&ecirc;n đi v&agrave; tha thứ sẽ gi&uacute;p bạn vui vẻ, v&igrave; vậy t&ocirc;i đang thực hiện n&oacute; v&agrave; t&ocirc;i hy vọng m&igrave;nh sẽ th&agrave;nh c&ocirc;ng.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 424px;\" width=\"555\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Ashish-Chowdhry.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Ashish Chowdhry</strong>: T&ocirc;i bị chứng rối loạn &aacute;m ảnh cưỡng chế (OCD). T&ocirc;i muốn mọi thứ sạch sẽ v&agrave; ho&agrave;n hảo. Vợ v&agrave; c&aacute;c con của t&ocirc;i rất gh&eacute;t điều n&agrave;y. V&igrave; vậy, t&ocirc;i muốn từ bỏ n&oacute;.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 409px;\" width=\"555\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Vishal-Singh-1.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Vishal Singh</strong>: T&ocirc;i lu&ocirc;n n&oacute;i chuyện điện thoại. Đ&oacute; l&agrave; l&yacute; do v&igrave; sao t&ocirc;i bỏ lỡ nhiều khoảnh khắc th&uacute; vị. T&ocirc;i muốn giảm bớt thời gian n&oacute;i chuyện điện thoại của m&igrave;nh.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 409px;\" width=\"551\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Shivin-Narang.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Shivin Narang</strong> : T&ocirc;i muốn thay đổi th&oacute;i quen hay kiểm tra điện thoại của m&igrave;nh để c&oacute; thể tập trung v&agrave;o những điều tốt hơn v&agrave; trải nghiệm cuộc sống hơn.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 409px;\" width=\"552\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Gunjan-Utreja.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Gunjan Utreja</strong> : T&ocirc;i muốn đi ngủ sớm v&agrave; thức dậy sớm. T&ocirc;i c&oacute; tật hay đọc b&aacute;o hoặc xem phim cho đến khuya. T&ocirc;i sẽ cố gắng để từ bỏ th&oacute;i quen n&agrave;y.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 409px;\" width=\"550\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Rashami-Desai-1.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Rashami Desai</strong> : T&ocirc;i muốn tho&aacute;t khỏi th&oacute;i quen l&agrave;m h&agrave;i l&ograve;ng tất cả mọi người để c&oacute; thể l&agrave; ch&iacute;nh m&igrave;nh.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 409px;\" width=\"555\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Vivian-Dsena-3.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Vivian Dsena</strong> : T&ocirc;i muốn từ bỏ th&oacute;i quen nghiện c&agrave; ph&ecirc;. T&ocirc;i muốn giảm bớt số lượng t&aacute;ch c&agrave; ph&ecirc; t&ocirc;i uống.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<table style=\"height: 424px;\" width=\"557\">\n<tbody>\n<tr>\n<td>\n<p><img src=\"/tinymce/source//Aishwarya-Sakuja.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Aishwarya Sakuja</strong> : T&ocirc;i c&oacute; th&oacute;i quen xấu l&agrave; thức dậy v&agrave;o l&uacute;c 4 hay 5 giờ s&aacute;ng để ăn đồ ngọt. Cơ thể của t&ocirc;i c&oacute; qu&aacute; nhiều chất đường v&agrave; sau 1 khoảng thời gian, răng của t&ocirc;i sẽ bị hỏng mất. Do vậy, t&ocirc;i muốn thay đổi th&oacute;i quen n&agrave;y.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kh&aacute;nh Linh (Newsroompost)</em></p>','Thói quen các ngôi sao truyền hình Ấn muốn từ bỏ ?','','',0,'http://tamdiem.com.vn/sources/images/Divyanka-Tripathi.jpg','',0,0,0,0,187,0,1449737467,1450235446,1),(143,'Divyanka Tripathi: Tôi là người thực tế','divyanka-tripathi-toi-la-nguoi-thuc-te-',16,'news_item','Divyanka Tripathi, Hạnh Phúc Muộn Màng, Karan Patel,Ruhanika Dhawan','Sau bộ phim Hạnh Phúc Muộn Màng, rất nhiều người đã cảm ơn tôi vì đã cho họ thấy một cái nhìn mới về ly dị và tái hôn. Một người phụ nữ không thể sinh con đã nhắn tin cho tôi rằng tôi đã khiến cô ấy nhận nuôi một em bé. Khi bạn tạo được sự kết nối như vậy, điều này rất khuyến khích.','Sau bộ phim Hạnh Phúc Muộn Màng, rất nhiều người đã cảm ơn tôi vì đã cho họ thấy một cái nhìn mới về ly dị và tái hôn. Một người phụ nữ không thể sinh con đã nhắn tin cho tôi rằng tôi đã khiến cô ấy nhận nuôi một em bé. Khi bạn tạo được sự kết nối như vậy, điều này rất khuyến khích.','<p style=\"text-align: justify;\">Đối với Divyanka Tripathi, nửa đầu năm 2015 l&agrave; khoảng thời gian mang đến cho c&ocirc; niềm vui v&agrave; cũng khiến c&ocirc; đau buồn. C&ocirc; đ&atilde; đạt được th&agrave;nh c&ocirc;ng tại nhiều lễ trao giải truyền h&igrave;nh. Nhưng mặt kh&aacute;c, về đời sống t&igrave;nh cảm c&aacute; nh&acirc;n, c&ocirc; phải trải qua biến cố chia tay với bạn trai l&acirc;u năm Ssharad Malhotra. Trong 1 cuộc n&oacute;i chuyện th&acirc;n mật, c&ocirc; đ&atilde; thẳng thắn chia sẻ về t&igrave;nh y&ecirc;u, c&ocirc;ng việc, giải thưởng v&agrave; nhiều điều hơn nữa.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td><img src=\"/tinymce/source/Divyanka-Tripathi-1.jpg\" alt=\"\" width=\"550\" height=\"370\" /></td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>&nbsp;</strong></p>\n<p style=\"text-align: justify;\"><strong>Bạn đ&atilde; qu&eacute;t sạch tất cả c&aacute;c hạng mục giải thưởng của giải thưởng truyền h&igrave;nh d&agrave;nh cho phim Hạnh Ph&uacute;c Muộn M&agrave;ng.</strong> <strong>Bạn c&oacute; dự đo&aacute;n trước được sự hưởng ứng nồng nhiệt như thế n&agrave;y kh&ocirc;ng?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka<strong>: </strong></strong>Đ&acirc;y quả thực l&agrave; đầy ngạc nhi&ecirc;n v&agrave; kh&ocirc;ng thể tưởng tượng nổi. Dẫu t&ocirc;i biết rằng c&aacute;c chương tr&igrave;nh của Ekta đều sẽ th&agrave;nh c&ocirc;ng, nhưng sự đ&oacute;n nhận như thế n&agrave;y quả thực l&agrave; kh&ocirc;ng thể ngờ.</p>\n<p style=\"text-align: justify;\"><strong>Bạn đ&atilde; d&agrave;nh được giải thưởng Shan-E-Bhopal. N&oacute; kh&aacute;c g&igrave; so với những giải thưởng kh&aacute;c?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Gulzar Saab đ&atilde; trao giải thưởng cho t&ocirc;i tại một buổi lễ long trọng được tổ chức ở Bharat Bhavan, Bhopal. Những người con g&aacute;i như t&ocirc;i thực sự cảm thấy tự tin hơn khi nhận được sự ủng hộ từ ph&iacute;a mọi người trong v&ugrave;ng. Giải thưởng n&agrave;y như một bước ngoặt đ&aacute;nh dấu t&igrave;nh y&ecirc;u v&agrave; sự c&ocirc;ng nhận của c&ocirc;ng ch&uacute;ng d&agrave;nh cho t&ocirc;i.</p>\n<p style=\"text-align: justify;\"><strong>Rất tiếc l&agrave; cuộc sống của bạn lại gặp x&aacute;o trộn. C&oacute; phải c&ocirc;ng việc đ&atilde; gi&uacute;p bạn vượt qua nỗi đau về sự đổ vỡ?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Kh&ocirc;ng hẳn như vậy. V&igrave; ch&uacute;ng t&ocirc;i đ&atilde; b&ecirc;n nhau một thời gian d&agrave;i n&ecirc;n t&ecirc;n của anh ấy vẫn được nhắc đến trong những buổi tr&ograve; chuyện với bạn b&egrave; v&agrave; cả người th&acirc;n. Điều nhỏ n&agrave;y cũng ảnh hưởng đến t&ocirc;i. May mắn, t&ocirc;i đ&atilde; bị g&atilde;y xương chỉ sau v&agrave;i ng&agrave;y chia tay. T&ocirc;i đ&atilde; b&oacute; ch&acirc;n v&agrave; tự m&igrave;nh xoay xở trong những ng&agrave;y đầu. T&ocirc;i cảm thấy khi t&ocirc;i xử l&yacute; được n&oacute;, t&ocirc;i c&oacute; thể giải quyết được bất cứ điều g&igrave; kh&ocirc;ng hay. T&ocirc;i xem việc chia tay l&agrave; một cơ hội để thư gi&atilde;n với bạn b&egrave; v&agrave; kh&aacute;m ph&aacute; một con người mới của m&igrave;nh.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td><img src=\"/tinymce/source//Divyanka-Tripathi 2.jpg\" alt=\"\" width=\"550\" height=\"600\" /></td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>&nbsp;</strong></p>\n<p style=\"text-align: justify;\"><strong>Liệu biến cố n&agrave;y c&oacute; l&agrave;m bạn ho&agrave;i nghi về t&igrave;nh y&ecirc;u? Bạn c&oacute; mở l&ograve;ng cho một mối quan hệ mới?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i l&agrave; một người thực tế. Nếu t&ocirc;i bước v&agrave;o một mối quan hệ, giờ đ&acirc;y t&ocirc;i sẽ từ từ hơn. C&oacute; thể t&ocirc;i sẽ ho&agrave;i nghi nhưng t&ocirc;i vẫn lạc quan. Ai biết được, cũng c&oacute; thể t&ocirc;i sẽ sắp xếp một cuộc h&ocirc;n nh&acirc;n cho bản th&acirc;n m&igrave;nh.</p>\n<p style=\"text-align: justify;\"><strong>Hạnh Ph&uacute;c Muộn M&agrave;ng đ&atilde; ph&aacute; vỡ nhiều sự rập khu&ocirc;n tr&ecirc;n truyền h&igrave;nh Ấn Độ qua việc khai th&aacute;c đề t&agrave;i t&aacute;i h&ocirc;n, v&ocirc; sinh v&agrave; kết h&ocirc;n v&igrave; t&igrave;nh y&ecirc;u d&agrave;nh cho một đứa trẻ. Bạn đến từ Bhopal. Vậy phản ứng ở đấy như thế n&agrave;o?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Sau bộ phim, rất nhiều người đ&atilde; cảm ơn t&ocirc;i v&igrave; đ&atilde; cho họ thấy một c&aacute;i nh&igrave;n mới về ly dị v&agrave; t&aacute;i h&ocirc;n. Một người phụ nữ kh&ocirc;ng thể sinh con đ&atilde; nhắn tin cho t&ocirc;i rằng t&ocirc;i đ&atilde; khiến c&ocirc; ấy nhận nu&ocirc;i một em b&eacute;. Khi bạn tạo được sự kết nối như vậy, điều n&agrave;y rất khuyến kh&iacute;ch.</p>\n<p style=\"text-align: justify;\"><strong>Sự kết hợp ăn &yacute; giữa Karen Patel với bạn khiến cho mọi người kh&ocirc;ng khỏi x&ocirc;n xao. Điều g&igrave; l&agrave;m hai người ăn &yacute; đ&ecirc;n như vậy?</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: L&agrave; diễn vi&ecirc;n chuy&ecirc;n nghiệp, ch&uacute;ng t&ocirc;i lu&ocirc;n muốn ứng biến v&agrave; thử nghiệm thật nhiều. Ch&uacute;ng t&ocirc;i kh&ocirc;ng ủng hộ &yacute; tưởng diễn xuất như một người chỉ đọc lời thoại. Sự phối hợp giữa 2 người ch&uacute;ng t&ocirc;i rất tốt, t&ocirc;i nghĩ l&agrave; một điều may mắn.</p>\n<table style=\"margin-left: auto; margin-right: auto;\">\n<tbody>\n<tr>\n<td style=\"text-align: center;\"><img src=\"/tinymce/source//Divyanka-Tripathi-3.jpg\" alt=\"\" width=\"550\" height=\"450\" /></td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\"><strong>&nbsp;</strong></p>\n<p style=\"text-align: justify;\"><strong>Mọi người xem bạn v&agrave; Ruhanika Dhawan giống như 2 mẹ con.</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Trẻ con kh&ocirc;ng dễ d&agrave;ng mở l&ograve;ng với những người lạ. Điều n&agrave;y kh&aacute; giống với Ruhi. T&ocirc;i phải tạo cho con b&eacute; sự thoải m&aacute;i để c&oacute; thể thấu hiểu nhau. V&agrave; điều đầu ti&ecirc;n l&agrave; t&ocirc;i l&agrave;m bạn v&agrave; d&agrave;nh thời gian chơi với con b&eacute; nhiều.</p>\n<p style=\"text-align: justify;\"><strong>Theo luật Lao động trẻ em được điều chỉnh, ch&iacute;nh phủ Ấn Độ miễn thuế cho trẻ em hoạt động trong ng&agrave;nh giải tr&iacute; (b&igrave;nh luận).</strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: T&ocirc;i cho rằng trẻ em kh&ocirc;ng n&ecirc;n bị buộc phải l&agrave;m việc. Trẻ em l&agrave;m việc trong ng&agrave;nh giải tr&iacute; sẽ dễ c&oacute; nguy cơ bị trầm cảm bởi v&igrave; ch&uacute;ng sớm thấy sự nổi tiếng khi c&ograve;n qu&aacute; nhỏ. C&aacute;c diễn vi&ecirc;n nh&iacute; trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng chỉ l&agrave;m việc trong khoảng thời gian nhất định. Bọn trẻ vẫn đạt được kết quả tốt trong c&aacute;c kỳ thi. Nếu kh&ocirc;ng t&ocirc;i sẽ kh&ocirc;ng ủng hộ việc cho c&aacute;c em đi đ&oacute;ng phim.</p>\n<p style=\"text-align: justify;\"><strong>Bạn c&oacute; mong muốn tham gia Bollywood kh&ocirc;ng?&nbsp;&nbsp; </strong></p>\n<p style=\"text-align: justify;\"><strong>Divyanka</strong>: Nhiều người mong muốn t&ocirc;i nhận một vai trong Bollywood nhưng t&ocirc;i sẽ kh&ocirc;ng chạy theo c&aacute;c dự &aacute;n phim. C&oacute; thể t&ocirc;i sẽ nhận một vai n&agrave;o đ&oacute; tr&ecirc;n c&aacute;c show truyền h&igrave;nh v&igrave; hiện tại c&oacute; rất nhiều vai th&uacute; vị d&agrave;nh cho c&aacute;c nữ diễn vi&ecirc;n.</p>\n<p style=\"text-align: right;\"><em>Kh&aacute;nh Linh (Theo Newsroompost)</em></p>','Divyanka Tripathi: Tôi là người thực tế.','','',0,'http://tamdiem.com.vn/sources/images/Divyanka-Tripathi-1.jpg','',0,0,0,0,186,0,1450234824,1450235599,1),(144,'Cặp mẹ con lý tưởng Ishita-Ruhi','cap-me-con-ly-tuong-ishita-ruhi',16,'news_item','phim Hạnh Phúc muộn màng, ishita-ruhi, cặp mẹ con lý tưởng','Ishita không phải là mẹ ruột của Ruhi. Thế nhưng, tình yêu thương mà Ishita dành cho Ruhi còn hơn cả mẹ ruột thật sự của cô bé. Ishita có thể làm tất cả mọi thứ để mang lại niềm vui cho Ruhi','Ishita không phải là mẹ ruột của Ruhi. Thế nhưng, tình yêu thương mà Ishita dành cho Ruhi còn hơn cả mẹ ruột thật sự của cô bé. Ishita có thể làm tất cả mọi thứ để mang lại niềm vui cho Ruhi','<p style=\"text-align: justify;\">Sau khi xem phim Hạnh Ph&uacute;c Muộn M&agrave;ng, chắc hẳn mỗi người mẹ đều muốn c&oacute; một đứa con g&aacute;i giống Ruhi v&agrave; mỗi người con g&aacute;i cũng mong muốn c&oacute; được một người mẹ giống Ishita. V&igrave; sao Ishita v&agrave; Ruhi lại được y&ecirc;u mến v&agrave; trở th&agrave;nh cặp mẹ con l&yacute; tưởng như vậy? &nbsp;Đ&oacute; l&agrave; bởi v&igrave;:</p>\n<p style=\"text-align: justify;\"><strong>Họ thấu hiểu lẫn nhau</strong></p>\n<p style=\"text-align: justify;\">Một người mẹ c&oacute; thể hiểu con g&aacute;i của m&igrave;nh v&agrave; thật tuyệt vời khi người con g&aacute;i cũng c&oacute; thể hiểu được nỗi đau của mẹ v&agrave; cố gắng gi&uacute;p mẹ vượt qua. Ruhi lu&ocirc;n l&agrave; người hỗ trợ cho Ishita mặc d&ugrave; c&ograve;n nhỏ tuổi. Mặt kh&aacute;c, Ishita cũng rất hiểu niềm đau v&agrave; sự bất an của Ruhi v&igrave; c&ocirc; b&eacute; đ&atilde; bị mẹ ruột bỏ rơi khi mới sinh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/ruhi-ishita 1.jpg\" alt=\"\" width=\"400\" height=\"400\" /></p>\n<p style=\"text-align: justify;\"><strong>Sợi d&acirc;y li&ecirc;n kết giữa Ruhi v&agrave; Ishita</strong></p>\n<p style=\"text-align: justify;\">Ishita kh&ocirc;ng phải l&agrave; mẹ ruột của Ruhi. Thế nhưng, t&igrave;nh y&ecirc;u thương m&agrave; Ishita d&agrave;nh cho Ruhi c&ograve;n hơn cả mẹ ruột thật sự của c&ocirc; b&eacute;. Ishita c&oacute; thể l&agrave;m tất cả mọi thứ để mang lại niềm vui cho Ruhi. Chỉ cần Ruhi c&oacute; 1 vết xước nhỏ, Ishita cũng cảm thấy lo lắng. Ruhi ch&iacute;nh l&agrave; l&yacute; do khiến Ishita quyết định kết h&ocirc;n với Raman Balla v&agrave; sống trong nh&agrave; Punjabi.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//Ruhi-Ishita 2.jpg\" alt=\"\" width=\"400\" height=\"480\" /></p>\n<p style=\"text-align: justify;\">Ruhi cũng rất y&ecirc;u mẹ Ishita. C&ocirc; b&eacute; lu&ocirc;n tạo những bất ngờ nho nhỏ để khiến mẹ cảm thấy vui. V&agrave; Ruhi ch&iacute;nh l&agrave; người t&aacute;c hợp cho ba Raman với mẹ Ishi.</p>\n<p style=\"text-align: justify;\"><strong>Cả hai đều đứng về ph&iacute;a nhau trong mọi ho&agrave;n cảnh</strong></p>\n<p style=\"text-align: justify;\">Khi ba Raman c&atilde;i nhau với mẹ Ishita, Ruhi lu&ocirc;n đứng ra để bảo vệ cho mẹ. Thậm ch&iacute; những l&uacute;c tất cả mọi người đều chống lại Ishita, Ruhi vẫn đứng về ph&iacute;a mẹ v&agrave; chứng minh rằng mẹ m&igrave;nh v&ocirc; tội. Hơn nữa, khi Ishita được y&ecirc;u cầu l&agrave;m m&oacute;n g&agrave;, Ruhi đ&atilde; nhiệt t&igrave;nh phụ gi&uacute;p mẹ.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//Ruhi-Ishita 3.jpg\" alt=\"\" width=\"400\" height=\"360\" /></p>\n<p style=\"text-align: justify;\">Ishita cũng lu&ocirc;n bảo vệ Ruhi nhất l&agrave; khi c&ocirc; b&eacute; bị ba Raman đối xử lạnh l&ugrave;ng v&igrave; những hiểu lầm. Kh&ocirc;ng chỉ th&aacute;o gỡ những hiểu lầm n&agrave;y, Ishita c&ograve;n gi&uacute;p hai cha con c&agrave;ng ng&agrave;y trở n&ecirc;n th&acirc;n thiết hơn.</p>\n<p style=\"text-align: justify;\"><strong>Giữa họ kh&ocirc;ng c&oacute; khoảng c&aacute;ch </strong></p>\n<p style=\"text-align: justify;\">Thường trong gia đ&igrave;nh, con c&aacute;i rất ngại b&agrave;y tỏ hay chia sẻ mọi thứ cho cha mẹ. Tuy nhi&ecirc;n, Ruhi v&agrave; Ishita ho&agrave;n to&agrave;n kh&aacute;c. Cả hai n&oacute;i chuyện với nhau rất th&acirc;n mật v&agrave; cởi mở. Điều n&agrave;y c&oacute; sự t&aacute;c động trong thời đại ng&agrave;y nay khi hầu hết c&aacute;c b&agrave; mẹ đều ngần ngại t&acirc;m sự với con g&aacute;i m&igrave;nh.</p>\n<p>&nbsp;<img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//ruhi-ishita 4.jpg\" alt=\"\" width=\"400\" height=\"450\" /></p>\n<p style=\"text-align: justify;\"><strong>T&igrave;nh y&ecirc;u cả hai d&agrave;nh cho nhau l&agrave; v&ocirc; điều kiện</strong></p>\n<p style=\"text-align: justify;\">Ishita l&agrave; mẹ kế của Ruhi nhưng t&igrave;nh y&ecirc;u Ruhi d&agrave;nh cho mẹ Ishi l&agrave; v&ocirc; điều kiện. Khi mẹ ruột Shagun cố gắng khiến Ruhi chống lại Ishita, c&ocirc; b&eacute; đ&atilde; kh&ocirc;ng l&agrave;m theo. Ruhi biết mẹ Ishi sẽ kh&ocirc;ng l&agrave;m bất cứ điều g&igrave; xấu với m&igrave;nh. T&igrave;nh y&ecirc;u giữa Ruhi v&agrave; Ishita kh&ocirc;ng ai c&oacute; thể ph&aacute; vỡ, thậm ch&iacute; người mẹ ruột.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//ruhi-ishita 5.jpg\" alt=\"\" width=\"400\" height=\"360\" /></p>\n<p style=\"text-align: justify;\">Mối quan hệ giữa Ishita v&agrave; Ruhi thực sự t&aacute;c động đến mọi người. Họ đ&atilde; trở th&agrave;nh mẫu gương cho thấy việc nhận con nu&ocirc;i l&agrave; một sự lựa chọn tốt cho cả cha mẹ v&agrave; con c&aacute;i.</p>\n<p style=\"text-align: right;\"><em>Kh&aacute;nh Linh (Theo India Opines)</em></p>','Cặp mẹ con lý tưởng Ishita-Ruhi','','',0,'http://tamdiem.com.vn/sources/images/ruhi-ishita%204.jpg','',0,0,0,0,185,0,1451038976,1451046820,1),(145,'Phim Việt hóa ăn khách bất ngờ ','phim-viet-hoa-an-khach-bat-ngo-',14,'news_item','phim Việt hóa, Em là bà nội của anh, Dù gió có thổi, Cô gái xấu xí, Ngôi nhà hạnh phúc, Người mẫu','Việt hóa phim ăn khách nước ngoài đang là xu hướng của điện ảnh Việt nhưng cũng dấy lên nhiều lo ngại.','Việt hóa phim ăn khách nước ngoài đang là xu hướng của điện ảnh Việt nhưng cũng dấy lên nhiều lo ngại.','<p><span style=\"color: #808080;\"><strong><em>Việt h&oacute;a phim ăn kh&aacute;ch nước ngo&agrave;i đang l&agrave; xu hướng của điện ảnh Việt nhưng cũng dấy l&ecirc;n nhiều lo ngại.</em></strong></span></p>\n<div id=\"divNewsContent\" class=\"contentdetail\" data-field=\"body\">\n<p style=\"text-align: justify;\">Nếu phi&ecirc;n bản gốc &ldquo;Miss Granny&rdquo; của H&agrave;n Quốc đứng thứ 2 trong 10 phim ăn kh&aacute;ch nhất của H&agrave;n Quốc năm 2014, phi&ecirc;n bản Trung Quốc thu về tr&ecirc;n 200 triệu nh&acirc;n d&acirc;n tệ (32 triệu USD) th&igrave; phi&ecirc;n bản Việt &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo; cũng kh&ocirc;ng k&eacute;m cạnh. Sau 18 ng&agrave;y c&ocirc;ng chiếu, t&aacute;c phẩm của đạo diễn Phan Gia Nhật Linh đ&atilde; chạm mốc 800.000 lượt xem với tổng doanh thu đạt 60 tỉ đồng.</p>\n<p style=\"text-align: justify;\"><strong>Ph&ugrave; hợp với văn h&oacute;a, lối sống người Việt</strong></p>\n<p style=\"text-align: justify;\">L&agrave;m lại phim nổi tiếng của một quốc gia l&agrave; c&aacute;ch thức kh&aacute; phổ biến ở điện ảnh Việt. Hầu hết c&aacute;c trường hợp, c&aacute;c nh&agrave; l&agrave;m phim thường lấy cảm hứng v&agrave; thực hiện phim dựa tr&ecirc;n kịch bản gốc, c&oacute; thay đổi t&ugrave;y mức độ. Nếu ai đ&atilde; từng xem &ldquo;Miss Granny&rdquo; của H&agrave;n Quốc th&igrave; sẽ thấy &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo; kh&aacute; trung th&agrave;nh với kịch bản gốc. Nghĩa l&agrave; kh&ocirc;ng c&oacute; qu&aacute; nhiều kh&aacute;c biệt ở đường d&acirc;y c&acirc;u chuyện, t&igrave;nh tiết, kể cả khu&ocirc;n h&igrave;nh, g&oacute;c m&aacute;y...</p>\n<p style=\"text-align: justify;\">Đạo diễn Phan Gia Nhật Linh cho biết ban đầu anh cũng đứng trước &aacute;p lực phải &ldquo;l&agrave;m mới&rdquo; bằng s&aacute;ng tạo ri&ecirc;ng để mang đến sự kh&aacute;c biệt. Song v&igrave; kịch bản gốc của H&agrave;n Quốc qu&aacute; chặt chẽ, những c&aacute;i mới khi th&ecirc;m thắt, chuyển đổi kh&ocirc;ng l&agrave;m phim hay hơn n&ecirc;n anh quyết định thực hiện giống kịch bản gốc.</p>\n<p>&nbsp;</p>\n<div class=\"VCSortableInPreviewMode\">\n<div><img id=\"img_278168\" style=\"max-width: 100% ! important; display: block; margin-left: auto; margin-right: auto;\" src=\"http://nld.vcmedia.vn/k:2015/chot-8-1451488080027/phim-viet-hoa-an-khach-bat-ngo.jpg\" alt=\"Cảnh trong phim &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo;. (Ảnh do nh&agrave; sản xuất cung cấp)\" /></div>\n<div class=\"PhotoCMS_Caption\" style=\"text-align: center;\"><span style=\"color: #999999;\"><em>Cảnh trong phim &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo;. (Ảnh do nh&agrave; sản xuất cung cấp)</em></span></div>\n</div>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Nhiều người cho rằng đ&oacute; cũng l&agrave; l&yacute; do người xem kh&ocirc;ng thấy được dấu ấn c&aacute; nh&acirc;n v&agrave; s&aacute;ng tạo của Phan Gia Nhật Linh trong phim n&agrave;y. Phan Gia Nhật Linh cũng thừa nhận anh kh&ocirc;ng l&agrave;m một &ldquo;ph&ugrave; thủy&rdquo; đưa c&acirc;u chuyện từ H&agrave;n Quốc sang Việt Nam v&agrave; tạo sự hấp dẫn từ sự... kh&aacute;c biệt qu&aacute; nhiều. Nhưng n&oacute;i như vậy kh&ocirc;ng c&oacute; nghĩa l&agrave; phim sao ch&eacute;p rập khu&ocirc;n, thiếu sự đầu tư tr&iacute; tuệ v&agrave; tr&aacute;ch nhiệm của đạo diễn.</p>\n<p style=\"text-align: justify;\">Theo l&yacute; giải của c&aacute;c nh&agrave; ph&ecirc; b&igrave;nh, ch&iacute;nh kịch bản pha trộn những x&uacute;c cảm Việt v&agrave;o mạch phim m&agrave; kh&ocirc;ng thay đổi nhiều đến cốt truyện gốc đ&atilde; đem đến th&agrave;nh c&ocirc;ng cho &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo;. Th&ecirc;m v&agrave;o đ&oacute;, diễn xuất sinh động v&agrave; đầy m&agrave;u sắc của Miu L&ecirc; v&agrave; d&agrave;n diễn vi&ecirc;n phụ đ&atilde; khiến phim vượt khỏi ranh giới của một phim l&agrave;m lại. Diễn vi&ecirc;n Hồng &Aacute;nh (người đ&oacute;ng vai người mẹ trong phim) cho rằng: &ldquo;Phim vẫn rất th&uacute; vị, gần gũi, mang kh&ocirc;ng kh&iacute; Việt được tạo ra từ sự ph&ugrave; hợp với văn h&oacute;a, lối sống người Việt chứ &iacute;t mang hơi thở H&agrave;n như c&aacute;c phim Việt h&oacute;a trước đ&oacute;&rdquo;.</p>\n<p style=\"text-align: justify;\">Phan Gia Nhật Linh cũng thừa nhận mục đ&iacute;ch của anh l&agrave; tạo n&ecirc;n một t&aacute;c phẩm điện ảnh đầy đặn cảm x&uacute;c, giữ được tinh thần &ldquo;h&agrave;i hước nhưng cảm động&rdquo; v&agrave; giữ được gi&aacute; trị cốt l&otilde;i của bộ phim chứ kh&ocirc;ng phải cố gắng s&aacute;ng tạo c&aacute;i mới m&agrave; kh&ocirc;ng ph&ugrave; hợp. Phim Việt h&oacute;a đ&atilde; c&oacute; qu&aacute; nhiều b&agrave;i học về sự thất bại v&igrave; đạo diễn tham vọng s&aacute;ng tạo, th&ecirc;m thắt qu&aacute; đ&agrave; l&agrave;m c&acirc;u chuyện khi&ecirc;n cưỡng, thiếu sức thuyết phục.</p>\n<p style=\"text-align: justify;\"><strong>Giải ph&aacute;p l&acirc;u d&agrave;i?</strong></p>\n<p style=\"text-align: justify;\">Tuy kh&ocirc;ng đ&igrave;nh đ&aacute;m bằng &ldquo;Em l&agrave; b&agrave; nội của anh&rdquo; nhưng trước đ&oacute; phim &ldquo;Y&ecirc;u&rdquo; của đạo diễn Việt Max (mua bản quyền từ phim &ldquo;The love of Siam&rdquo; của Th&aacute;i Lan) cũng nhận được nhiều phản ứng t&iacute;ch cực của kh&aacute;n giả. L&yacute; giải cho sức h&uacute;t của 2 phim n&agrave;y, đạo diễn Đặng Nhật Minh cho biết: &ldquo;Th&agrave;nh c&ocirc;ng c&oacute; phần quyết định kh&ocirc;ng nhỏ từ kịch bản qu&aacute; hay, c&acirc;u chuyện cuốn h&uacute;t, nh&acirc;n vật được khắc họa t&iacute;nh c&aacute;ch r&otilde; n&eacute;t; t&igrave;nh huống logic, th&uacute; vị. Đ&oacute; l&agrave; điểm mạnh ở những kịch bản của nước ngo&agrave;i, cũng l&agrave; điểm yếu của kịch bản trong nước&rdquo;.</p>\n<p style=\"text-align: justify;\">Đạo diễn L&ecirc; Cung Bắc đ&aacute;nh gi&aacute; cao c&aacute;ch l&agrave;m n&agrave;y v&igrave; giải quyết t&igrave;nh trạng khan hiếm kịch bản phim v&agrave; đ&aacute;p ứng nhu cầu giải tr&iacute; của kh&aacute;n giả bởi theo thống k&ecirc; của Cục Điện ảnh, trong năm 2015, Việt Nam c&oacute; 40 phim điện ảnh ra rạp. Con số n&agrave;y ước chừng sẽ l&agrave; 60 trong năm 2017. Cơn kh&aacute;t kịch bản hay l&agrave; điều dễ thấy. Đạo diễn Phan Gia Nhật Linh thừa nhận: &ldquo;T&ocirc;i kh&ocirc;ng c&oacute; l&yacute; do g&igrave; để từ chối một kịch bản vừa h&agrave;i hước, c&oacute; t&iacute;nh giải tr&iacute; lại vừa cảm động, s&acirc;u sắc, nh&acirc;n văn như phim n&agrave;y để chạy theo những kịch bản phim mới do bi&ecirc;n kịch trong nước viết rất ngh&egrave;o n&agrave;n &yacute; tưởng, hời hợt&rdquo;. Hơn nữa, kh&aacute;n giả đ&atilde; qu&aacute; nh&agrave;m ch&aacute;n với h&agrave;i nhảm, kinh dị th&igrave; những t&aacute;c phẩm chỉn chu, duy&ecirc;n d&aacute;ng rất c&oacute; thể sẽ tạo n&ecirc;n sức h&uacute;t mới.</p>\n<p style=\"text-align: justify;\">Một kịch bản được thể hiện qua nhiều ng&ocirc;n ngữ của c&aacute;c quốc gia kh&aacute;c nhau l&agrave; m&ocirc; h&igrave;nh mới của C&ocirc;ng ty CJ Entertainment (H&agrave;n Quốc) m&agrave; &ldquo;Miss Granny&rdquo; l&agrave; dự &aacute;n mở m&agrave;n. Đại diện c&ocirc;ng ty n&agrave;y cho biết sức h&uacute;t vượt trội của phi&ecirc;n bản Việt l&agrave; động lực để đẩy mạnh m&ocirc; h&igrave;nh kinh doanh đầy s&aacute;ng tạo n&agrave;y trong thời gian tới. Với doanh thu cao, sự đ&aacute;nh gi&aacute; tốt của giới chuy&ecirc;n m&ocirc;n v&agrave; tiết kiệm thời gian đầu tư, tr&agrave;o lưu phim &ldquo;remake&rdquo; (l&agrave;m lại) ở Việt Nam cũng v&igrave; thế m&agrave; c&oacute; khả năng ph&aacute;t triển rầm rộ hơn v&agrave; kh&aacute;n giả cũng sẽ quan t&acirc;m nhiều hơn d&ograve;ng phim n&agrave;y trong thời gian tới.</p>\n<p style=\"text-align: justify;\">Tuy nhi&ecirc;n, tr&agrave;o lưu n&agrave;y cũng dấy l&ecirc;n nhiều lo ngại. C&acirc;u chuyện th&uacute; vị c&oacute; sẵn nhưng ch&uacute;ng ta phải Việt h&oacute;a. Nghĩa l&agrave; phim cần mi&ecirc;u tả văn h&oacute;a bản địa, lối sống tương th&iacute;ch với bối cảnh, con người cũng như những xung đột x&atilde; hội ph&ugrave; hợp. C&oacute; như vậy kh&aacute;n giả mới dễ d&agrave;ng tiếp nhận. Phim l&agrave;m lại chỉ ph&ugrave; hợp với thể loại h&agrave;i, t&igrave;nh cảm l&atilde;ng mạn; những thể loại kh&aacute;c chắc chắn sẽ l&agrave; th&aacute;ch thức rất lớn cho điện ảnh Việt c&ograve;n qu&aacute; non k&eacute;m như hiện nay.</p>\n<p>&nbsp;</p>\n<div id=\"ObjectBoxContent_1451488470002\" class=\"VCSortableInPreviewMode\" dir=\"center\" style=\"border: 1px solid #999999; background: #e6e6fa none repeat scroll 0% 0%; float: none; width: 90%; margin: 0px auto;\" data-back=\"E6E6FA\" data-border=\"999\">\n<p><strong>&nbsp;&nbsp; Đa phần thất bại</strong></p>\n<p>&nbsp; Trong khi phim điện ảnh mới bắt đầu những bước đi đầu ti&ecirc;n th&igrave; phim truyền h&igrave;nh Việt h&oacute;a đ&atilde; ph&aacute;t triển từ rất l&acirc;u với h&agrave;ng chục phim như: &ldquo;V&ograve;ng xo&aacute;y&nbsp; t&igrave;nh y&ecirc;u&rdquo;, &ldquo;D&ugrave; gi&oacute; c&oacute; thổi&rdquo;, &ldquo;C&ocirc; g&aacute;i xấu x&iacute;&rdquo;, &ldquo;Ng&ocirc;i nh&agrave; hạnh ph&uacute;c&rdquo;, &ldquo;Người mẫu&rdquo;, &ldquo;Lối sống sai lầm&rdquo;, &ldquo;Đam m&ecirc; nghiệt ng&atilde;&rdquo;... song đa phần c&aacute;c phim n&agrave;y đều kh&ocirc;ng tạo được dấu ấn so với phi&ecirc;n bản gốc. Xu hướng l&agrave;m lại, l&agrave;m mới c&aacute;c phim truyền h&igrave;nh nước ngo&agrave;i c&oacute; dấu hiệu chững lại v&agrave;i năm nay.</p>\n<p>C&oacute; rất nhiều nguy&ecirc;n nh&acirc;n khiến phim truyền h&igrave;nh Việt h&oacute;a thất bại nhưng đạo diễn Nguyễn Minh Chung (người từng l&agrave;m nhiều phim Việt h&oacute;a) cho rằng kinh ph&iacute; thấp v&agrave; tay nghề yếu k&eacute;m l&agrave; yếu tố chi phối tất cả.</p>\n</div>\n<p>&nbsp;</p>\n<div class=\"nguon-tin-detail\" style=\"text-align: right;\"><em>Minh Nga (nld.com.vn)</em></div>\n</div>','Phim Việt hóa ăn khách bất ngờ ','','',0,'http://tamdiem.com.vn/sources/images/phim-viet-hoa-an-khach-bat-ngo.jpg','',0,0,0,0,469,0,1451545850,1453866260,1),(146,'Những điều thú vị về Karan Patel','nhung-dieu-thu-vi-ve-karan-patel',16,'news_item','Karan Patel, Raman Kumar Bhalla, Hạnh Phúc Muộn Màng','Trong phim Hạnh Phúc Muộn Màng, Karan Patel đã được biết đến khi hóa thân thành nhân vật Raman Kumar Bhalla kiêu ngạo, nóng tính và hay nổi giận.  Còn ở ngoài đời, Karan Patel là người thế nào. Dưới đây là những thông tin thú vị về anh chàng diễn viên nổi tiếng này.','Trong phim Hạnh Phúc Muộn Màng, Karan Patel đã được biết đến khi hóa thân thành nhân vật Raman Kumar Bhalla kiêu ngạo, nóng tính và hay nổi giận.  Còn ở ngoài đời, Karan Patel là người thế nào. Dưới đây là những thông tin thú vị về anh chàng diễn viên nổi tiếng này.','<p style=\"text-align: justify;\">Trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng, Karan Patel được biết đến khi h&oacute;a th&acirc;n th&agrave;nh nh&acirc;n vật Raman Kumar Bhalla ki&ecirc;u ngạo, n&oacute;ng t&iacute;nh v&agrave; hay nổi giận. C&ograve;n ở ngo&agrave;i đời, Karan Patel l&agrave; người thế n&agrave;o. Dưới đ&acirc;y l&agrave; những th&ocirc;ng tin th&uacute; vị về ch&agrave;ng diễn vi&ecirc;n nổi tiếng n&agrave;y.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 1.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Karan Patel xuất th&acirc;n từ một gia đ&igrave;nh kinh doanh</strong></p>\n<p style=\"text-align: justify;\">Karan sinh ng&agrave;y 23/11/1983 tại Calcutta, Ấn Độ. Sau đ&oacute; Karan chuyển đến Mumbai v&agrave; theo học tại đ&acirc;y. Anh đ&atilde; tốt nghiệp trường đại học Mithabhai. Karan l&agrave; con trai duy nhất trong gia đ&igrave;nh kinh doanh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/karan-patel 2.jpg\" alt=\"\" width=\"550\" height=\"300\" /></p>\n<p><strong>Karan l&agrave; ch&agrave;ng trai của mẹ</strong></p>\n<p style=\"text-align: justify;\">Karan Patel lu&ocirc;n hỏi mẹ về mọi thứ v&agrave; tham khảo lời khuy&ecirc;n của mẹ. Anh tin rằng nhờ c&oacute; mẹ, anh đ&atilde; bỏ được những th&oacute;i quen xấu v&agrave; tr&aacute;nh được những người bạn xấu. Kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi tin rằng Karan chia tay với Kamya Punjabi v&igrave; cha mẹ.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 3.jpg\" alt=\"\" width=\"450\" height=\"400\" /></p>\n<p><strong>Karan theo học diễn xuất tại Học Viện Nghệ Thuật Lu&acirc;n Đ&ocirc;n</strong></p>\n<p style=\"text-align: justify;\">Karan đ&atilde; học diễn xuất tại Học Viện Kishore Namit Kapoor &ndash; nơi rất nhiều ng&ocirc;i sao nổi tiếng Bollywood như Kareen Kapoor Khan, Hrithik Roshan, Priyanka Chopra, Imran Khan, Saif Ali Khan v&agrave; Ranveer Singh cũng đ&atilde; theo học. Ngo&agrave;i ra, Karan cũng đến Học Viện Nghệ Thuật Lu&acirc;n Đ&ocirc;n để trao dồi th&ecirc;m kỹ năng diễn xuất của m&igrave;nh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 4.jpg\" alt=\"\" width=\"550\" height=\"380\" /></p>\n<p><strong>Karan Patel l&agrave; vũ c&ocirc;ng được huấn luyện</strong></p>\n<p style=\"text-align: justify;\">Khi xem phim Hạnh Ph&uacute;c Muộn M&agrave;ng, ch&uacute;ng ta thấy thỉnh thoảng Raman Kumar Bhalla nhảy c&ugrave;ng với vợ Ishita. Anh đ&atilde; học nhảy ở trường Shiamak Davar. Karan cũng tham gia trong chương tr&igrave;nh Nach Baliye 3 (2007) v&agrave; Jhalak Dikhhla Jaa 6 (2013).</p>\n<p><iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.youtube.com/embed/64ON4bkbIYs\" width=\"550\" height=\"350\"></iframe></p>\n<p style=\"text-align: justify;\"><strong>Karan bắt đầu diễn xuất trong chương tr&igrave;nh Kasautii Zindagi Kay v&agrave;o năm 2005</strong></p>\n<p style=\"text-align: justify;\">Mặc d&ugrave; chỉ xoay quanh 4 nh&acirc;n vật ch&iacute;nh Anurag, Prerna, &Ocirc;ng Bajaj v&agrave; Komolika, chương tr&igrave;nh Kasautii Zindagi Kay đ&atilde; quy tụ rất nhiều ng&ocirc;i sao truyền h&igrave;nh hiện nay trong đ&oacute; c&oacute; cả Karan Patel với vai diễn nhỏ. Ngo&agrave;i ra, Karan đ&atilde; từng tham gia những vai nhỏ trong Kesar, Kavyanjali cho đến khi anh nhận vai ch&iacute;nh đầu ti&ecirc;n Robbie Sabarwal trong Kasturi.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 5.jpg\" alt=\"\" width=\"550\" height=\"330\" /></p>\n<p><strong>Karan từng hẹn h&ograve; với Amita Chandekar - bạn diễn trong Kasturi</strong></p>\n<p>Khi tham gia chương tr&igrave;nh Nach Baliye 3, Karan đ&atilde; hẹn h&ograve; với Amita. Cặp đ&ocirc;i thậm ch&iacute; c&ograve;n n&oacute;i rằng họ y&ecirc;u nhau rất m&atilde;nh liệt. Tuy nhi&ecirc;n, khi Nach Baliye kết th&uacute;c, cả hai cũng đường ai nấy đi. Sau đ&oacute;, Karan l&ecirc;n tiếng phủ nhận mối quan hệ n&agrave;y. Điều n&agrave;y thực sự l&agrave;m nhiều người h&acirc;m mộ cảm thấy tức giận v&igrave; họ đ&atilde; dựng l&ecirc;n một c&acirc;u chuyện t&igrave;nh y&ecirc;u giả cho chương tr&igrave;nh thực tế.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 6.jpg\" alt=\"\" width=\"550\" height=\"450\" /></p>\n<p><strong>Karan đ&atilde; từng bị &aacute;m ảnh bởi Payal Rohatgi</strong></p>\n<p>&Iacute;t người biết rằng khi Karan Patel v&agrave; Payal Rohatgi tham gia chương tr&igrave;nh thực tế mang t&iacute;nh thử th&aacute;ch Survivors India (2011), Karan cố gắng gần gũi với Payal hai lần. Lần đầu ti&ecirc;n, anh đ&atilde; đến trước ph&ograve;ng c&ocirc; v&agrave;o l&uacute;c 2 giờ s&aacute;ng v&agrave; g&otilde; cửa li&ecirc;n tục. Lần thứ hai cũng vậy khiến Payal Rohatgi gọi Karan l&agrave; kẻ hiếp d&acirc;m. V&agrave; bạn trai của Payal - Sangram Singh phải can thiệp để chấm dứt điều n&agrave;y.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 7.jpg\" alt=\"\" width=\"550\" height=\"450\" /></p>\n<p><strong>Karan v&agrave; Ankita sẽ c&ugrave;ng đ&oacute;ng trong phim Hạnh Ph&uacute;c Muộn M&agrave;ng</strong></p>\n<p>Ankita kh&ocirc;ng chỉ bước v&agrave;o cuộc sống của Karan ngo&agrave;i đời m&agrave; cả ở trong phim nữa. C&ocirc; ấy sẽ tham gia Hạnh Ph&uacute;c Muộn M&agrave;ng v&agrave; can thiệp v&agrave;o mối quan hệ giữa Raman v&agrave; Ishita.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//ankita-bhargava-karan-patel 8.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p><strong>Karan Patel đ&atilde; tham gia 2 bộ phim Bollywood</strong></p>\n<p>Trước khi quay trở lại truyền h&igrave;nh với vai Raman Kumar Bhalla, Patel đ&atilde; đ&oacute;ng phim City of Gold của Mahesh Manjreka (2010 ) v&agrave; Shootout At Wadala của Ekta Kapoor (2013).</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//karan-patel 9.jpg\" alt=\"\" width=\"550\" height=\"400\" /></p>\n<p><strong>Những điều y&ecirc;u th&iacute;ch của Karan Patel</strong></p>\n<p>M&agrave;u sắc y&ecirc;u th&iacute;ch: Đen</p>\n<p>B&agrave;i h&aacute;t y&ecirc;u th&iacute;ch: &ldquo;Deewana Dil Deewana &hellip;&rdquo; trong bộ phim h&agrave;i l&atilde;ng mạn Kabhi Haan Kabhi Naa</p>\n<p>Người h&ugrave;ng y&ecirc;u th&iacute;ch: Batman</p>\n<p>Điểm du lịch y&ecirc;u th&iacute;ch: Lu&acirc;n Đ&ocirc;n</p>\n<p>Hoạt động y&ecirc;u th&iacute;ch : Cưỡi ngựa v&agrave; Trekking</p>\n<p style=\"text-align: right;\"><em>Kh&aacute;nh Linh (Theo India Opines)</em></p>\n<p>&nbsp;</p>','Những điều thú vị về Karan Patel','','',0,'http://tamdiem.com.vn/sources/images/karan-patel%201.jpg','',0,0,0,0,184,0,1452052863,1452054419,1),(147,'Cát-xê một đêm của danh hài Việt đủ sống một đời?','cat-xe-mot-dem-cua-danh-hai-viet-du-song-mot-doi',14,'news_item','Cát-xê, danh hài Việt, Hoài Linh, Trường Giang hay Trấn Thành','Với mức cát-xê đồn thổi cao ngất ngưởng, thực sự các danh hài Hoài Linh, Trường Giang hay Trấn Thành giàu đến mức nào? ','Với mức cát-xê đồn thổi cao ngất ngưởng, thực sự các danh hài Hoài Linh, Trường Giang hay Trấn Thành giàu đến mức nào? ','<p><strong>Với mức c&aacute;t-x&ecirc; đồn thổi cao ngất ngưởng, thực sự c&aacute;c danh h&agrave;i Ho&agrave;i Linh, Trường Giang hay Trấn Th&agrave;nh gi&agrave;u đến mức n&agrave;o?</strong></p>\n<div class=\"the-article-body\">\n<p><strong>Danh h&agrave;i Ho&agrave;i Linh</strong></p>\n<table class=\"picture\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"pic\"><img src=\"http://img.v3.news.zdn.vn/w660/Uploaded/ycgmvvbt/2016_01_10/hoailinh.jpg\" alt=\"C&aacute;t-x&ecirc; một đ&ecirc;m của danh h&agrave;i Việt đủ sống một đời?\" /></td>\n</tr>\n<tr>\n<td class=\"pCaption caption\">\n<p><span style=\"color: #808080;\">Ho&agrave;i Linh l&agrave; diễn vi&ecirc;n c&oacute; c&aacute;t-x&ecirc; khủng nhất hiện nay.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">C&aacute;ch đ&acirc;y &iacute;t l&acirc;u, dư luận trong v&agrave; ngo&agrave;i giới giải tr&iacute; đều cho&aacute;ng v&aacute;ng trước tin đồn c&aacute;t-x&ecirc; 3 tỷ đồng để mời danh h&agrave;i Ho&agrave;i Linh đ&oacute;ng phim. Mặc d&ugrave; tin đồn chưa được chứng thực nhưng những người trong giới đều biết Ho&agrave;i Linh l&agrave; danh h&agrave;i thuộc h&agrave;ng đắt s&ocirc; nhất hiện nay. V&agrave; mặc d&ugrave; c&aacute;t-x&ecirc; của Ho&agrave;i Linh rất cao nhưng hầu hết c&aacute;c bầu show đều cho rằng xứng đ&aacute;ng.</p>\n<p style=\"text-align: justify;\">Mọi người hay thắc mắc, so s&aacute;nh c&aacute;t-x&ecirc; của những ng&ocirc;i sao ca nhạc hạng A, hạng B m&agrave; &iacute;t khi để &yacute; đến những nghệ sĩ trong những lĩnh vực nghệ thuật kh&aacute;c. Ch&iacute;nh v&igrave; thế, khi c&aacute;t-x&ecirc; của Ho&agrave;i Linh được h&eacute; lộ, kh&ocirc;ng &iacute;t người cho&aacute;ng v&aacute;ng.&nbsp;</p>\n<p style=\"text-align: justify;\">Theo th&ocirc;ng tin một người giấu t&ecirc;n chuy&ecirc;n tổ chức c&aacute;c chương tr&igrave;nh ca nhạc tạp kỹ đ&atilde; cho biết: \"C&aacute;t-x&ecirc; của danh h&agrave;i Ho&agrave;i Linh tầm khoảng 100 triệu đồng, nếu biểu diễn ở ph&ograve;ng tr&agrave;. T&igrave;nh nghĩa, du di th&igrave; gi&aacute; thấp nhất cũng khoảng 80 triệu đồng. C&ograve;n c&aacute;c show lẻ tẻ ở c&aacute;c chương tr&igrave;nh lẻ tẻ th&igrave; c&oacute; gi&aacute; khoảng v&agrave;i chục triệu v&agrave;o dịp Tết\".</p>\n<p style=\"text-align: justify;\">Ho&agrave;i Linh vốn đắt show quanh năm v&igrave; vậy \"v&agrave;o dịp tết, muốn mời Ho&agrave;i Linh phải đặt trước từ rất l&acirc;u&rdquo;. Cũng ch&iacute;nh v&igrave; chạy show Tết nhiều, Ho&agrave;i Linh từng kiệt sức đến mức phải nhập viện.</p>\n<p style=\"text-align: justify;\">Thậm ch&iacute;, từng c&oacute; chuyện c&aacute;c ph&ograve;ng tr&agrave; đẳng cấp ở S&agrave;i G&ograve;n vốn chỉ mời c&aacute;c ca sĩ nổi tiếng về h&aacute;t đ&atilde; ph&aacute; lệ sang trọng mời Ho&agrave;i Linh về diễn. Tiền phụ thu cho một kh&aacute;ch v&agrave;o ph&ograve;ng tr&agrave;, chưa kể tiền nước đ&atilde; l&agrave; 800.000 đồng.</p>\n<p style=\"text-align: justify;\">Mặc d&ugrave; c&aacute;t-x&ecirc; của Ho&agrave;i Linh rất cao nhưng hầu hết c&aacute;c bầu show đều cho rằng xứng đ&aacute;ng với những g&igrave; danh h&agrave;i đầu tư bởi đa phần c&aacute;c tiểu phẩm h&agrave;i đều do tự tay anh viết kịch bản, đo ni đ&oacute;ng gi&agrave;y cho ch&iacute;nh m&igrave;nh.</p>\n<p style=\"text-align: justify;\">Điều l&agrave;m n&ecirc;n c&aacute;i chất Ho&agrave;i Linh l&agrave; nội dung c&aacute;c tiểu phẩm đều gần gũi đời thường, ch&acirc;m biếm một c&aacute;ch s&acirc;u cay những th&oacute;i hư, tật xấu. Ch&iacute;nh v&igrave; vậy, Ho&agrave;i Linh lu&ocirc;n đắt show v&agrave; cao gi&aacute;. Nhiều người s&agrave;nh sỏi showbiz cũng đồn đại, nếu n&oacute;i đại gia trong giới th&igrave; Ho&agrave;i Linh mới xứng danh. Tuy nhi&ecirc;n, ở ngo&agrave;i đời, anh rất xuề xo&agrave;, kh&ocirc;ng trau chuốt h&agrave;ng hiệu như những ng&ocirc;i sao kh&aacute;c n&ecirc;n &iacute;t g&acirc;y sự ch&uacute; &yacute; về mức độ gi&agrave;u c&oacute;.&nbsp;</p>\n<p><strong>Danh h&agrave;i Xu&acirc;n Bắc</strong></p>\n<table class=\"picture\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"pic\"><img src=\"http://img.v3.news.zdn.vn/w660/Uploaded/ycgmvvbt/2016_01_10/xuanbac.jpg\" alt=\"C&aacute;t-x&ecirc; một đ&ecirc;m của danh h&agrave;i Việt đủ sống một đời?\" /></td>\n</tr>\n<tr>\n<td class=\"pCaption caption\">\n<p><span style=\"color: #808080;\">Xu&acirc;n Bắc đang l&agrave; người sở hữu c&aacute;t-x&ecirc; cao nhất với 8 tỷ đồng.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">C&aacute;ch đ&acirc;y v&agrave;i th&aacute;ng, dư luận được một phen &ldquo;ngỡ ng&agrave;ng&rdquo; với mức c&aacute;t-x&ecirc; 8 tỷ đồng của MC, nghệ sĩ h&agrave;i Xu&acirc;n Bắc. Con số 8 tỷ đồng vốn l&agrave; một con số rất lớn, v&agrave; đối với Xu&acirc;n Bắc l&agrave; một nghệ sĩ h&agrave;i, MC th&igrave; mức c&aacute;t-x&ecirc; n&agrave;y g&acirc;y bất ngờ cho rất nhiều người. Thậm ch&iacute; nhiều kh&aacute;n giả c&ograve;n kh&ocirc;ng tin, tỏ ra &ldquo;nghi ngờ&rdquo; về con số n&agrave;y.</p>\n<p style=\"text-align: justify;\">Con số 8 tỷ đồng m&agrave; Xu&acirc;n Bắc nhận được l&agrave; sự thật. Đ&oacute; l&agrave; mức c&aacute;t-x&ecirc; m&agrave; Xu&acirc;n Bắc nhận được khi một c&ocirc;ng ty mua bản quyền h&igrave;nh ảnh, tiếng của anh với chương tr&igrave;nh Đuổi h&igrave;nh bắt chữ. Đ&acirc;y l&agrave; một th&ocirc;ng tin đ&atilde; l&agrave;m &ldquo;s&aacute;ng tỏ&rdquo; con số c&aacute;t-x&ecirc; &ldquo;khủng&rdquo; kia v&agrave; kh&aacute;n giả đều cho rằng Xu&acirc;n Bắc ho&agrave;n to&agrave;n xứng đ&aacute;ng bởi những đ&oacute;ng g&oacute;p của anh cho th&agrave;nh c&ocirc;ng của chương tr&igrave;nh Đuổi h&igrave;nh bắt chữ.</p>\n<p><strong>Danh h&agrave;i Trấn Th&agrave;nh</strong></p>\n<table class=\"picture\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"pic\"><img src=\"http://img.v3.news.zdn.vn/w660/Uploaded/ycgmvvbt/2016_01_10/tranthanh.jpg\" alt=\"C&aacute;t-x&ecirc; một đ&ecirc;m của danh h&agrave;i Việt đủ sống một đời?\" /></td>\n</tr>\n<tr>\n<td class=\"pCaption caption\"><span style=\"color: #808080;\">Trấn Th&agrave;nh.</span></td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Một trong những ng&ocirc;i sao trẻ đ&igrave;nh đ&aacute;m nhất của l&agrave;ng h&agrave;i hiện n&agrave;y l&agrave; Trấn Th&agrave;nh. Với sự th&ocirc;ng minh v&agrave; hoạt ng&ocirc;n, Trấn Th&agrave;nh xuất hiện với tần suất ch&oacute;ng mặt tr&ecirc;n truyền h&igrave;nh với c&aacute;c vai tr&ograve;: diễn h&agrave;i, MC, đ&oacute;ng phim, l&agrave;m gi&aacute;m khảo v&agrave; cả ca h&aacute;t.</p>\n<p style=\"text-align: justify;\">Chỉ cần mỗi lần kh&aacute;n giả thấy sự xuất hiện của Trấn Th&agrave;nh tr&ecirc;n truyền h&igrave;nh, anh đ&atilde; thu về cho m&igrave;nh tầm 30 triệu đồng. C&ograve;n với c&aacute;c chương tr&igrave;nh b&ecirc;n ngo&agrave;i, một bầu show tiết lộ để mời Trấn Th&agrave;nh th&igrave; c&aacute;i gi&aacute; l&ecirc;n tới 6.000 USD.</p>\n<p style=\"text-align: justify;\">C&aacute;i t&ecirc;n Trấn Th&agrave;nh cũng rất thu h&uacute;t c&aacute;c nh&atilde;n h&agrave;ng n&ecirc;n anh c&ograve;n c&oacute; thể kiếm được v&agrave;i tỷ đồng cho những hợp đồng quảng c&aacute;o. Trong một số liệu về tổng thu nhập, Trấn Th&agrave;nh khiến cho nhiều kh&aacute;n giả bất ngờ khi con số của anh cao gấp đ&ocirc;i so với danh h&agrave;i l&atilde;o l&agrave;ng Xu&acirc;n Bắc.</p>\n<p><strong>Danh h&agrave;i Trường Giang</strong></p>\n<table class=\"picture\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"pic\"><img src=\"http://img.v3.news.zdn.vn/w660/Uploaded/ycgmvvbt/2016_01_10/truonggiang.jpg\" alt=\"C&aacute;t-x&ecirc; một đ&ecirc;m của danh h&agrave;i Việt đủ sống một đời?\" /></td>\n</tr>\n<tr>\n<td class=\"pCaption caption\">\n<p><span style=\"color: #808080;\">Trường Giang đang l&agrave; nam diễn vi&ecirc;n được y&ecirc;u mến.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">C&aacute;ch đ&acirc;y 10 năm, Trường Giang c&ograve;n đang phải sống vất vả c&ugrave;ng với gia đ&igrave;nh ở Đồng Nai. Được bạn b&egrave; khuy&ecirc;n thi v&agrave;o trường S&acirc;n khấu điện ảnh, Trường Giang quyết định rời qu&ecirc; để l&ecirc;n S&agrave;i G&ograve;n học.</p>\n<p style=\"text-align: justify;\">Từ những vai quần ch&uacute;ng, Trường Giang bắt đầu thể hiện được t&agrave;i năng v&agrave; dần khẳng định m&igrave;nh ở trung t&acirc;m Nụ Cười Mới. Ở thời điểm đ&oacute;, Trường Giang cho biết th&ugrave; lao chỉ l&agrave; 50.000 đồng.</p>\n<p style=\"text-align: justify;\">Trong tiểu phẩm <em>Kh&oacute;</em>, Trường Giang g&acirc;y ấn tượng mạnh mẽ cho kh&aacute;n giả với h&igrave;nh ảnh &ocirc;ng gi&agrave; Quảng Nam kh&oacute; t&iacute;nh nhưng rất duy&ecirc;n d&aacute;ng, h&agrave;i hước. Sau vai diễn n&agrave;y, t&ecirc;n tuổi của Trường Giang bắt đầu được c&ocirc;ng ch&uacute;ng biết đến v&agrave; anh thực hiện nhiều minishow c&ugrave;ng c&aacute;c c&aacute;c nghệ sĩ nổi tiếng như Ho&agrave;i Linh, Ch&iacute; T&agrave;i, Trấn Th&agrave;nh...</p>\n<p style=\"text-align: justify;\">Hiện tại, th&ugrave; lao để mời được Trường Giang biểu diễn một tiết mục l&agrave; con số kh&aacute; khủng - từ 40 đến 60 triệu đồng. Ngo&agrave;i ra, c&aacute;c chương tr&igrave;nh m&agrave; Trường Giang l&agrave;m MC gi&uacute;p anh thu về 30 triệu đồng cho mỗi tập. Tất cả l&agrave; c&ograve;n chưa kể đến những thu nhập từ quảng c&aacute;o v&agrave; đ&oacute;ng phim.</p>\n<p style=\"text-align: justify;\">Trường Giang b&acirc;y giờ đang l&agrave; một trong những nghệ sĩ h&agrave;i đắt show nhất hiện tại. Kh&ocirc;ng phải v&ocirc; duy&ecirc;n v&ocirc; cớ m&agrave; bộ phim <em>49 ng&agrave;y</em> do Trường Giang thủ vai ch&iacute;nh đ&atilde; đem về doanh thu 15 tỷ đồng sau 3 ng&agrave;y c&ocirc;ng chiếu. Như vậy, Trường Giang ho&agrave;n to&agrave;n c&oacute; thể thu về cho m&igrave;nh hơn 1 tỷ đồng cho mỗi th&aacute;ng l&agrave;m việc.</p>\n<p><strong>NSƯT Đức Hải</strong></p>\n<table class=\"picture\" style=\"height: 804px;\" width=\"529\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"pic\"><img src=\"http://img.v3.news.zdn.vn/w660/Uploaded/ycgmvvbt/2016_01_10/duchai.jpg\" alt=\"C&aacute;t-x&ecirc; một đ&ecirc;m của danh h&agrave;i Việt đủ sống một đời?\" width=\"523\" height=\"785\" /></td>\n</tr>\n<tr>\n<td class=\"pCaption caption\">\n<p><span style=\"color: #808080;\">NSƯT Đức Hải chịu &aacute;p lực lớn kinh tế v&igrave; anh c&oacute; tới 4 đứa con trong đ&oacute; lần thứ 2 l&agrave; sinh 3.</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<p style=\"text-align: justify;\">NSƯT Đức Hải được biết đến l&agrave; một diễn vi&ecirc;n h&agrave;i mang lại tiếng cười cho kh&aacute;n giả với lối diễn xuất h&agrave;i hước, d&iacute; dỏm. Tuy nhi&ecirc;n trong v&agrave;i năm trở lại đ&acirc;y, anh kh&aacute; im tiếng khi phải tập trung lo cho gia đ&igrave;nh v&agrave; c&aacute;c c&ocirc;ng việc l&agrave;m th&ecirc;m b&ecirc;n ngo&agrave;i. Mặc d&ugrave; hoạt động kh&aacute; im tiếng nhưng Đức Hải tiết lộ rằng mức c&aacute;t-x&ecirc; của anh vẫn rất cao.</p>\n<p style=\"text-align: justify;\">\"Show n&agrave;o mời t&ocirc;i m&agrave; c&aacute;t-x&ecirc; dưới 2.000 USD, chưa kể thuế l&agrave; t&ocirc;i kh&ocirc;ng l&agrave;m. Nếu t&ocirc;i l&agrave;m 2,3 show một th&aacute;ng với mức c&aacute;t-x&ecirc; đ&oacute; th&igrave; cuộc sống của t&ocirc;i ổn định, nhưng nếu c&aacute;t-x&ecirc; thấp hơn, một th&aacute;ng t&ocirc;i phải chạy mấy chục show, li&ecirc;n tục 30 ng&agrave;y th&igrave; đ&oacute; l&agrave; t&ocirc;i đang b&agrave;o m&ograve;n cơ thể, kh&ocirc;ng c&oacute; thời gian d&agrave;nh cho cho gia đ&igrave;nh. Đ&oacute; l&agrave; lối đi của t&ocirc;i, nhưng lựa chọn như thế n&agrave;o l&agrave; chuyện của kh&aacute;ch h&agrave;ng, thực tế t&ocirc;i vẫn c&oacute; rất nhiều lời mời biểu diễn. Cũng c&oacute; những chương tr&igrave;nh t&ocirc;i kh&ocirc;ng lấy c&aacute;t-x&ecirc;, t&ugrave;y t&acirc;m v&agrave;o nh&agrave; sản xuất v&agrave; t&ocirc;i sẽ đ&oacute;ng g&oacute;p v&agrave;o th&agrave;nh c&ocirc;ng của chương tr&igrave;nh.\" - nghệ sĩ Đức Hải thẳng thắn chia sẻ.</p>\n<p style=\"text-align: justify;\">Nam nghệ sĩ cho hay, sau khi trở th&agrave;nh bố của bốn đứa con, anh chịu &aacute;p lực lớn về kinh tế. Anh kh&ocirc;ng ngại ra gi&aacute; cao trong hoạt động nghệ thuật để c&oacute; tiền lo cho gia đ&igrave;nh.&nbsp;</p>\n<p style=\"text-align: right;\"><em>Theo K.N/B&aacute;o Gia Đ&igrave;nh &amp; X&atilde; Hội</em></p>\n</div>','Cát-xê một đêm của danh hài Việt đủ sống một đời','','',0,'http://tamdiem.com.vn/sources/images/hoailinh.jpg','',0,0,1,0,495,0,1452485693,1452485873,1),(148,'Bật mí thông tin về nữ diễn viên Divyanka Tripathi ','bat-mi-thong-tin-ve-nu-dien-vien-divyanka-tripathi-',16,'news_item','nữ diễn viên Divyanka Tripathi, Cinestars Ki Khoj, Miss Bhopal 2005, ','Divyanka Tripathi từng muốn trở thành sĩ quan quân đội\nĐó có thể là lý do tại sao cô ấy đã hoàn thành khoá học leo núi tại Học Viện Nehru, Uttarkashi – một trong những trường dạy leo núi tốt nhất ở Ấn Đô. Ishita Bhalla của Hạnh Phúc Muộn Màng cũng đã giành được huy chương vàng của cuộc thi bắn súng trường.\n','Divyanka Tripathi từng muốn trở thành sĩ quan quân đội\nĐó có thể là lý do tại sao cô ấy đã hoàn thành khoá học leo núi tại Học Viện Nehru, Uttarkashi – một trong những trường dạy leo núi tốt nhất ở Ấn Đô. Ishita Bhalla của Hạnh Phúc Muộn Màng cũng đã giành được huy chương vàng của cuộc thi bắn súng trường.\n','<p><strong>Divyanka Tripathi từng muốn trở th&agrave;nh sĩ quan qu&acirc;n đội</strong></p>\n<p style=\"text-align: justify;\">Đ&oacute; c&oacute; thể l&agrave; l&yacute; do tại sao c&ocirc; ấy đ&atilde; ho&agrave;n th&agrave;nh kho&aacute; học leo n&uacute;i tại Học Viện Nehru, Uttarkashi &ndash; một trong những trường dạy leo n&uacute;i tốt nhất ở Ấn Đ&ocirc;. Ishita Bhalla của Hạnh Ph&uacute;c Muộn M&agrave;ng cũng đ&atilde; gi&agrave;nh được huy chương v&agrave;ng của cuộc thi bắn s&uacute;ng trường.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyanka-tripathi-early-pics-rifle-shooting.jpg\" alt=\"\" width=\"550\" height=\"430\" /></p>\n<p style=\"text-align: justify;\"><strong>Nhưng sau đ&oacute;, c&ocirc; lại trở th&agrave;nh người chiến thắng trong chương tr&igrave;nh truyền h&igrave;nh Cinestars Ki Khoj</strong></p>\n<p style=\"text-align: justify;\">Cinestars Ki Khoj l&agrave; một chương tr&igrave;nh để c&aacute;c diễn vi&ecirc;n thể hiện t&agrave;i năng với giải thưởng l&agrave; vai ch&iacute;nh trong một bộ phim. Divyanka Tripathi l&agrave; người chiến thắng trong chương tr&igrave;nh ở khu vực Bhopal. Đồng nghiệp v&agrave; cũng l&agrave; bạn trai cũ của c&ocirc; Ssharad Malhotra được chọn từ khu vực Kolkata v&agrave; Ankita Lokhande l&agrave; đại diện cho khu vực Nagpur. Cả ba đều đ&atilde; thể hiện rất tốt.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyanka-tripathi-zee-cine-star-ki-khoj.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p style=\"text-align: justify;\"><strong>Divyanka đ&atilde; gi&agrave;nh danh hiệu Miss Bhopal 2005 v&agrave; Miss Beautiful Skin</strong></p>\n<p style=\"text-align: justify;\">Ở Bhopal, Divyanka đ&atilde; bắt đầu l&agrave;m người mẫu. C&ocirc; ấy đ&atilde; đăng quang Miss Bhopal v&agrave;o năm 2005. Hai năm trước đ&oacute;, Divyanka đ&atilde; gi&agrave;nh danh hiệu Miss Beautiful Skin trong cuộc thi \'Teen Queen Contest \"của Zee.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyanka-tripathi-modelling.jpg\" alt=\"\" width=\"550\" height=\"380\" /></p>\n<p style=\"text-align: justify;\"><strong>Divyanka đ&atilde; khởi nghiệp với Doordarshan v&agrave; Akash Vani</strong></p>\n<p style=\"text-align: justify;\">Divyanka lần đầu ti&ecirc;n được ch&uacute; &yacute; trong phim Banoo Mai Teri Dulhan (tạm dịch: Em sẽ l&agrave; c&ocirc; d&acirc;u của anh) nhưng đ&acirc;y kh&ocirc;ng phải l&agrave; điểm khởi nghiệp của c&ocirc;. Divyanka bắt đầu sự nghiệp với Doordarshan v&agrave; Akash Vani. Với c&ocirc;, Cinestars Ki Khoj mang đến sự tự tin trong diễn xuất, Doordarshan v&agrave; Aakash Vani gi&uacute;p c&ocirc; được c&ocirc;ng nhận l&agrave; một diễn vi&ecirc;n.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//facts-divyanka-tripathi-ishita-iyer.jpg\" alt=\"\" width=\"550\" height=\"350\" /></p>\n<p style=\"text-align: justify;\"><strong>Vai diễn y&ecirc;u th&iacute;ch nhất của c&ocirc; l&agrave; Sharma trong phim Mrs and Mr.Allahabaadwalle</strong></p>\n<p style=\"text-align: justify;\">Divyanka Tripathi đ&atilde; đ&oacute;ng nhiều vai kh&aacute;c nhau như Meera/Radha trong phim Ssshh..Phir Koi Hai (2006), Vidya/Divya trong phim Banoo Mai Teri Dulhan (2006-09), Chinki trong phim Chintu Chinki Aur Ek Badi Si Love Story (2011-12), Nikita trong phim Teri Meri Love Stories (2012). Tuy nhi&ecirc;n, vai diễn y&ecirc;u th&iacute;ch nhất của c&ocirc; đ&oacute; l&agrave; Rashmi Sharma trong Mrs and Mr.Allahabaadwalle- một bộ phim truyền h&igrave;nh Ấn Độ được ph&aacute;t s&oacute;ng từ năm 2010 đển 2011 n&oacute;i về cuộc h&agrave;nh tr&igrave;nh h&ograve;a nhập của một gia đ&igrave;nh trung lưu ở thị trấn nhỏ khi họ chuyển đến Mumbai. Nh&acirc;n vật Rashmi Sharma l&agrave; h&igrave;nh mẫu của b&agrave; nội trợ Ấn Độ. Rashmi chưa bao giờ thấy th&agrave;nh phố lớn v&agrave; đặt niềm tin ho&agrave;n to&agrave;n v&agrave;o chồng m&igrave;nh.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyanka-tripathi-comedy-pics.jpg\" alt=\"\" width=\"550\" height=\"630\" /></p>\n<p style=\"text-align: justify;\"><strong>Nếu c&oacute; cơ hội, c&ocirc; muốn h&oacute;a th&acirc;n th&agrave;nh một người t&acirc;m thần hoặc một cảnh s&aacute;t tr&ecirc;n truyền h&igrave;nh</strong></p>\n<p style=\"text-align: justify;\">Thật l&agrave; ngạc nhi&ecirc;n. Hy vọng một số nh&agrave; sản xuất sẽ biết được điều n&agrave;y v&agrave; cho c&ocirc; ấy thử vai. Chắc chắn người phụ nữ xinh đẹp sẽ ho&agrave;n th&agrave;nh tốt vai diễn th&uacute; vị n&agrave;y.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source//divyanka-tripathi-western-outfit.jpg\" alt=\"\" width=\"550\" height=\"630\" /></p>\n<h5><strong>Th&ocirc;ng tin th&ecirc;m về Divyanka Tripathi</strong></h5>\n<p style=\"text-align: justify;\">C&ocirc; ấy kh&ocirc;ng chỉ l&agrave; người ăn chay trong phim như Ishita Bhalla m&agrave; cả trong cuộc sống ngo&agrave;i đời. Nữ diễn vi&ecirc;n xinh đẹp cũng th&iacute;ch thưởng thức một số kem v&agrave; chocolate.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/tinymce/source/divyanka-tripathi-veg.jpg\" alt=\"\" width=\"550\" height=\"700\" /></p>\n<p style=\"text-align: justify;\">C&ocirc; ấy rất th&iacute;ch m&agrave;u trắng.</p>\n<p style=\"text-align: justify;\">Địa điểm mua sắm y&ecirc;u th&iacute;ch của c&ocirc; ấy l&agrave; Lokhandwala, Mumbai.</p>\n<p style=\"text-align: justify;\">C&ocirc; ấy l&agrave; fan trung th&agrave;nh của Lakme.</p>\n<p style=\"text-align: right;\"><em>Kh&aacute;nh Linh tổng hợp</em></p>\n<p>&nbsp;</p>','Bật mí thông tin về nữ diễn viên Divyanka Tripathi ','','',0,'http://tamdiem.com.vn/sources/images/divyanka-tripathi-in-saree.jpg','',0,0,0,0,183,0,1452765664,1452767041,1),(149,'Thời tiết','thoi-tiet',0,'cate_news','Thời tiết','Thời tiết','','','Thời tiết','','',0,'','',0,0,0,0,4,0,1453865020,1453865020,1),(150,'Dự báo thời tiết ngày 27/01/2016','du-bao-thoi-tiet-ngay-27-01-2016',149,'news_item','Dự báo thời tiết','Không khí lạnh có cường độ suy yếu dần. Khu vực Nam Bộ nằm ở rìa phía nam của khối khí lạnh này. Do vậy, thời tiết không mưa, ngày nắng và nhiệt độ có khuynh hướng nhích nhẹ lên. Tuy nhiên vào ban đêm và sáng sớm, nền nhiệt vẫn phổ biến dưới 23 độ, trong đó một số nơi ở miền Đông trời lạnh với nền nhiệt dưới 20 độ.','Không khí lạnh có cường độ suy yếu dần. Khu vực Nam Bộ nằm ở rìa phía nam của khối khí lạnh này. Do vậy, thời tiết không mưa, ngày nắng và nhiệt độ có khuynh hướng nhích nhẹ lên. Tuy nhiên vào ban đêm và sáng sớm, nền nhiệt vẫn phổ biến dưới 23 độ, trong đó một số nơi ở miền Đông trời lạnh với nền nhiệt dưới 20 độ.','<p style=\"text-align: justify;\">Kh&ocirc;ng kh&iacute; lạnh c&oacute; cường độ suy yếu dần. Khu vực Nam Bộ nằm ở r&igrave;a ph&iacute;a nam của khối kh&iacute; lạnh n&agrave;y. Do vậy, thời tiết kh&ocirc;ng mưa, ng&agrave;y nắng v&agrave; nhiệt độ c&oacute; khuynh hướng nh&iacute;ch nhẹ l&ecirc;n. Tuy nhi&ecirc;n v&agrave;o ban đ&ecirc;m v&agrave; s&aacute;ng sớm, nền nhiệt vẫn phổ biến dưới 23 độ, trong đ&oacute; một số nơi ở miền Đ&ocirc;ng trời lạnh với nền nhiệt dưới 20 độ.</p>\n<p>&nbsp;</p>\n<p><iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.youtube.com/embed/KJgRsnUxRh8\" width=\"708\" height=\"439\"></iframe></p>','Dự báo thời tiết ngày 27/01/2016','','',0,'http://tamdiem.com.vn/sources/images/DBTT_1.JPG','',0,0,1,0,1,0,1453865560,1453866222,1);

